REPLACE INTO `stardew_animal` (`id`, `sys_code`, `data_mode`, `name_ch`, `buy_price`, `sell_price`, `need_build`, `animal_output`, `remark`, `obtain`) VALUES
	(1, 'AN0001', 1, '鸡', '800', '1,040', '鸡舍', '蛋，大鸡蛋', '精心培育的成年母鸡每天都能下蛋。饲养在鸡舍里。\n\n可以在玛妮的牧场花800金购买一只鸡。', '-'),
	(2, 'AN0002', 1, '金色的鸡', '0', '1,040', '大鸡舍', '金蛋 ', '精心培育的成年母鸡每天都能下蛋。饲养在鸡舍里。\n\n游戏完成度达到百分百时，在齐先生处用100齐钻交换金蛋。\n', '-'),
	(4, 'AN0005', 1, '牛', '1,500', '1,950', '畜棚', '牛奶，大壶牛奶', '成年奶牛每天都能产奶。需要奶桶来盛这些奶。饲养在畜棚里。\n\n可以从玛妮的牧场花1,500金购买。', '-'),
	(5, 'AN0006', 1, '山羊', '4,000', '5,200', '大畜棚', '羊奶，大瓶羊奶', '快乐的成年山羊每隔一天都会产奶。需要奶桶来盛这些奶。饲养在畜棚里。\n\n可以从玛妮的牧场花4,000金购买。', '-'),
	(6, 'AN0009', 1, '虚空鸡', '0', '1,040', '大鸡舍', '虚空蛋', '精心培育的成年母鸡每天都能下蛋。饲养在鸡舍里。\n\n虚空蛋可从玩家睡觉后发生的随机事件中获得，亦可从旅行货车或科罗布斯处购得。', '-'),
	(7, 'AN0008', 1, '鸵鸟', '0', '20,800', '畜棚', '鸵鸟蛋 ', '玩家最开始可以通过日记残页#10或是在火山地牢箱子的随机掉落中获得鸵鸟蛋。', '-'),
	(8, 'AN0011', 1, '猪', '16,000', '20,800', '高级畜棚', '松露 ', '可从玛妮的牧场以16,000金购买，成年的猪在寻找松露方面训练有素！（需要被放出畜棚）', '-'),
	(9, 'AN0010', 1, '鸭', '1,200', '1,560', '大鸡舍', '鸭蛋，鸭毛', '快乐的成年鸭子每隔一天都下鸭蛋。饲养在鸡舍里。\n\n可从玛妮的牧场以1,200金购买。', '-'),
	(10, 'AN0004', 1, '绵羊', '8,000', '10,400', '高级畜棚', '动物毛 ', '成年绵羊身上的羊毛可以剪下来。与主人关系密切的绵羊长毛更快。需要一把剪刀来剪羊毛。饲养在畜棚里。\n\n可从玛妮的牧场以8,000金购买。', '-'),
	(11, 'AN0003', 1, '恐龙', '0', '1,278', '大鸡舍', '恐龙蛋', '恐龙蛋可通过钓鱼宝箱、挖掘远古斑点、消灭霸王喷火龙、在骷髅洞穴的史前层中采集、电影院的抓娃娃机等方式获得。', '-'),
	(12, 'AN0007', 1, '兔子', '8,000', '10,400', '高级鸡舍', '动物毛，兔子的脚', '毛绒绒的兔子！它们每隔几天就脱下珍贵的毛。饲养在鸡舍里。\n\n可从玛妮的牧场以8,000金购买。', '-'),
	(14, 'AN0014', 1, '猫', '40,000', '0', '宠物碗', '-', '玩家在创建人物时可以领养一只猫或一只狗。（所有的猫都可以被戴上帽子。）\n\n玛妮‏‎在你赚到1,000金后，将在春季的第一个晴天的周三或周五的早晨在门外带着狗或猫过来找你。\n你必须在上午6点至9:30之间走出农舍以触发事件（如果你在20日之前没有赚得足够的金钱，那么事件会无条件触发）。\n是否留下宠物是可选的（玛妮‏‎会问你是否想留下）。\n当你选择留下它时，你可以选择您想要的宠物名称（如果当玛妮带着宠物来找你时你还没遇到过她，她会在会面时做自我介绍。跳过剧情会自动领养宠物）。\n\n在初始宠物达到最大好感度，或在第二年还没有宠物的情况下，玩家可在玛妮的牧场购买宠物饲养证以领养新的宠物。\n', '-'),
	(15, 'AN0013', 1, '狗', '40,000', '0', '宠物碗', '-', '玩家在创建人物时可以领养一只猫或一只狗。（所有的狗都可以被戴上帽子。）\n\n玛妮‏‎在你赚到1,000金后，将在春季的第一个晴天的周三或周五的早晨在门外带着狗或猫过来找你。\n你必须在上午6点至9:30之间走出农舍以触发事件（如果你在20日之前没有赚得足够的金钱，那么事件会无条件触发）。\n是否留下宠物是可选的（玛妮‏‎会问你是否想留下）。\n当你选择留下它时，你可以选择您想要的宠物名称（如果当玛妮带着宠物来找你时你还没遇到过她，她会在会面时做自我介绍。跳过剧情会自动领养宠物）。\n\n在初始宠物达到最大好感度，或在第二年还没有宠物的情况下，玩家可在玛妮的牧场购买宠物饲养证以领养新的宠物。\n', '-'),
	(16, 'AN0015', 1, '乌龟', '60,000', '0', '宠物碗', '-', '乌龟不能戴帽子。\n\n在初始宠物达到最大好感度，或在第二年还没有宠物的情况下，玩家可在玛妮的牧场购买宠物饲养证以领养新的宠物。\n\n海龟有两种种类，第一种为每只60,000金，另一种为每只500,000金。\n\n玩家需要使用木匠的商店为额外的宠物建造宠物碗，否则宠物会感到不安。', '-'),
	(17, 'AN0016', 1, '马', '0', '0', '马厩', '-', '-', '每个马厩自带一匹马');
