	(1, 'AR0055', 5, '山脊森林', ''),
	(2, 'AR0029', 2, '高地', '高地是一个特殊区域\r\n需要解锁兰斯，且好感达到2心\r\n完成“马龙的船”任务后解锁'),
	(3, 'AR0043', 2, '顶峰', '顶峰是位于铁路北面的区域 \r\nSVE中完成“山顶巨石”任务才能解锁'),
	(4, 'AR0049', 2, '城堡村', '城堡村位于加尔朵拉大陆\r\n是一座充满探险家的城市\r\n该地点处于开发中……'),
	(5, 'AR0002', 1, '巴士站', '巴士站是靠近星露谷中心的一个区域\r\n也是玩家开始游戏后第一个到达的地方'),
	(6, 'AR0023', 2, '海鸥桥', '海鸥桥位于鹈鹕镇东部\r\n是鹈鹕镇的入口。可以在这里钓鱼\r\n'),
	(7, 'AR0065', 5, '山脊池塘', ''),
	(8, 'AR0015', 1, '顶峰', '顶峰是位于铁路北面的区域\r\n需要达到完美成就才会开放'),
	(9, 'AR0031', 2, '精灵泉', '精灵泉位于西部森林东南角\r\n完成“魔法墨水”任务后解锁\r\n非常漂亮的一个地方'),
	(10, 'AR0042', 2, '秘密森林', '秘密森林位于煤矿森林的西北角\r\n有许多大树桩\r\nSVE中新增了一个小型迷宫和采集点'),
	(11, 'AR0062', 5, '山顶农场', ''),
	(12, 'AR0046', 2, '姜岛-北部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(13, 'AR0017', 2, '鹈鹕镇', '鹈鹕镇位于星露谷\r\n大部分的村民都在此生活工作和进行社交\r\nSVE中新增了詹金斯家和鹈鹕雕像等'),
	(14, 'AR0007', 1, '下水道', '下水道必须得到生锈的钥匙解锁\r\n可以从鹈鹕镇的南方或煤矿森林南方的下水道口进入'),
	(15, 'AR0036', 2, '塘德林郊区', '塘德林郊区属于格兰普顿\r\n需要索菲娅的8心事件解锁'),
	(16, 'AR0027', 2, '海滩', '海滩位于鹈鹕镇的南部\r\n是个钓鱼的好去处\r\nSVE中它可往西通往蓝月亮葡萄园'),
	(17, 'AR0039', 2, '边远森林', '边远森林是星露谷西北部的一个郊区\r\nSVE中这里可以通往魔法森林'),
	(18, 'AR0040', 2, '下水道', '下水道必须得到生锈的钥匙解锁\r\n可以从鹈鹕镇的南方或煤矿森林南方的下水道口进入\r\nSVE中需要触发马龙“下水道钥匙”事件后才可进入'),
	(19, 'AR0006', 1, '煤矿森林', '煤矿森林位于星露谷的西南部\r\n是一片广袤的森林'),
	(20, 'AR0019', 2, '巴士站', '巴士站是靠近星露谷中心的一个区域\r\n也是玩家开始游戏后第一个到达的地方\r\nSVE中巴士站下放多了一条路'),
	(21, 'AR0070', 1, '巫婆沼泽', '沼泽传送阵位于铁路地区的东北部的洞穴中\r\n女巫小屋内有三个暗黑神殿\r\n虚空蛋黄酱可以在巫婆沼泽通过钓鱼获得(虚空蛋黄酱每个存档只能钓到一次)'),
	(22, 'AR0045', 2, '姜岛-西部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿\r\nSVE中新增了一个前往寓言礁的传送阵，需要兰斯8心事件解锁'),
	(23, 'AR0010', 1, '姜岛-东部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(24, 'AR0032', 2, '探险家公会', '探险家公会位于深山最深处\r\n与原版不同的是，它变成一个独立的区域'),
	(25, 'AR0044', 2, '姜岛-南部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(26, 'AR0030', 2, '旧社区花园', '旧社区花园属于鹈鹕镇，位于鹈鹕镇东南部\r\n需要游戏进入第二年才可以解锁'),
	(27, 'AR0003', 1, '海滩', '海滩位于鹈鹕镇的南部\r\n是个钓鱼的好去处'),
	(28, 'AR0052', 5, '缆车站', ''),
	(29, 'AR0004', 1, '沙漠', '卡利科沙漠是星露谷西北方向上的一个偏远区域\r\n这里的天气永远晴朗，并且四季如一'),
	(30, 'AR0035', 2, '格兰普顿平原', '格兰普顿平原位于海鸥桥东边\r\n它是一个可选的地图，需要额外安装模组\r\n在SVE下载界面下方可以找到'),
	(31, 'AR0025', 2, '祝尼魔森林', '祝尼魔森林是一个特殊区域\r\n解锁社区中心后可以通过煤矿森林西南部的传送阵进入\r\nJoja线无法进入该区域'),
	(32, 'AR0041', 2, '蓝月亮葡萄园', '蓝月亮葡萄园位于沙滩和森林之间\r\n它之前的主人是索菲娅的父母，罗伯特和爱丽丝\r\n现在的主人是索菲娅\r\n'),
	(33, 'AR0026', 2, '沙漠', '卡利科沙漠是星露谷西北方向上的一个偏远区域\r\n这里的天气永远晴朗，并且四季如一\r\nSVE中对该区域部分场景做了较大移动，例：三根柱子在沙漠最南方'),
	(34, 'AR0024', 2, '绯红荒原', '绯红荒原位于加尔朵拉大陆\r\n是一片由古代黑魔法创造出来的沙漠废土'),
	(35, 'AR0014', 1, '边远森林', '边远森林是星露谷西北部的一个郊区'),
	(36, 'AR0069', 2, '城堡村前哨站', '城堡村前哨是位于绯红荒原的一座瞭望塔\r\n由城堡村的探险家协会维护'),
	(37, 'AR0061', 5, '灵界', ''),
	(38, 'AR0048', 2, '姜岛-东南部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(39, 'AR0011', 1, '姜岛-北部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(40, 'AR0020', 2, '深山', '深山位于星露谷北部地区\r\nSVE中探险家公会不在属于这个区域'),
	(41, 'AR0033', 2, '西部森林', '西部森林位于煤矿森林西边\r\n有一大片松树，这里曾是元素战争的战场'),
	(42, 'AR0016', 1, '秘密森林', '秘密森林位于煤矿森林的西北角\r\n有许多大树桩'),
	(43, 'AR0059', 5, '悬崖', ''),
	(44, 'AR0005', 1, '深山', '深山位于星露谷北部地区'),
	(45, 'AR0071', 2, '巫婆沼泽', '沼泽传送阵位于铁路地区的东北部的洞穴中\r\n女巫小屋内有三个暗黑神殿\r\n虚空蛋黄酱可以在巫婆沼泽通过钓鱼获得(虚空蛋黄酱每个存档只能钓到一次)'),
	(46, 'AR0028', 2, '寓言礁', '寓言礁与姜岛同样位于宝石海\r\n它是宝石海的中心，非常靠近姜岛'),
	(47, 'AR0047', 2, '姜岛-东部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(48, 'AR0064', 5, '山脊瀑布', ''),
	(49, 'AR0021', 2, '煤矿森林', '煤矿森林位于星露谷的西南部\r\n是一片广袤的森林\r\nSVE中添加了费文黑尔农场，西部森林路口等'),
	(50, 'AR0056', 5, '山脊', ''),
	(51, 'AR0050', 5, '里奇赛德村', ''),
	(52, 'AR0009', 1, '姜岛-东南部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(53, 'AR0058', 5, '里奇赛德村下水道', ''),
	(54, 'AR0053', 5, '远足小径', ''),
	(55, 'AR0012', 1, '姜岛-南部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(56, 'AR0013', 1, '姜岛-西部', '姜岛属于芬群岛，位于宝石海\r\n是群岛中最大的一个岛屿'),
	(57, 'AR0066', 2, '魔法森林', '魔法森林位于边远森林北部\r\n一共可以开启7个传送阵\r\n可以在这里采集到露水浆果'),
	(58, 'AR0038', 2, '铁路', '铁路位于深山北方\r\n夏季3日后才可以进入\r\nSVE中新增了翡翠农场和探险家墓地'),
