	(153, 1, 'DE0006', '罐头瓶', '04', '-', '-', '[{"name": "配方来源", "value": "耕种（等级4）"}]', '将蔬菜转换成泡菜，水果为果酱。'),
	(154, 1, 'DE0021', '史莱姆压蛋器', '13', '-', '-', '[{"name": "配方来源", "value": "战斗（等级6）"}]', '将 100 团史莱姆泥压成一个史莱姆蛋。简直是奇迹！'),
	(155, 1, 'DE0015', '煤炭窑', '13', '-', '-', '[{"name": "配方来源", "value": "采集（等级2）"}]', '将 10 块木头转换成一块煤炭。'),
