	(1, 'MA0016', '放射性矿锭', 1, '3000', '熔炉', '', '祖祖城安委会已知它会对人体造成不可逆的伤害。', '01'),
	(2, 'MA0023', '金锭', 1, '250', '打造，熔炉，矿井的木箱和木桶，怪物掉落', '用途很广泛', '一条纯金锭。', '01'),
	(3, 'MA0026', '龙牙', 1, '500', '火山地牢，黄貂鱼鱼塘', '', '传言说这是古代巨蛇的牙。它的牙釉质是由纯铱构成的！', '01'),
	(4, 'MA0013', '骨头碎片', 1, '12', '骷髅，熔岩潜伏怪，远古斑点，骨头矿', '', '一小块骨头。', '01'),
	(5, 'MA0025', '精炼石英', 1, '50', '熔炉', '', '一种纯度更高的石英。', '01'),
	(6, 'MA0011', '硬木', 1, '15', '采集，木跃鱼（鱼塘），桃花心木树', '用途很广泛', '一种特殊的木材，有更高的强度和观赏性。', '01'),
	(7, 'MA0002', '虫肉', 1, '8', '臭虫，苍蝇，蛆，突变苍蝇，突变蛆', '', '一小团多汁的虫肉。', '02'),
	(8, 'MA0022', '铁锭', 1, '120', '打造，熔炉，暗影狂徒，暗影萨满', '用途很广泛', '一条纯铁锭。', '01'),
	(9, 'MA0010', '石头', 1, '2', '岩石，木匠的商店，钓鱼宝箱，沙漠节', '用途很广泛', '一种常见的材料，在加工和建筑中有很广泛的用途', '01'),
	(10, 'MA0003', '蝙蝠翅膀', 1, '15', '蝙蝠', '', '这个素材出奇的精致。', '02'),
	(11, 'MA0024', '铱锭', 1, '1000', '熔炉，暗影狂徒，暗影萨满，铱蝠，紫色史莱姆', '用途很广泛', '一条纯铱锭。', '01'),
	(12, 'MA0004', '太阳精华', 1, '40', '幽灵，金属大头，木乃伊，鱿鱼娃', '', '发光的表面摸起来很温暖。', '02'),
	(13, 'MA0007', '火山晶石', 1, '50', '姜岛，黄貂鱼鱼塘', '', '你可以从石头里感受到温度。', '01'),
	(14, 'MA0012', '粘土', 1, '20', '远古斑点，晶球，粘土矿', '', '用于加工和建筑。', '01'),
	(15, 'MA0014', '纤维', 1, '1', '杂草，荒野石魔，沙漠商人', '用途很广泛', '源自植物的原生材料。', '01'),
	(16, 'MA0006', '木材', 1, '2', '树，木匠的商店，书摊老板，钓鱼宝箱，木跃鱼鱼塘，碎木机', '用途很广泛', '结实而有韧性的植物材料，有非常广泛的用途。', '01'),
	(17, 'MA0018', '铁矿石', 1, '10', '矿井，铁匠铺，钓鱼，晶球，淘金，旅行货车', '', '一种常见的矿石，能被熔炼成铁锭。', '01'),
	(18, 'MA0009', '电池组', 1, '500', '避雷针，太阳能板', '', '充满了宝贵的能量。', '01'),
	(19, 'MA0005', '虚空精华', 1, '50', '暗影狂徒，暗影萨满，飞蛇', '', '黑暗能量在其中涌动着。', '02'),
	(20, 'MA0001', '史莱姆泥', 1, '5', '史莱姆', '', '没有气味、发着微光的一团凝胶。', '01'),
	(21, 'MA0015', '放射性矿石', 1, '300', '特别任务“深处的危险”，激活挑战之神庙，特别任务“骷髅洞穴入侵”', '', '它散发出危险的能量……可以熔炼成矿锭。', '01'),
	(22, 'MA0020', '铱矿石', 1, '100', '采矿，完美雕像，钓鱼宝箱，岩浆晶球，万象晶球，大海参鱼塘，淘金，旅行货车', '', '一种有着很多稀奇属性的独特矿石。能被熔炼成铱锭。', '01'),
	(23, 'MA0019', '黄金矿石', 1, '25', '矿井，铁匠铺，钓鱼宝箱，冰封晶球，岩浆晶球，万象晶球，淘盘，旅行货车', '', '一种珍贵的矿石，能被熔炼成金锭。', '01'),
	(24, 'MA0017', '铜矿石', 1, '5', '矿井，铁匠铺，钓鱼，远古斑点，晶球，淘金，旅行货车', '', '一种常见的矿石，能被熔炼成铜锭。', '01'),
	(25, 'MA0008', '煤炭', 1, '15', '采矿，灰尘精灵，铁匠铺，煤炭窑，淘金，回收机，远古斑点，钓鱼宝箱，晶球，书摊老板', '用途很广泛', '一种可燃的矿石，可用于加工和熔炼。', '01'),
	(26, 'MA0021', '铜锭', 1, '60', '熔炉，暗影狂徒，暗影萨满', '用途很广泛', '一条纯铜锭。', '01'),
	(32, 'MA0027', '史莱姆蛋', 1, '1000', '史莱姆压蛋器，史莱姆，鱼塘', '', '可以在史莱姆孵化器中孵化。', '02'),
	(33, 'MA0028', '苔藓', 1, '5', '树 ，杂草(仅限苔雨期间)', '', '生长在阴暗处的特殊植物。', '01');
