REPLACE INTO `stardew_note` (`id`, `rel_type`, `sys_code`, `title`, `type`, `content`, `image_mode`) VALUES
	(1366, 'fish', 'FH0129', '', '01', '<p><br></p><p><strong>虾虎鱼</strong>是一种可以在任何季节在煤矿森林南部的瀑布底部捕获的鱼。 在正确的区域钓鱼且不使用训练用鱼竿时，有15%的机会钓到虾虎鱼。如果使用珍稀诱钩，几率增加到30%，如果使用虾虎鱼诱饵，几率增加到35%。同时使用珍稀诱钩和虾虎鱼诱饵会效果叠加，有55%的几率钩住虾虎鱼。</p><p>威利对<strong>虾虎鱼</strong>的提示:</p>', NULL),
	(1367, 'fish', 'FH0129', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1368, 'fish', 'FH0129', '裁制', '01', '<p>虾虎鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。</p>', NULL),
	(1369, 'fish', 'FH0129', '鱼塘', '01', '<p>虾虎鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1370, 'fish', 'FH0070', '', '01', '<p><br></p><p><strong>牡蛎</strong>是一种鱼，可以在任何季节使用蟹笼在海洋中找到。它也可以从海滩采集。它也可能出现在旅行货车中，售价为120g ~ 1,000。像其他从蟹笼中捕获的鱼一样，牡蛎不能直接食用，但可以用于烹饪。</p>', NULL),
	(1371, 'fish', 'FH0070', '收集包', '01', '<p>牡蛎是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1372, 'fish', 'FH0070', '裁制', '01', '<p>牡蛎可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。牡蛎可用于染色。放置在缝纫机内时，牡蛎可用作<strong>灰色</strong>染料。</p>', NULL),
	(1373, 'fish', 'FH0070', '鱼塘', '01', '<p>牡蛎可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1374, 'fish', 'FH0070', '任务', '01', '<ul><li>章鱼、虹鳟鱼、幽灵鱼以及大海参可能会在鱼塘扩容任务中要求 1 个牡蛎，以将鱼塘的容量从 3 增加到 5。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个牡蛎。</li></ul>', NULL),
	(1375, 'fish', 'FH0069', '', '01', '<p><br></p><p><strong>玉黍螺</strong>是一种鱼，在任何季节中都可以在淡水中使用蟹笼找到。宠物猫达到最大好感度后，每天第一次被抚摸也有可能掉落玉黍螺。它也可能出现在旅行货车中，售价为100g ~ 1,000。像其他从蟹笼中捕获的鱼一样，玉黍螺不能直接食用，但可以用于烹饪。</p>', NULL),
	(1376, 'fish', 'FH0069', '收集包', '01', '<p>玉黍螺是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1377, 'fish', 'FH0069', '裁制', '01', '<p>玉黍螺可通过缝纫机使用布料制成雨衣。玉黍螺可用于染色。放置在染料罐内时，玉黍螺可用作<strong>蓝色</strong>染料。放置在缝纫机内时，玉黍螺还可用作<strong>青色</strong>染料。</p>', NULL),
	(1378, 'fish', 'FH0069', '鱼塘', '01', '<p>玉黍螺可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1379, 'fish', 'FH0069', '任务', '01', '<ul><li>鲷鱼、鲢鱼、河鲈、鲑鱼、西鲱、小嘴鲈鱼、大眼鱼和虾虎鱼可能在鱼塘扩容任务中要求一只玉黍螺，完成后将鱼塘容量从7增加到10。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供5只玉黍螺。</li></ul>', NULL),
	(1380, 'fish', 'FH0069', '注意', '01', '<p>玉黍螺本身的最高价值不超过 37g，将其制作为生鱼片能出售更高的价格75g。</p>', NULL),
	(1381, 'fish', 'FH0068', '', '01', '<p><br></p><p><strong>蜗牛</strong>是一种鱼，在任何季节中都可以在淡水中使用蟹笼找到。抚摸达到最大好感度的宠物猫也可能掉落。它也可能随机出现在旅行货车中，售价为195g ~ 1,000。像其他从蟹笼中捕获的鱼一样，蜗牛不能直接食用，但可以用于烹饪。</p><p>完成社区中心鱼缸收集包后，原本破损的鱼缸会变成一个可以使用的鱼缸，其中自带的三条鱼会包括一只蜗牛。</p>', NULL),
	(1382, 'fish', 'FH0068', '收集包', '01', '<p>蜗牛是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1383, 'fish', 'FH0068', '裁制', '01', '<p>蜗牛可通过缝纫机使用布料制成雨衣。蜗牛还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1384, 'fish', 'FH0068', '鱼塘', '01', '<p>蜗牛可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1385, 'fish', 'FH0068', '任务', '01', '<ul><li>在鱼塘扩容任务中，大头鱼、大嘴鲈鱼可能会要求玩家提供1只蜗牛，将池塘的容量从7增加到10。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供5只蜗牛。</li></ul>', NULL),
	(1386, 'fish', 'FH0067', '', '01', '<p><br></p><p><strong>虾</strong>是一种鱼，在任何季节中都可以在海洋中使用蟹笼找到。宠物猫好感度达到最大时也有可能会送给玩家一条虾。它也可能出现在旅行货车中，售价为180g ~ 1,000。像其他从蟹笼中捕获的鱼一样，虾不能直接食用，但可以用于烹饪。</p>', NULL),
	(1387, 'fish', 'FH0067', '收集包', '01', '<p>虾是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1388, 'fish', 'FH0067', '裁制', '01', '<p>虾可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。虾可用于染色。放置在染料罐内时，虾可用作<strong>红色</strong>染料。放置在缝纫机内时，虾还可用作<strong>粉色</strong>染料。</p>', NULL),
	(1389, 'fish', 'FH0067', '鱼塘', '01', '<p>虾可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1390, 'fish', 'FH0067', '任务', '01', '<p>虾不用于任何任务。</p>', NULL),
	(1391, 'fish', 'FH0066', '', '01', '<p><br></p><p><strong>蚌</strong>是一种鱼，在任何季节都可以在海洋使用蟹笼捕获。它也可以在海滩上采集获得。它还可以在姜岛西部的海滩上采掘蚌矿获得。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落蚌。或者在旅行货车中以100g ~ 1,000的价格买到。像其他从蟹笼中捕获的鱼一样，蚌不能直接食用，但可以用于烹饪。</p><p>可以在姜岛商人处以75个蚌换1个芒果幼苗。</p>', NULL),
	(1392, 'fish', 'FH0066', '收集包', '01', '<p>蚌是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1393, 'fish', 'FH0066', '裁制', '01', '<p>蚌可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。蚌可用于染色。放置在缝纫机内时，蚌可用作<strong>灰色</strong>染料。</p>', NULL),
	(1394, 'fish', 'FH0066', '鱼塘', '01', '<p>蚌可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1395, 'fish', 'FH0066', '任务', '01', '<ul><li>某些海鱼可能会在鱼塘扩容任务中要求 1 个蚌，以将鱼塘的容量从 7 增加到 10。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个蚌。</li></ul>', NULL),
	(1396, 'fish', 'FH0065', '', '01', '<p><br></p><p><strong>鸟蛤</strong>是一种鱼，可以在任何季节使用蟹笼在海洋中找到。它也可以从海滩采集。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落鸟蛤。它也可能出现在旅行货车中，售价为150g ~ 1,000。像其他从蟹笼中捕获的鱼一样，鸟蛤不能直接食用，但可以用于烹饪。</p>', NULL),
	(1397, 'fish', 'FH0065', '收集包', '01', '<p>鸟蛤是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1398, 'fish', 'FH0065', '裁制', '01', '<p>鸟蛤可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鸟蛤可用于染色。放置在缝纫机内时，鸟蛤可用作<strong>深灰色</strong>染料。</p>', NULL),
	(1399, 'fish', 'FH0065', '鱼塘', '01', '<p>鸟蛤可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1400, 'fish', 'FH0065', '任务', '01', '<ul><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个鸟蛤。</li></ul>', NULL),
	(1401, 'fish', 'FH0064', '', '01', '<p><br></p><p><strong>螃蟹</strong>是一种鱼，在任何季节都可以在海洋中用蟹笼捕获。也可以从岩石蟹（15%概率）或熔岩蟹（25%概率）掉落。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落螃蟹。它也可能出现在旅行货车中，售价为300g ~ 1,000。沙漠节期间艾利欧特会以 30 卡利科三花蛋的价格出售螃蟹（限5只）。像其他从蟹笼中捕获的鱼一样，螃蟹不能直接食用，但可以用于烹饪。</p>', NULL),
	(1402, 'fish', 'FH0064', '收集包', '01', '<p>螃蟹是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1403, 'fish', 'FH0064', '鱼塘', '01', '<p>螃蟹可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1404, 'fish', 'FH0064', '任务', '01', '<p>螃蟹不用于任何任务。</p>', NULL),
	(1405, 'fish', 'FH0064', '裁制', '01', '<p>螃蟹可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。螃蟹还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p>', NULL),
	(1406, 'fish', 'FH0064', '注意', '01', '<ul><li>从岩石蟹上掉落后，螃蟹会四处跳动直到被玩家捡起。</li></ul>', NULL),
	(1407, 'fish', 'FH0064', '趣闻', '01', '<ul><li>当鱼塘里的螃蟹到达 10 只后，下次玩家和威利说话可以得到一个珍珠。</li></ul>', NULL),
	(1408, 'fish', 'FH0063', '', '01', '<p><br></p><p><strong>小龙虾</strong>是一种鱼，在任何季节都可以在淡水中使用蟹笼捕获。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落小龙虾。它也可能出现在旅行货车中，售价为225g ~ 1,000。就像其他的在蟹笼中抓到的鱼一样，小龙虾不能直接食用，但可以用于烹饪。</p>', NULL),
	(1409, 'fish', 'FH0063', '收集包', '01', '<p>小龙虾是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1410, 'fish', 'FH0063', '裁制', '01', '<p>小龙虾可通过缝纫机使用布料制成雨衣。小龙虾可用于染色。放置在缝纫机内时，小龙虾可用作<strong>深灰色</strong>染料。</p>', NULL),
	(1411, 'fish', 'FH0063', '鱼塘', '01', '<p>小龙虾可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1412, 'fish', 'FH0063', '任务', '01', '<ul><li>某些鱼可能会在鱼塘扩容任务中要求 1 只小龙虾，以将鱼塘的容量从 7 增加到 10。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 只小龙虾。</li></ul>', NULL),
	(1413, 'fish', 'FH0062', '', '01', '<p><br></p><p><strong>蛤</strong>是一种鱼，可以在任何季节的海洋中使用蟹笼捕获。它也可以在海滩采集获得。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落蛤。偶尔会出现在旅行货车中，售价150g ~ 1,000。冬日星盛宴期间，皮埃尔的摊位可能会以500g的价格出售一个蛤。在挑战之神庙已激活，或处于“深处的危险”任务期间时，在矿井1~29层也有可能采集到蛤。</p>', NULL),
	(1414, 'fish', 'FH0062', '收集包', '01', '<p>蛤是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1415, 'fish', 'FH0062', '建筑', '01', '<p>从法师塔购买水之图腾柱需要10个蛤。</p>', NULL),
	(1416, 'fish', 'FH0062', '裁制', '01', '<p>蛤可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。蛤可用于染色。放置在染料罐内时，蛤可用作<strong>蓝色</strong>染料。放置在缝纫机内时，蛤还可用作<strong>淡青色</strong>染料。</p>', NULL),
	(1417, 'fish', 'FH0062', '鱼塘', '01', '<p>蛤可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1418, 'fish', 'FH0062', '任务', '01', '<ul><li>某些海鱼可能会在鱼塘扩容任务中要求 2 个蛤，以将鱼塘的容量从 5 增加到 7。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个蛤。</li></ul>', NULL),
	(1419, 'fish', 'FH0062', '趣闻', '01', '<ul><li>在先前版本中，艾芙琳讨厌蛤，但她还是有关于童年收集蛤的对话。随后版本更新使艾芙琳对蛤的态度由讨厌变成了喜欢。</li><li>1.6版本前，尽管它可以使用蟹笼捕获，但游戏并没有将蛤归类为“鱼”。因此，它不能用于可以使用任意鱼的食谱、不能放入鱼塘、不受渔夫或垂钓者职业加成，也不算入四个钓鱼相关的成就中。并且，在收集品页面中，蛤在“售出的物品”一类，而不是“鱼”类。</li></ul>', NULL),
	(1420, 'fish', 'FH0061', '', '01', '<p><br></p><p><strong>龙虾</strong>是一种鱼，在任何季节都可以在海洋中使用蟹笼捕获。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落龙虾。它也可能出现在旅行货车中，售价为360g ~ 1,000。无尽财富之雕像会在艾利欧特生日时（秋季5日）生产一个龙虾。</p><p>像其他从蟹笼中捕获的鱼一样，龙虾不能直接食用，但可以用于烹饪。</p>', NULL),
	(1421, 'fish', 'FH0061', '收集包', '01', '<p>龙虾是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(1422, 'fish', 'FH0061', '裁制', '01', '<p>龙虾可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。龙虾还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p>', NULL),
	(1423, 'fish', 'FH0061', '鱼塘', '01', '<p>龙虾可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1424, 'fish', 'FH0061', '任务', '01', '<ul><li>格斯会在“征集：龙虾”任务中于第二年秋季6日通过邮件向玩家要求一只龙虾，报酬为500g和1颗心的友谊点。</li></ul>', NULL),
	(1425, 'fish', 'FH0060', '', '01', '<p></p><p><b>放射性鲤鱼</b>是齐先生的特别任务“大家族”中的五种传说鱼类之一。它的售价、治疗效果和钓鱼地点均与变种鲤鱼相同，但只有在任务期间才能被钓到。</p>', NULL),
	(1426, 'fish', 'FH0060', '钓鱼地点', '01', '<p>放射性鲤鱼可以在下水道被钓到。</p>', NULL),
	(1427, 'fish', 'FH0060', '收集包', '01', '<p>放射性鲤鱼不用于任何收集包。</p>', NULL),
	(1428, 'fish', 'FH0060', '鱼塘', '01', '<p>放射性鲤鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为           （RGB 色值：50, 220, 110）。</p><h3>产物</h3><p> 放射性鲤鱼每天大约会产出 1.113 个鱼籽。</p><p><br/></p>', NULL),
	(1429, 'fish', 'FH0060', '任务', '01', '<p>放射性鲤鱼只能在齐先生的特别任务“大家族”期间被钓到。</p>', NULL),
	(1430, 'fish', 'FH0059', '', '01', '<p></p><p><b>小冰川鱼</b>是齐先生的特别任务“大家族”中的五种传说鱼类之一。它的售价、治疗效果和钓鱼地点均与冰川鱼相同，但只有在任务期间才能被钓到。</p>', NULL),
	(1431, 'fish', 'FH0059', '钓鱼地点', '01', '<p>小冰川鱼可以在煤矿森林被钓到。</p><p></p>', NULL),
	(1432, 'fish', 'FH0059', '收集包', '01', '<p>小冰川鱼不用于任何收集包。</p>', NULL),
	(1433, 'fish', 'FH0059', '鱼塘', '01', '<p>小冰川鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为           （RGB 色值：100, 240, 220）。</p><h3>产物</h3><p> 小冰川鱼每天大约会产出 1.178 个鱼籽。</p><p><br/></p>', NULL),
	(1434, 'fish', 'FH0059', '任务', '01', '<p>小冰川鱼只能在齐先生的特别任务“大家族”期间被钓到。</p>', NULL),
	(1435, 'fish', 'FH0058', '', '01', '<p><br></p><p><strong>传说之鱼二代</strong>是齐先生的特别任务“大家族”中的五种传说鱼类之一。它的售价、治疗效果和钓鱼地点均与传说之鱼相同，但只有在任务期间才能被钓到。</p>', NULL),
	(1436, 'fish', 'FH0058', '钓鱼地点', '01', '<p>传说之鱼二代可以在山区湖泊被钓到。</p>', NULL),
	(1437, 'fish', 'FH0058', '收集包', '01', '<p>传说之鱼二代不用于任何收集包。</p>', NULL),
	(1438, 'fish', 'FH0058', '鱼塘', '01', '<p>传说之鱼二代可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><h3>产物</h3><p> 传说之鱼二代每天大约会产出 0.518 个鱼籽。</p><p><br></p>', NULL),
	(1439, 'fish', 'FH0058', '任务', '01', '<p>传说之鱼二代只能在齐先生的特别任务“大家族”期间被钓到。</p>', NULL),
	(1440, 'fish', 'FH0056', '', '01', '<p></p><p><b>绯红鱼之子</b>属于属于传说鱼类二代，是齐先生的特别任务“大家族”中的传说鱼类的家族成员之一。</p><p>绯红鱼之子的售价、治疗效果、钓鱼地点和钓取条件均与绯红鱼相同，但只能在任务期间钓到，而且没有钓取数量的限制。</p><p>夜市的3天内整天无法钓取。</p>', NULL),
	(1441, 'fish', 'FH0056', '地图', '01', '<p>可在沙滩东侧找到绯红鱼之子。</p>', NULL),
	(1442, 'fish', 'FH0056', '收集包', '01', '<p>绯红鱼之子不用于任何收集包。</p>', NULL),
	(1443, 'fish', 'FH0056', '鱼塘', '01', '<p>绯红鱼之子可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为           （RGB 色值：230, 70, 110）。</p><h3>产物</h3><p> 绯红鱼之子每天大约会产出 0.827 个鱼籽。</p><p><br/></p>', NULL),
	(1444, 'fish', 'FH0056', '任务', '01', '<p>齐先生的特别任务“大家族”期间才能钓到绯红鱼之子。</p>', NULL),
	(1445, 'fish', 'FH0055', '', '01', '<p></p><p><b>变种鲤鱼</b>是一种鱼，在任何季节的下水道中都可以钓到。它是五条传说鱼类之一。每个玩家角色只能钓起一次。</p><p>钓起变种鲤鱼没有任何特殊要求。只要在下水道中，每次甩杆，变种鲤鱼都有10%的几率咬钩（如果使用了珍稀诱钩则提升到20%）。</p>', NULL),
	(1446, 'fish', 'FH0055', '地图', '01', '<p>变种鲤鱼可以在下水道中找到。</p>', NULL),
	(1447, 'fish', 'FH0055', '策略技巧', '01', '<p>捕获这条鱼的策略是停留在钓鱼框的中间，直到鱼开始停在其他地方（例如底部或顶部），然后再移动到那里。鱼会不断地上下移动，以摆脱玩家，因此停留在中间可以慢慢地捕获它。</p>', NULL),
	(1448, 'fish', 'FH0055', '收集包', '01', '<p>变种鲤鱼不用于任何收集包。</p>', NULL),
	(1449, 'fish', 'FH0055', '鱼塘', '01', '<p>变种鲤鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为           （RGB 色值：50, 220, 110）。</p><h3>产物</h3><p> 变种鲤鱼每天大约会产出 1.113 个鱼籽。</p><p><br/></p>', NULL),
	(1450, 'fish', 'FH0055', '任务', '01', '<p>变种鲤鱼不用于任何任务。</p>', NULL),
	(1451, 'fish', 'FH0055', '趣闻', '01', '<p>在钓上<b>变种鲤鱼</b>后找到威利并进行交谈，他会说以下话语。</p>', NULL),
	(1452, 'fish', 'FH0054', '', '01', '<p></p><p><b>冰川鱼</b>是一种可以在冬季的煤矿森林中钓到的鱼。它是五条传说鱼类之一。每个玩家角色只能钓起一次。</p><p>钓起冰川鱼所需的特殊要求是：</p><ul><li>钓鱼等级达到6（通过部分食物或附魔有“精通”的鱼竿带来的暂时钓鱼等级提升也算在内）。</li><li>玩家必须站在特定的位置：煤矿森林中一个形似“箭头”的岛的最南端。遗失之书《渔夫，第二幕》中提示了这一点。</li><li>钓鱼浮标必须位于钓鱼区至少为3的水中，即它必须在各个方向上与岸边（包括桥梁）至少相距4格。<li class="ql-indent-1">如果浮标抛进了钓鱼区为2的钓鱼气泡中，依然可以钓到冰川鱼。</li></li></ul><p>当以上条件都满足时，每次甩杆，冰川鱼都有50%的几率咬钩。不同于其他传说鱼类，使用珍稀诱钩不会增加它的上钩率。</p><p>如果使用了魔法鱼饵，不论什么季节，都可能在以上条件满足时钓到冰川鱼。</p>', NULL),
	(1453, 'fish', 'FH0054', '地图', '01', '<p>在冬季的煤矿森林中可以找到。</p><p></p>', NULL),
	(1454, 'fish', 'FH0054', '收集包', '01', '<p>冰川鱼不用于任何收集包。</p>', NULL),
	(1455, 'fish', 'FH0054', '鱼塘', '01', '<p>冰川鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为           （RGB 色值：100, 240, 220）。</p><h3>产物</h3><p> 冰川鱼每天大约会产出 1.178 个鱼籽。</p><p><br/></p>', NULL),
	(1456, 'fish', 'FH0054', '任务', '01', '<p>冰川鱼不用于任何任务。</p>', NULL),
	(1457, 'fish', 'FH0054', '趣闻', '01', '<p>在钓上冰川鱼后找到威利并进行交谈，他会说以下话语。</p>', NULL),
	(1458, 'fish', 'FH0053', '', '01', '<p><br></p><p><strong>传说之鱼</strong>是一种能在春季雨天的深山湖泊钓到的鱼。它是五条传说鱼类之一。每个玩家角色只能钓起一次。</p><p>钓起传说之鱼所需的特殊要求是：</p><ul><li>钓鱼等级达到10（通过部分食物或附魔有“精通”的鱼竿带来的暂时钓鱼等级提升也算在内）。</li><li>浮标必须位于钓鱼区为5的水中，即它必须在各个方向上与岸边（包括桥梁）至少相距5格。</li><li class="ql-indent-1">如果浮标抛进了钓鱼区为3的钓鱼气泡中，依然可以钓到传说之鱼。</li><li class="ql-indent-1">正如《传奇之鱼的秘密》中所建议的那样，山间湖泊中的木桩区域满足此要求。当然，湖中还有其他地方也可以。</li></ul><p>当以上条件都满足时，每次甩杆，传说之鱼都有10%的几率咬钩（如果使用了珍稀诱钩则是20%）。不同于《渔夫，第二幕》中提供的信息，想钓到传说之鱼，不需要先钓起过其它传奇鱼类。</p><p>尽管钓到传说之鱼后就不能再钓到它，但依然可以在单人游戏中获得多条传说之鱼。玩家使用挑战鱼饵钓鱼时最多可钓到3条传说之鱼；使用附魔捕鱼的淘盘在传说之鱼对应的水域淘金时可以无数量限制地获得传说之鱼（前提是玩家还未钓到它）。</p><p>如果使用了魔法鱼饵，不论天气或季节，都可能在以上条件满足时钓到传说之鱼。</p>', NULL),
	(1459, 'fish', 'FH0053', '地图', '01', '<p>传说之鱼可以在深山湖泊钓到。</p>', NULL),
	(1460, 'fish', 'FH0053', '收集包', '01', '<p>传说之鱼不用于任何收集包。</p>', NULL),
	(1461, 'fish', 'FH0053', '裁制', '01', '<p>传说之鱼可用于染色。放置在染料罐内时，传说之鱼可用作<strong>绿色</strong>染料。</p>', NULL),
	(1462, 'fish', 'FH0053', '鱼塘', '01', '<p>传说之鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为。</p><h3>产物</h3><p> 传说之鱼每天大约会产出 0.518 个鱼籽。</p><p><br></p>', NULL),
	(1463, 'fish', 'FH0053', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(1464, 'fish', 'FH0053', '趣闻', '01', '<p>在钓上传说之鱼后找到威利并进行交谈，他会说以下话语。</p><p><br></p><p>“谢天谢地……你竟然钓到“传奇之鱼”了，我没想到能在有生之年看到……嗯，小伙子/小姑娘，你成功了……你是星露谷难得一见的钓鱼好手……”</p>', NULL),
	(1465, 'fish', 'FH0052', '', '01', '<p><br></p><p><strong>鮟鱇鱼</strong>是一种能在秋季的鹈鹕镇中钓到的鱼，它是五条传说鱼类之一。每个玩家角色只能钓起一次。</p><p>钓起鮟鱇鱼所需的特殊要求是：</p><ul><li>钓鱼等级达到3（通过部分食物或附魔有“精通”的鱼竿带来的暂时钓鱼等级提升也算在内）。</li><li>玩家必须站在鹈鹕镇上河流的最北端（地图边缘15格以内）。这个范围包括一座独木桥、木桥以北的所有河岸，以及木桥向南3格范围内的河岸。</li><li>没有钓鱼区的限制，只要玩家站在上述范围内，钓鱼浮标可以在河流中的任意位置。</li></ul><p>当以上条件都满足时，每次甩杆，鮟鱇鱼都有20%的几率咬钩（如果使用了珍稀诱钩则是25%）。</p><p>如果使用了魔法鱼饵，不论什么季节，都可能在以上条件满足时钓到鮟鱇鱼。</p>', NULL),
	(1466, 'fish', 'FH0052', '分布图', '01', '<p>鮟鱇鱼可以在鹈鹕镇上Joja超市以北的河流中被找到。</p>', NULL),
	(1467, 'fish', 'FH0052', '收集包', '01', '<p>没有任何收集包需要鮟鱇鱼。</p>', NULL),
	(1468, 'fish', 'FH0052', '染色', '01', '<p>鮟鱇鱼可用于染色。鮟鱇鱼可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1469, 'fish', 'FH0052', '鱼塘', '01', '<p>鮟鱇鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（RGB 色值：255, 120, 0）。</p><h3>产物</h3><p> 鮟鱇鱼每天大约会产出 1.141 个鱼籽。</p><p><br></p>', NULL),
	(1470, 'fish', 'FH0052', '任务', '01', '<p>没有任务中需要鮟鱇鱼。</p>', NULL),
	(1471, 'fish', 'FH0052', '趣闻', '01', '<p>在钓上<strong>鮟鱇鱼</strong>后找到威利并进行交谈，他会说以下话语。</p><p>\n</p><p>“哦，你钓到鮟鱇鱼了啊？那么钓稀有鱼你算是入门了。”\n</p>', NULL),
	(1472, 'fish', 'FH0051', '', '01', '<p><br></p><p><strong>绯红鱼</strong>是一种夏季时可在沙滩钓到的鱼。绯红鱼是五条传说鱼类之一，每个存档只能钓到一次，合作模式下则为每个玩家一次。</p><p><strong>钓取绯红鱼需要满足以下特殊条件：</strong></p><ul><li>钓鱼技能达到5级（通过部分食物或附魔有“精通”的鱼竿带来的暂时钓鱼等级提升也算在内）。</li><li>玩家需要站在沙滩最东侧的海岸或码头上（位置如下图深绿色区域所示）。要到达该地点，玩家需要使用300木材修复桥梁，或购买第二次社区升级。</li><li>浮标必须落在钓鱼区至少为3的水域中，即距离陆地区域任何方向至少4格的位置。（位置如下图浅绿色区域所示）</li><li class="ql-indent-1">如果浮标抛进了钓鱼区为2的钓鱼气泡中（位置如下图橙色区域所示），依然可以钓到绯红鱼。</li></ul><p>满足上述所有条件后，玩家每一次抛竿都有18%的概率钓到绯红鱼（使用珍稀诱钩后可将概率提升至25%）。</p><p>如果使用了魔法鱼饵，不论什么季节，都可能在以上条件满足时钓到绯红鱼。</p>', NULL),
	(1473, 'fish', 'FH0051', '地图', '01', '<p>可在沙滩东侧找到绯红鱼。</p>', NULL),
	(1474, 'fish', 'FH0051', '收集包', '01', '<p>绯红鱼不用于任何收集包。</p>', NULL),
	(1475, 'fish', 'FH0051', '染色', '01', '<p>绯红鱼可用作染料罐和缝纫机的<strong>红色</strong>染料。</p>', NULL),
	(1476, 'fish', 'FH0051', '鱼塘', '01', '<p>绯红鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p><p>放入鱼塘后，鱼塘的颜色会变为。</p><h3>产物</h3><p> 绯红鱼每天大约会产出 0.827 个鱼籽。</p><p><br></p>', NULL),
	(1477, 'fish', 'FH0051', '任务', '01', '<p>绯红鱼不用于任何任务。</p>', NULL),
	(1478, 'fish', 'FH0051', '趣闻', '01', '<p>在钓上<strong>绯红鱼</strong>后找到威利并进行交谈，他会说以下话语。</p>', NULL),
	(1479, 'fish', 'FH0050', '', '01', '<p><br></p><p><strong>水滴鱼</strong>是一种鱼，出现在夜市，你可以在冬季节的夜市潜水艇通过钓鱼获得它。你可以在冬季节的夜市（冬季15-17）潜水艇中钓鱼来获得。它也可以在海滩西南角使用魔法鱼饵来捕获：在最左边的码头的西南角，面朝西即可。</p><p>在夜市的潜艇之旅中，有10%的几率上钩（如果使用珍稀诱钩，则为20%）。如果在海滩的西南角使用魔法鱼饵，有3.3%的几率上钩（如果使用珍稀诱钩，则为10.2%）。</p><p>水滴鱼和河豚是仅有的两种在钓鱼小游戏中运动模式为“漂浮型”的鱼。</p>', NULL),
	(1480, 'fish', 'FH0050', '地图', '01', '<p>水滴鱼可以在夜市（冬季15-17）期间的钓鱼潜艇中钓到。</p>', NULL),
	(1481, 'fish', 'FH0050', '收集包', '01', '<p>水滴鱼是鱼缸（重新混合后）的 钓鱼大师收集包中的一个选项。</p>', NULL),
	(1482, 'fish', 'FH0050', '裁制', '01', '<p>水滴鱼可通过缝纫机使用布料制成水滴鱼面具。水滴鱼可用于染色。放置在染料罐内时，水滴鱼可用作<strong>黄色</strong>染料。放置在缝纫机内时，水滴鱼还可用作<strong>沙色</strong>染料。</p>', NULL),
	(1483, 'fish', 'FH0050', '鱼塘', '01', '<p>水滴鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1484, 'fish', 'FH0050', '任务', '01', '<p>水滴鱼不用于任何任务。</p>', NULL),
	(1485, 'fish', 'FH0050', '注意', '01', '<ul><li>由于钓鱼潜艇的水池大小限制了它的钓鱼区等级，在海滩上使用魔法鱼饵钓鱼时水滴鱼的最大尺寸也只有66厘米。</li></ul>', NULL),
	(1486, 'fish', 'FH0049', '', '01', '<p><br></p><p><strong>幽灵鱼</strong>是一种鱼，出现在夜市，你可以在冬季节的夜市（冬季15-17）潜水艇中钓鱼来获得。它也可以在海滩西南角使用魔法鱼饵来捕获：在最左边的码头的西南角，面朝西即可。</p><p>在夜市的潜艇之旅中，有16%的几率上钩（如果使用珍稀诱钩，则为18%）。如果在海滩的西南角使用魔法鱼饵，有3.3%的几率上钩（如果使用珍稀诱钩，则为10.2%）。</p>', NULL),
	(1487, 'fish', 'FH0049', '地图', '01', '<p>幽灵鱼可以在夜市（冬季15-17）期间的钓鱼潜艇中钓到。</p>', NULL),
	(1488, 'fish', 'FH0049', '收集包', '01', '<p>幽灵鱼不用于任何收集包。</p>', NULL),
	(1489, 'fish', 'FH0049', '裁制', '01', '<p>幽灵鱼可通过缝纫机使用布料制成钓鱼帽。幽灵鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1490, 'fish', 'FH0049', '鱼塘', '01', '<p>幽灵鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1491, 'fish', 'FH0049', '任务', '01', '<p>幽灵鱼不用于任何任务。</p>', NULL),
	(1492, 'fish', 'FH0049', '注意', '01', '<ul><li>由于钓鱼潜艇的水池大小限制了它的钓鱼区等级，在海滩上使用魔法鱼饵钓鱼时幽灵鱼的最大尺寸也只有66厘米。</li></ul>', NULL),
	(1493, 'fish', 'FH0048', '', '01', '<p><br></p><p><strong>午夜鱿鱼</strong>是一种鱼，只出现在夜市（冬季 15-17），你可以在冬季节的夜市潜水艇通过钓鱼获得它。在海滩的西南角使用魔法鱼饵也可以钓到，具体方式为：从最左边的码头的尽头西南角面向西方钓鱼。</p><p>在夜市的潜艇中，有21%的几率钓到午夜鱿鱼（如果使用珍稀诱钩则为17%）。如果在海滩西南角使用魔法鱼饵，概率为3.3%（如果使用珍稀诱钩则为10.2%）。</p><p>当养在鱼塘时，午夜鱿鱼会产生鱿鱼墨汁而不是鱼籽。午夜鱿鱼鱼籽可在阅读海之宝石后于钓鱼宝箱中获得。</p>', NULL),
	(1494, 'fish', 'FH0048', '地图', '01', '<p>午夜鱿鱼可以在夜市（冬季15-17）期间的钓鱼潜艇中钓到。</p>', NULL),
	(1495, 'fish', 'FH0048', '收集包', '01', '<p>午夜鱿鱼不用于任何收集包。</p>', NULL),
	(1496, 'fish', 'FH0048', '裁制', '01', '<p>午夜鱿鱼可通过缝纫机使用布料制成钓鱼帽。午夜鱿鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1497, 'fish', 'FH0048', '鱼塘', '01', '<p>午夜鱿鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1498, 'fish', 'FH0048', '任务', '01', '<p>午夜鱿鱼不用于任何任务。</p>', NULL),
	(1499, 'fish', 'FH0048', '注意', '01', '<ul><li>由于钓鱼潜艇的水池大小限制了它的钓鱼区等级，在海滩上使用魔法鱼饵钓鱼时午夜鱿鱼的最大尺寸也只有66厘米。</li></ul>', NULL),
	(1500, 'fish', 'FH0047', '', '01', '<p><br></p><p><strong>黄貂鱼</strong>是一种可以在姜岛的海盗湾中发现的鱼。需要先解锁海岛度假村。</p><p>玩家可以在姜岛商人处用1个黄貂鱼交换1个桃花心木种子。</p>', NULL),
	(1501, 'fish', 'FH0047', '地图', '01', '<p>可以在海盗湾中钓上黄貂鱼。</p>', NULL),
	(1502, 'fish', 'FH0047', '收集包', '01', '<p>黄貂鱼不用于任何收集包。</p>', NULL),
	(1503, 'fish', 'FH0047', '裁制', '01', '<p>黄貂鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。黄貂鱼可用于染色。放置在染料罐内时，黄貂鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，黄貂鱼还可用作<strong>棕色</strong>染料。</p>', NULL),
	(1504, 'fish', 'FH0047', '鱼塘', '01', '<p>黄貂鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1505, 'fish', 'FH0047', '任务', '01', '<ul><li>姜岛解锁后，威利或许会在特别任务“热带鱼”中要求抓住5条黄貂鱼。</li></ul>', NULL),
	(1506, 'fish', 'FH0046', '', '01', '<p><br></p><p><strong>蓝铁饼鱼</strong>是一种鱼，可以在姜岛北部和西部的淡水中钓到。</p><p>每周六可在姜岛商人处用3个蓝铁饼鱼交换1个海洋地毯。</p>', NULL),
	(1507, 'fish', 'FH0046', '地图', '01', '<p>蓝铁饼鱼可以在姜岛北部和西部的淡水中钓到。</p>', NULL),
	(1508, 'fish', 'FH0046', '收集包', '01', '<p>蓝铁饼鱼不用于任何收集包。</p>', NULL),
	(1509, 'fish', 'FH0046', '裁制', '01', '<p>蓝铁饼鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。蓝铁饼鱼可用于染色。放置在染料罐内时，蓝铁饼鱼可用作<strong>蓝色</strong>染料。放置在缝纫机内时，蓝铁饼鱼还可用作<strong>青色</strong>染料。</p>', NULL),
	(1510, 'fish', 'FH0046', '鱼塘', '01', '<p>蓝铁饼鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1511, 'fish', 'FH0046', '任务', '01', '<ul><li>姜岛解锁后，威利或许会在特别任务“热带鱼”中要求抓住5条蓝铁饼鱼。</li></ul>', NULL),
	(1512, 'fish', 'FH0045', '', '01', '<p><br></p><p><strong>狮子鱼</strong>是一种可以在任何季节在姜岛任意海洋（海盗湾除外）中发现的鱼。</p><p>姜岛商人在每个季节的偶数日以每张一条狮子鱼的价格出售热带椅子。</p>', NULL),
	(1513, 'fish', 'FH0045', '地图', '01', '<p>狮子鱼可以在姜岛海洋中被发现。</p>', NULL),
	(1514, 'fish', 'FH0045', '收集包', '01', '<p>狮子鱼不用于任何收集包。</p>', NULL),
	(1515, 'fish', 'FH0045', '裁制', '01', '<p>狮子鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。狮子鱼还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(1516, 'fish', 'FH0045', '鱼塘', '01', '<p>狮子鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1517, 'fish', 'FH0045', '任务', '01', '<ul><li>姜岛解锁后，威利或许会在特别任务“热带鱼”中要求抓住5条狮子鱼。</li></ul>', NULL),
	(1518, 'fish', 'FH0044', '', '01', '<p><br></p><p><strong>史莱姆鱼</strong>只出现在突变虫穴，你可以在任何季节的突变虫穴通过钓鱼获得它。</p><p>单人钓史莱姆鱼是具有挑战性的，因为你不仅要钓鱼，还要忙着对付突变虫穴里的突变苍蝇。可以通过在入口处还未惊动任何苍蝇时钓鱼来避免被攻击。</p>', NULL),
	(1519, 'fish', 'FH0044', '地图', '01', '<p>史莱姆鱼可以在突变虫穴中找到。</p>', NULL),
	(1520, 'fish', 'FH0044', '收集包', '01', '<p>史莱姆鱼不用于任何收集包</p>', NULL),
	(1521, 'fish', 'FH0044', '裁制', '01', '<p>史莱姆鱼可通过缝纫机使用布料制成钓鱼帽。史莱姆鱼还可用作染料罐和缝纫机的<strong>绿色</strong>染料。</p><p>它可以用作绿色染料染色。</p>', NULL),
	(1522, 'fish', 'FH0044', '鱼塘', '01', '<p>史莱姆鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p><p>鱼塘内有至少 3 条鱼时，鱼塘的颜色会变为 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（RGB 色值：60, 255, 60）。</p>', NULL),
	(1523, 'fish', 'FH0044', '任务', '01', '<p>史莱姆鱼不用于任何任务</p>', NULL),
	(1524, 'fish', 'FH0043', '', '01', '<p><br></p><p><strong>虚空鲑鱼</strong>只出现在巫婆沼泽，你可以在任何季节的任何时候在巫婆沼泽通过钓鱼获得它。</p>', NULL),
	(1525, 'fish', 'FH0043', '地图', '01', '<p><strong>虚空鲑鱼</strong>可以在巫婆沼泽中找到。</p>', NULL),
	(1526, 'fish', 'FH0043', '收集包', '01', '<p>金星（或者铱星）虚空鲑鱼用于废弃乔家超市中的 失踪的收集包。</p>', NULL),
	(1527, 'fish', 'FH0043', '裁制', '01', '<p>虚空鲑鱼可通过缝纫机使用布料制成钓鱼帽。虚空鲑鱼可用于染色。放置在染料罐内时，虚空鲑鱼可用作<strong>紫色</strong>染料。放置在缝纫机内时，虚空鲑鱼还可用作<strong>深紫色</strong>染料。</p>', NULL),
	(1528, 'fish', 'FH0043', '鱼塘', '01', '<p>虚空鲑鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p><p>鱼塘内有至少 3 条鱼时，鱼塘的颜色会变为 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（RGB 色值：120, 20, 110）。</p>', NULL),
	(1529, 'fish', 'FH0043', '任务', '01', '<p>虚空鲑鱼不用于任何任务。</p>', NULL),
	(1530, 'fish', 'FH0042', '', '01', '<p><br></p><p><strong>木跃鱼</strong>是一种鱼，可以在任意季节的秘密森林的湖中钓到。它在不下雨的时候更容易钓到，根据钓鱼技能等级，每次抛竿有15%到27%的几率钓到木跃鱼。在水更深的钓鱼区，有更高的上钩概率。也可以在森林农场钓到，根据每日运气有0%到17.5%的概率。 它也可能出现在旅行货车中，售价为225g ~ 1,000。</p>', NULL),
	(1531, 'fish', 'FH0042', '收集包', '01', '<p>它用于鱼缸的 特色鱼类收集包。</p>', NULL),
	(1532, 'fish', 'FH0042', '裁制', '01', '<p>木跃鱼可通过缝纫机使用布料制成钓鱼背心。木跃鱼还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(1533, 'fish', 'FH0042', '鱼塘', '01', '<p>木跃鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1534, 'fish', 'FH0042', '任务', '01', '<ul><li>在鱼塘任务中，麻哈脂鲤、蛇齿单线鱼以及狗鱼可能会要求两条木跃鱼，任务完成后，鱼塘的容量将会从7增加到10。</li></ul>', NULL),
	(1535, 'fish', 'FH0041', '', '01', '<p><br></p><p><strong>大比目鱼</strong>出现在春季，夏季以及冬季的海洋里（包括海滩农场），它也可能在星期三随机出现在科罗布斯的商店中,售价为200g。它也可能出现在旅行货车中，售价为240g ~ 1,000.</p><p>在一天的任何时间，都可以使用魔法鱼饵不受季节限制地钓到大比目鱼。</p>', NULL),
	(1536, 'fish', 'FH0041', '地图', '01', '<p>大比目鱼可以在海洋中发现。</p>', NULL),
	(1537, 'fish', 'FH0041', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1538, 'fish', 'FH0041', '裁制', '01', '<p>大比目鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。大比目鱼可用于染色。放置在缝纫机内时，大比目鱼可用作<strong>深灰色</strong>染料。</p>', NULL),
	(1539, 'fish', 'FH0041', '鱼塘', '01', '<p>大比目鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1540, 'fish', 'FH0041', '任务', '01', '<p>在春季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条大比目鱼，以获得相当于售价的奖励和农场电脑制作方式。</p>', NULL),
	(1541, 'fish', 'FH0040', '', '01', '<p><br></p><p><strong>蛇齿单线鱼</strong>可以在冬天的河流以及深山湖泊泊钓到。宠物猫达到最大好感度后，每天第一次被抚摸也有可能掉落蛇齿单线鱼。当然，无论在什么季节，它都可能以200g的价格随机出现在每周三的科罗布斯的商店，或者出现在旅行货车里，价格为360g ~ 1,000。</p><p>可以使用魔法鱼饵不受季节限制地钓到蛇齿单线鱼。</p>', NULL),
	(1542, 'fish', 'FH0040', '地图', '01', '<p>蛇齿单线鱼可以在河流和深山湖泊中找到。</p>', NULL),
	(1543, 'fish', 'FH0040', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1544, 'fish', 'FH0040', '裁制', '01', '<p>蛇齿单线鱼可通过缝纫机使用布料制成钓鱼背心。蛇齿单线鱼可用于染色。放置在染料罐内时，蛇齿单线鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，蛇齿单线鱼还可用作<strong>棕色</strong>染料。</p><p>它可以用作橙色染料染色。</p>', NULL),
	(1545, 'fish', 'FH0040', '鱼塘', '01', '<p>蛇齿单线鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1546, 'fish', 'FH0040', '任务', '01', '<ul><li>第二年冬季13日，威利会在任务捕捞蛇齿单线鱼中写信给你，目标是钓一条蛇齿单线鱼，报酬为550g和1颗心的友谊点。可以使用在接受任务之前钓到的蛇齿单线鱼。</li><li><b>水生物泛滥：</b> 在冬季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条蛇齿单线鱼，以获得相当于鱼售价的奖金和农场电脑制作方式。</li></ul>', NULL),
	(1547, 'fish', 'FH0040', '趣闻', '01', '<p>实际上有两种鱼被同时称为蛇齿单线鱼: 一、长蛇齿单线鱼（六线鱼科），一种海鱼；二、江鳕（鳕科），一种淡水鱼。游戏中的蛇齿单线鱼应该指后者。</p>', NULL),
	(1548, 'fish', 'FH0039', '', '01', '<p><br></p><p><strong>西鲱</strong>是一种鱼，在春季，夏季以及秋季的雨天的河流（鹈鹕镇与煤矿森林）中出现，包括河边农场、森林农场和山顶农场。宠物猫好感度达到最大时也有可能会送给玩家一条西鲱。当然，无论在什么季节，它都可能以200g的价格随机出现在每周三的科罗布斯的商店里，或者以180g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受季节、天气和时间限制地钓到西鲱。</p>', NULL),
	(1549, 'fish', 'FH0039', '地图', '01', '<p>西鲱可以在河流中找到。</p>', NULL),
	(1550, 'fish', 'FH0039', '收集包', '01', '<ul><li>西鲱用于鱼缸的 河鱼收集包。</li><li>1个金/铱星品质的西鲱用于鱼缸（重新混合后）的 优质鱼收集包。</li></ul>', NULL),
	(1551, 'fish', 'FH0039', '裁制', '01', '<p>西鲱可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。西鲱还可用作染料罐和缝纫机的<strong>绿色</strong>染料。</p>', NULL),
	(1552, 'fish', 'FH0039', '鱼塘', '01', '<p>西鲱可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1553, 'fish', 'FH0039', '任务', '01', '<p>西鲱不用于任何任务。</p>', NULL),
	(1554, 'fish', 'FH0038', '', '01', '<p><br></p><p><strong>青花鱼</strong>是在秋季和冬季时，你可以在清晨和傍晚的海洋里发现的鱼（包括海滩农场）。它也可能在星期三随机出现在科罗布斯的商店中，售价为200g。它也可能出现在旅行货车中，售价为225g ~ 1,000。</p><p>可以使用魔法鱼饵不受季节限制地钓到青花鱼。</p>', NULL),
	(1555, 'fish', 'FH0038', '地图', '01', '<p>青花鱼可以在海洋中被发现。</p>', NULL),
	(1556, 'fish', 'FH0038', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1557, 'fish', 'FH0038', '裁制', '01', '<p>青花鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。青花鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p><p>它可以用作蓝色染料染色。</p>', NULL),
	(1558, 'fish', 'FH0038', '鱼塘', '01', '<p>青花鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1559, 'fish', 'FH0038', '任务', '01', '<ul><li>格斯在“烩鱼汤”任务中于冬季26日通过邮件要求一个青花鱼。报酬为400g和1颗心的友谊点。</li><li>在秋季和冬季时威利可能会在皮埃尔的杂货店外的布告栏板上随机要求1-4条青花鱼。报酬为每条鱼75g(鱼不需要上交)</li><li>在秋季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条青花鱼，以获得相当于售价的奖励和农场电脑制作方式。</li></ul>', NULL),
	(1560, 'fish', 'FH0037', '', '01', '<p><br></p><p><strong>麻哈脂鲤</strong>是一种鱼，在夏季的煤矿森林河流出现。宠物猫好感度达到最大时也有可能会送给玩家一条麻哈脂鲤。当然，无论在什么季节，它都可能出现在每周三的科罗布斯的商店里，价格为200g，或者旅行货车中，价格为300g ~ 1,000。</p><p>在一天的任何时间，都可以使用魔法鱼饵不受季节限制地钓到麻哈脂鲤。</p>', NULL),
	(1561, 'fish', 'FH0037', '地图', '01', '<p>麻哈脂鲤可以在煤矿森林河流中找到。</p>', NULL),
	(1562, 'fish', 'FH0037', '收集包', '01', '<p>麻哈脂鲤不用于任何收集包。</p>', NULL),
	(1563, 'fish', 'FH0037', '裁制', '01', '<p>麻哈脂鲤可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。麻哈脂鲤还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(1564, 'fish', 'FH0037', '鱼塘', '01', '<p>麻哈脂鲤可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1565, 'fish', 'FH0037', '任务', '01', '<ul><li>在夏季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条麻哈脂鲤，报酬是等同于鱼基础售价的金币和农场电脑配方。</li></ul>', NULL),
	(1566, 'fish', 'FH0036', '', '01', '<p><br></p><p><strong>鲢鱼</strong>是一种鱼，可以于任何季节在深山湖泊以及煤矿森林河流中钓到。与煤矿森林河流中的大多数鱼不同，它不能在鹈鹕镇河流中钓到。宠物猫好感度达到最大时也有可能会送给玩家一条鲢鱼。它可能会以200g的价格出现在每周三的科罗布斯的商店中，或者以150g ~ 1,000的价格随机出现在旅行货车中。</p>', NULL),
	(1567, 'fish', 'FH0036', '地图', '01', '<p>鲢鱼可以在深山湖泊和煤矿森林河流中找到。</p>', NULL),
	(1568, 'fish', 'FH0036', '收集包', '01', '<p>鲢鱼被用于布告栏的 土地研究收集包。</p>', NULL),
	(1569, 'fish', 'FH0036', '裁制', '01', '<p>鲢鱼可通过缝纫机使用布料制成钓鱼背心。鲢鱼可用于染色。放置在缝纫机内时，鲢鱼可用作<strong>灰色</strong>染料。</p>', NULL),
	(1570, 'fish', 'FH0036', '鱼塘', '01', '<p>鲢鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1571, 'fish', 'FH0036', '任务', '01', '<ul><li>在任何季节，威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条鲢鱼。报酬为每条鱼50g(鱼不需要上交)</li><li>在鱼塘任务中，麻哈脂鲤, 蛇齿单线鱼以及狗鱼可能会要求两个鲢鱼，任务完成后，鱼塘的容量将会从3增加到5。</li></ul>', NULL),
	(1572, 'fish', 'FH0035', '', '01', '<p><br></p><p><strong>罗非鱼</strong>是在夏季和秋季时，可以在海洋里被发现的鱼（包括海滩农场）。也可以在任何季节从姜岛西部（淡水）或北部捕获。它也可能在星期三以200g的价格随机出现在科罗布斯的商店中，或者以225g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受季节限制地钓到罗非鱼。</p>', NULL),
	(1573, 'fish', 'FH0035', '地图', '01', '<p>罗非鱼可以在海洋和姜岛中被发现。</p>', NULL),
	(1574, 'fish', 'FH0035', '收集包', '01', '<p>罗非鱼属于鱼缸的海鱼收集包</p>', NULL),
	(1575, 'fish', 'FH0035', '裁制', '01', '<p>罗非鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。罗非鱼可用于染色。放置在缝纫机内时，罗非鱼可用作<strong>灰色</strong>染料。</p>', NULL),
	(1576, 'fish', 'FH0035', '鱼塘', '01', '<p>罗非鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1577, 'fish', 'FH0035', '任务', '01', '<ul><li>在夏季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条罗非鱼，以获得相当于售价的d奖励和农场电脑制作方式。</li></ul>', NULL),
	(1578, 'fish', 'FH0034', '', '01', '<p><br></p><p><strong>大头鱼</strong>是一种鱼，可以在任何季节在深山湖泊以及荒野农场被发现。宠物猫好感度达到最大时也有可能会送给玩家一条大头鱼。它也可能以200g的价格出现在每周三的科罗布斯的商店中，或者以225g ~ 1,000的价格随机出现在旅行货车中。</p>', NULL),
	(1579, 'fish', 'FH0034', '地图', '01', '<p>大头鱼可以在深山湖泊中找到。</p>', NULL),
	(1580, 'fish', 'FH0034', '收集包', '01', '<p>大头鱼被用于鱼缸的 湖鱼收集包。</p>', NULL),
	(1581, 'fish', 'FH0034', '裁制', '01', '<p>大头鱼可通过缝纫机使用布料制成钓鱼背心。大头鱼可用于染色。放置在染料罐内时，大头鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，大头鱼还可用作<strong>棕色</strong>染料。</p>', NULL),
	(1582, 'fish', 'FH0034', '鱼塘', '01', '<p>大头鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1583, 'fish', 'FH0034', '任务', '01', '<p>在鱼塘任务中，麻哈脂鲤, 蛇齿单线鱼以及狗鱼可能会要求两个大头鱼，任务完成后，鱼塘的容量将会从5增加到7。</p>', NULL),
	(1584, 'fish', 'FH0033', '', '01', '<p><br></p><p><strong>虎纹鳟鱼</strong>在秋季和冬季的河流、河边农场、森林农场、草原农场和山顶农场出现。宠物猫达到最大好感度后，每天第一次被抚摸也有可能掉落虎纹鳟鱼。当然，无论在什么季节，它都可能以200g的价格随机出现在每周三的科罗布斯的商店里，或者以450g ~ 1,000的价格随机出现在旅行货车中。</p><p>在一天的任何时间，都可以使用魔法鱼饵不受季节限制地钓到虎纹鳟鱼。</p>', NULL),
	(1585, 'fish', 'FH0033', '地图', '01', '<p>虎纹鳟鱼可以在河流中找到。</p>', NULL),
	(1586, 'fish', 'FH0033', '收集包', '01', '<p>虎纹鳟鱼用于鱼缸的 河鱼收集包。</p>', NULL),
	(1587, 'fish', 'FH0033', '裁制', '01', '<p>虎纹鳟鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。虎纹鳟鱼可用于染色。放置在染料罐内时，虎纹鳟鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，虎纹鳟鱼还可用作<strong>棕色</strong>染料。</p><p>它可以用作橙色染料染色。</p>', NULL),
	(1588, 'fish', 'FH0033', '鱼塘', '01', '<p>虎纹鳟鱼可以通过鱼塘养殖，但是不可繁殖，只能放入玩家捕获的鱼。鱼塘的初始容量为 10，不需要完成任务来提高容量。</p>', NULL),
	(1589, 'fish', 'FH0033', '任务', '01', '<ul><li>在秋季和冬季时威利可能会在皮埃尔的杂货店外的布告栏板上随机要求1-4条虎纹鳟鱼。报酬为每条鱼150g(鱼不需要上交)</li><li>在秋季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条虎纹鳟鱼，以获得相当于售价的data-sort-value=""&gt;奖励和农场电脑制作方式。</li></ul>', NULL),
	(1590, 'fish', 'FH0033', '趣闻', '01', '<ul><li>在现实生活中，就像在游戏，虎纹鳟鱼是褐鳟和 美洲红点鲑杂交而成，无法繁殖。</li><li>在游戏文件中，虎纹鳟鱼的繁殖频率被设置为999999，这意味着它实际可以在该天数后繁殖一条。假设从第1年春季1日将一条虎纹鳟鱼丢入鱼塘中，会在第8928年秋季7日繁殖一条。</li></ul>', NULL),
	(1591, 'fish', 'FH0032', '', '01', '<p><br></p><p><strong>鲟鱼</strong>是一种鱼，可以在夏季和冬季可以在深山湖泊和荒野农场捕获的鱼。在夏季的雨天鲟鱼会更常见（占所有捕获物品中的概率为11%-16%），而当在冬天使用雨水图腾得到的降雨中会变得少见。抛竿到离海岸越远的地方钓到鲟鱼的概率越高。宠物猫好感度达到最大时也有可能会送给玩家一条鲟鱼。</p><p>鲟鱼也可能在每周三的科罗布斯商店里随机出现以200g价格售出，或在旅行货车以600g ~ 1,000价格售出。</p><p>可以使用魔法鱼饵不受季节限制地钓到鲟鱼。</p><p>鲟鱼可以放置在鱼塘中来生产鱼籽。鲟鱼鱼籽可以放置在罐头瓶中来生产鱼籽酱。</p>', NULL),
	(1592, 'fish', 'FH0032', '地图', '01', '<p>鲟鱼可以在深山湖泊中捕获。</p>', NULL),
	(1593, 'fish', 'FH0032', '收集包', '01', '<p>鲟鱼被用于鱼缸的 湖鱼收集包。</p>', NULL),
	(1594, 'fish', 'FH0032', '裁制', '01', '<p>鲟鱼可通过缝纫机使用布料制成钓鱼背心。鲟鱼可用于染色。放置在染料罐内时，鲟鱼可用作<strong>蓝色</strong>染料。放置在缝纫机内时，鲟鱼还可用作<strong>青色</strong>染料。</p><p>它可以用作蓝色染料染色。</p>', NULL),
	(1595, 'fish', 'FH0032', '鱼塘', '01', '<p>鲟鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1596, 'fish', 'FH0032', '任务', '01', '<p>鲟鱼不用于任何任务。</p>', NULL),
	(1597, 'fish', 'FH0031', '', '01', '<p><br></p><p><strong>午夜鲤鱼</strong>是一种只出现在深夜的鱼。可以在秋季和冬季的深山湖泊和煤矿森林的池塘里捕获；也可以在任何季节的姜岛北部和西部的淡水中捕获。许多标准农场以外的农场地图上都可以钓到午夜鲤鱼，例如：森林农场、河边农场、荒野农场和四角农场左下角的大池塘。宠物猫好感度达到最大时也有可能会送给玩家一条午夜鲤鱼。午夜鲤鱼有时也会以450g ~ 1,000的价格随机出现在旅行货车中。在秋季和冬季翻动垃圾桶也有概率找到午夜鲤鱼。</p><p>可以使用魔法鱼饵不受季节和时间限制地钓到午夜鲤鱼。</p>', NULL),
	(1598, 'fish', 'FH0031', '地图', '01', '<p>可以在深山的湖泊和煤矿森林的池塘里钓到午夜鲤鱼。姜岛北部和西部的淡水区域也可以钓到午夜鲤鱼。</p>', NULL),
	(1599, 'fish', 'FH0031', '收集包', '01', '<p>午夜鲤鱼不用于任何收集包。</p>', NULL),
	(1600, 'fish', 'FH0031', '鱼塘', '01', '<p>午夜鲤鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1601, 'fish', 'FH0031', '染色', '01', '<p>午夜鲤鱼可用于染色。放置在染料罐内时，可用作<strong>紫色</strong>染料。放置在缝纫机内时，午夜鲤鱼还可用作<strong>亮紫色</strong>染料。</p>', NULL),
	(1602, 'fish', 'FH0031', '任务', '01', '<ul><li><b>求助任务：</b>在秋季或者冬季，皮埃尔的杂货店外的布告板上可能会有人贴出“需要一条午夜鲤鱼”的求助，报酬是450g和150友谊点。</li><li><b>特别任务：</b> 在秋季时，德米特里厄斯可能会在镇长的庄园外的特别任务板上发布一个需要10条午夜鲤鱼的特别任务（详见水生物泛滥），奖励是等同于10条午夜鲤鱼售价的金币和农场电脑的配方。</li></ul>', NULL),
	(1603, 'fish', 'FH0030', '', '01', '<p><br></p><p><strong>比目鱼</strong>是一种春季和夏季可以在海洋中（包括海滩农场）捕捉到的鱼。它也可以在姜岛西部（海洋）、南部、东南部和海盗湾的任何季节捕获。它也可能以300g ~ 1,000的价格随机出现在旅行货车中。在春季和夏季翻动垃圾桶也有概率找到比目鱼。</p><p>在一天中任何时间，都可以使用魔法鱼饵不受季节限制地钓到比目鱼。</p>', NULL),
	(1604, 'fish', 'FH0030', '地图', '01', '<p>比目鱼可以在海洋和姜岛找到。</p>', NULL),
	(1605, 'fish', 'FH0030', '收集包', '01', '<p>不用于任何收集包.</p>', NULL),
	(1606, 'fish', 'FH0030', '鱼塘', '01', '<p>比目鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1607, 'fish', 'FH0030', '染色', '01', '<p>比目鱼可用于染色。放置在染料罐内时，比目鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，比目鱼还可用作<strong>棕色</strong>染料。</p>', NULL),
	(1608, 'fish', 'FH0030', '任务', '01', '<ul><li>在春季或夏季，皮埃尔的杂货店外的布告栏上可能随机出现要求一条比目鱼的任务，报酬是300g和 150点友谊。</li><li>在春季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条比目鱼，以获得相当于售价的data-sort-value=""&gt;奖励和农场电脑制作方式。</li></ul>', NULL),
	(1609, 'fish', 'FH0029', '', '01', '<p><br></p><p><strong>蝎鲤鱼</strong>出现在任何季节的沙漠。前提是你的钓鱼等级必须大于等于四级（可以依靠增益效果）。它也会随机出现在旅行货车里，价格为450g ~ 1,000。在解锁沙漠以后，有时也可以在垃圾桶中找到。</p><p>除了沙鱼以外，这是你在沙漠唯一能钓到的鱼，并且它比沙鱼更加稀有，更加贵，也更加难钓。它是除了传说之鱼之外唯一有钓鱼等级前置要求的鱼。如果满足钓鱼等级条件，每次抛竿有12-20%的几率钓到蝎鲤鱼，钓鱼等级越高几率越高。</p><p>可以使用魔法鱼饵不受一天当中时间限制地钓到蝎鲤鱼。</p>', NULL),
	(1610, 'fish', 'FH0029', '地图', '01', '<p>蝎鲤鱼可以在沙漠中找到。</p>', NULL),
	(1611, 'fish', 'FH0029', '收集包', '01', '<p>蝎鲤鱼是鱼缸（重新混合后）的 钓鱼大师收集包中的一个选项。</p>', NULL),
	(1612, 'fish', 'FH0029', '裁制', '01', '<p>蝎鲤鱼可通过缝纫机使用布料制成钓鱼帽。蝎鲤鱼可用于染色。放置在染料罐内时，蝎鲤鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，蝎鲤鱼还可用作<strong>棕色</strong>染料。</p><p>它可以用作橙色染料染色。</p>', NULL),
	(1613, 'fish', 'FH0029', '鱼塘', '01', '<p>蝎鲤鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1614, 'fish', 'FH0029', '任务', '01', '<ul><li>解锁沙漠后，皮埃尔的杂货店外的布告栏板上可能会随机要求蝎鲤鱼的任务。报酬是为每条鱼450g和150友谊点。</li></ul><p>在沙漠节上，威利有时会要求玩家钓一条蝎鲤鱼。</p>', NULL),
	(1615, 'fish', 'FH0028', '', '01', '<p><br></p><p><strong>沙鱼</strong>是一种鱼，在沙漠中一年四季都可以钓到。沙鱼偶尔会以225g ~ 1,000的价格随机出现在旅行货车中。在玩家解锁沙漠以后，有时也可以在垃圾桶中找到沙鱼。</p><p>可以使用魔法鱼饵不受游戏内时间限制地钓到沙鱼。</p>', NULL),
	(1616, 'fish', 'FH0028', '地图', '01', '<p>沙鱼可在沙漠中钓到。</p>', NULL),
	(1617, 'fish', 'FH0028', '收集包', '01', '<p>用于鱼缸的 特色鱼类收集包.</p>', NULL),
	(1618, 'fish', 'FH0028', '裁制', '01', '<p>沙鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。沙鱼还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1619, 'fish', 'FH0028', '鱼塘', '01', '<p>沙鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1620, 'fish', 'FH0028', '任务', '01', '<ul><li>解锁沙漠之后，所有的季节中，皮埃尔的杂货店外的布告栏上都可能会随机出现要求沙鱼的任务，报酬是每条鱼225g和150友谊点。</li><li>在鱼塘任务中，麻哈脂鲤, 蛇齿单线鱼以及狗鱼可能会要求两个沙鱼，任务完成后，鱼塘的容量将会从7增加到10。</li></ul>', NULL),
	(1621, 'fish', 'FH0027', '', '01', '<p><br></p><p><strong>岩浆鳗鱼</strong>出现在矿井100层和火山口，你可以在任何季节的矿井100层通过钓鱼获得它；或者是姜岛火山口的岩浆湖里也可以垂钓得到。它是唯一可以在岩浆中生成的鱼。</p><p>这是你在矿井100层唯一能钓到的鱼，并且十分稀有，它的捕获难度仅次于传奇鱼和章鱼，和蝎鲤鱼相当，同时也是普通鱼中价格最高的鱼。</p><p>岩浆鳗鱼不是鳗鱼，不能代替鳗鱼做菜。</p>', NULL),
	(1622, 'fish', 'FH0027', '上钩概率', '01', '<p>在火山地牢第10层，岩浆鳗鱼的上钩概率等于钓鱼区乘以10%（前提是已钓上《物理101》，未钓上《物理101》时将有5%概率为该画作）。例如，玩家在火山第10层抛钩且已钓上《物理101》，钓鱼区为5，那么岩浆鳗鱼的上钩概率为50%；若未钓上《物理101》则上钩概率为47.5%。</p><p>在矿井100层的矿洞湖中钓鱼，岩浆鳗鱼的上钩概率与玩家的钓鱼等级和钓鱼区有关。使用珍稀诱钩的时候，上钩概率会增加4%。例如，玩家的钓鱼等级为10级，钓鱼区为5，那么岩浆鳗鱼的上钩概率为5.4%。装配珍稀诱钩后这一概率将达到9.4%。</p>', NULL),
	(1623, 'fish', 'FH0027', '地图', '01', '<p>岩浆鳗鱼可以在矿井和火山地牢中找到。</p>', NULL),
	(1624, 'fish', 'FH0027', '收集包', '01', '<p>岩浆鳗鱼是鱼缸（重新混合后）的 钓鱼大师收集包中的一个选项。</p>', NULL),
	(1625, 'fish', 'FH0027', '裁制', '01', '<p>岩浆鳗鱼可通过缝纫机使用布料制成标志帽。岩浆鳗鱼还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p><p>它可以用作红色染料染色。</p>', NULL),
	(1626, 'fish', 'FH0027', '鱼塘', '01', '<p>岩浆鳗鱼可以通过鱼塘养殖，每 5 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p><p>完成首个任务，并且鱼塘内有至少 1 条鱼时，鱼塘的颜色会变为 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（RGB 色值：250, 30, 30）。</p>', NULL),
	(1627, 'fish', 'FH0027', '任务', '01', '<p>岩浆鳗鱼不用于任何任务。</p>', NULL),
	(1628, 'fish', 'FH0027', '趣闻', '01', '<p>在首次钓上<strong>岩浆鳗鱼</strong>后找到矮人并进行交谈，他会说以下话语。</p>', NULL),
	(1629, 'fish', 'FH0026', '', '01', '<p><br></p><p><strong>冰柱鱼</strong>只出现在矿井，你可以在任何季节的矿井60层通过钓鱼获得它。除了鬼鱼以外，这是你在矿井60层唯一能钓到的鱼。</p><p>钓到冰柱鱼的几率取决于钓鱼等级和钓鱼点的钓鱼区，如果装备珍稀诱钩概率会增加4.5%。例如，在钓鱼等级为10，浮标位于5级钓鱼区时，钓到冰柱鱼的几率为6.45%。装备珍稀诱钩后，这个数值增加到10.95%。</p>', NULL),
	(1630, 'fish', 'FH0026', '地图', '01', '<p>冰柱鱼可以在矿井中找到。</p>', NULL),
	(1631, 'fish', 'FH0026', '收集包', '01', '<p>冰柱鱼不用于任何收集包</p>', NULL),
	(1632, 'fish', 'FH0026', '裁制', '01', '<p>冰柱鱼可通过缝纫机使用布料制成钓鱼帽。冰柱鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1633, 'fish', 'FH0026', '鱼塘', '01', '<p>冰柱鱼可以通过鱼塘养殖，每 5 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1634, 'fish', 'FH0026', '任务', '01', '<p>冰柱鱼不用于任何任务</p>', NULL),
	(1635, 'fish', 'FH0026', '趣闻', '01', '<p>在首次钓上冰柱鱼后找到矮人并进行交谈，他会说以下话语。</p>', NULL),
	(1636, 'fish', 'FH0025', '', '01', '<p><br></p><p><strong>石鱼</strong>只出现在矿井，你可以在任何季节的矿井20层通过钓鱼获得它。除了鬼鱼以外，这是你在矿井20层唯一能钓到的鱼。</p><p>石鱼上钩的概率与钓鱼等级和钓鱼区有关。同时，装备了珍稀诱钩还可以提升5%的概率。例如，当你的钓鱼等级达到了10，同时浮漂位于钓鱼区5的位置，那么将有<strong>7.5%</strong>的概率上钩；装配珍稀诱钩后这个概率可以达到<strong>12.5%</strong>。</p><p>石鱼和章鱼是唯二必须烹饪或使用熏鱼机制成熏鱼后才能食用的用鱼竿钓到的鱼。</p>', NULL),
	(1637, 'fish', 'FH0025', '地图', '01', '<p>石鱼可以在矿井20层找到。</p>', NULL),
	(1638, 'fish', 'FH0025', '收集包', '01', '<p>石鱼不用于任何收集包。</p>', NULL),
	(1639, 'fish', 'FH0025', '裁制', '01', '<p>石鱼可通过缝纫机使用布料制成钓鱼帽。石鱼可用于染色。放置在染料罐内时，石鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，石鱼还可用作<strong>棕色</strong>染料。</p><p>染色时可用作橙色染料。</p>', NULL),
	(1640, 'fish', 'FH0025', '鱼塘', '01', '<p>石鱼可以通过鱼塘养殖，每 5 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1641, 'fish', 'FH0025', '任务', '01', '<p>石鱼不用于任何任务。</p>', NULL),
	(1642, 'fish', 'FH0025', '趣闻', '01', '<p>在首次钓上石鱼后找到矮人并进行交谈，他会说以下话语。</p>', NULL),
	(1643, 'fish', 'FH0024', '', '01', '<p><br></p><p><strong>鬼鱼</strong>是一种鱼，在任何季节都可以在矿井20层和60层的湖里钓到它。击杀幽灵时也有8%的概率掉落鬼鱼。当然，它也可能以135g ~ 1,000的价格随机出现在旅行货车中。</p>', NULL),
	(1644, 'fish', 'FH0024', '地图', '01', '<p>鬼鱼可以在矿井中找到。</p>', NULL),
	(1645, 'fish', 'FH0024', '收集包', '01', '<p>鬼鱼被用于鱼缸的 特色鱼类收集包。</p>', NULL),
	(1646, 'fish', 'FH0024', '裁制', '01', '<p>鬼鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。鬼鱼可用于染色。放置在缝纫机内时，鬼鱼可用作<strong>白色</strong>染料。</p>', NULL),
	(1647, 'fish', 'FH0024', '鱼塘', '01', '<p>鬼鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 1，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 4 次任务。</p>', NULL),
	(1648, 'fish', 'FH0024', '任务', '01', '<ul><li>麻哈脂鲤、蛇齿单线鱼以及狗鱼可能会在鱼塘扩容任务中要求 2 条鬼鱼，以将鱼塘的容量从 7 增加到 10。</li><li>在浣熊的请求的第五个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 3 条鬼鱼。</li></ul>', NULL),
	(1649, 'fish', 'FH0023', '', '01', '<p><br></p><p><strong>大海参</strong>是一种出现在夏季和秋季夜晚海洋的鱼（包括海滩农场），以较小的概率也能在同季的垃圾桶中找到。它也会在任何季节出现在姜岛南部、东南部、西部海洋以及海盗湾。不论任何季节，它都可能以750g ~ 1,250的价格随机出现在旅行货车中。在夜市的钓鱼潜水艇中也有4％的机会可以捕获它（如果使用珍稀诱钩，则为7%）。无尽财富之雕像在法师的生日（冬季17）会生产一个大海参。</p><p>在一天的任何时间，都可以使用魔法鱼饵不受季节限制地钓到大海参。</p>', NULL),
	(1650, 'fish', 'FH0023', '地图', '01', '<p>大海参可以在海洋，姜岛，以及夜市中找到。</p>', NULL),
	(1651, 'fish', 'FH0023', '收集包', '01', '<p>没有收集包需要大海参。</p>', NULL),
	(1652, 'fish', 'FH0023', '裁制', '01', '<p>大海参可通过缝纫机使用布料制成可染色的抹胸背心。大海参还可用作染料罐和缝纫机的<strong>紫色</strong>染料。</p>', NULL),
	(1653, 'fish', 'FH0023', '鱼塘', '01', '<p>大海参可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p><p>鱼塘内有至少 3 条鱼时，鱼塘的颜色会变为。</p>', NULL),
	(1654, 'fish', 'FH0023', '任务', '01', '<ul><li><b>求助任务:</b>威利会在夏季或秋季，皮埃尔的杂货店外的布告栏上要求你收集一个大海参，回报是750g和150 好感值。</li></ul>', NULL),
	(1655, 'fish', 'FH0023', '秘密', '01', '<p>如果把大海参放到铁匠铺北边围栏中间的棕色箱子中玩家将会收到一个 ??HMTGF??。</p>', NULL),
	(1656, 'fish', 'FH0022', '', '01', '<p><br></p><p><strong>海参</strong>是一种能在秋天和冬天期间的海滩钓到的鱼。（包括海滩农场的海洋）它也可以在同季的垃圾桶中随机找到，或者在旅行货车中以225g ~ 1,000的价格随机出售。在冬季15-17日期间，在夜市的深海潜艇中有5%的几率捕获海参。（如果使用珍稀诱钩，则为4%）</p><p>可以使用魔法鱼饵不受季节限制地钓到海参。</p>', NULL),
	(1657, 'fish', 'FH0022', '分布图', '01', '<p>海参可以在海边钓到。</p>', NULL),
	(1658, 'fish', 'FH0022', '收集包', '01', '<p>没有收集包需要海参。</p>', NULL),
	(1659, 'fish', 'FH0022', '裁制', '01', '<p>海参可通过缝纫机使用布料制成可染色的抹胸背心。海参还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p><p>它可以用作黄色染料染色。</p>', NULL),
	(1660, 'fish', 'FH0022', '鱼塘', '01', '<p>海参可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1661, 'fish', 'FH0022', '任务', '01', '<ul><li>在秋季和冬季，皮埃尔的杂货店外的布告栏上都可能会随机出现要求一条海参的任务，报酬是225g和150友谊点。</li></ul>', NULL),
	(1662, 'fish', 'FH0021', '', '01', '<p><br></p><p><strong>鱿鱼</strong>是一种可以在冬季夜晚的海洋（以及海滩农场）被发现的鱼。你偶尔可以在同季的垃圾桶里找到它，有时它会以240g ~ 1,000的价格随机出现在旅行货车中。在冬季12日与13日的鱿鱼节时，全天均可捕获鱿鱼。沙漠节期间，皮埃尔会在其商铺以 10 卡利科三花蛋的价格出售鱿鱼（限5条）。</p><p>当放置在鱼塘中时，鱿鱼会生产鱿鱼墨汁而不是鱼籽。</p><p>一天中无论时间或季节，都可以使用魔法鱼饵捕获鱿鱼。</p>', NULL),
	(1663, 'fish', 'FH0021', '地图', '01', '<p><strong>鱿鱼</strong>可以在海洋中被发现。</p>', NULL),
	(1664, 'fish', 'FH0021', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1665, 'fish', 'FH0021', '裁制', '01', '<p>鱿鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鱿鱼可用于染色。放置在缝纫机内时，鱿鱼可用作<strong>白色</strong>染料。</p>', NULL),
	(1666, 'fish', 'FH0021', '鱼塘', '01', '<p>鱿鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p><p><br></p><h3>任务</h3><h3>产物</h3>', NULL),
	(1667, 'fish', 'FH0021', '任务', '01', '<ul><li>威利在会在捕条鱿鱼任务中于冬季2日通过邮件向玩家要求一只鱿鱼，报酬为800g和1颗心的友谊点。</li><li>在冬季时，皮埃尔的杂货店外的布告栏板上可能会随机出现鱿鱼的任务。报酬是为每条鱼240g和150友谊点。</li><li>在冬季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条鱿鱼。报酬为每条鱼80g(鱼不需要上交)</li><li>在冬季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条鱿鱼，以获得相当于售价的奖励和农场电脑制作方式。</li></ul>', NULL),
	(1668, 'fish', 'FH0020', '', '01', '<p><br></p><p><strong>红鲷鱼</strong>是在夏季和秋季时，可以在雨天的海洋里被发现的鱼（包括海滩农场）。如果你使用雨水图腾,那么它也可以在冬季被发现。它也可以在夏季、秋季或冬季期间在垃圾桶中随机找到，或者在旅行货车中以150g ~ 1,000的价格买到。</p><p>可以使用魔法鱼饵不受季节限制地钓到红鲷鱼。</p>', NULL),
	(1669, 'fish', 'FH0020', '地图', '01', '<p>红鲷鱼可以在海洋中被发现。</p>', NULL),
	(1670, 'fish', 'FH0020', '收集包', '01', '<p>红鲷鱼属于鱼缸的 海鱼收集包</p>', NULL),
	(1671, 'fish', 'FH0020', '裁制', '01', '<p>红鲷鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。红鲷鱼还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p><p>它可以用作红色染料染色。</p>', NULL),
	(1672, 'fish', 'FH0020', '鱼塘', '01', '<p>红鲷鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1673, 'fish', 'FH0020', '任务', '01', '<ul><li>在夏季和秋季时，皮埃尔的杂货店外的布告栏板上可能会随机出现红鲷鱼的任务。报酬是为每条鱼150g和150友谊点。</li><li>在夏季和秋季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条红鲷鱼。报酬为每条鱼50g。(鱼不需要上交)</li></ul>', NULL),
	(1674, 'fish', 'FH0019', '', '01', '<p><br></p><p><strong>章鱼</strong>是在夏季沙滩和姜岛西部的海洋中可以发现的鱼（包括海滩农场）。在夏季翻动垃圾桶也有概率找到章鱼。当然，无论在什么季节，它都会随机出现在旅行货车里，价格为450g ~ 1,000。又或者，你可以通过乘坐夜市的深海潜艇到深海钓鱼来获得它，上钩的概率为2％（如果使用珍稀诱钩，则为1.6%）。</p><p>可以使用魔法鱼饵不受季节和时间限制地钓到章鱼。</p><p>章鱼和石鱼是唯二必须烹饪或使用熏鱼机制成熏鱼后才能食用的用鱼竿钓到的鱼。</p><p>这是游戏中除了传说之鱼以外最难钓到的鱼。每次抛竿有4-11%的几率在海滩钓到章鱼，在姜岛西部则有5-18%的几率，概率在某种程度上取决于确切的时间和天气，并且在较高的钓鱼等级和钓鱼区有更高的概率。</p>', NULL),
	(1675, 'fish', 'FH0019', '地图', '01', '<p><strong>章鱼</strong>可以在海洋和姜岛西部被发现。</p>', NULL),
	(1676, 'fish', 'FH0019', '攻略', '01', '<p>这是一种极难钓到的鱼，即使与传说鱼相比也是如此。更苛刻的是，钓到章鱼的概率很小，捕获章鱼的时间窗口都非常短。为了更好的钓到章鱼，可以先达到钓鱼等级10，使用铱金鱼竿，吃一种能提供钓鱼增益效果的食物，并且在鱼竿上装陷阱浮标或软木塞浮标。1.6版本更新过后，因为加入了高级鱼饵和高级铱金鱼竿从而使垂钓时更为便捷。</p>', NULL),
	(1677, 'fish', 'FH0019', '收集包', '01', '<p>章鱼可用于鱼缸（重新混合后）的 钓鱼大师收集包。</p>', NULL),
	(1678, 'fish', 'FH0019', '裁制', '01', '<p>章鱼可通过缝纫机使用布料制成可染色的 男款蓝色纽扣汗衫 或女款蓝色纽扣汗衫。章鱼还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p><p>它可以用作橙色染料染色。</p>', NULL),
	(1679, 'fish', 'FH0019', '鱼塘', '01', '<p>章鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1680, 'fish', 'FH0019', '任务', '01', '<ul><li>在夏季时，皮埃尔的杂货店外的布告栏板上可能会随机要求章鱼的任务。报酬是为每条鱼450g和150友谊点。</li><li>在夏季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条章鱼。报酬为每条鱼150g。(鱼不需要上交)</li></ul>', NULL),
	(1681, 'fish', 'FH0018', '', '01', '<p><br></p><p><strong>鳗鱼</strong>在春季和秋季的雨天出现，可以在从下午4点到凌晨2点的的海洋里钓到（包括海滩农场）。鳗鱼也会以255g ~ 1,000的价格随机出现在旅行货车中。在春季和秋季翻动垃圾桶也有概率找到鳗鱼。</p><p>可以使用魔法鱼饵不受季节和天气限制地钓到鳗鱼。</p>', NULL),
	(1682, 'fish', 'FH0018', '地图', '01', '<p>鳗鱼可以在海洋中被发现。</p>', NULL),
	(1683, 'fish', 'FH0018', '收集包', '01', '<p>鳗鱼属于夜间垂钓收集包。</p>', NULL),
	(1684, 'fish', 'FH0018', '裁制', '01', '<p>鳗鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鳗鱼可用于染色。放置在染料罐内时，鳗鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，鳗鱼还可用作<strong>铜色</strong>染料。</p><p>它可以用作橙色染料染色。</p>', NULL),
	(1685, 'fish', 'FH0018', '鱼塘', '01', '<p>鳗鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1686, 'fish', 'FH0018', '任务', '01', '<ul><li>在春季和秋季时，皮埃尔的杂货店外的布告栏板上可能会随机鳗鱼的任务。报酬是为每条鱼255g和150友谊点。</li></ul>', NULL),
	(1687, 'fish', 'FH0017', '', '01', '<p><br></p><p><strong>鲱鱼</strong>是一种可以在春季和冬季的海洋里发现的鱼（包括海滩农场）。当然，无论什么季节，鲱鱼都有可能以100g ~ 1,000的价格随机出现在旅行货车中。在春季和冬季翻动垃圾桶也有概率找到鲱鱼。</p><p>可以使用魔法鱼饵不受季节限制地钓到鲱鱼。</p>', NULL),
	(1688, 'fish', 'FH0017', '地图', '01', '<p>鲱鱼可以在海洋中发现。</p>', NULL),
	(1689, 'fish', 'FH0017', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1690, 'fish', 'FH0017', '裁制', '01', '<p>鲱鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鲱鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p><p>它可以用作蓝色染料染色。</p>', NULL),
	(1691, 'fish', 'FH0017', '鱼塘', '01', '<p>鲱鱼可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1692, 'fish', 'FH0017', '任务', '01', '<ul><li>在春季和冬季时，皮埃尔的杂货店外的布告栏板上可能会随机鳗鱼的任务。报酬是为每条鱼90g和150友谊点。</li><li>在春季和冬季时，威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-5条鲱鱼。报酬为每条鱼30g(鱼不需要上交)</li></ul>', NULL),
	(1693, 'fish', 'FH0016', '', '01', '<p><br></p><p><strong>红鲻鱼</strong>是一种可以在夏季和冬季海洋（包括海滩农场）里被发现的鱼。它也可能出现在旅行货车中，售价为225g ~ 1,000。在夏季和冬季翻动垃圾桶也有概率找到红鲻鱼。</p><p>可以使用魔法鱼饵不受季节限制地钓到红鲻鱼。</p>', NULL),
	(1694, 'fish', 'FH0016', '地图', '01', '<p>红鲻鱼可以在海洋中被发现。</p>', NULL),
	(1695, 'fish', 'FH0016', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1696, 'fish', 'FH0016', '裁制', '01', '<p>红鲻鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。红鲻鱼还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p><p>它可以用作红色染料染色。</p>', NULL),
	(1697, 'fish', 'FH0016', '鱼塘', '01', '<p>红鲻鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1698, 'fish', 'FH0016', '任务', '01', '<ul><li>在夏季和冬季时，皮埃尔的杂货店外的布告栏板上可能会随机出现红鲻鱼的任务。报酬是为每条鱼225g和150友谊点。</li><li>在夏季和冬季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条红鲻鱼。报酬为每条鱼75g。(鱼不需要上交)</li><li>在夏季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条红鲻鱼，以获得相当于售价的奖励和农场电脑制作方式。</li></ul>', NULL),
	(1699, 'fish', 'FH0015', '', '01', '<p><br></p><p><strong>太阳鱼</strong>是一种鱼，在春季和夏季的晴朗天气出现，可以在河流（鹈鹕镇与煤矿森林）中钓到。宠物猫好感度达到最大时也有可能会送给玩家一条太阳鱼。你也可以在春季和夏季的垃圾桶中找到它。当然，无论在什么季节，它都会随机出现在旅行货车里，价格为100g ~ 1,000。太阳鱼也可以在河边农场、森林农场和山顶农场的河流里被发现。</p><p>太阳鱼将替换钓鱼小游戏教程中任何不符合条件的鱼或非鱼类物品。</p><p>社区中心鱼缸修复后里面会出现一条太阳鱼。</p><p>可以使用魔法鱼饵不受季节、天气和时间限制地钓到太阳鱼。</p>', NULL),
	(1700, 'fish', 'FH0015', '地图', '01', '<p>太阳鱼可以在河流中找到。</p>', NULL),
	(1701, 'fish', 'FH0015', '收集包', '01', '<p>太阳鱼被用于鱼缸的 河鱼收集包。</p>', NULL),
	(1702, 'fish', 'FH0015', '裁制', '01', '<p>太阳鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。太阳鱼还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(1703, 'fish', 'FH0015', '鱼塘', '01', '<p>太阳鱼可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1704, 'fish', 'FH0015', '任务', '01', '<ul><li>在春季或夏季时，皮埃尔的杂货店外的布告栏板上可能会随机要求一条太阳鱼的任务。报酬是90g和150友谊点。</li><li>在春季或夏季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求1-5条太阳鱼。报酬为每条鱼30g。(鱼不需要上交)</li><li>在春季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条太阳鱼，报酬是等同于鱼基础售价的金币和农场电脑配方。</li></ul>', NULL),
	(1705, 'fish', 'FH0014', '', '01', '<p><br></p><p><strong>狗鱼</strong>是一种鱼，可以在夏季和冬季的所有河流（鹈鹕镇与煤矿森林）以及煤矿森林的池塘中发现。狗鱼也可以在河边农场的任何地方和森林农场的大池塘中捕获。宠物猫好感度达到最大时也有可能会送给玩家一条狗鱼。在夏季和冬季翻动垃圾桶也有概率找到狗鱼。</p><p>可以使用魔法鱼饵不受季节限制地钓到狗鱼。</p>', NULL),
	(1706, 'fish', 'FH0014', '分布图', '01', '<p>狗鱼可以在所有河流以及煤矿森林的池塘中找到。</p>', NULL),
	(1707, 'fish', 'FH0014', '收集包', '01', '<p>狗鱼不用于任何收集包。</p>', NULL),
	(1708, 'fish', 'FH0014', '裁制', '01', '<p>狗鱼可通过缝纫机使用布料制成钓鱼背心。狗鱼还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1709, 'fish', 'FH0014', '鱼塘', '01', '<p>狗鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1710, 'fish', 'FH0014', '任务', '01', '<p>在夏季和冬季，皮埃尔的杂货店外的布告栏板上可能会随机要求一条狗鱼的任务。报酬是300g和150友谊点。</p><p>在夏季和冬季，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求在1-4条狗鱼。报酬为每条鱼100g(鱼不需要上交)。</p>', NULL),
	(1711, 'fish', 'FH0013', '', '01', '<p><br></p><p><strong>鲶鱼</strong>是一种鱼，可以在春季，秋季的雨天，在河流（小镇+煤矿森林），秘密森林池塘，巫婆沼泽，河边农场，森林农场，山顶农场钓到。其中秘密森林池塘和巫婆沼泽在夏季雨天也可以钓到。如果使用了雨水图腾，冬季也可在这些地点钓到。宠物猫好感度达到最大时也有可能会送给玩家一条鲶鱼。它也有几率在春季和秋季的垃圾桶中出现。旅行货车也可能出售，价格为600g ~ 1,000。莱纳斯有时会送给玩家一条鲶鱼作为礼物。</p><p>社区中心鱼缸修复后里面会出现一条鲶鱼。</p><p>可以使用魔法鱼饵不受季节、天气和时间限制地钓到鲶鱼。</p>', NULL),
	(1712, 'fish', 'FH0013', '地图', '01', '<p>鲶鱼可以在河流、秘密森林池塘和巫婆沼泽找到。</p>', NULL),
	(1713, 'fish', 'FH0013', '收集包', '01', '<p>鲶鱼被用于鱼缸的 河鱼收集包。</p>', NULL),
	(1714, 'fish', 'FH0013', '裁制', '01', '<p>鲶鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。鲶鱼可用于染色。放置在缝纫机内时，鲶鱼可用作<strong>灰色</strong>染料。</p>', NULL),
	(1715, 'fish', 'FH0013', '鱼塘', '01', '<p>鲶鱼可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1716, 'fish', 'FH0013', '任务', '01', '<ul><li><b>物品寄送：</b> 在春季，秋季，皮埃尔的杂货店外的布告栏上可能会随机出现请求一条鲶鱼的任务，任务会奖励600g和150友谊点。</li><li><b>钓鱼：</b> 德米特里厄斯 和 威利 在春季，秋季可能会随机在皮埃尔的杂货店外的布告栏上发布1~4条鲶鱼的需求，奖励是每条鱼200g。（你可以保留这些鱼）</li></ul>', NULL),
	(1717, 'fish', 'FH0012', '', '01', '<p><br></p><p><strong>鲤鱼</strong>是一种鱼，可以在春、夏、秋三季的深山湖泊中钓到（如果玩家选择了荒野农场，也可在农场上钓到），也可以在任何季节的秘密森林、下水道和突变虫穴中钓到。宠物猫好感度达到最大时也有可能会送给玩家一条鲤鱼。有时会以100g ~ 1,000的价格随机出现在旅行货车中。在春季、夏季和秋季翻动垃圾桶也有概率找到鲤鱼。</p><p>鲤鱼在秘密森林中的上钩概率最大，不下雨时，在此处钓到鲤鱼的概率为 41％ ~ 53％。</p>', NULL),
	(1718, 'fish', 'FH0012', '地图', '01', '<p>鲤鱼可以在深山湖泊、秘密森林、下水道以及突变虫穴钓到。</p>', NULL),
	(1719, 'fish', 'FH0012', '收集包', '01', '<p>鱼缸的 湖鱼收集包需要一条鲤鱼。</p>', NULL),
	(1720, 'fish', 'FH0012', '裁制', '01', '<p>鲤鱼可通过缝纫机使用布料制成钓鱼背心。鲤鱼还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1721, 'fish', 'FH0012', '鱼塘', '01', '<p>鲤鱼可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 2 次任务。</p>', NULL),
	(1722, 'fish', 'FH0012', '任务', '01', '<ul><li>在春季、夏季和秋季，皮埃尔的杂货店外的布告栏上可能会随机出现需要1条鲤鱼的求助任务。报酬是90g和150友谊点。</li><li>在春季、夏季和秋季，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏上发布悬赏，要求玩家捕获1~5条鲤鱼。奖励为每条鱼30g，且玩家无需提交钓到的鱼。</li><li>麻哈脂鲤、蛇齿单线鱼和狗鱼的鱼塘在容量从3提升到5的时候，可能会出现要求玩家提供2条鲤鱼的鱼塘任务。</li></ul>', NULL),
	(1723, 'fish', 'FH0011', '', '01', '<p><br></p><p><strong>河鲈</strong>是一种鱼，可以在冬季期间在深山湖泊、河流（鹈鹕镇与煤矿森林）、煤矿森林池塘中发现它。宠物猫好感度达到最大时也有可能会送给玩家一条河鲈。它有时也可以在冬季的垃圾桶中找到。河鲈也会随机以165g ~ 1,000的价格在旅行货车出售。冬日星盛宴期间，皮埃尔的摊位可能会以500g的价格出售一条河鲈。</p><p>可以使用魔法鱼饵不受季节限制地钓到河鲈。</p>', NULL),
	(1724, 'fish', 'FH0011', '分布图', '01', '<p>河鲈可以在深山湖泊和河流以及煤矿森林的池塘中找到。</p>', NULL),
	(1725, 'fish', 'FH0011', '收集包', '01', '<p>河鲈不用于任何收集包。</p>', NULL),
	(1726, 'fish', 'FH0011', '裁制', '01', '<p>河鲈可通过缝纫机使用布料制成钓鱼背心。河鲈还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1727, 'fish', 'FH0011', '鱼塘', '01', '<p>河鲈可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1728, 'fish', 'FH0011', '任务', '01', '<ul><li>在冬季，皮埃尔的杂货店外的布告栏上可能会随机出现要求一条河鲈的任务，报酬是165g和150友谊点。</li><li>在冬季，德米特里厄斯或威利可能在皮埃尔的杂货店外的布告栏上要求1-4条河鲈，每条鱼55g的奖励。（你可以保留鱼）</li><li>在冬季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条河鲈，报酬是等同于鱼基础售价的金币和农场电脑配方。</li></ul>', NULL),
	(1729, 'fish', 'FH0010', '', '01', '<p><br></p><p><strong>大眼鱼</strong>是一种鱼，在秋天和冬天的雨天出现，可以在河流（鹈鹕镇与煤矿森林）、深山湖泊、煤矿森林池塘钓到。如果使用了雨水图腾，在冬季也可钓到。宠物猫好感度达到最大时也有可能会送给玩家一条大眼鱼。你也可以在秋季和冬季的垃圾桶中找到它。无论在什么季节，它都会随机出现在旅行货车里，价格为315g ~ 1,000。如果玩家选择了河边农场或森林农场，也可以在农场池塘中捕获大眼鱼。</p><p>可以使用魔法鱼饵不受季节、天气和时间限制地钓到大眼鱼。</p><p>大眼鱼在煤矿森林的池塘中最为常见。在秋季的雨天，每次抛竿平均有32%的概率钓到大眼鱼。</p>', NULL),
	(1730, 'fish', 'FH0010', '地图', '01', '<p>大眼鱼可以在秋季和冬季的雨天时在河流、深山湖泊和煤矿森林的池塘中找到。</p>', NULL),
	(1731, 'fish', 'FH0010', '收集包', '01', '<ul><li>大眼鱼被用于鱼缸的 夜间垂钓收集包。</li><li>1个金/铱星品质的大眼鱼被用于鱼缸（重新混合后）的 优质鱼收集包。</li></ul>', NULL),
	(1732, 'fish', 'FH0010', '裁制', '01', '<p>大眼鱼可通过缝纫机使用布料制成钓鱼背心。大眼鱼还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1733, 'fish', 'FH0010', '鱼塘', '01', '<p>大眼鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1734, 'fish', 'FH0010', '任务', '01', '<ul><li>在秋季和冬季，皮埃尔的杂货店外的布告栏上可能会随机出现需要一条大眼鱼的求助任务。报酬是315g和150友谊点。</li></ul>', NULL),
	(1735, 'fish', 'FH0009', '', '01', '<p><br></p><p><strong>鲑鱼</strong>是一种鱼，可以在秋季的所有河流（鹈鹕镇与煤矿森林）中钓到。宠物猫好感度达到最大时也有可能会送给玩家一条鲑鱼。你也可以在秋季的垃圾桶中找到它。当然，无论在什么季节，它都会随机出现在旅行货车里，价格为225g ~ 1,000。</p><p>在秋季，任何时刻都可以在煤矿森林南部的瀑布中钓到鲑鱼。</p><p>可以使用魔法鱼饵不受季节限制地钓到鲑鱼。</p>', NULL),
	(1736, 'fish', 'FH0009', '地图', '01', '<p>鲑鱼可以在河流中找到。</p>', NULL),
	(1737, 'fish', 'FH0009', '收集包', '01', '<p>鲑鱼不用于任何收集包。</p>', NULL),
	(1738, 'fish', 'FH0009', '裁制', '01', '<p>鲑鱼可通过缝纫机使用布料制成褪色牛仔上衣。鲑鱼可用于染色。放置在染料罐内时，鲑鱼可用作<strong>红色</strong>染料。放置在缝纫机内时，鲑鱼还可用作<strong>橙红色</strong>染料。</p>', NULL),
	(1739, 'fish', 'FH0009', '鱼塘', '01', '<p>鲑鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1740, 'fish', 'FH0009', '任务', '01', '<ul><li>在秋季时，皮埃尔的杂货店外的布告栏板上可能会出现随机要求一条鲑鱼的任务。报酬是225g和150友谊点。</li><li>在秋季时，德米特里厄斯或者威利可能会在皮埃尔的杂货店外的布告栏板上随机要求1-4条鲑鱼。报酬为每条鱼75g(鱼不需要上交)。</li><li>在秋季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条鲑鱼，报酬是等同于鱼基础售价的金币和农场电脑配方。</li></ul>', NULL),
	(1741, 'fish', 'FH0008', '', '01', '<p><br></p><p><strong>虹鳟鱼</strong>是一种鱼，可于夏季的晴天在河流（鹈鹕镇和煤矿森林）及深山湖泊中钓到。宠物猫好感度达到最大时也有可能会送给玩家一条虹鳟鱼。它有时也可以在夏季的垃圾桶中找到，或者以195g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受季节、天气和时间限制地钓到虹鳟鱼。在夏季20日、21日的鳟鱼大赛期间，无论天气如何，全天都可以在煤矿森林河流里钓到虹鳟鱼。鳟鱼大赛期间，也可以在能钓到煤矿森林鱼类的农场地图中不受限制地钓到虹鳟鱼，如河边农场、山顶农场、森林农场和草原农场。</p>', NULL),
	(1742, 'fish', 'FH0008', '地图', '01', '<p>虹鳟鱼可以在河流（包括鹈鹕镇和煤矿森林）和深山湖泊中找到。</p>', NULL),
	(1743, 'fish', 'FH0008', '收集包', '01', '<p>虹鳟鱼不用于任何收集包。</p>', NULL),
	(1744, 'fish', 'FH0008', '裁制', '01', '<p>虹鳟鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。虹鳟鱼还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(1745, 'fish', 'FH0008', '鱼塘', '01', '<p>虹鳟鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1746, 'fish', 'FH0008', '任务', '01', '<ul><li><b>交易：</b> 在夏季，皮埃尔的杂货店外的布告栏上可能会随机出现要求 1 条虹鳟鱼的求助任务，报酬是195g及150友谊点。</li><li><b>钓鱼：</b> 在夏季，德米特里厄斯或威利可能会于皮埃尔的杂货店外的布告栏上要求玩家捕获1-4条虹鳟鱼，报酬是每条鱼65g。完成任务后可保留鱼。</li><li><b>水生物泛滥：</b>在夏季时，德米特里厄斯可能在镇长的庄园前的特别任务板上发布特别任务，要求玩家捕获10条虹鳟鱼，报酬是等同于鱼基础售价的金币和农场电脑配方。</li></ul>', NULL),
	(1747, 'fish', 'FH0007', '', '01', '<p><br></p><p><strong>小嘴鲈鱼</strong>是一种可以在春季和秋季期间从河流和森林池塘中找到的鱼。有时也会以150g ~ 1,000的价格随机出现在旅行货车中。在春季和秋季翻动垃圾桶也有概率找到小嘴鲈鱼。</p><p>可以使用魔法鱼饵不受季节限制地钓到小嘴鲈鱼。</p>', NULL),
	(1748, 'fish', 'FH0007', '地图', '01', '<p><strong>小嘴鲈鱼</strong>可以在河流和森林池塘中找到。</p>', NULL),
	(1749, 'fish', 'FH0007', '收集包', '01', '<p>它不用于任何收集包。</p>', NULL),
	(1750, 'fish', 'FH0007', '裁制', '01', '<p>小嘴鲈鱼可通过缝纫机使用布料制成钓鱼背心。小嘴鲈鱼可用于染色。放置在染料罐内时，小嘴鲈鱼可用作<strong>橙色</strong>染料。放置在缝纫机内时，小嘴鲈鱼还可用作<strong>棕色</strong>染料。</p><p>它可以用作橙色染料染色</p>', NULL),
	(1751, 'fish', 'FH0007', '鱼塘', '01', '<p>小嘴鲈鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1752, 'fish', 'FH0007', '任务', '01', '<ul><li>在春季或秋季，皮埃尔的杂货店外的布告栏上都可能会随机出现要求小嘴鲈鱼的任务，报酬是每条鱼150g和150友谊点。</li><li>德米特里厄斯或威利可能在春季或秋季在皮埃尔的杂货店外的布告栏上要求1-5条小嘴鲈鱼，每条鱼50g的奖励。（你可以保留鱼）</li></ul>', NULL),
	(1753, 'fish', 'FH0006', '', '01', '<p><br></p><p><strong>大嘴鲈鱼</strong>是一种鱼，一年四季都可在深山湖泊钓到。在春季和秋季期间，约有19%到20%的概率捕获。但在夏季和冬季下降到15%左右。它最有可能在离岸边较远的格子被捕获。 它也可以在荒野农场捕获。宠物猫好感度达到最大时也有可能会送给玩家一条大嘴鲈鱼。它可能会随机出现在垃圾桶中，或在旅行货车中以300g ~ 1,000的价格随机出现。莱纳斯有时会送给玩家一条大嘴鲈鱼作为礼物。</p><p>可以使用魔法鱼饵不受时间限制地钓到大嘴鲈鱼。</p>', NULL),
	(1754, 'fish', 'FH0006', '地图', '01', '<p>大嘴鲈鱼只存在于深山湖泊。</p>', NULL),
	(1755, 'fish', 'FH0006', '收集包', '01', '<ul><li>大嘴鲈鱼被用于鱼缸的 湖鱼收集包。</li><li>1个金/铱星品质的大嘴鲈鱼被用于鱼缸（重新混合后）的 优质鱼收集包。</li></ul>', NULL),
	(1756, 'fish', 'FH0006', '裁制', '01', '<p>大嘴鲈鱼可通过缝纫机使用布料制成钓鱼背心。大嘴鲈鱼还可用作染料罐和缝纫机的<strong>绿色</strong>染料。</p>', NULL),
	(1757, 'fish', 'FH0006', '鱼塘', '01', '<p>大嘴鲈鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1758, 'fish', 'FH0006', '任务', '01', '<ul><li>玩家和乔迪达到四颗心好感度时，她会在任务“砂锅鱼”中请求玩家带一条大嘴鲈鱼，完成任务会触发一段在乔迪家的动画。</li><li><b>交易任务：</b> 在任意季节，皮埃尔的杂货店外的布告栏上可能会出现要求一条大嘴鲈鱼的任务，报酬是 300g和150友谊点。</li><li><b>钓鱼任务：</b> 德米特里厄斯或威利可能会在任意季节在皮埃尔的杂货店外的布告栏上发布要求捕获1-4条大嘴鲈鱼的任务，报酬是每条鱼 100g。完成任务后会保留鱼。</li><li>在春季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条大嘴鲈鱼，报酬是等同于鱼基础售价的金币和农场电脑配方。</li><li>麻哈脂鲤、蛇齿单线鱼和狗鱼可能在鱼塘扩容任务中要求两条大嘴鲈鱼，完成后将池塘容量从5增加到7。</li></ul>', NULL),
	(1759, 'fish', 'FH0005', '', '01', '<p><br></p><p><strong>鲷鱼</strong>是一种鱼，每天18:00后在任何季节都能从河里（流经鹈鹕镇和煤矿森林的河流）钓到。宠物猫好感度达到最大时也有可能会送给玩家一条鲷鱼。它有时也可以在任何季节的垃圾桶中找到，或者以135g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受一天当中的时间限制地钓到鲷鱼。</p>', NULL),
	(1760, 'fish', 'FH0005', '地图', '01', '<p>鲷鱼可以在河流中找到。</p>', NULL),
	(1761, 'fish', 'FH0005', '收集包', '01', '<p>鲷鱼被用于鱼缸的 夜间垂钓收集包。</p>', NULL),
	(1762, 'fish', 'FH0005', '裁制', '01', '<p>鲷鱼可通过缝纫机使用布料制成鱼图案可染色的鱼T恤。鲷鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1763, 'fish', 'FH0005', '鱼塘', '01', '<p>鲷鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1764, 'fish', 'FH0005', '任务', '01', '<ul><li><b>物品快递：</b>一条鲷鱼会在任何季节随机出现在皮埃尔的杂货店外边的“布告栏”上的求助任务中，完成任务的奖励是135g和150友谊值。</li><li>饲养有麻哈脂鲤、蛇齿单线鱼或狗鱼的鱼塘在数量从3提升到5的时候，会出现要求玩家提供两条鲷鱼的鱼塘求助任务。</li></ul>', NULL),
	(1765, 'fish', 'FH0004', '', '01', '<p><br></p><p><strong>沙丁鱼</strong>是一种春季，秋季和冬季可以在海洋中（包括海滩农场）发现的鱼。它有时也可以在同季的垃圾桶中找到，或者以120g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受季节限制地钓到沙丁鱼。</p>', NULL),
	(1766, 'fish', 'FH0004', '地图', '01', '<p>沙丁鱼可以在海洋中找到。</p>', NULL),
	(1767, 'fish', 'FH0004', '收集包', '01', '<p>它被用于鱼缸的 海鱼收集包.</p>', NULL),
	(1768, 'fish', 'FH0004', '裁制', '01', '<p>沙丁鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。沙丁鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p><p>可以用作蓝色染料。</p>', NULL),
	(1769, 'fish', 'FH0004', '鱼塘', '01', '<p>沙丁鱼可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1770, 'fish', 'FH0004', '任务', '01', '<ul><li>所有的季节中，皮埃尔的杂货店外的布告栏上都可能会随机出现要求沙丁鱼的任务，报酬是每条鱼120g和150友谊点。</li><li>在春季或秋季，德米特里厄斯或威利可能在皮埃尔的杂货店外的布告栏上要求1-5条每条沙丁鱼，每条鱼40g的奖励。（你可以保留鱼）</li><li>在春季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条沙丁鱼，以获得相当于售价的data-sort-value=""&gt;奖励和农场电脑制作方式。</li><li>在鱼塘扩容任务中，午夜鱿鱼会要求两条沙丁鱼，将池塘的容量从5增加到8。</li><li>在鱼塘扩容任务鱿鱼中会请求两条沙丁鱼，将池塘的容量从5增加到10。</li></ul>', NULL),
	(1771, 'fish', 'FH0003', '', '01', '<p><br></p><p><strong>金枪鱼</strong>是一种夏季和冬季可以在海洋中（包括海滩农场）发现的鱼。也可以在同季的垃圾桶中随机找到，虽然不常见。它也可以在姜岛西（海洋）、南部、东南部和海盗湾的任何季节捕获。金枪鱼会以300g ~ 1,000的价格随机出现在旅行货车中。</p><p>可以使用魔法鱼饵不受季节限制地钓到金枪鱼。</p>', NULL),
	(1772, 'fish', 'FH0003', '地图', '01', '<p>金枪鱼可以在海洋、姜岛西部（海洋）、南部、东南部和海盗湾中找到。</p>', NULL),
	(1773, 'fish', 'FH0003', '收集包', '01', '<ul><li>它被用于鱼缸的 海鱼收集包.</li><li>1个金星/铱星金枪鱼被用于鱼缸（重新混合后）的 优质鱼收集包.</li></ul>', NULL),
	(1774, 'fish', 'FH0003', '裁制', '01', '<p>金枪鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。金枪鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1775, 'fish', 'FH0003', '鱼塘', '01', '<p>金枪鱼可以通过鱼塘养殖，每 3 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1776, 'fish', 'FH0003', '任务', '01', '<ul><li>在夏季或冬季，皮埃尔的杂货店外的布告栏上可能会随机出现要求金枪鱼的任务，报酬是每条鱼300g和150友谊点。</li><li>德米特里厄斯或威利可能在夏季或冬季在皮埃尔的杂货店外的布告栏上要求1-4条金枪鱼，每条鱼100g的奖励。（你可以保留鱼）</li><li>在夏季时，德米特里厄斯可能在镇长的庄园前的订单板上的特殊订单中要求捕获10条金枪鱼，以获得相当于售价的data-sort-value=""&gt;奖励和农场电脑制作方式。</li></ul>', NULL),
	(1777, 'fish', 'FH0002', '', '01', '<p><br></p><p><strong>鳀鱼</strong>是一种春季和秋季可以在海洋中发现的鱼。在更高的钓鱼等级下，小于最大距离的抛投可能会增加钓到的几率。</p><p>似乎在威利的房子前以低蓄力甩钓竿更容易抓到它。</p><p>可以使用魔法鱼饵不受季节限制地钓到鳀鱼。</p><p>在春季和秋季翻动垃圾桶也有概率找到鳀鱼。</p>', NULL),
	(1778, 'fish', 'FH0002', '地图', '01', '<p>鳀鱼可以在海洋中发现。</p>', NULL),
	(1779, 'fish', 'FH0002', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(1780, 'fish', 'FH0002', '裁制', '01', '<p>鳀鱼可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鳀鱼还可用作染料罐和缝纫机的<strong>蓝色</strong>染料。</p>', NULL),
	(1781, 'fish', 'FH0002', '鱼塘', '01', '<p>鳀鱼可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1782, 'fish', 'FH0002', '任务', '01', '<ul><li>在春季或秋季，皮埃尔的杂货店外的布告栏上可能会随机出现要求鳀鱼的任务，报酬是每条鱼90g和150友谊点。</li><li>德米特里厄斯或威利可能在春季或秋季在皮埃尔的杂货店外的布告栏上要求1-5条每条鳀鱼，每条鱼 30g 的奖励。（你可以保留鱼）。</li></ul>', NULL),
	(1783, 'fish', 'FH0002', '注意', '01', '<ul><li>鳀鱼在任何情况下售价都不会超过67g，把它做成生鱼片 (75g)卖掉赚的钱更多。</li></ul>', NULL),
	(1784, 'fish', 'FH0001', '', '01', '<p><br></p><p><strong>河豚</strong>是一种可以在沙滩抓到的鱼（包括海滩农场），只有在夏季晴天的 12:00 到 16:00 之间才能抓到。也可以在同季的垃圾桶中找到。它也可以在姜岛西（海洋）、南部、东南部和海盗湾的任何季节捕获。或在旅行货车以600g ~ 1,000买到。</p><p>可以使用魔法鱼饵不受季节、天气、时间限制地钓到河豚。</p><p>河豚和水滴鱼是仅有的两种在钓鱼小游戏中运动模式为“漂浮型”的鱼。</p>', NULL),
	(1785, 'fish', 'FH0001', '地图', '01', '<p>河豚可以在海洋和姜岛找到。</p>', NULL),
	(1786, 'fish', 'FH0001', '收集包', '01', '<p>河豚可用于鱼缸的 特色鱼类收集包。</p>', NULL),
	(1787, 'fish', 'FH0001', '裁制', '01', '<p>河豚可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。河豚还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(1788, 'fish', 'FH0001', '鱼塘', '01', '<p>河豚可以通过鱼塘养殖，每 4 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p>', NULL),
	(1789, 'fish', 'FH0001', '任务', '01', '<ul><li>德米特里厄斯在夏季（第2年）的第6天会在"水产调查"任务要求一条河豚。报酬是1,000g和1心友谊。</li><li>皮埃尔的杂货店外的布告栏上可能会在夏季随机出现要求河豚的任务，报酬是每条鱼600g和150友谊点。</li><li>威利可能在夏季在皮埃尔的杂货店外的布告栏上要求1-4条河豚，每条鱼200g的奖励。（你可以保留鱼）</li></ul>', NULL),
	(3319, 'craft', 'DE0043', '', '01', '<p></p><p><b>分解机</b>可以用于销毁打造的物品，并回收其中最有价值的材料。可以在齐先生的核桃房花费 20齐钻 颗齐钻购买。</p>', NULL),
	(3320, 'craft', 'DE0043', '使用', '01', '<p>游戏会检查放进分解机的物品的打造材料并比较这些材料放进出货箱后的售卖价格。回收完成后，它将会 100% 退还售价最高的打造材料。分解物品需要花费 60 分钟（1 小时）。</p><p>没有打造配方的物品与一次可制作多个的物品（如铁围栏和高级生长激素）无法放进分解机。</p><p>下表列出了可以放进分解机的所有物品：</p>', NULL),
	(3321, 'craft', 'DE0043', '提示', '01', '<ul><li>部分从商店购买的物品可以放入分解机中分解为资源，这相当于允许玩家间接从商店购买资源。这些物品包括：</li></ul><ul><li>用上述分解物品获得的一些资源比从商店购买的等量原材料要便宜，即：锭比在铁匠铺购买煤炭和相应的矿石要便宜，粉碎硬木的木材比在第 2 年以后从木匠的商店购买的要便宜。</li><li>渔具可以在任何耐久度下分解。这允许像寻宝者和陷阱浮标这样的特定渔具以很少甚至免费的成本进行刷新。</li><li>可以反复制作和分解宝箱、楼梯、镂空火炬或骷髅火炬等物品，以“收集”特别任务的资源。</li><li>如果尚未找到古代种子古物，可以分解从种子生产器获得的上古种子来获得古物，这样就能用于捐赠。</li><li>如果一个物品的配方在某个时候被改变了（例如骷髅火炬），分解机将根据当前的配方退还价值最高的物品——它不会回头检查最初使用的材料，即使特定物品是在配方改变之前制作的。<li class="ql-indent-1">无论玩家是否拥有捕猎者职业，分解蟹笼始终会得到两个铜锭。</li></li><li>从骷髅洞穴的宝藏房间获得的雨水图腾可以被分解为松露油，以将售价从20g大幅提高到1,065g，拥有工匠职业则是1,491g。</li><li>潘姆在春季25日以及冬季12日和13日当天会在公交车旁留下文字标牌，你可以将其拆除并放进分解机以回收25个木材</li></ul>', NULL),
	(3322, 'craft', 'DE0202', '', '01', '<p><b>珍稀诱钩</b>是一种渔具，可以使玩家在钓鱼时增加钓到稀有鱼的几率。骷髅洞穴中的木乃伊有1%的概率掉落珍稀诱钩。也可作为特殊物品掉落于骷髅洞穴和采石场矿井的怪物，或者打破采石场矿井、骷髅洞穴或火山地牢中的木箱和木桶后可能获得的一种掉落物。</p><p>它是唯一的既不能从鱼店购买也不能通过打造获得的渔具（铅制浮标不可打造但可以在鱼店购买，优质浮标不可购买但可以通过打造获得）。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3323, 'craft', 'DE0202', '普遍效果', '01', '<p>当使用珍稀诱钩垂钓时，钓到所有鱼的概率都会增加。因为它增加了所有鱼的上钩率，所以这间接地降低了钓上垃圾的概率。</p><p>注意： 此处的概率增加指的是算法决定哪种鱼会咬钩，因此使用这个浮标不会影响鱼上钩的时间。</p>', NULL),
	(3324, 'craft', 'DE0202', '单独计算的鱼类', '01', '<p>有三种鱼类被单独计算，并不属于上述概率提升的一部分:传说鱼类，矿井中的鱼，夜市潜水艇中的鱼。这是因为这些鱼上钩的几率是在游戏代码中处理的，而不是在 <samp>data\\fish.xnb</samp> 提供的数据中。</p><p>承接上文，假设使这些鱼上钩的所有条件都满足，珍稀诱钩对三种单独计算的鱼类的影响详情如下：</p><p><b>传说鱼类</b></p><ul><li>钓到绯红鱼的概率从18%增长至25%，这同样适用于绯红鱼之子。</li><li>钓到传说之鱼的概率从10%增长至20%，这同样适用于传说之鱼二代。</li><li>钓到变种鲤鱼的概率从10%增长至20%，这同样适用于放射性鲤鱼。</li><li>钓到𩽾𩾌鱼的概率从20%增长至25%，这同样适用于雌鮟鱇鱼。</li></ul><p><b>矿井鱼类</b></p><p>钓到每种鱼的基础概率取决于钓鱼区域和钓鱼技能等级。珍稀诱钩只对每种鱼增加固定的上钩几率。</p><ul><li>钓到石鱼的概率增加5%。</li><li>钓到冰柱鱼的概率增加4.5%。</li><li>钓到岩浆鳗鱼的概率增加4%。</li></ul><p><b>夜市鱼类</b></p><ul><li>使用珍稀诱钩时，钓到幽灵鱼, 水滴鱼, 大海参和珍珠的概率增加。因此， 钓上其他物品的概率下降。<li class="ql-indent-1">珍稀诱钩会提升使用魔法鱼饵在沙滩钓上夜市鱼的概率，从3.3%增加至6.8%。</li></li></ul>', NULL),
	(3325, 'craft', 'DE0202', '不受影响的鱼', '01', '<p>在一些情况下，鱼的咬钩率不会增加。因为这些鱼的咬钩率是由游戏内的一些代码决定的，而这些代码不允许在钓这些鱼时出现更高的概率，所以用珍稀诱钩也不会增加上钩概率。</p><ul><li>钓到冰川鱼及小冰川鱼的概率并不会增加，尽管它们是传说鱼类。</li><li>在夜市钓到章鱼的概率不会增加，反而会从2.2%降至1.6%，尽管它是游戏中上钩率最低的鱼。</li><li>在火山地牢中钓到岩浆鳗鱼的概率不会增加，但矿井100层会。</li></ul>', NULL),
	(3326, 'craft', 'DE0202', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3327, 'craft', 'DE0202', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3328, 'craft', 'DE0201', '', '01', '<p><b>铅制浮标</b>是一种渔具，可以使玩家在钓鱼时防止绿条触底反弹，达到钓鱼等级6 后可在鱼店以 200g 购买。也会随机出现在旅行货车的货物中，售价为450g ~ 1,000。</p><p>铅制浮标是两种不能通过打造获得的渔具之一，另一个是珍稀诱钩。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3329, 'craft', 'DE0201', '收集包', '01', '<p>铅制浮标不被用于任何收集包。</p>', NULL),
	(3330, 'craft', 'DE0201', '任务', '01', '<p>铅制浮标不被用于任何收集包。</p>', NULL),
	(3331, 'craft', 'DE0198', '', '01', '<p></p><p>迷你锻造台是姜岛火山口锻造台的便携版本，它们的功能完全相同，不同的是迷你锻造台可以放置在任何地方。迷你锻造台的打造配方可通过解锁战斗精通后获得。</p>', NULL),
	(3332, 'craft', 'DE0197', '', '01', '<p></p><p><b>铁砧</b>是一种允许玩家重铸饰品属性的设备，蜥怪的爪子或魔法发胶无法重铸。在领取战斗精通后可以进行打造。</p>', NULL),
	(3333, 'craft', 'DE0197', '使用', '01', '<p>想要使用铁砧，玩家必须在拿着可重铸的饰品的同时与之互动。每次重铸消耗玩家物品栏中的3个铱锭，重铸需要消耗 10分钟。</p><h3>仙女盒</h3><p>仙女盒召唤一名等级可以被铁砧改变的仙女。这个级别的范围从1到5，它决定了回复血量的频率和数量。仙女的视觉风格与其等级无关，无法改变。</p><h3>青蛙蛋</h3><p>青蛙蛋召唤一只饥饿青蛙跟随玩家，其颜色可以被铁砧改变。青蛙的颜色仅仅是装饰，不会影响它的能力。</p><h3>黄金马刺</h3><p>铁砧可以改变黄金马刺速度提升的持续时间。时间范围为5到10秒，此时间确定为秒。六个可能的值概率都是相同，被滚动的几率约为16.67%。</p><h3>寒冰法杖</h3><p>铁砧可以改变寒冰法杖发射冰球的频率和冻结时间。这两个统计数据是相互独立滚动的。发射冰球的时间在3到5秒之间，冻结的时间在2到4秒之间，这些时间被确定为毫秒，所有可能的值概率都是相同的。有0.05%的几率获得任何单个值（<i>例如</i>，3.456秒的频率或2.345秒的冻结时间）。</p><p>当重铸时，寒冰法杖有5%的几率变成“完美”，并更名为“完美寒冰法杖”。完美寒冰法杖会每隔3秒发射一个冰球，将路径上的所有怪物冻结4秒。</p><h3>魔法箭筒</h3><p>魔法箭筒的类型可以被铁砧改变。有四种类型：“常规型”、“完美型”、“快速型”和“重型”。箭筒的类型将决定射击后冷却的时间、最小伤害和最大伤害的可能值。下面列出的生成率是获得每种特定类型的概率。</p><p>对于常规型和重型，冷却时间确定为十分之一秒，对于快速型，则确定为百分之一秒。对于所有的类型，伤害都确定为整数。冷却和伤害是相互独立确定的。这两种属性的所有可能值对其特定类型的可能性都是相同的（<i>例如</i>，“快速魔法箭筒”有约9.09%的几率有0.65秒的冷却时间，20%的几率有9-14的伤害）。当饰品被重铸时，这些属性也会被重新滚动，因此可以用不同的属性获得相同类型的箭筒。</p><h3>鹦鹉蛋</h3><p>鹦鹉蛋召唤一只跟随玩家的鹦鹉，其等级可以被铁砧改变。这个级别的范围从1到4，它决定了敌人掉落金币的概率（每个金币价值250g）。敌人每掉落一枚金币，就有可能再掉落一枚金币。</p><p>当鹦鹉蛋被重铸时，玩家的总收入会限制最高级别。一旦玩家达到收入阈值，每个可能的等级都有同样的概率（<i>例如</i>，如果玩家的收入在750,000g到1,500,000g之间，则有50%的几率获得2级鹦鹉）.</p>', NULL),
	(3334, 'craft', 'DE0197', '笔记', '01', '<ul><li>由于每次重铸饰品时，铁砧的输出都是随机确定的，因此玩家可以在重铸同一个饰品时退出重进存档，以获得不同的属性。当铱锭有限时，这可能很有用。</li></ul>', NULL),
	(3335, 'craft', 'DE0196', '', '01', '<p></p><p><b>祝福雕像</b>是一种可打造物品，每天触摸后都会给玩家带来祝福。祝福雕像的打造配方在达到耕种精通后解锁。</p><p>在合作模式下，如果其中一名玩家制作并摆放了祝福雕像，而另一名玩家在未解锁耕种精通的情况下与祝福雕像互动，会提示一条系统消息“你掌握的知识不足，无法使用这个……”并且无法获得祝福。</p>', NULL),
	(3336, 'craft', 'DE0196', '祝福列表', '01', '<p>每天一次，雕像会从下面的列表中给予玩家一项随机祝福。除蝴蝶祝福与水域祝福外，其他祝福在游戏内的一天里全天生效。</p><p>玩家在游戏内某一天所获得的祝福看似是随机的，实际上在这一天开始时就已经确定了。因此，玩家重新开始这一天或是制作多个祝福雕像都不能改变这一天的祝福。</p>', NULL),
	(3337, 'craft', 'DE0195', '', '01', '<p></p><p><b>矮人之王雕像</b>每天可以互动一次，交互后会提供两种增益供玩家选择，增益效果持续至当天结束，期间不可更改。矮人之王雕像的打造配方来源于精通山洞中采矿精通的奖励。</p>', NULL),
	(3338, 'craft', 'DE0195', '增益', '01', '<p>每天玩家可以在与雕像互动后在两种增益中选择一种。有5种不同的增益，如下所示。</p><h3>每个采矿点 +1 个矿石</h3><p>当此能力激活时，在矿井内外开采特定种类的矿石采矿点将额外掉落一块矿石。 受影响的矿石种类如下：</p><h3>找到晶球的几率更高</h3><p>当此能力激活时，在矿井内外使用十字镐或炸弹破坏岩石时获得晶球的概率提升25%（乘算）。 然而，此能力并不影响在海滩农场补给箱中获得晶球的概率。</p><h3>找到煤炭的几率更高</h3><p>当此能力激活时，矿井里敲碎岩石时掉落煤炭的概率提升40%（乘算），在拥有勘探者职业时提升20%。在非采石场的户外敲碎岩石时，掉落煤炭的概率会提升3%。在户外的任何地方敲碎岩石，都有2.5%的概率额外掉落一块煤炭。这将使玩家找到煤炭的概率比选择了勘探者职业还要高。</p><h3>找到梯子和竖井的几率更高</h3><p>当此能力激活时，在矿井和骷髅洞穴中破坏岩石时生成梯子和竖井的概率提升25%（乘算）。击杀怪物时生成梯子的概率也从平时的15%增加到22%。</p><h3>炸弹无法对你造成伤害</h3><p>当此能力激活时，玩家在矿井内外都会免疫由樱桃炸弹、炸弹、超级炸弹和爆炸弹丸造成的所有伤害。玩家也会免疫火山地牢中熔岩大头的爆炸伤害。</p>', NULL),
	(3339, 'craft', 'DE0194', '', '01', '<p><strong>便携帐篷</strong>是一种可制作物品，允许玩家建造一个帐篷睡觉，它只能在户外使用并且需要2×3的空间放置，且每个便携帐篷只能使用一次。</p><p>在姜岛丛林的一个盒子里可以发现三个便携帐篷。</p><p>玩家在鳟鱼大赛第一次使用黄金标签兑换奖励时可以获得1个便携帐篷。</p><p>如果玩家已经使用了金色Joja鹦鹉，那么蜗牛教授将赠送一个便携帐篷作为将蝙蝠木乃伊捐赠给岛屿办事处的奖励。</p>', NULL),
	(3340, 'craft', 'DE0194', '用途', '01', '<p>玩家通过选择2x3区域的左下角将便携帐篷放置到室外。如果没有足够的空间，玩家尝试在室内（例如矿井）使用便携帐篷，或第二天是节日，则会显示错误消息。否则，便携帐篷将被放置，玩家可以像使用农舍中的床一样使用帐篷。使用帐篷后，游戏将被保存，玩家第二天早上6点将在同一位置醒来，帐篷本身会消失。在多人游戏中，多人可以同时使用帐篷。放置在室外的未使用的帐篷套件将在第二天消失。</p>', NULL),
	(3341, 'craft', 'DE0194', '收集包', '01', '<p>便携帐篷不被用于任何收集包</p>', NULL),
	(3342, 'craft', 'DE0194', '裁缝', '01', '<p>便携帐篷不被用于任何裁缝，它可以作为橙色的染料，用于缝纫机的染料罐。在进料口中如果有可染色的衣服，它也可以用作棕色的染料。</p>', NULL),
	(3343, 'craft', 'DE0194', '任务', '01', '<p>便携帐篷不被用于任何任务</p>', NULL),
	(3345, 'craft', 'DE0193', '', '01', '<p></p><p><b>文字标牌</b>是一种可打造物品，玩家可以在上面写入一段文本。当玩家靠近文字标牌或将鼠标悬停在其上时，文字标牌会出现一个对话气泡来展示存有的文本，这段文本可以通过与文字标牌互动以重新修改。</p><p>文字标牌可写入的字数上限是60个字符。</p><p>巴士站修复之后，如果潘姆当天休息，她会在公交车旁留下一个文字标牌，通知玩家需要自己开巴士去沙漠。文字标牌会在第二天自动移除，玩家也可以将其提前占为己有。</p>', NULL),
	(3346, 'craft', 'DE0192', '', '01', '<p></p><p><b>石制大箱子</b>是一种可以储存多达70种不同物品的容器，其储存量几乎为普通石箱(能储存36种物品)的两倍。在普通的宝箱或石箱上使用以替换之，同时保留原来的内容物并掉落旧的宝箱或石箱。石制大箱子的合成配方可以在矿井中的矮人处购买，但需要先从罗宾处获得石箱的配方(需要完成特殊任务罗宾的资源活动)。</p><p>可以用工具击打箱子来移动箱子。村民路径中的<b>石制大箱子</b>会被推走而不是被摧毁。</p>', NULL),
	(3347, 'craft', 'DE0192', '颜色', '01', '<p>打开箱子时，在物品窗口上方有一串不同颜色的方块，可让玩家选择箱子的外观颜色。颜色可以随时更改，无需付费。颜色选择器中有20种可供选择的颜色，外加一个未选择颜色时的默认颜色。</p><p></p>', NULL),
	(3348, 'craft', 'DE0191', '', '01', '<p></p><p><b>大箱子</b>是一种用于存储物品的设备，最多可以存储70种不同类型的物品，几乎是普通宝箱的两倍容量。</p><p>拿在手上对宝箱，石箱，石制大箱子其中之一可以直接替换原来的箱子。</p><p>可以用工具击打箱子来移动箱子。村民路径中的<b>大箱子</b>会被推走而不是被摧毁。</p>', NULL),
	(3349, 'craft', 'DE0191', '颜色', '01', '<p>打开箱子时，在物品窗口上方有一串不同颜色的方块，可让玩家选择箱子的外观颜色。颜色可以随时更改，无需付费。颜色选择器中有20种可供选择的颜色，外加一个未选择颜色时的默认颜色。</p><p></p>', NULL),
	(3350, 'craft', 'DE0190', '', '01', '<p></p><p><b>鱼饵制造机</b>是一种精炼设备，用于将鱼转化为针对性鱼饵。加工鱼饵需要 10 分钟。制作配方在达到钓鱼等级6级时解锁。</p><p>将任意鱼放入鱼饵制造机，就可以得到“[鱼的名字]鱼饵”，用它来钓鱼能够增加钓上这种鱼的概率。所有鱼类都可以放入此设备，包括传说鱼类和蟹笼鱼类。海草、藻类和凝胶不能放入设备。</p>', NULL),
	(3351, 'craft', 'DE0190', '提示', '01', '<ul><li>鱼饵制造机每次产生约 5-10 个鱼饵，这使得玩家可以专注于一种价值较高的鱼，搭配熏鱼机可以获得可观的利润。</li><li>在使用鱼饵制造机时，玩家可能需要权衡鱼的稀有程度和钓鱼难度，鱼饵并不能保证每次钓上来的都是对应的鱼，所以玩家可能完全会浪费掉用来制作鱼饵的昂贵的鱼（例如岩浆鳗鱼）。<li class="ql-indent-1">使用稀有鱼类打造的鱼饵时，玩家可以使用珍稀诱钩并在鱼对应的活动时间和地点内钓鱼来提高钓到对应鱼的机会。</li></li><li>使用特定鱼饵钓鱼并不能让玩家在鱼活动时间和特定活动地点之外钓上鱼，而会被杂物替代（比如海凝胶和垃圾等），即便玩家有着高钓鱼等级并在 5 级钓鱼区。<li class="ql-indent-1">例如，如果在 22：00 使用比目鱼鱼饵在海洋钓鱼（比目鱼的出现时间是 6：00-20:00），玩家会钓到海凝胶、垃圾、海草等。</li></li><li>在冬季的鱿鱼节上，使用鱼饵制造机制造鱿鱼鱼饵可以让玩家钓上大量鱿鱼，从而轻松获得所有的钓鱼奖励。</li></ul>', NULL),
	(3352, 'craft', 'DE0190', '趣闻', '01', '<ul><li>由于鱼饵制造机加工鱼饵只需要 10分钟，如果玩家投入鱼的瞬间游戏时间发生了变化（即度过了 10 分钟），那么鱼饵会立即完成。</li><li>制作出的鱼饵的颜色和玩家投入的鱼的颜色是相近的。</li><li>玩家可以将传说鱼类放进鱼饵制造机中，并产生对应的鱼饵。但这没有用处，因为每个存档玩家只能捕捉到一条传说鱼类。</li></ul>', NULL),
	(3353, 'craft', 'DE0189', '', '01', '<p></p><p><b>高级虫饵盒</b>是一种精炼设备，用于每天稳定产出钓鱼用的高级鱼饵，其打造配方在达到钓鱼等级 8 时解锁。</p><p>高级虫饵盒可以在每日开始时产出4-5个高级鱼饵，不需要投入任何原料。</p>', NULL),
	(3354, 'craft', 'DE0188', '', '01', '<p></p><p><b>重型熔炉</b>是一种精炼设备，是熔炉的进阶版本。其需要 25 块矿石（或 5 块石英/火水晶）和 3 块煤炭来运行。重型熔炉的打造配方在达到采矿精通后获得。</p>', NULL),
	(3355, 'craft', 'DE0188', '熔炼', '01', '<p>只要玩家物品栏中有足够的煤炭，将材料拿在手上并点击重型熔炉，就可以进行熔炼。</p><p>无论材料或时间长短，每次熔炼都需要 3 块煤炭。此外在熔炼时，有一定概率额外产出一个矿锭。每种产出的概率相同，即矿石有 50% 的概率产生 5 个，有 50% 的概率产生 6 个；火水晶产出 15~20 个精炼石英的概率均为 1/6。</p><p>当熔炼出五个时，与正常的熔炉相比，重型熔炉可节省 40% 的煤炭。当熔炼出六个时，与正常的熔炉相比可节省 50% 的煤炭和六分之一的矿石。</p>', NULL),
	(3356, 'craft', 'DE0187', '', '01', '<p><b>宝藏图腾</b>是一种消耗品，打造配方在达到采集精通后获得。当在室外可挖掘的地块上使用时，它会在玩家周围的一个圆圈中产生 16 个远古斑点：在北部、东部、南部和西部各生成 3 个，在东北部、东南部、西南部和西北部各生成 1 个。如果有障碍物占据了某格地块，对应位置就不会生成远古斑点。</p>', NULL),
	(3357, 'craft', 'DE0187', '收集包', '01', '<p>宝藏图腾不被用于任何收集包。</p>', NULL),
	(3358, 'craft', 'DE0187', '裁制', '01', '<p>宝藏图腾可通过缝纫机使用布料制成 图腾面具。宝藏图腾可用于染色。放置在染料罐内时，可用作<b>橙色</b>染料。放置在缝纫机内时，宝藏图腾还可用作<b>棕色</b>染料。</p>', NULL),
	(3359, 'craft', 'DE0187', '任务', '01', '<p>宝藏图腾不被用于任何任务。</p>', NULL),
	(3360, 'craft', 'DE0187', '提示', '01', '<p>在姜岛的可挖掘地形使用宝藏图腾，并使用带有丰富附魔的锄头，平均可产出价值3,000g的宝藏（这需要将获得的万象晶球兑换为古物宝藏，并在铁匠处砸开它们）。</p>', NULL),
	(3361, 'craft', 'DE0186', '', '01', '<p><b>声纳浮漂</b>是一种渔具，可以使玩家在钓鱼时提前知道上钩的鱼的种类。达到钓鱼等级6 后可以通过打造获得，同时解锁鱼店以500g出售，并可能出现在钓鱼宝箱中（3-4% 概率）。达到钓鱼精通后，也有 9-10% 的概率在金色钓鱼宝箱中找到。</p><p>装备声纳浮漂时，钓鱼小游戏界面右上角提前显示将会咬钩的鱼的种类。注意，在高级铱金鱼竿上装备两个声纳浮漂不会有任何额外收益。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3362, 'craft', 'DE0186', '收集包', '01', '<p>声纳浮漂不用于任何收集包。</p>', NULL),
	(3363, 'craft', 'DE0186', '染色', '01', '<p>声纳浮漂可用作放置在缝纫机内时的<b>灰色</b>染料。</p>', NULL),
	(3364, 'craft', 'DE0186', '任务', '01', '<p>声纳浮漂不用于任何任务。</p>', NULL),
	(3365, 'craft', 'DE0185', '', '01', '<p><b>挑战鱼饵</b>是一种鱼饵，可以使玩家在钓鱼时一次钓到3条鱼。达到钓鱼精通后可以通过打造获得，也有7%的概率在金色钓鱼宝箱中找到。</p><p>在钓鱼时使用挑战鱼饵可以使鱼的上钩时间减少62.5%，并且达成完美钓鱼时可以获得3条鱼。当鱼每次脱离绿条，就减少一条鱼，捕获的鱼数量会以图标的形式显示在钓鱼小游戏界面的右上方。<b>当鱼第三次脱离“钓鱼条”时，会直接判定为脱钩。</b>钓3条鱼的效果对传说鱼类同样有效。挑战鱼饵也可以在蟹笼中使用，不过效果与普通鱼饵完全相同。</p><p>注意，使用挑战鱼饵一次钓到3条鱼不会获得3倍的钓鱼经验值，但是在进行钓鱼相关任务时会将3条鱼计入所需总数。</p>', NULL),
	(3366, 'craft', 'DE0185', '使用', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3367, 'craft', 'DE0185', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3368, 'craft', 'DE0184', '', '01', '<p><b>高级鱼饵</b>是一种鱼饵，可以使玩家在钓鱼时的绿条变长。达到钓鱼等级4 后可以通过打造获得，同时解锁鱼店以100g出售。</p><p>高级鱼饵也有许多其他获取途径，具体为：</p><ul><li>达到钓鱼等级6 后可以在钓鱼宝箱中发现。</li><li>完成 河鱼收集包可以获得30个高级鱼饵。</li><li>高级虫饵盒每天产出4-5个高级鱼饵。</li><li>在鳟鱼大赛期间可用 黄金标签兑换20个高级鱼饵。</li><li>冬季12日的鱿鱼节的铜牌奖励为20个高级鱼饵。</li><li>在书摊老板处可以使用书籍“鱼饵和浮漂”换取30个高级鱼饵。</li></ul><p>在钓鱼时使用高级鱼饵可以使鱼上钩的时间减少67%，同时增加12像素的绿条尺寸。在蟹笼中使用高级鱼饵会降低一半捕获垃圾的概率（如果玩家没有水手职业），同时蟹笼产出的鱼类均会变为银星品质。</p>', NULL),
	(3369, 'craft', 'DE0184', '使用', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3370, 'craft', 'DE0184', '收集包', '01', '<p>高级鱼饵不被用于任何收集包。</p>', NULL),
	(3371, 'craft', 'DE0184', '任务', '01', '<p>高级鱼饵不被用于任何任务。</p>', NULL),
	(3372, 'craft', 'DE0184', '裁制', '01', '<p>高级鱼饵不可用于裁缝或染色。</p>', NULL),
	(3373, 'craft', 'DE0183', '', '01', '<p></p><p><b>神秘树种</b>是一种树苗，可以长出神秘树。解锁采集精通后即可获得神秘树种的制作配方，且只能通过制作获得。摇晃或砍伐神秘树无法获得神秘树种，神秘树成熟后也不会掉落任何种子。</p><p>与其他树不同，向神秘树种使用树肥每晚仅有 30% 的概率生长到下一阶段，包括冬季。未施肥的神秘树种在春季、夏季或秋季每晚有 15% 的概率生长到下一阶段。</p>', NULL),
	(3374, 'craft', 'DE0183', '收集包', '01', '<p>神秘树种不用于任何收集包。</p>', NULL),
	(3375, 'craft', 'DE0183', '任务', '01', '<p>神秘树种不用于任何任务。</p>', NULL),
	(3376, 'craft', 'DE0183', '裁缝', '01', '<p>神秘树种不用于任何裁缝和染色。</p>', NULL),
	(3377, 'craft', 'DE0182', '', '01', '<p></p><p><b>蓝色草籽</b>放置在地上会立即成为一丛蓝草。随着时间推移，草丛会在农场中生长和扩散。与草相同，当筒仓建好后用镰刀收割蓝草草丛会获得干草，但是会产出双份。</p><p>蓝色草籽可以在冬季种植，但是不会在农场中生长和扩散。</p>', NULL),
	(3378, 'craft', 'DE0182', '裁缝', '01', '<p>蓝色草籽不可通过裁缝制成任何衣物。蓝色草籽可用作染料罐和缝纫机的<b>蓝色</b>染料。</p>', NULL),
	(3379, 'craft', 'DE0181', '', '01', '<p></p><p><b>熏鱼机</b>是一种工匠设备，用于将鱼加工成熏鱼，其打造配方通过在鱼店以10,000g的价格购买解锁。</p><p>熏鱼机每次加工鱼都需要消耗一个煤炭，加工后的制品为熏鱼，其保留鱼品质的同时将基础售价提升至原来的 2 倍，并将恢复的能量和生命值提升 1.5 倍。熏鱼机也可以用于加工传说鱼类以及部分蟹笼鱼类，且部分不可食用的鱼加工后变为可食用。</p><p>熏鱼机也可作为兑奖机的第12个奖品（50%概率）获得。玩家如果选择了河边农场，那么游戏开始后每个农舍内都会有一台熏鱼机，找罗宾新建的每个联机小屋内也会自带一台熏鱼机。这意味着选择河边农场可以以每台100g的成本不断建造拆除联机小屋获得无限量的熏鱼机。</p>', NULL),
	(3380, 'craft', 'DE0181', '产物', '01', '<p>熏鱼属于工匠物品，所以可同时获得钓鱼职业与工匠的价格加成，最高的价格加成可以让鱼的价值增加至基础售价的420%。熏鱼的品质与烟熏前相同。</p><p><br/></p>', NULL),
	(3381, 'craft', 'DE0180', '', '01', '<p></p><p><b>烘干机</b>是一种工匠设备，用于将各种水果和蘑菇烘干并加工成工匠物品，其打造配方通过在皮埃尔的杂货店以10,000g的价格购买解锁。</p><p>烘干机也可作为兑奖机的第12个奖品（50%概率）获得。玩家如果选择了蘑菇山洞也会附带一台烘干机。</p>', NULL),
	(3382, 'craft', 'DE0180', '产物', '01', '<p>烘干机加工需要五个<b>类型</b>和<b>品质</b>都相同的蘑菇或水果，无论何时放入都会在次日早上完成并产出。</p>', NULL),
	(3383, 'craft', 'DE0180', '提示', '01', '<ul><li>烘干后物品的品质星级<b>不会</b>保留。</li><li>一些不可食用的水果在烘干后转变为可食用。回复能量的计算公式为：<samp>[基础水果售价÷ 2 × 0.5 × 5]</samp>, 或 <samp>[基础水果售价 × 1.25]</samp>. 这与基础银星品质的基准卖价计算方法相同，但是计算结果是四舍五入而不是抹零。<li class="ql-indent-1">上古水果烘干后可回复 688 能量和 309 生命值。</li><li class="ql-indent-1">椰子烘干后可回复 125 能量和 56 生命值。</li><li class="ql-indent-1">大黄烘干后可回复 275 能量和 123 生命值。</li></li><li>由于烘干机需要 5 个水果或者蘑菇，而产品的基础恢复效果只是原来的 3 倍，所以食用水果干和蘑菇干是亏损的。</li><li>烘干机可以有效地使金星品质的蘑菇或水果以每个价格 +5 出售。</li><li>宝石甜莓不属于水果，松露不属于蘑菇，二者都不能放入烘干机。</li></ul>', NULL),
	(3384, 'craft', 'DE0179', '', '01', '<p><b>蘑菇树桩</b>是一种可自然生产蘑菇的可打造物品，打造配方于采集4级时解锁。兑奖机的第7个奖品有33%的概率获得4个蘑菇树桩。收获蘑菇树桩的蘑菇将获得5点采集经验。</p>', NULL),
	(3385, 'craft', 'DE0179', '机制', '01', '<p>蘑菇树桩每四天自然产出一次蘑菇，下雨会导致其提前一天产出蘑菇。蘑菇树桩一年四季都可以产出蘑菇。</p><h3>数量</h3><p>蘑菇的产量受树（注意，果树不计入在内）的数量的影响——以蘑菇树桩为中心，计入7×7的范围内的树（树的任意阶段都会被计入，包括树种）。蘑菇的产量最少为1个，最大不超过5个。</p><p>计算方式如下：树的数量÷2，取整，然后有50%的概率×2。如果要保证达到最大的产量（5个蘑菇），则需要保证范围内有10棵树。</p><p>假设7×7范围内有7棵树，7÷2=3.5，取整为3，此时50%概率产出3个蘑菇，50%产出2×3=6，即5个蘑菇。</p><h3>种类</h3><p>蘑菇的种类受树的种类的影响。游戏会创建一份蘑菇的列表，然后从中随机选出一种作为最终的产出，具体步骤如下。</p><ul><li><b>基础分化列表：</b>计算计入7×7的范围内树的数量（包括未成熟的树），将其乘以<sup>3</sup>⁄<sub>4</sub>，然后取整，得到数字 n（n 小于1时取1），选取 n 种随机蘑菇，加入列表。<li class="ql-indent-1">每次选取时，5%的概率是紫蘑菇，14.25%的概率是红蘑菇，80.75%的概率是普通蘑菇。</li></li><li><b>成熟特定列表：</b>计算范围内成熟的树，每成熟一棵则加入一种对应的蘑菇到列表。<li class="ql-indent-1">橡树对应羊肚菌；松树对应鸡油菌；神秘树对应紫蘑菇；枫树则10%的概率加入紫蘑菇、90%的概率加入红蘑菇；其他种类的树则按基础分化列表的概率计算并加入。</li></li><li>从上述最终形成的列表随机选取一种蘑菇作为最终产出。</li></ul><p>由此可得，因为基础列表的存在，不可能保证蘑菇树桩生成特定的某种蘑菇，增加指定的树也无法保证能够提升其对应的蘑菇的概率（二者的关系并非线性对应）。</p><ul><li>假设一个蘑菇树桩范围内有3棵橡树、1棵松树、4棵桃花心木树且均已成熟，那么根据数学期望，产出紫蘑菇的概率为<sup>1</sup>⁄<sub>28</sub>，红蘑菇为<sup>57</sup>⁄<sub>560</sub>≈10.18%，普通蘑菇为<sup>323</sup>⁄<sub>560</sub>≈57.68%，羊肚菌为<sup>3</sup>⁄<sub>14</sub>，鸡油菌为<sup>1</sup>⁄<sub>14</sub>。</li><li>假设一个蘑菇树桩范围内只有一种树（橡树、松树或神秘树）且均已成熟，那么产出对应蘑菇的概率理论上是固定的：橡树与松树必然为<sup>4</sup>⁄<sub>7</sub>≈57.14%，而神秘树产出紫蘑菇的概率必然为<sup>83</sup>⁄<sub>140</sub>≈59.29%。<li class="ql-indent-1">由于第一步向下取整会造成误差，上述概率会有所浮动，并以4为周期回归，理论值适用于4的倍数，实际值如下方表格所示。</li></li></ul><p>下表给出了蘑菇树桩周围仅有一种成熟的树时蘑菇产出的概率。如果周围有混合的成熟树木种类，则需要单独计算复合的概率产出。鉴于未成熟的树仅影响基础生产的蘑菇数量，而非加入列表的对应特殊蘑菇的数量，这里提供的数值仅在周围所有一种树都完全成熟的情况下准确。</p><h3>品质</h3><p>蘑菇树桩生产的蘑菇可能具有不同的品质，但不受植物学家职业的影响，而是取决于范围内的树的数量，以及它们是否带有苔藓。计数时，每棵带有苔藓的树计为两棵。将这一计数结果除以40，得数即为蘑菇品质升至下一级的成功率。蘑菇的品质会从普通开始，以这一成功率反复尝试升至下一级，直到达到铱星品质或失败。举例而言，如果一个蘑菇树桩范围内的树计有20棵，则升级成功率为50%，也就是说生产的蘑菇有50%的概率为普通品质，25%的概率为银星，12.5%的概率为金星，12.5%的概率为铱星。如果计有40棵则成功率为100%，此时一定会产出5个铱星品质的蘑菇。</p>', NULL),
	(3386, 'craft', 'DE0067', '', '01', '<p><b>顶级肥料</b>是一种可以用于培育高品质作物的肥料。顶级肥料可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。</p><p>撒上顶级肥料的作物将有几率收获<b>铱星品质</b>，这也是目前唯一得到铱星品质农作物的方法。</p><p>顶级肥料只影响基础的作物收获，对于可以多次收割农产品的农作物（例如土豆、蓝莓或蔓越莓）单次收割获得的额外农产品一般是普通品质。</p><p>配方可在齐先生的核桃房使用20齐钻购买。</p>', NULL),
	(3387, 'craft', 'DE0067', '作物品质比例', '01', '<p>使用顶级肥料时不会产出普通品质的作物。</p><p><br/></p>', NULL),
	(3388, 'craft', 'DE0067', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3389, 'craft', 'DE0126', '', '01', '<p></p><p><b>纤维种子</b>是一种通过打造得到的种子。种下后无需浇水，并且可以在冬季生长，但必须种在可耕种的土壤上。如果不在稻草人的保护范围中，它们也可能会被乌鸦吃掉。</p><p>制造配方在完成了特别任务告示板上的特别任务：社区清理后，由莱纳斯附信赠予。</p><p>在完全长成后用镰刀收获可以掉落大约4到7个纤维，还有10%的概率掉落一个混合种子。</p>', NULL),
	(3390, 'craft', 'DE0126', '生长阶段', '01', '<p><br/></p>', NULL),
	(3391, 'craft', 'DE0126', '提示', '01', '<ul><li>可以通过在每个季节（特别是秋季）最后一天种植纤维种子以避免换季造成的肥料流失。</li><li>虽然纤维并非一种农作物，在祝尼魔屋范围内的祝尼魔也会收集成熟的纤维。</li></ul>', NULL),
	(3392, 'craft', 'DE0151', '', '01', '<p></p><p><b>铱环</b>是一种戒指，可以在达到 战斗等级 9 后通过打造获得，也能在达到钓鱼等级 2 后在钓鱼宝箱中发现。击杀怪物铱石魔也有概率掉落铱环，也能在“深处的危险”任务期间或激活“挑战之神庙”后在矿井中打破木箱和木桶或通过杀死怪物后的掉落物获得。</p><p>铱环结合了光辉戒指、磁铁戒指和红宝石戒指的所有效果（发出半径10格的光并增加2格收集范围同时提升10%攻击），并且可与另一个铱环、辉石戒指、光辉戒指、小型光辉戒指、磁铁戒指、小型磁铁戒指、或红宝石戒指叠加。</p>', NULL),
	(3393, 'craft', 'DE0151', '染色', '01', '<p>铱环可用作染料罐的<b>紫色</b>染料，放置在缝纫机内时还可用作<b>亮紫色</b>染料。</p>', NULL),
	(3394, 'craft', 'DE0044', '', '01', '<p></p><p><b>工作台</b>可以让玩家使用旁边宝箱内的材料进行打造。在木匠的商店以2,000g购买。</p><p>将工作台放置于宝箱旁可以直接使用宝箱内储存的材料进行打造。这与农舍内的冰箱的工作原理是相似的。</p><p>工作台只会从普通宝箱中拿取物品，不包括祝尼魔箱。工作台可以拿取邻接的8个格子上宝箱中的物品。如果多个宝箱中有相同的物品，工作台拿取物品的顺序取决于宝箱的相对位置，按照如下顺序决定：</p><p>如果一个宝箱中所需的物品不足，工作台会尝试从多个宝箱中拿取物品。</p>', NULL),
	(3395, 'craft', 'DE0145', '', '01', '<p></p><p><b>一簇鲜花</b>是一种可制作的家具，会在春季和夏季盛开。可以在花舞节时从皮埃尔的摊位购买制作配方或是成品。</p>', NULL),
	(3396, 'craft', 'DE0145', '注意', '01', '<p>在对应季节从皮埃尔的杂货店里购买打造配方所需的四种种子，总共只花费200g。</p>', NULL),
	(3397, 'craft', 'DE0029', '', '01', '<p></p><p>花盆是一种可以在室内种植任何季节作物，或者在户外种植当季作物的打造物品。但花盆无论在什么季节、什么地方都不能种植上古种子。</p><p>在修复温室后，艾芙琳将会到访玩家的农场，并给玩家一个花盆，以及花盆的打造配方。这个场景将会在晴天玩家上午6:00到11：00玩家离开房间时触发。在通过完成社区中心的茶水间收集包建造温室后，事件在前3天内无法触发，可能需要更长时间。但是，通过Joja社区发展申请书修复温室时，事件不会有3天的延迟。</p>', NULL),
	(3398, 'craft', 'DE0029', '用途', '01', '<p>花盆可以放置在任何室内场所用于种植农作物，不受天气的影响。室内场所包括所有的农场建筑、农场山洞、隧道、下水道、突变虫穴、巫婆沼泽、矿井和骷髅洞穴的入口，以及居民的房子里。但在农场之外的区域里的花盆将不能放置种子。</p><p>由于洒水器无法为其浇水，所以花盆需要由玩家每天使用喷壶浇水。花盆里能使用所有种类的种子或肥料，肥料使用的限制与收益与在土地里使用时相同。</p><p>放置后，花盆可以使用工具移除，也可以长按鼠标左键移除。但如果移除时花盆里有种子或农作物，种子或农作物将会被摧毁。</p><p>种植在花盆中的花朵可以使附近的蜂房产出相应的蜂蜜。</p>', NULL),
	(3399, 'craft', 'DE0029', '注意', '01', '<ul><li>如果试图在花盆中种植上古种子，将会出现信息“她的根长得很深...她在那里不会高兴。”</li><li>如果试图在农场之外的花盆里种植当季的种子或仙人掌种子，将会出现信息“必须种在农场上。”。</li><li>种植在户外花盆中的仙人掌种子会在第二天死亡。</li><li>放置在村民的行走路径上的花盆会被摧毁。有关安全位置的地图，请参阅打造。</li><li>花盆和温室一样，不会长出巨大作物。</li></ul>', NULL),
	(3400, 'craft', 'DE0041', '', '01', '<p></p><p><b>咖啡机</b>每天早晨会自动提供一杯咖啡。玩家在完成特别任务“给乔治的礼物”后，艾芙琳会邮寄一个咖啡机给玩家。 </p>', NULL),
	(3401, 'craft', 'DE0123', '', '01', '<p></p><p><b>秋季种子</b>是一种通过打造得到的种子。打造配方的名字是“野生种子（秋季）”，但得到的物品是“秋季种子”。作物成熟时，会随机变为黑莓、普通蘑菇、榛子和野梅。</p><p>秋季种子和春季种子，夏季种子以及冬季种子一样，是一种只能在特定季节种下的野生种子，长出的作物通常也是对应季节的采集物品。通常，耕种技能可以提升长出高品质作物的几率，但冬季种子长出高品质作物的几率是由采集技能决定的。选择“植物学家”职业可以保证收获的作物都是铱星品质。</p><p>由秋季种子长成的作物可能会在收获阶段使所在的地块变为没有锄过的地块。这时，所有使用过的肥料效果都会消失。</p><p>乌鸦不会侵害由秋季种子长成的作物，因此并没有必要使用稻草人。</p>', NULL),
	(3402, 'craft', 'DE0123', '来源', '01', '<ul><li>打造 (每次制造生产10份秋季种子。)</li><li>把一个普通蘑菇放进种子生产器（产出：1-3份秋季种子）</li><li>完成工艺室的 秋季采集收集包 （奖励：30份秋季种子）</li><li>旅行货车 (售价135g ~ 1,000)</li><li>有时也可以从骷髅洞穴宝箱中找到5-20个</li></ul>', NULL),
	(3403, 'craft', 'DE0123', '收获作物', '01', '<p>秋季种子收获时，变为下面任一农作物的几率是相同的。</p>', NULL),
	(3404, 'craft', 'DE0123', '利润计算', '01', '<p>秋季种子在没有植物学家职业的情况下是一个有利可图的配方。制作一组秋季种子可获得10份种子，总价值为450g，而制作它所需的普通品质材料仅能卖230g，金星品质材料则能卖345g。拥有植物学家职业时，单卖这些作物可获得460g。</p>', NULL),
	(3405, 'craft', 'DE0123', '画廊', '01', '<p>所有季节的野生种子在生长到第二阶段时候看起来很像枯萎的作物。</p><p></p>', NULL),
	(3406, 'craft', 'DE0141', '', '01', '<p></p><p><b>深色牌子</b>是一种可以展示物品栏任何的物品的图像的可制作物品。与木牌和石牌类似，玩家只要让角色双手持有一个物品，然后鼠标右键点击深色牌子，就能让石碑显示该物品的图像。该过程不消耗物品。</p><p>深色牌子可以放在星露谷的任何地方。如果把深色牌子放在村民活动的路径上，深色牌子会被村民破坏。详见：农场外的打造物品。</p><p>把深色牌子放置在鱼塘上，深色牌子会变成可以显示鱼塘内有多少鱼的计数器。深色牌子上可以看到鱼的图像，右下角有一个数字。</p>', NULL),
	(3407, 'craft', 'DE0071', '', '01', '<p><b>高级生长激素</b>是一种肥料，可以促进作物生长速度加快 25%（在有农业学家技能时共加速 35%）。高级生长激素可以自己制作，也可以在皮埃尔的杂货店用 150g 购买（第二年及以后）。或者是礼拜四在绿洲用80g 购买。在冬日星盛宴上，玩家可能会收到作为交换礼物的10个高级生长激素。打开谜之盒或者金色谜之盒也有概率获得10或20个高级生长激素。沙漠节期间，德米特里厄斯会在其商铺以 2 卡利科三花蛋的价格出售高级生长激素（限100个）。</p><p>高级生长激素可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。对于可多次收获的作物，只有第一次收获的周期会缩短，高级生长激素对后续收获没有作用。</p>', NULL),
	(3408, 'craft', 'DE0071', '成本比对', '01', '<p>下列表格对比了打造<b>五个</b>高级生长激素和直接在绿洲或皮埃尔的杂货店购买相同的量的价格差距。打造成品的价值按所用的原料的售价计算。这只意味着当你拥有可以制作高级生长激素的材料时，直接将其制作成高级生长激素更为划算而不是出售原料再去购买。直接购买高级生长激素与制作高级生长激素之间是无法如同“找矮人购买炸弹”与“找克林特购买原料制作炸弹”一般进行成本比较的。</p>', NULL),
	(3409, 'craft', 'DE0071', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3410, 'craft', 'DE0071', '裁制', '01', '<p>高级生长激素可通过缝纫机使用布料制成上半部分可染色的高腰上衣。高级生长激素可用于染色。放置在染料罐内时，高级生长激素可用作<b>橙色</b>染料。放置在缝纫机内时，高级生长激素还可用作<b>棕色</b>染料。</p>', NULL),
	(3411, 'craft', 'DE0071', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3412, 'craft', 'DE0037', '', '01', '<p>玩家可以使用<strong>野炊工具</strong>制造一个可用于烹饪的篝火。使用该工具就可在农舍之外的其他地方进行烹饪。</p><p>放置后的野炊工具会在次日消失，玩家也可通过空手或工具手动摧毁，摧毁之后不会有任何掉落物。</p><p>完成工艺室（重新混合后）的 野生药材收集包后，玩家会获得两个野炊工具的奖励。</p>', NULL),
	(3414, 'craft', 'DE0010', '', '01', '<p></p><p><b>产油机</b>是一种用于制造工匠物品的工匠设备，需要特定的原料并将其转化为不同的油。</p>', NULL),
	(3415, 'craft', 'DE0010', '制作', '01', '<p>关于 松露油 与 油 的盈利能力，见：动物产品的盈利能力。</p><p><b>注意:</b> 处理时间在凌晨2点到早上6点之间的计算方式不同。从早上6点到凌晨2点，1小时=60分钟，从凌晨2点到早上6点，1小时=100分钟。</p><p><br/></p>', NULL),
	(3416, 'craft', 'DE0028', '', '01', '<p></p><p><b>石箱</b>是一种可打造的存储设备，与木制宝箱相同，它可容纳36种不同的物品。一个格子里最多能放下999个相同物品。物品在箱子里不会腐坏。</p><p>石箱可以放在游戏中的许多地方。然而，如果石箱阻挡了村民的行走路线，石箱会被村民推到旁边。关于可以安全放置石箱的区域，详见“打造#在农场外放置设备”</p><p>玩家可以通过使用任何工具敲击一个空的石箱使其掉落并进入背包。装有物品的石箱是无法拾取的。如果用任何工具敲击（或空手反复点击）装有物品的石箱，它将被推离玩家一格。</p>', NULL),
	(3417, 'craft', 'DE0028', '染色', '01', '<p>打开石箱时，在其库存窗口上方有一个色彩条，允许玩家给石箱选择不同的颜色。改变颜色不需要任何花费。一共有20种不同的颜色可供选择。默认颜色与色彩条中的白色相同。</p><p></p>', NULL),
	(3418, 'craft', 'DE0015', '', '01', '<p></p><p><b>煤炭窑</b>是一种精炼设备，用于将木材转换为煤炭，其打造配方在达到采集等级 2 后解锁。</p><p>煤炭窑每次可将10木材转换为1煤炭，耗时 30分钟（约现实中23秒）。</p><p>煤炭可用于一些打造配方中，也是将金属熔炼为金属锭时必需的燃料。</p><p>玩家完成工艺室的 建筑收集包、工艺室（重新混合后）的 森林收集包和工艺室（重新混合后）的 黏糊糊收集包都可以获得一个煤炭窑。</p>', NULL),
	(3419, 'craft', 'DE0021', '', '01', '<p></p><p><b>史莱姆压蛋器</b>是一种精炼设备，用于制作史莱姆蛋，其打造配方在达到战斗等级 6 时解锁。史莱姆蛋在史莱姆屋内可以孵化出新的史莱姆（怪物），也可以卖掉换成钱（1,000g~5,000g）。</p><p>史莱姆压蛋器可以放置在任何地方，并可以使用工具左键点击成掉落物后捡起。</p><p>史莱姆压蛋器可以把100个史莱姆泥加工制成史莱姆蛋，加工需要 1200分钟（20小时） 。</p><p>史莱姆蛋的颜色是随机的，性别只有当孵化后才能确定（史莱姆蛋没有确定的性别，但是史莱姆必定是具有确定性别的。）用于加工制成史莱姆蛋的史莱姆泥原料来源的史莱姆（怪物）的颜色不影响最终产出的史莱姆蛋的颜色。</p><p>压蛋器产出各种颜色的史莱姆蛋的几率如下：</p><ul><li><b>64.125%</b>绿色史莱姆蛋</li><li><b>21.375%</b>蓝色史莱姆蛋</li><li><b>9.5%</b>红色史莱姆蛋</li><li><b>5%</b>紫色史莱姆蛋</li></ul>', NULL),
	(3420, 'craft', 'DE0021', '备注', '01', '<p>由史莱姆压蛋器生产的史莱姆蛋的平均价值大约是 1,500g ，是原材料售价（500g）的三倍。</p>', NULL),
	(3421, 'craft', 'DE0006', '', '01', '<p></p><p><b>罐头瓶</b>是一种能生产工匠物品的工匠设备。它会将水果或蔬菜加工成果酱和腌菜，将鲟鱼的鱼籽加工成鱼籽酱，其他的鱼籽则会加工成腌鱼籽。一些野生采集品也可加工成腌菜，例如蒲公英、韭葱、野山葵、大葱、榛子、冬根、雪山药、山洞萝卜、普通蘑菇、紫蘑菇、鸡油菌、羊肚菌、熔岩菇和姜等。</p><p>罐头瓶消耗的时间少于小桶，但通常利润也会更低。然而，低价值高产量的作物（如黑莓、青豆、玉米）放入罐头瓶中比在小桶中获利更多，并且加工速度要快得多。例如，在小桶中制成的玉米果汁价格为113g，但罐头瓶制成的玉米腌菜价格为150g。在小桶中制成的美洲大树莓果酒价格为15g，但罐头瓶制成的美洲大树莓果酱价格为60g。</p><p>果酱和腌菜是村民们普遍喜欢的礼物。</p><p>完成茶水间的 高品质作物收集包或茶水间（重新混合后）的 稀有作物收集包都会得到一个罐头瓶作为奖励。</p>', NULL),
	(3422, 'craft', 'DE0006', '制品', '01', '<p>材料的品质对成品没有任何影响。制品的价格取决于使用原料的基础价格（即没有银星或金星时的价格）。然而，罐头制品的售价还是比直接卖金星原料的更高，尽管制品利润率比无星材料低。</p>', NULL),
	(3423, 'craft', 'DE0006', '罐头瓶 vs. 小桶', '01', '<p>从原料类型上区分，罐头瓶可以加工水果、蔬菜和鱼籽，而小桶可以加工水果、蔬菜、咖啡豆、蜂蜜和茶叶。在加工水果和蔬菜时，由于价格公式的不同，二者在加工利润方面有所区别。使用罐头瓶加工的增值公式为：（2 × 基础农作物价格+ 50）; 而小桶则是将水果的基础价格 × 3，蔬菜价格 × 2.25。因此，如玉米或西红柿等低价值、高产量作物放入罐头瓶加工会更有价值。而小桶则更合适售价较高的作物，如上古水果或甜瓜。二者的平衡点，对于水果是50g，对于蔬菜是200g。下面是一个放罐头瓶加工会更有价值（仅从单价来看）的作物列表，列出了作物的基础价格。</p><p>* 虽然该物品的果酒和果酱的价值都是150g，但是果酱比果酒要制作的快</p><p>注意，上述计算只考虑了最大利润方面；如果根据所需加工时间来看每日盈利，无论作物基础价值如何，罐头瓶的盈利效率始终超过小桶，因为罐头瓶的加工速度要快得多。尽管如此，在游戏的第一年，考虑到制造材料的成本，实际上小桶比罐头瓶更适合大规模生产。如果木材、石头、矿石和煤炭全部从木匠的商店和铁匠铺购买，1个小桶的制造成本为1725金加1个橡树树脂（不包括树液采集器的成本），而1个罐头瓶的制造成本为2500金。如果不购买材料，游戏中获取木材比石头容易，获取树脂比煤炭容易得多（使用树肥种橡树）。另外，啤酒花是第一年收益最高的作物，淡啤酒只能用小桶加工。</p>', NULL),
	(3424, 'craft', 'DE0117', '', '01', '<p></p><p><b>树桩火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3425, 'craft', 'DE0077', '', '01', '<p><b>蒜油</b>是一种可食用的制造物品，达到战斗等级6 后可以通过打造获得，还可以以3,000g在矮人处购买。</p><p>使用蒜油会获得一个持续10分钟的蒜油效果，玩家来到矿井将不会再刷新怪物，除了地牢层。在骷髅洞穴中，该效果阻止普通的感染层的怪物生成，但不影响史前层生成。该效果也能防止任何层内生成群虫攻击。</p><p>蒜油效果在危险的矿井和危险的骷髅洞穴中不会生效。</p><p>蒜油效果是一种特殊的效果，它不会被其它的饮料的效果覆盖，反之亦然。</p><p>注意：这是一个可盈利的配方，但需要使用最低品质的原料。</p>', NULL),
	(3426, 'craft', 'DE0077', '裁制', '01', '<p>蒜油可通过缝纫机使用布料制成可染色的 男款深色荧光衬衫 或女款深色荧光衬衫。蒜油可用于染色。放置在染料罐内时，蒜油可用作<b>绿色</b>染料。放置在缝纫机内时，蒜油还可用作<b>黄绿色</b>染料。</p>', NULL),
	(3427, 'craft', 'DE0077', '任务', '01', '<p>蒜油不用于任何任务。</p>', NULL),
	(3428, 'craft', 'DE0002', '', '01', '<p><b>优质洒水器</b>是一件可以在每天早晨灌溉临近的8个土壤方格（3×3的区域）的可打造物品。灌溉仅限于已开垦的土壤方格。优质洒水器的合成配方在耕种等级达到6级时解锁。</p><p>完成 夏季作物收集包或茶水间（重新混合后）的 花园收集包时，都可以获得一个优质洒水器作为奖励。优质洒水器也可能会随机出现在旅行货车中，售价为1,350g ~ 2,250。玩家可能会在骷髅洞穴的宝箱层找到4个优质洒水器。鳟鱼大赛的奖品之一为1个优质洒水器。打开谜之盒或金色谜之盒也有概率获得1个优质洒水器。兑奖机的第7个奖品有33%概率获得4个优质洒水器。</p><p>优质洒水器可以给史莱姆屋里的水槽添水，但不能给花盆浇水或给宠物的碗中添水。优质洒水器不能放置在沙地上。</p><p>可以将火把放置在优质洒水器上。</p><p></p>', NULL),
	(3429, 'craft', 'DE0002', '升级部件', '01', '<p>升级部件可以安装在已放置的洒水器上，拾取洒水器时可以在不破坏它的情况下卸下升级部件。</p><p>同一个洒水器一次只能安装一个升级部件。</p>', NULL),
	(3430, 'craft', 'DE0002', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3431, 'craft', 'DE0002', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3432, 'craft', 'DE0061', '', '01', '<p><b>寻宝者</b>是一种渔具，可以使玩家在钓鱼时收集宝箱的同时不会让鱼脱钩，并将找到钓鱼宝箱的概率提高 5% (基础概率为15%，提升后为20%)。达到钓鱼等级7 后可以通过打造获得，同时解锁鱼店以750g出售。寻宝者也会随机出现在旅行货车的货物中，售价为750g ~ 1,250。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3433, 'craft', 'DE0061', '收集包', '01', '<p>寻宝者不被用于任何收集包。</p>', NULL),
	(3434, 'craft', 'DE0061', '任务', '01', '<p>寻宝者不被用于任何收集包。</p>', NULL),
	(3435, 'craft', 'DE0061', '备注', '01', '<p>在鱼店直接购买寻宝者比购买并打造 2 块金锭所需的黄金矿石和煤炭更便宜。</p>', NULL),
	(3436, 'craft', 'DE0138', '', '01', '<p><strong>水晶小径</strong>是一种装饰物，会因被闪电击中而掉落。</p><p><strong>水晶小径</strong>的配方可在木匠的商店花费200g解锁。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3438, 'craft', 'DE0053', '', '01', '<p><b>倒刺钩</b>是一种渔具，可以使玩家在钓鱼时的绿条随着鱼移动。达到钓鱼等级8 后可以通过打造获得，同时解锁鱼店以1,000g出售。倒刺钩也会随机出现在旅行货车的货物中，售价为1,500g ~ 2,500。第一次在冰雪节的钓鱼比赛中取得胜利时，会得到一个倒刺钩作为奖励。</p><p>如果在高级铱金鱼竿中装备两个倒刺钩，能更有效地将绿条固定在鱼上。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3439, 'craft', 'DE0053', '收集包', '01', '<p>倒刺钩不被用于任何收集包。</p>', NULL),
	(3440, 'craft', 'DE0053', '任务', '01', '<p>倒刺钩不被用于任何任务。</p>', NULL),
	(3441, 'craft', 'DE0053', '注意', '01', '<ul><li>装备倒钩时移动钓鱼条很麻烦。鱼跳动越快，手动移动钓鱼条就越容易移动，但倒钩的效用就越差。</li><li>除非有技能铁匠专精，否则制作并出售一个倒钩比单纯出售三块金属锭还多赚 70g 。</li></ul>', NULL),
	(3442, 'craft', 'DE0137', '', '01', '<p><strong>水晶地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>水晶地板的配方可在科罗布斯处以价格500g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3444, 'craft', 'DE0136', '', '01', '<p><strong>石制走道地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>石制走道地板的配方可在木匠的商店以200g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3446, 'craft', 'DE0055', '', '01', '<p><b>魔法鱼饵</b>是一种鱼饵，可以使玩家在钓鱼时无视季节、时间、天气的限制。可以在齐先生的核桃房中花费 5齐钻齐钻 购买20个魔法鱼饵，也可以花费 20齐钻齐钻 购买打造配方。</p><p>与普通鱼饵相似，魔法鱼饵也可以使鱼上钩的时间减少50%。</p><p>魔法鱼饵也可以在蟹笼中使用，不过效果与普通鱼饵完全相同，但并不会改变产出的蟹笼鱼类的种类。</p>', NULL),
	(3447, 'craft', 'DE0055', '用法', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3448, 'craft', 'DE0055', '钓鱼地点', '01', '<p>有关各地点能钓到的鱼的种类，请查看：钓鱼攻略#地点表格。</p><p>只要装备了魔法鱼饵，无论鱼的出现有什么季节、天气或时间的限制，都可以在对应的地点钓起。夜市鱼类可以在沙滩的西南角（如右图所示）钓起。传说鱼类也可在各自对应的位置使用魔法鱼饵钓起。</p>', NULL),
	(3449, 'craft', 'DE0055', '任务', '01', '<p>魔法鱼饵不被用于任何任务。</p>', NULL),
	(3450, 'craft', 'DE0065', '', '01', '<p><strong>初级肥料</strong>是一种可用于提高土壤品质的肥料。可以手工打造或第一年春季15日后在皮埃尔的杂货店花费 100g 购买。</p><p>初级肥料必须在种子发芽前施用在可耕种的土壤方格上。</p><p>初级肥料只影响基础收获，这就意味着，在一次收获中得到的额外作物(土豆、蓝莓、蔓越莓等) 的品质和未施肥时一样。</p>', NULL),
	(3452, 'craft', 'DE0065', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3453, 'craft', 'DE0065', '裁制', '01', '<p>初级肥料可通过缝纫机使用布料制成下半部分可染色的高腰上衣。初级肥料可用于染色。放置在染料罐内时，初级肥料可用作<strong>橙色</strong>染料。放置在缝纫机内时，初级肥料还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3454, 'craft', 'DE0065', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3455, 'craft', 'DE0146', '', '01', '<p><b>长笛块</b>是一种可打造的家具。合成配方来自罗宾的6心事件。向博物馆捐赠包含骨笛的3件古物即可获得作为捐赠奖励的长笛块。</p>', NULL),
	(3456, 'craft', 'DE0146', '音乐', '01', '<p>放置的长笛块在玩家走到它旁边时会发出声音，如果玩家在它附近停留，则每秒会发出一次声音。</p><p>长笛块有25个可能的音高，可以右键点击长笛块来改变它的音高，每次点击升高半音，到达C5后再次右击会重置为C3。</p><p>你可以放置一系列长笛块和鼓块，当你沿着它们行走时，可以创造音乐。</p>', NULL),
	(3457, 'craft', 'DE0146', '特殊音效', '01', '<p>当玩家手持部分物品经过长笛块时，长笛块会发出不同的音色。</p>', NULL),
	(3458, 'craft', 'DE0146', '收集包', '01', '<p>长笛块不用于任何收集包。</p>', NULL),
	(3459, 'craft', 'DE0146', '裁缝', '01', '<p>长笛块不用于裁缝或染色。</p>', NULL),
	(3460, 'craft', 'DE0146', '任务', '01', '<p>需要长笛块奏响特定的音符以从姜岛美人鱼处换取5个金核桃。</p>', NULL),
	(3461, 'craft', 'DE0114', '', '01', '<p></p><p><b>木头火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3462, 'craft', 'DE0003', '', '01', '<p><b>铱制洒水器</b>是一件可以在每天早晨灌溉临近的 24 个地块（5×5 的区域）的可打造物品，灌溉仅限于已开垦的地块。铱制洒水器的合成配方会在耕种 9 级时解锁。</p><p>玩家可以每周五用10,000g 向科罗布斯购买一个铱制洒水器；也可能会在骷髅洞穴的宝箱层找到 1~2 个铱制洒水器。此外，铱制洒水器也是兑奖机的奖励之一。</p><p>可以将火把放在铱制洒水器上以提供一些照明。</p><p>铱制洒水器可以给史莱姆屋里的水槽添水，但不能给花盆浇水或给宠物的碗中添水。铱制洒水器不能放置在沙地上。</p><p></p>', NULL),
	(3463, 'craft', 'DE0003', '升级部件', '01', '<p>升级部件可以安装在已放置的洒水器上，拾取洒水器时可以在不破坏它的情况下卸下升级部件。</p><p>同一个洒水器一次只能安装一个升级部件。</p>', NULL),
	(3464, 'craft', 'DE0003', '收集包', '01', '<p>铱制洒水器不用于任何收集包。</p>', NULL),
	(3465, 'craft', 'DE0003', '任务', '01', '<p>铱制洒水器不用于任何任务。</p>', NULL),
	(3466, 'craft', 'DE0127', '', '01', '<p><strong>稻草地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>稻草地板的配方可在木匠的商店以500g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3468, 'craft', 'DE0066', '', '01', '<p><b>顶级保湿土壤</b>是一种肥料，可以使土壤保持湿润。打造该物品需要先在姜岛商人处花费50火山晶石解锁其配方。偶尔也会在海滩农场的补给箱中出现。</p><p>顶级保湿土壤可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。保湿的几率是100%，这意味着一次浇水就可以使土壤永久保持湿润，这对于洒水器不能工作的地方特别有用，如花盆和海滩农场。</p>', NULL),
	(3469, 'craft', 'DE0066', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3470, 'craft', 'DE0066', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3471, 'craft', 'DE0004', '', '01', '<p></p><p><b>蛋黄酱机</b>是一种用于制造工匠物品的工匠设备，可以将蛋变成各种各样的蛋黄酱。</p>', NULL),
	(3472, 'craft', 'DE0004', '产品', '01', '<p>普通品质的棕色或白的鸡蛋产生普通品质的蛋黄酱，大鸡蛋产生金星品质的蛋黄酱。</p><p>使用鸵鸟蛋将会产出10瓶品质与<b>鸵鸟蛋</b>相同的蛋黄酱。一个金蛋将会产出3个金星蛋黄酱。</p><p><br/></p>', NULL),
	(3473, 'craft', 'DE0153', '', '01', '<p></p><p><b>战士戒指</b>是一种戒指，其打造配方在战斗等级到达 4 级时解锁。</p><p>玩家佩戴战士戒指时，击杀怪物时玩家有一定的概率获得战士能量效果——持续 5秒， 攻击+10。这一效果可以和食物或戒指提供的其他攻击增益叠加，佩戴多个战士戒指不能提高触发的概率。</p><p>运气增益，也就是食物提供的运气增益和幸运戒指能够提升获得增益效果的概率，概率不受每日运气的影响：</p>', NULL),
	(3474, 'craft', 'DE0153', '染色', '01', '<p>战士戒指可用作染料罐的<b>黄色</b>染料，放置在缝纫机内时还可用作<b>沙色</b>染料。</p>', NULL),
	(3475, 'craft', 'DE0036', '', '01', '<p><b>加料器</b>是一种可打造物品，配方可以在齐先生的核桃房花费 50齐钻 齐钻购买，或是以 10齐钻 齐钻直接购买加料器。</p><p>当加料器前面的机器没有工作且产出的物品已经被收走，同时加料器里的材料可以加入这个机器，它会自动将内部存放的材料加进它前面的机器里。</p><p>通过这种方式生产出来的物品依然需要手动取走。此外，加料器可以添加多种不同原材料，也可以同时添加两种材料到与它相连的机器内。例如可以同时在加料器内加入煤炭和多种不同的矿石，并在加料器的前面放置一个熔炉，那么加料器会按照从左到右的顺序依次给熔炉加入矿石进行冶炼制造不同的矿锭。</p>', NULL),
	(3476, 'craft', 'DE0073', '', '01', '<p><b>树肥</b>是一种可以使野生的树加速生长的肥料，但是不能加速果树和茶树。它必须洒在树种或树苗上，而不是未种植的地面上。配方在达到采集等级 7 级时解锁。</p><p>施肥后，撒上树肥的种子/幼树将呈现红色，并且每晚将提前一个生长阶段，直到第四个阶段，两天后再达到生长的最后阶段。例外的是，施肥后的桃花心木树每晚只有 60% 的机会进入下一阶段,施肥后的神秘树每晚只有30%的机会进入下一阶段。</p><p>施肥后的树种和树苗即使在冬季也能生长。未施肥的树不会在冬季生长。</p>', NULL),
	(3477, 'craft', 'DE0073', '裁制', '01', '<p>树肥可通过缝纫机使用布料制成下半部分可染色的高腰上衣。树肥可用于染色。放置在染料罐内时，树肥可用作<b>橙色</b>染料。放置在缝纫机内时，树肥还可用作<b>棕色</b>染料。</p>', NULL),
	(3478, 'craft', 'DE0073', '趣闻', '01', '<p>如果冬季在蘑菇树幼树上施肥会使它们第二天变成树桩。</p>', NULL),
	(3479, 'craft', 'DE0152', '', '01', '<p></p><p><b>由巴的戒指</b>是一种戒指，可以在达到 战斗等级 7 后通过打造获得。</p><p>玩家佩戴由巴的戒指时，受到伤害后有一定概率获得由巴的祝福：该效果使玩家在 5秒 内无敌，但无法免疫骷髅洞穴的摔落伤害。</p><p>获得该效果的概率随生命值降低而提高，运气增益也可提高该概率（但每日运气无影响）。在玩家生命值降至90以下后，由巴的戒指才可能生效。之后，生效概率会随着生命值降低线性增加。降低至15及以下后，生效概率额外增加20%。计算公式 如下 <code>(0.9 - 生命值 / 100) / (3 - 运气等级 / 10) + 额外增加</code>。生命值降低至15及以下时，<code>额外增加</code> 值为0.2。例如，运气增益为0时，生命值在0-15时，生效概率为50%-45%；生命值在16-90时，生效概率为25%-0。</p><p>注意：佩戴多个由巴的戒指<b>无法</b>叠加触发的概率。</p>', NULL),
	(3480, 'craft', 'DE0152', '染色', '01', '<p>由巴的戒指可用作染料罐的<b>黄色</b>染料，放置在缝纫机内时还可用作<b>金色</b>染料。</p>', NULL),
	(3481, 'craft', 'DE0115', '', '01', '<p><b>南瓜灯</b>是一种可打造的光源装饰物，玩家还可以在万灵节时皮埃尔的摊位处以750g的价格直接购买南瓜灯。万灵节的摊位还会以2,000g的价格出售南瓜灯的制作配方，每位玩家只能购买一次配方，之后即可用南瓜和火把进行制作。进入冬季后，被放置的南瓜灯将会变成腐烂的植物，将其回收至宝箱或玩家的物品栏中可以避免这一现象。</p><p>由于南瓜灯发出的光要弱于火把，它作为装饰要比作为灯具更合适。</p>', NULL),
	(3482, 'craft', 'DE0115', '收集包', '01', '<p>南瓜灯可用于 布告栏（重新混合后）的 幽灵之夜收集包。</p>', NULL),
	(3483, 'craft', 'DE0115', '裁制', '01', '<p>南瓜灯可通过缝纫机使用布料制成南瓜面具。南瓜灯还可用作染料罐和缝纫机的<b>橙色</b>染料。</p>', NULL),
	(3484, 'craft', 'DE0115', '任务', '01', '<p>南瓜灯没有被用于任何任务。</p>', NULL),
	(3485, 'craft', 'DE0149', '', '01', '<p></p><p><b>结实戒指</b>是一种戒指，可以在达到战斗等级 1 后通过打造获得。打开谜之盒也有概率获得。</p><p>玩家佩戴结实戒指时，所有来自怪物的负面效果的持续时间将会减少一半。</p>', NULL),
	(3486, 'craft', 'DE0149', '染色', '01', '<p>结实戒指可用作染料罐的<b>蓝色</b>染料，放置在缝纫机内时还可用作<b>青色</b>染料。</p>', NULL),
	(3487, 'craft', 'DE0149', '备注', '01', '<ul><li>打造一个结实戒指，然后再在探险家公会以 305g 卖掉，会比直接把原材料卖给公会赚得更多（245g ~加上铁匠职业增益）。这可能是有用的，因为在游戏初期就可以获得配方和原料，不过这样做需要大量的怪物战利品，并且收集起来会很麻烦。</li></ul>', NULL),
	(3488, 'craft', 'DE0122', '', '01', '<p></p><p><b>冬季种子</b>是一种通过打造得到的种子。打造配方的名字是“野生种子（冬季）”，但得到的物品是“冬季种子”。作物成熟时，会随机变为番红花、水晶果、雪山药和冬根。</p><p>冬季种子和春季种子、夏季种子以及秋季种子一样，是一种只能在特定季节种下的野生种子，长出的作物通常也是对应季节的采集物品。通常，耕种技能可以提升长出高品质作物的几率，但冬季种子长出高品质作物的几率是由采集技能决定的。选择“植物学家”职业可以保证收获的作物都是铱星品质。</p><p>由冬季种子长成的作物可能会在收获阶段使所在的地块变为没有锄过的地块。这时，所有使用过的肥料效果都会消失。</p><p>乌鸦不会侵害由冬季种子长成的作物，因此并没有必要使用稻草人。</p>', NULL),
	(3489, 'craft', 'DE0122', '来源', '01', '<ul><li>打造 (每次制造生产10份冬季种子。)</li><li>把一个冬根放进种子生产器（产出：1-3份冬季种子）</li><li>完成工艺室的 冬季采集收集包 （奖励：30份冬季种子）</li><li>旅行货车 (售价100g ~ 1,000)</li><li>有时也可以从骷髅洞穴宝箱中找到5-20个</li></ul>', NULL),
	(3490, 'craft', 'DE0122', '收获作物', '01', '<p><b>冬季种子</b>收获时，变为下面任一农作物的几率是相同的。</p>', NULL),
	(3491, 'craft', 'DE0122', '利润计算', '01', '<p>制作并出售冬季种子是没有利润的。制作冬季种子可产出 10 份，总售价为 300g，但用于制作的普通品质原料直接出售价格为 380g。</p>', NULL),
	(3492, 'craft', 'DE0122', '画廊', '01', '<p>所有季节的野生种子在生长到第二阶段时候看起来很像枯萎的作物。</p><p></p>', NULL),
	(3493, 'craft', 'DE0121', '', '01', '<p><br></p><p><strong>春季种子</strong>是一种通过打造得到的种子。打造配方的名字是“野生种子（春季）”，但得到的物品是“春季种子”。作物成熟时，会随机变为野山葵、黄水仙、韭葱和蒲公英。</p><p>春季种子和夏季种子、秋季种子以及冬季种子一样，是一种只能在特定季节种下的野生种子，长出的作物通常也是对应季节的采集物品。通常，耕种技能可以提升长出高品质作物的几率，但冬季种子长出高品质作物的几率是由采集技能决定的。选择“植物学家”职业可以保证收获的作物都是铱星品质。</p><p>由春季种子长成的作物可能会在收获阶段使所在的地块变为没有锄过的地块。这时，所有使用过的肥料效果都会消失。</p><p>乌鸦不会侵害由春季种子长成的作物，因此并没有必要使用稻草人。</p><p>正如自然生成的采集物，春季种子成熟后的作物不能穿过，除非把它捡起。</p>', NULL),
	(3494, 'craft', 'DE0121', '来源', '01', '<ul><li>打造 (每次制造生产10份春季种子。)</li><li>把一个野山葵放进种子生产器（产出：1-3份春季种子）</li><li>完成工艺室的 春季采集收集包 （奖励：30份春季种子）</li><li>旅行货车 (售价105g ~ 1,000)</li><li>有时也可以从骷髅洞穴宝箱中找到5-20个</li></ul>', NULL),
	(3495, 'craft', 'DE0121', '收获作物', '01', '<p>春季种子收获时，变为下面任一农作物的几率是相同的。</p>', NULL),
	(3496, 'craft', 'DE0121', '利润计算', '01', '<p>春季种子在没有植物学家职业的情况下是一个有利可图的配方。制作一组春季种子可获得10颗种子，总价值为350g，而制作它所需的普通品质材料仅能卖180g，金星品质材料则能卖270g。拥有植物学家职业时，单卖这些作物可获得360g。</p><p>直接卖掉收集的物品的长期平均价格为每个45g（1/4*(30+40+50+60)）。然而，平均下来这相当于2.5组种子，总价值为875g，相当于每颗种子的价格为data-sort-value="87.5"&gt;87g.5。扣除每颗种子35g的初始成本，利润为52.5，整个生长周期下，每天的利润为7.5</p>', NULL),
	(3497, 'craft', 'DE0121', '画廊', '01', '<p>所有季节的野生种子在生长到第二阶段时候看起来很像枯萎的作物。</p><p><br></p>', NULL),
	(3498, 'craft', 'DE0031', '', '01', '<p><br></p><p><strong>豪华稻草人</strong>是一种可打造的稻草人，用于防止乌鸦吃掉玩家的作物。玩家在集齐全8个稀有稻草人后，每晚会有90.5%的几率收到“Z.C.珍奇乌鸦协会”给玩家寄来的信件，其中含有豪华稻草人的制作配方。</p><p>豪华稻草人的作用半径（16格）是普通稻草人（8格）的2倍，因此作用面积是普通稻草人的四倍，能覆盖888个土壤方格。</p>', NULL),
	(3499, 'craft', 'DE0031', '信件', '01', '<p>“亲爱的 &lt;玩家&gt;：\n你的贡献令人赞叹……\n\n只有少数人能够集齐全套稀有稻草人！\n\n请收下这张图纸以纪念你的成就。”\n— Z.C.珍奇乌鸦协会</p>', NULL),
	(3500, 'craft', 'DE0052', '', '01', '<p><b>磁铁</b>是一种鱼饵，可以使玩家在钓鱼时找到钓鱼宝箱的概率翻倍（从15%的基础概率提升到30%）。达到钓鱼等级9 后可以通过打造获得，同时解锁鱼店以1,000g出售。也可在赌场以1,000的价格购买。达到钓鱼精通后，也有7%的概率在金色钓鱼宝箱中找到。</p><p>第一次在冰雪节的钓鱼比赛中取得胜利时，会得到一个磁铁作为奖励。在冬日星盛宴期间，威利可能会把一个磁铁作为礼物赠送给玩家。星期三时，磁铁在会出现在科罗布斯的商店中。磁铁也会随机出现在旅行货车的货物中，售价为100g ~ 1,000。</p><p>磁铁也可以在蟹笼中使用，不过效果与普通鱼饵完全相同，因为蟹笼并不会产生钓鱼宝箱。</p>', NULL),
	(3501, 'craft', 'DE0052', '使用', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3502, 'craft', 'DE0052', '收集包', '01', '<p>磁铁不被用于任何收集包。</p>', NULL),
	(3503, 'craft', 'DE0052', '任务', '01', '<p>磁铁不被用于任何任务。</p>', NULL),
	(3504, 'craft', 'DE0052', '注意', '01', '<ul><li>尽管描述中说“鱼儿可不喜欢它的味道”，但磁铁对鱼咬钩时间的影响与普通鱼饵相同。</li></ul><ul><li>虽然磁铁可以在鱼店以1,000g的价格购买，但手工制作更为划算，即使打造铁锭所需的原料完全通过购买获得。在第1年，从铁匠铺中购买5个铁矿石和1个煤炭需要花费900g(即每个磁铁 300g)。在第2年及以后，购买5个铁矿石和1个煤炭需要花费1,500g（即每个磁铁 500g）。</li></ul><ul><li>如果玩家能够在赌场中通过赌博赚取大量齐币，之后便可以从赌场商店中获得几乎免费的磁铁。</li></ul>', NULL),
	(3505, 'craft', 'DE0101', '', '01', '<p><b>铁锭</b>可由 5 块铁矿石和 1 块煤炭在熔炉中熔炼而成，熔铸铁锭需要游戏内的 2 小时。采矿 4 级之后可在打造界面用 3 块铜锭制成 1 块铁锭。</p><p>解锁熔炉的配方之后，有一定几率在镇上的垃圾桶里找到铁锭。杀死暗影狂徒和暗影萨满也有 2% 的概率掉落铁锭。铁锭广泛用于各种物品的打造，并且是升级钢制工具的原料。</p><p>一块铁锭的售价是120g，如果玩家具备“铁匠”职业，则是180g。</p>', NULL),
	(3506, 'craft', 'DE0101', '工具升级', '01', '<p>将工具第二次升级需要铁锭。</p>', NULL),
	(3507, 'craft', 'DE0101', '收集包', '01', '<p>铁锭是锅炉房的 铁匠的收集包所需的收集道具之一。</p>', NULL),
	(3508, 'craft', 'DE0101', '裁制', '01', '<p>铁锭可通过缝纫机使用布料制成钢胸甲。铁锭可用于染色。放置在缝纫机内时，铁锭可用作<b>铁色</b>染料。</p>', NULL),
	(3509, 'craft', 'DE0101', '任务', '01', '<ul><li>克林特将在冬季 17 日时会寄来信件，请求玩家给一块铁锭， 详情请看"克林特的小忙"。该任务报酬为500g和 1 心友谊。</li><li>需要铁锭的任务会不分季节地随机出现在皮埃尔的杂货店外的求助任务版上，报酬为360g和 150 友谊点。而需要交付铁锭的任务在得到熔炉蓝图之前不会出现。</li><li>饲养有鲶鱼的鱼塘在数量从 7 提升到 10 的时候，会出现要求玩家提供 3 块铁锭的鱼塘任务。</li><li>饲养有冰柱鱼的鱼塘在数量从 7 提升到 10 的时候，会出现要求玩家提供 4 块铁锭的鱼塘任务。</li><li>饲养有幽灵鱼的鱼塘在数量从 3 提升到 5 的时候，会出现要求玩家提供 3 块铁锭的鱼塘任务。</li></ul>', NULL),
	(3510, 'craft', 'DE0101', '笔记', '01', '<ul><li>出售铁锭时，可以将它们制作成金锭，每块金锭增加10g利润（铁匠职业为15g）。</li></ul>', NULL),
	(3511, 'craft', 'DE0090', '', '01', '<p><b>硬木围栏</b>是一件可打造物品，可以让玩家在农场中隔开某些区域。硬木围栏是游戏中最坚固的围栏，可以维持完好状态502~505天。可以用100（1,000g）在赌场中购买到硬木围栏。</p>', NULL),
	(3512, 'craft', 'DE0054', '', '01', '<p><b>精装旋式鱼饵</b>是一种渔具，可以使玩家在钓鱼时的最长等待上钩时间缩短7.5秒。如果玩家使用高级铱金鱼竿装备了两个精装旋式鱼饵，则缩短15秒。</p><p>精装旋式鱼饵可通过以下方式获得：</p><ul><li>达到钓鱼等级8 后打造获得，同时解锁鱼店以1,000g出售。</li><li>达到钓鱼等级6 后以4%的概率在钓鱼宝箱中获得。金色钓鱼宝箱中的获得概率为9%。</li><li>随机出现在旅行货车的货品中，售价为1,500g ~ 2,500。</li><li>威利可能会在冬日星盛宴时把精装旋式鱼饵作为礼物送给你。</li><li>完成 湖鱼收集包时，会获得一个精装旋式鱼饵作为奖励。</li><li>第一次在冰雪节的钓鱼比赛中获胜时，奖品之一是一个精装旋式鱼饵。</li><li>达到钓鱼等级6 后有概率从谜之盒和金色谜之盒中开出。</li></ul><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3513, 'craft', 'DE0054', '收集包', '01', '<p>精装旋式鱼饵不用于任何收集包中。</p>', NULL),
	(3514, 'craft', 'DE0054', '任务', '01', '<p>精装旋式鱼饵不用于任何任务中。</p>', NULL),
	(3515, 'craft', 'DE0079', '', '01', '<p><strong>传送图腾：农场</strong>是一种传送图腾，在物品栏中选中并右键点击屏幕中无法直接交互的任意处，便可传送玩家至农场。它可在达到采集等级 8 解锁配方后打造获得。</p>', NULL),
	(3516, 'craft', 'DE0079', '其他来源', '01', '<ul><li>打开谜之盒有概率获得1个或3个传送图腾：农场。</li><li>打开金色谜之盒有概率获得3个传送图腾：农场。</li><li>可以在赌场内以1,000齐币的价格购买。</li><li>向博物馆捐赠80种物品后的奖励为5个传送图腾：农场。</li><li>任何季节电影院内抓娃娃机的奖品。</li><li>水滴鱼鱼塘内的鱼达到至少9条时，每天有1.7%-1.9%的概率产出5个传送图腾：农场。</li><li>骷髅洞穴宝藏房间的宝箱偶尔也能开出5个传送图腾：农场。</li><li>第1年夏季1日开始，传送图腾：农场有10%概率在远古斑点中替换粘土出现。</li><li>姜岛商人以5芋头的价格出售传送图腾：农场。</li><li>鳟鱼大赛奖品之一为3个传送图腾：农场。</li></ul>', NULL),
	(3518, 'craft', 'DE0079', '收集包', '01', '<p>传送图腾：农场不用于任何收集包。</p>', NULL),
	(3519, 'craft', 'DE0079', '裁制', '01', '<p>传送图腾可通过缝纫机使用布料制成图腾面具。传送图腾可用于染色。放置在染料罐内时，传送图腾可用作<strong>橙色</strong>染料。放置在缝纫机内时，传送图腾还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3520, 'craft', 'DE0079', '任务', '01', '<p>传送图腾：农场不用于任何任务。</p>', NULL),
	(3521, 'craft', 'DE0112', '', '01', '<p></p><p><b>木灯柱</b>是一种可制作的照明物品，可以拿在手中或放在地面上以提供照明。</p><p>木灯柱的照明范围大约为7个地格，越靠近光源的地方越明亮。这个灯柱可以放在室内或室外，也可以放在矿井等地方。用锄头、十字镐或斧头敲击它，就可以将其拆除。与可以切换开关状态的火炬不同，木灯柱和铁灯柱永远是亮着的。它们提供的照明比任何其他可制作的照明物品都要多。</p>', NULL),
	(3522, 'craft', 'DE0069', '', '01', '<p><b>高级保湿土壤</b>是一种肥料，可以让土壤保持湿润。高级保湿土壤可以经打造或第二年开始后在皮埃尔的杂货店用150g购买，或是礼拜六在绿洲用200g购买。</p><p>高级保湿土壤可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。保湿的几率是66%。</p>', NULL),
	(3523, 'craft', 'DE0069', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3524, 'craft', 'DE0069', '裁制', '01', '<p>高级保湿土壤可通过缝纫机使用布料制成上半部分可染色的高腰上衣。高级保湿土壤可用于染色。放置在染料罐内时，高级保湿土壤可用作<b>橙色</b>染料。放置在缝纫机内时，高级保湿土壤还可用作<b>棕色</b>染料。</p>', NULL),
	(3525, 'craft', 'DE0069', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3526, 'craft', 'DE0080', '', '01', '<p><strong>传送图腾：沙漠</strong>是一种传送图腾，在物品栏中选中并右键点击屏幕中无法直接交互的任意处，便可传送玩家至沙漠。它可在沙漠商人处花费10个铱锭购买配方后打造获得，也可以直接花费3个万象晶球从沙漠商人那里购买。</p>', NULL),
	(3528, 'craft', 'DE0080', '收集包', '01', '<p>传送图腾：沙漠不用于任何收集包。</p>', NULL),
	(3529, 'craft', 'DE0080', '裁制', '01', '<p>传送图腾可通过缝纫机使用布料制成图腾面具。传送图腾可用于染色。放置在染料罐内时，传送图腾可用作<strong>橙色</strong>染料。放置在缝纫机内时，传送图腾还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3530, 'craft', 'DE0080', '任务', '01', '<p>传送图腾：沙漠不用于任何任务。</p>', NULL),
	(3531, 'craft', 'DE0080', '趣闻', '01', '<p>如果传送至沙漠后，在潘姆到达巴士站之前返回小镇，那么会出现玩家驾驶巴士的动画。</p>', NULL),
	(3532, 'craft', 'DE0118', '', '01', '<p></p><p><b>铁灯柱</b>是一种可制作的照明物品，可以拿在手中或放在地面上以提供照明。</p><p>铁灯柱的照明范围大约为7个地格，越靠近光源的地方越明亮。这个灯柱可以放在室内或室外，也可以放在矿井等地方。用锄头、十字镐或斧头敲击它，就可以将其拆除。与可以切换开关状态的火炬不同，木灯柱和铁灯柱永远是亮着的。它们提供的照明比任何其他可制作的照明物品都要多。</p>', NULL),
	(3533, 'craft', 'DE0008', '', '01', '<p></p><p><b>织布机</b>是一种用来制作工匠物品的工匠设备。它会把动物毛加工成布料。</p><p>将高品质的动物毛放入织布机有概率产出双份布料。具体概率为：银星：15%，金星：50%，铱星：100%。这使得布料成为游戏中唯一一个实际性受到投入原料的品质影响的工匠物品。</p>', NULL),
	(3534, 'craft', 'DE0033', '', '01', '<p><b>迷你点唱机</b>是一件特殊的家具，它可以让玩家选择播放游戏中的音乐。迷你点唱机和它的配方在农舍外触发格斯的剧情后可获得。玩家需要与格斯达到5心好感，并在晴朗的早上6:00到11:30之间从农舍出门可触发剧情。</p><p>放置之后，迷你点唱机的使用模式与星之果实餐吧内的点唱机一致。放置在室外的迷你点唱机在雨天无法播放。在雨天与室外的迷你点唱机交互时会出现提示“迷你点唱机不能在雨中播放！” </p><p>迷你点唱机可以被放置在农场的任何地方并使用，包括农舍、农舍地窖、农场洞穴、温室、任何畜棚和鸡舍，以及姜岛农场、姜岛农舍和饕餮青蛙洞穴。 当与这些地点以外的迷你点唱机互动时会出现提示：“迷你点唱机只能在你的农场上播放！”</p>', NULL),
	(3535, 'craft', 'DE0074', '', '01', '<p><b>虫肉块</b>是一种可食用的打造物品，战斗等级达到1级时解锁配方。</p>', NULL),
	(3536, 'craft', 'DE0074', '裁制', '01', '<p>虫肉块可通过缝纫机使用布料制成护目镜。虫肉块可用于染色。放置在染料罐内时，虫肉块可用作<b>红色</b>染料。放置在缝纫机内时，虫肉块还可用作<b>粉色</b>染料。</p>', NULL),
	(3537, 'craft', 'DE0074', '任务', '01', '<p>虫肉块不用于任何任务。</p>', NULL),
	(3538, 'craft', 'DE0074', '趣闻', '01', '<p>大部分可食用物品恢复的生命值遵循 45% × 恢复的能量值的公式，然而虫肉排、生命药水和星之果实却是例外。虫肉排恢复的生命值为 68% × 能量值。</p>', NULL),
	(3539, 'craft', 'DE0132', '', '01', '<p><strong>砾石小径</strong>是一种打造的装饰品，可以在农场中放置来装饰地板，可以被闪电击落和被杂草蔓延而破坏。</p><p>配方是玩家初始即拥有的。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3541, 'craft', 'DE0014', '', '01', '<p><br></p><p><strong>宝石复制机</strong>是一种精炼设备，用于复制任意放入的宝石、可采集矿物或晶球矿物，除了五彩碎片，在博物馆中的一本书中写道：“不知为何，这种碎片的电动势会对机器产生不良的影响。”）。将宝石放入复制机中后，宝石复制机将会无限复制该物品。当宝石或矿物被放入宝石复制机中，在宝石复制机运作时，可以通过移除宝石复制机来取出其中的宝石。</p><p>宝石复制机可通过打造获得，其打造配方在达到采矿等级 9 后解锁。当捐赠50种矿物给博物馆，或完成25,000金收集包 后，将会分别得到一个作为奖励。</p>', NULL),
	(3542, 'craft', 'DE0014', '用途', '01', '<p>设备加工的时间流速并不是均匀的，从早上 6 点到凌晨 2 点，1 小时 = 60 分钟，共 1200 分钟，但从凌晨 2 点到早上 6 点，1 小时 = 100 分钟，共 400 分钟，一游戏日共 1600 分钟。</p><p>考虑到时间流速非均匀，且凌晨 2 点到早上 6 点的时间段内玩家无法收取；加工时间超过一天时，计算收益时以早上 6 点为循环起点，计算时以x天共收获y次为一个完整循环周期，下面的表格是按上述方法进行计算的结果。</p><p>如果只考虑 6 点起床收获一次，收获周期如下：</p><ul><li>1天1收：石英、地晶、黄水晶、火水晶、紫水晶</li><li>2天1收：绿宝石、红宝石、海蓝宝石、翡翠</li><li>5天1收：钻石</li><li>4天1收：其他矿石</li></ul>', NULL),
	(3543, 'craft', 'DE0014', '提示', '01', '<p>复制钻石可以获得最大利润，如果复制完成时马上收取的话（游戏内4天半），利润会是每天166.67。以此效率，你可以在29天后赚回制作复制机的成本（不包括最初用于复制的钻石，以及由于职业技能带来的制作物品的价值提高）。\n同时，钻石也是大部分NPC所喜欢或最爱的物品，用来送礼十分有效。\n复制石英只需要7个小时，在所有矿物中最短。虽然复制机可以有效地复制石英以用于制造精炼石英，但石英也可以在挖矿时轻松、大量地发现，而且可以发现复制火水晶来制作精炼石英相比直接复制石英制作同等数量的精炼石英节省了2个煤炭。精炼石英也可以通过将捕鱼时候捕到的垃圾放入回收机内产生，并且这样做不需要煤炭。\n在沙漠商人处可以使用5个石英换取一个炸弹，这样你就不用花费1,000来找矮人购买了。\n由于沙漠商人在周日用翡翠换楼梯，复制翡翠变得特别有价值了。\n复制红宝石在沙漠商人处兑换香辣鳗鱼食用可以提供一段时间的+1加速和+1幸运，可能是前期使用宝石复制机性价比最高的方案。</p>', NULL),
	(3544, 'craft', 'DE0144', '', '01', '<p></p><p><b>邪恶雕像</b>是一种可打造的装饰性物品。它的配方来自于科罗布斯（售价 1,000g）。</p><p>把邪恶雕像放置在史莱姆屋可以防止女巫拜访此地的时候，导致所有的史莱姆（怪物）变成黑色。</p>', NULL),
	(3545, 'craft', 'DE0144', '趣闻', '01', '<p>邪恶雕像可能是指任天堂游戏《地球冒险2 基格的逆袭》中的玛尼玛尼雕像（the Mani Mani statue）。</p>', NULL),
	(3546, 'craft', 'DE0005', '', '01', '<p></p><p><b>蜂房</b>是一种用来制作工匠物品的设备。在农场放置蜂房后或收获蜂房后，经过4个夜晚正常工作后会产出蜂蜜。完成茶水间的 秋季作物收集包后，可以获得一个蜂房。兑奖机的第19个奖品有50%概率使玩家获得4个蜂房。</p><p>蜂房在任何室内或冬季的室外都不会产出蜂蜜。而在姜岛放置蜂房，则一年四季都会产出蜂蜜。</p><p>如果在蜂房范围内存在一株完全长成的花，则蜂房将产出对应种类的蜂蜜，有花的蜂蜜价格总是比没有花时的野蜂蜜高。通常由采集获得的花(例如黄水仙、蒲公英、番红花、甜豌豆)不会产出对应的蜂蜜。花盆中的花现在也可以产出对应的蜂蜜。</p><p>产出的蜂蜜种类只在收获的那一刻确定。当玩家收获蜂房中的蜂蜜时，如果蜂房范围内存在一株完全长成的花那么此时收获的蜂蜜将是对应种类的蜂蜜。当收获时范围内一株成熟的花都没有，蜂房会给予玩家“野蜂蜜”。</p><p>使用工具敲掉一个待收获的蜂房，掉出蜂蜜的种类也遵循上述收获判定。但在旧版本的游戏中，则会变成“野蜂蜜”。</p>', NULL),
	(3547, 'craft', 'DE0005', '范围', '01', '<p>除了番红花和甜豌豆，只要在蜂房的作用范围（一个水平为5列，垂直为5列的菱形区域）内有盛开的花朵，蜂房都可以产出相应的蜂蜜。</p><p>如果在蜂房的判定范围内有不同种类的花，则蜂房会根据花的位置顺序生产蜂蜜，如下图所示，数字最小的位置的花的种类将作为蜂房产出蜂蜜的种类。</p>', NULL),
	(3548, 'craft', 'DE0005', '提示', '01', '<ul><li>在姜岛的蜂房附近种植花朵将全年生产花蜜。<li class="ql-indent-1">推荐在开放姜岛西部的农场区域后将蜂房放置在姜岛农场，这样蜂房能够一年四季都产出蜂蜜，并且由于在姜岛种植作物不受季节限制不会枯萎，所以完全可以在蜂房周围种上最贵的花朵玫瑰仙子，使其产出花蜜以达到收益最大化。</li></li><li>一个在冬季保留的蜂房将会在春季5日首次生产蜂蜜，而一个正好在冬季28日放置的蜂房会在春季4日首次生产蜂蜜，这样可以额外收获一次。注：该特性已在1.6版本被修复，现在无论如何放置/保留蜂房都无法使蜂蜜提前产出。</li><li>由于蜂房的产出效率是固定的，在1.6版本以后，基础价格低于300的蜂蜜适合用小桶加工成蜜蜂酒售出（在1.6以前的版本蜜蜂酒的基础售价为200块）。而基础价格高于300的蜂蜜（只有虞美人蜂蜜、玫瑰仙子蜂蜜）更适合直接售出。</li></ul>', NULL),
	(3549, 'craft', 'DE0108', '', '01', '<p><b>火把</b>是一种可打造光源，用于在黑暗提供照明。把湿透的报纸放进回收机也有概率产出三个火把。</p><p>火把可以直接拿在手上，或放置在室内外以及矿井以提供大约半径3格的照明。放置后，可以通过使用锄头、十字镐或者斧头移除。玩家可以站在火把所占用的格子上。</p><p>火把还可以被放置在围栏和洒水器上。</p>', NULL),
	(3550, 'craft', 'DE0108', '裁制', '01', '<p>火把可通过缝纫机使用布料制成高热T恤。火把还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(3551, 'craft', 'DE0108', '趣闻', '01', '<p>如果你用喷壶给火把浇水，火不会熄灭，将一直保持发光。</p>', NULL),
	(3552, 'craft', 'DE0063', '', '01', '<p><b>鱼饵</b>是所有鱼饵中最基础的一种，可以使玩家在钓鱼时鱼的上钩时间减少50%。达到钓鱼等级2 后可以通过打造获得，同时解锁鱼店以5g出售。鱼饵也可以在钓鱼宝箱中发现，也会随机出现在旅行货车的货物中，价格为100g ~ 1,000。虫饵盒每天还会产出4-5个鱼饵。</p><p>鱼饵可以在蟹笼中使用。</p>', NULL),
	(3553, 'craft', 'DE0063', '使用', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3554, 'craft', 'DE0063', '收集包', '01', '<p>鱼饵不被用于任何收集包。</p>', NULL),
	(3555, 'craft', 'DE0063', '染色', '01', '<p>鱼饵可用于染色。放置在染料罐内时，可用作<b>红色</b>染料。放置在缝纫机内时，鱼饵还可用作<b>粉色</b>染料。</p>', NULL),
	(3556, 'craft', 'DE0063', '任务', '01', '<p>鱼饵不被用于任何任务。</p>', NULL),
	(3557, 'craft', 'DE0125', '', '01', '<p></p><p><b>夏季种子</b>是一种通过打造得到的种子。打造配方的名字是“野生种子（夏季）”，但得到的物品是“夏季种子”。作物成熟时，会随机变为香味浆果、葡萄和甜豌豆。葡萄是唯一一种可通过采集和种植两种途径得到的水果。</p><p>夏季种子和春季种子、秋季种子以及冬季种子一样，是一种只能在特定季节种下的野生种子，长出的作物通常也是对应季节的采集物品。通常，耕种技能可以提升长出高品质作物的几率，但冬季种子长出高品质作物的几率是由采集技能决定的。选择“植物学家”职业可以保证收获的作物都是铱星品质。</p><p>由夏季种子长成的作物可能会在收获阶段使所在的地块变为没有锄过的地块。这时，所有使用过的肥料效果都会消失。</p><p>乌鸦不会侵害由夏季种子长成的作物，因此并没有必要使用稻草人。</p>', NULL),
	(3558, 'craft', 'DE0125', '来源', '01', '<ul><li>打造 (每次制造生产10份夏季种子。)</li><li>把一个香味浆果放进种子生产器（产出：1-3份夏季种子）</li><li>完成夏季采集收集包 （奖励：30份夏季种子）</li><li>旅行货车 (售价165g ~ 1,000)</li><li>有时也可以从骷髅洞穴宝箱中找到5-20个</li></ul>', NULL),
	(3559, 'craft', 'DE0125', '收获作物', '01', '<p>夏季种子收获时，变为下面任一农作物的几率是相同的。</p>', NULL),
	(3560, 'craft', 'DE0125', '利润计算', '01', '<p>夏季种子是一个有利可图的配方。制作一组夏季种子可获得10份种子，总价值为550g，而制作夏季种子所需的铱星品质材料仅能卖420g（拥有植物学家职业时为436g）。</p>', NULL),
	(3561, 'craft', 'DE0125', '画廊', '01', '<p>所有季节的野生种子在生长到第二阶段时候看起来很像枯萎的作物。</p><p></p>', NULL),
	(3562, 'craft', 'DE0113', '', '01', '<p></p><p><b>木桶火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3563, 'craft', 'DE0060', '', '01', '<p><strong>旋式鱼饵</strong>是一种渔具，可以使玩家在钓鱼时的最长等待上钩时间缩短3.75秒，从而提升效率。达到钓鱼等级6 后可以通过打造获得，同时解锁鱼店以500g出售。旋式鱼饵也会随机出现在旅行货车的货物中，售价为750g ~ 1,250。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3564, 'craft', 'DE0060', '收集包', '01', '<p>旋式鱼饵不用于任何收集包。</p>', NULL),
	(3565, 'craft', 'DE0060', '任务', '01', '<p>旋式鱼饵不用于任何任务。</p>', NULL),
	(3566, 'craft', 'DE0060', '收益计算', '01', '<ul><li>制作旋式鱼饵并出售是有收益的，每个可以获得10g的利润。如果玩家拥有“铁匠”职业，则会亏损−110（因为这不如直接出售铁锭）。</li></ul>', NULL),
	(3567, 'craft', 'DE0013', '', '01', '<p></p><p><b>种子生产器</b>是一种精炼设备，放入成熟农作物或以下四种采集物品：野山葵，香味浆果，普通蘑菇和冬根可得到种子，花费游戏内 20分钟。</p><p>打造配方在耕种等级9级时解锁。完成布告栏的 染料收集包后玩家会得到一个种子生产器。偶尔也会在骷髅洞穴的宝箱层获得种子生产器。</p>', NULL),
	(3568, 'craft', 'DE0013', '产出的数量', '01', '<p>种子生产器有0.5%的概率产出1个上古种子，有1.99%概率产生1-4个混合种子，有97.51%概率产出1-3个投入作物同类的种子（期望为每个作物会产出1.9502个种子，上古水果为1.9552个种子）。产出种子数和类型都不受运气影响。</p><p>如果在创建存档时选中了“Use Legacy Randomization”游戏会使用一种伪随机的方式来计算产出的种子数量：</p><p><code>(已游玩天数) + (存档 ID) / 2 + (放置的横坐标) + (放置的纵坐标) × 77 + (当前时间)</code></p><p>以上公式决定了产出的种子类型和数量。当日运气和增益的幸运值依旧不会对结果产生影响。</p><p>如果想以使用种子生产器代替购买新种子，每次平均需要牺牲 51% 的收成（= 1 / (0.9751 * 2)）以获取用于种植下一个收成周期的新种子。</p>', NULL),
	(3569, 'craft', 'DE0013', '不适用的作物', '01', '<p>种子生产器不适用于以下物品：</p><ul><li> 黑莓</li><li> 椰子</li><li> 蕨菜</li><li> 茶叶</li><li> 野梅</li><li> 咖啡豆</li><li> 番红花</li><li> 甜豌豆</li><li> 水晶果</li><li> 美洲大树莓</li><li> 果树结出的水果</li><li>所有分类为采集的物品<li class="ql-indent-1">例外： 野山葵、 香味浆果、 普通蘑菇和 冬根会生产对应的季节种子</li></li></ul>', NULL),
	(3570, 'craft', 'DE0001', '', '01', '<p><b>洒水器</b>是一件在每天早晨灌溉毗邻的4个土壤方格（上、下、左、右）的可打造物品。灌溉仅限于已开垦的土壤方格。洒水器的合成配方会在耕种等级达到2级时解锁。</p><p>可以将火把放置在洒水器上。</p><p>洒水器可以给史莱姆屋里的水槽添水，但不能给花盆浇水或给宠物的碗中添水。洒水器不能放置在沙地上。</p><p></p>', NULL),
	(3571, 'craft', 'DE0001', '升级部件', '01', '<p>升级部件可以安装在已放置的洒水器上，拾取洒水器时可以在不破坏它的情况下卸下升级部件。</p><p>同一个洒水器一次只能安装一个升级部件。</p>', NULL),
	(3572, 'craft', 'DE0001', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3573, 'craft', 'DE0001', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3574, 'craft', 'DE0147', '', '01', '<p></p><p><b>辉石戒指</b>是一种戒指，可以在达到采矿等级4 后通过打造获得。它也可能作为随机库存出现在星露谷展览会的商店中，价格是1,000星星币。打开谜之盒也有概率获得。</p><p>辉石戒指结合了光辉戒指和磁铁戒指的效果（发出半径10格的光并增加2格收集范围），并且可以与另一个辉石戒指、光辉戒指、磁铁戒指、小型光辉戒指、小型磁铁戒指或铱环叠加。</p>', NULL),
	(3575, 'craft', 'DE0147', '染色', '01', '<p>辉石戒指可用作染料罐的<b>黄色</b>染料，放置在缝纫机内时还可用作<b>金色</b>染料。</p>', NULL),
	(3576, 'craft', 'DE0100', '', '01', '<p><b>金锭</b>可以通过使用熔炉融化 5 个黄金矿石和1个煤炭铸造而成，用熔炉融化黄金矿石需要花费游戏内 5 个小时。采矿 7 级后可在打造界面用 2 块铁锭制成 1 块金锭。</p><p>当玩家获得熔炉的配方后，有极小概率会从铁匠铺附近的垃圾桶中获得金锭。完成布告栏的 魔法师收集包可以获得 5 金锭。冬日星盛宴上，如果给玩家准备神秘礼物的是克林特，那他可能会给你 5 个金锭作为神秘礼物。金锭有几率从怪物掉落，分别为灰尘精灵（0.1%）、鱿鱼娃（5%）、暗影狂徒（1%）、暗影萨满（1%）。在矿井的 41 层到 80 层挖掘沙地，有 0.1% 的几率挖出金锭。金锭广泛用于各种物品的打造，并且是升级金制工具的原料。</p><p>一个金锭的售价是250g，如果玩家具备“铁匠”职业，则是375g。</p>', NULL),
	(3577, 'craft', 'DE0100', '工具升级', '01', '<p>将工具第三次升级需要金锭。</p>', NULL),
	(3578, 'craft', 'DE0100', '收集包', '01', '<p>锅炉房的 铁匠的收集包需要 1 个金锭。完成布告栏的 魔法师收集包的收集的奖励是 5 个金锭。</p>', NULL),
	(3579, 'craft', 'DE0100', '裁制', '01', '<p>金锭可通过缝纫机使用布料制成金胸甲。金锭可用于染色。放置在染料罐内时，金锭可用作<b>黄色</b>染料。放置在缝纫机内时，金锭还可用作<b>金色</b>染料。</p>', NULL),
	(3580, 'craft', 'DE0100', '任务', '01', '<ul><li>布告栏会在任何季节随机出现请求提供金锭的求助任务，奖励是750g和150友谊点。这个求助任务只有在玩家获取了熔炉的制作配方之后才会出现。</li><li>饲养有大海参、虹鳟鱼或幽灵鱼的鱼塘在数量从7提升到10的时候，可能会出现要求玩家提供3块金锭的鱼塘任务。</li></ul>', NULL),
	(3581, 'craft', 'DE0100', '提示', '01', '<p>玩家拥有分解机后，可以从威利处以750g购买寻宝者并分解来获得 2 个金锭。</p>', NULL),
	(3582, 'craft', 'DE0056', '', '01', '<p><b>软木塞浮标</b>是一种渔具，可以使玩家在钓鱼时的绿条尺寸增加24像素。如果玩家使用高级铱金鱼竿装备了两个软木塞浮标，则增加48像素。达到钓鱼等级7 后可以通过打造获得，同时解锁鱼店以750g出售。软木塞浮标也会随机出现在旅行货车的货物中，售价为750g ~ 1,250。达到钓鱼等级6 后也有概率从谜之盒和金色谜之盒中开出。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3583, 'craft', 'DE0056', '收集包', '01', '<p>软木塞浮标不用于任何收集包。</p>', NULL),
	(3584, 'craft', 'DE0056', '任务', '01', '<p>软木塞浮标不用于任何任务。</p>', NULL),
	(3585, 'craft', 'DE0056', '注意', '01', '<ul><li>这个配方是有利润的，利润为105g。不过，将史莱姆泥压成史莱姆蛋并将硬木放入碎木机中加工，其平均利润会更高。</li></ul>', NULL),
	(3586, 'craft', 'DE0027', '', '01', '<p><br></p><p><strong>宝箱</strong>是游戏里的一种用于存储物品的设备。它可以通过打造获得。宝箱可以存储多达36种不同类型的物品。最多可以将999个相同的物品放到一个格子里。物品在箱子里不会腐坏。</p><p>宝箱可以放在游戏中的许多地方。如果宝箱阻挡了村民的行走路线，宝箱会被村民推到旁边。</p><p>用任何工具敲击（或空手反复点击）一个空的箱子，可以将其拾起并收回背包。但是，只要箱子里放了东西，就无法用任何方式拾起它；除非你将箱子清空。</p><p>用任何工具敲击（或空手反复点击）装有物品的箱子，它将移动一格。多数时候，移动的方向都是远离玩家的，但如果远离玩家的格子上不能摆放物品，箱子就会移动到最近的能摆放的格子上。</p>', NULL),
	(3587, 'craft', 'DE0027', '颜色', '01', '<p>打开箱子时，在物品窗口上方有一串不同颜色的方块，可让玩家选择箱子的外观颜色。颜色可以随时更改，无需付费。颜色选择器中有20种可供选择的颜色，外加一个未选择颜色时的棕色。</p>', NULL),
	(3588, 'craft', 'DE0072', '', '01', '<p><b>生长激素</b>是一种肥料，可以加快作物生长速度 10%（在有农业学家技能时共加速 20%）。获取途径有：第一年春季15号后在皮埃尔的杂货店用 100g 购买、手工打造、从碎骨机获得。完成茶水间的 春季作物收集包会得到20个生长激素。</p><p>生长激素可以在任何时候添加到犁过的土壤中，包括播种前、播种后、或是作物的任何生长阶段。对于可多次收获的作物，只有第一次收获的周期会缩短，生长激素对后续收获没有作用。</p>', NULL),
	(3589, 'craft', 'DE0072', '收集包', '01', '<p>不用于任何收集包。完成茶水间的 春季作物收集包会奖励20个生长激素。</p>', NULL),
	(3590, 'craft', 'DE0072', '裁制', '01', '<p>生长激素可通过缝纫机使用布料制成下半部分可染色的高腰上衣。生长激素可用于染色。放置在染料罐内时，生长激素可用作<b>橙色</b>染料。放置在缝纫机内时，生长激素还可用作<b>棕色</b>染料。</p>', NULL),
	(3591, 'craft', 'DE0072', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3592, 'craft', 'DE0091', '', '01', '<p><b>爆炸弹丸</b>是一种一次性使用的爆炸物。它能够对爆炸半径内的部分物品造成破坏，爆炸效果与樱桃炸弹的效果相同。但和其他炸弹不同，爆炸弹丸必须用弹弓发射出去。</p><p>在战斗等级达到8级时，玩家可以获得爆炸弹丸的打造配方。在配方解锁后，玩家也可在探险家公会以300g的单价购买。</p><p>要把爆炸弹丸装备在弹弓上，先选中爆炸弹丸，然后然后鼠标右键单击弹弓即可。</p><p>当爆炸弹丸击中怪物、墙壁、楼梯或水体时，它才会引爆。否则，它会直接从障碍物（例如石头或植物）的上方穿过。</p><p>爆炸弹丸可以用来击杀濒死的木乃伊。</p>', NULL),
	(3593, 'craft', 'DE0091', '半径', '01', '<p>爆炸弹丸的爆炸半径为2。整个方形为造成伤害的区域，去掉紫色部分即为爆炸范围，红色部分为可能开垦出的耕地范围。 </p><p></p>', NULL),
	(3594, 'craft', 'DE0011', '', '01', '<p><br></p><p><strong>木桶</strong>是用于生产工匠物品的设备。可以放入果酒、蜂蜜酒、啤酒、淡啤酒、奶酪、山羊奶酪进行陈酿，来增加它们的品质和价值。</p><p>可以随时用斧头、锄头或十字镐敲掉木桶，来提前取出正常、银星和金星品质的物品。木桶工作时，正面会显示一个星星，表示当前桶中物品的品质。</p><p>木桶能够生产出“铱星”品质的物品。铱星是最高级别的品质，物品的价值会翻倍。不同类型的物品需要发酵的时间不同。果酒从基本品质到铱星品质的时间最长，需要总共2个季节（56天）的发酵。</p><p>木桶可以放置在任何地方，但是只有放在地窖里时才能使用。</p>', NULL),
	(3595, 'craft', 'DE0011', '摆放策略', '01', '<p>地窖起初有33个木桶。可以用125个木桶（在PC上）装满酒窖，并且保留可触及所有木桶的路径，如本页面下方的画廊中所示。</p><p>也可以将木桶完全放满酒窖。总共189个木桶, 需要156个硬木和3120个木材。为了让189个木桶全部一起工作，玩家必须边从里到外走边放置木桶和发酵物品。方便起见可以先摆好前文所述的125桶布局再沿路径填满木桶。为了收获成品，玩家需要边走边破坏木桶。这很费时间，所以建议只用在高价并且费时的物品上面，例如杨桃和上古水果果酒。</p><p>手机版由于操作问题，无法收集角落的木桶，因此最多能摆放118个木桶。使用控制器时，可能无法触及对角线角落的木桶。</p><p>上文所述键鼠和手柄/手机版布局见于下方的画廊。</p>', NULL),
	(3597, 'craft', 'DE0109', '', '01', '<p></p><p><b>金制火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3598, 'craft', 'DE0032', '', '01', '<p></p><p><b>楼梯</b>是一种可消耗的物品，用于在矿井或骷髅洞穴中快速移动到下一层。不能被放置在矿井的第0层或120层。</p><p>楼梯的工作方式和矿井中本身有的阶梯一样。楼梯只能使用一次。如果你使用楼梯离开这一层，再回到该层时楼梯已经消失。楼梯一旦被放置，则不能被拾起，即使未被使用。</p><p>如果你没有足够的石头来打造楼梯，你也可以从木匠的商店处购买石头。售价为每个石头20金（第二年起，价格会大幅增加为100金），即一个楼梯的成本为20*99=1980金（第一年）或100*99=9900金（第二年及之后）。此外，每周日也可在沙漠商人处使用一个翡翠兑换一个楼梯。兑换次数是无限。基于翡翠的基础售价为200金，使用翡翠兑换楼梯是更有性价比的选择。沙漠节期间，肯特会在其商铺以 30 卡利科三花蛋的价格出售楼梯（限10个）。</p>', NULL),
	(3599, 'craft', 'DE0032', '提示', '01', '<ul><li>可以通过使用楼梯避免与怪物进行战斗，从而节约时间，因为矿井中的一些强大怪物难以在短时间内被击杀。你也可以将获得更好的武器作为一个选项。</li><li>可以通过在骷髅洞穴中大量使用楼梯快速到达非常深的楼层，从而获得更多有价值的物品，如铱矿石、五彩碎片以及任何可能在宝箱层出现的战利品（自动抚摸机、雨水图腾、史莱姆蛋等）。</li><li>注意：楼梯不能被放置在有地砖花纹的地板上（如骷髅洞穴的木乃伊层的部分地面），以及可被锄头挖掘的地面上。在骷髅洞穴中使用楼梯时，应当尤其注意这一点。</li><li>使用宝石复制机复制翡翠，在周日和沙漠商人兑换楼梯并使用分解机分解楼梯可获得99石头。这可以作为一种快速获取大量石头的方式。</li></ul>', NULL),
	(3600, 'craft', 'DE0083', '', '01', '<p><strong>传送图腾：海滩</strong>是一种传送图腾，在物品栏中选中并右键点击屏幕中无法直接交互的任意处，便可传送玩家至沙滩。它可在达到采集等级 6 解锁配方后打造获得。</p><p>传送图腾：海滩可用于绕开鹈鹕镇的事件或节日活动且不触发它们，但依然会触发在沙滩发生的事件或节日活动。</p><p>注意：夏威夷宴会或月光水母起舞节日当天，如果在节日开始前使用传送图腾：海滩，那么只会消耗掉图腾，并不会传送玩家至沙滩。</p>', NULL),
	(3601, 'craft', 'DE0083', '其他来源', '01', '<ul><li>打开谜之盒有概率获得1个传送图腾：海滩。</li><li>养有龙虾、螃蟹、鸟蛤、蚌、虾或牡蛎的鱼塘，在鱼的数量达到至少9条后，每天有小概率产出1个传送图腾：海滩。这六种鱼的鱼塘对于传送图腾：海滩的每日产出概率是相同的（9条鱼为6%，10条鱼为7%）。</li><li>在冬季25日的冬日星盛宴，如果随机的村民是威利，他会赠送玩家25个传送图腾：海滩。</li><li>完成鱼缸的 海鱼收集包的奖励是5个传送图腾：海滩。</li><li>第1年夏季1日开始，传送图腾：海滩有10%概率在远古斑点中替换粘土出现。</li></ul>', NULL),
	(3603, 'craft', 'DE0083', '收集包', '01', '<p>传送图腾：海滩不用于任何收集包。</p>', NULL),
	(3604, 'craft', 'DE0083', '裁制', '01', '<p>传送图腾可通过缝纫机使用布料制成图腾面具。传送图腾可用于染色。放置在染料罐内时，传送图腾可用作<strong>橙色</strong>染料。放置在缝纫机内时，传送图腾还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3605, 'craft', 'DE0083', '任务', '01', '<p>传送图腾：海滩不用于任何任务。</p>', NULL),
	(3606, 'craft', 'DE0078', '', '01', '<p><strong>传送图腾：姜岛</strong>是一种传送图腾，在物品栏中选中并右键点击屏幕中无法直接交互的任意处，便可传送玩家至姜岛。它可通过在火山矮人商店花费10,000g购买配方后打造获得。修好姜岛的农场图腾柱后会获得1个传送图腾：姜岛。</p>', NULL),
	(3608, 'craft', 'DE0078', '收集包', '01', '<p>传送图腾：姜岛不用于任何收集包。</p>', NULL),
	(3609, 'craft', 'DE0078', '裁制', '01', '<p>传送图腾可通过缝纫机使用布料制成图腾面具。传送图腾可用于染色。放置在染料罐内时，传送图腾可用作<strong>橙色</strong>染料。放置在缝纫机内时，传送图腾还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3610, 'craft', 'DE0078', '任务', '01', '<p>传送图腾：姜岛不用于任何任务。</p>', NULL),
	(3611, 'craft', 'DE0057', '', '01', '<p><b>万能鱼饵</b>是一种鱼饵，可以使玩家在钓鱼时有机会一次钓到2条鱼，通过莱纳斯在4心事件中给予的配方打造获取。</p><p>在玩家与莱纳斯达到4心友谊时，在晚上8点左右去他的帐篷，便会得到万能鱼饵的配方。一旦学会配方，便可以从钓鱼宝箱中获得。如果尚未触发莱纳斯4心事件，也可通过挖掘姜岛上的远古斑点获得。</p><p>在钓鱼时使用万能鱼饵可以使鱼咬杆的时间减少62.5%。并且有概率钓到2条鱼，概率等于 <samp>0.25 + 当日运气 / 2.0</samp> 。钓2条鱼的效果不能作用于传说鱼类，但此效果依旧适用于齐先生的特别任务“大家族”进行期间限定的传说鱼类二代。在蟹笼中使用万能鱼饵会降低一半获取垃圾的概率（如果玩家没有水手职业），同时，万能鱼饵也会使蟹笼有概率一次收获两个，概率等于 <samp>0.25 + 当日运气 / 2.0</samp> 。</p><p>注意，使用万能鱼饵一次钓到两条鱼不会获得双倍的钓鱼经验值，但是在进行钓鱼相关任务时会将两条鱼计入所需总数。</p>', NULL),
	(3612, 'craft', 'DE0057', '使用方法', '01', '<p>条件：训练用鱼竿和竹鱼竿不能使用鱼饵。只有玻璃纤维鱼竿、铱金鱼竿和高级铱金鱼竿可以使用鱼饵。</p><p>每次抛竿都会消耗一块鱼饵。鱼饵用光时，游戏会弹出一个通知提醒“你已经用掉了最后一块鱼饵”。</p><p><b>键鼠</b></p><p>若要把鱼饵放进鱼竿里，打开背包，鼠标点击鱼饵（ 左键或右键，取决于你要放置多少鱼饵），然后右键鱼竿。</p><p>若要移除鱼饵， 右键鱼竿。</p><p><b>手柄</b></p><p>在 Xbox 手柄上，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 PS4 手柄上， 按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>在 Nintendo Switch 手柄上，打开背包，按选取一组鱼饵（或按选取单个鱼饵），然后按放在鱼竿上。</p><p>按手柄上的 （NS 上是 ）来移除鱼饵。</p><p><b>移动端</b></p><p>对于移动版，若要把鱼饵放进鱼竿里，打开背包并将鱼饵拖到鱼竿上即可。</p>', NULL),
	(3613, 'craft', 'DE0057', '收集包', '01', '<p>万能鱼饵不用于任何收集包。</p>', NULL),
	(3614, 'craft', 'DE0057', '配方', '01', '<p>万能鱼饵不用于任何配方。</p>', NULL),
	(3615, 'craft', 'DE0057', '裁制', '01', '<p>万能鱼饵可通过缝纫机使用布料制成上衣。</p>', NULL),
	(3616, 'craft', 'DE0057', '任务', '01', '<p>万能鱼饵可能会被用于鱼塘任务，具体为：</p><ul><li>在史莱姆鱼的数量达到 5 时可能会需要 5 个。</li><li>在大头鱼和大嘴鲈鱼的数量达到 7 时候可能会需要 5 个。</li><li>在小龙虾、蜗牛、玉黍螺、蚌、鸟蛤、牡蛎、螃蟹、龙虾数量达到 7 时可能会需要 1 个。</li></ul>', NULL),
	(3617, 'craft', 'DE0082', '', '01', '<p><b>怪兽香水</b>是一种可制造物品，用来吸引更多怪物。其配方在完成法师的特别任务“五彩胶冻”后解锁。书摊老板也有出售。</p><p>怪兽香水的持续时间是10分钟，使用怪兽香水后会产生紫色气体特效并触发一段角色表示恶心的表情动画。</p><p>使用怪兽香水后可使矿井、骷髅洞穴或火山地牢的敌人数量翻倍。怪兽香水不会清除食物或饮料带来的增益效果。</p>', NULL),
	(3618, 'craft', 'DE0082', '染色', '01', '<p>怪兽香水可用作染料罐和缝纫机的<b>紫色</b>染料。</p>', NULL),
	(3619, 'craft', 'DE0062', '', '01', '<p><b>优质浮标</b>是一种渔具，可以使玩家在钓鱼时可以提高钓到的鱼的品质。在得到日记残页 #4后可以在纸条中描述的位置挖出一个优质浮标。在完成威利的特别任务“需要多汁的虫子！”后，可以触发威利的剧情动画并获得制作配方。</p><p>装备优质浮标后可以使钓上来的鱼提升一级品质（如金星提升为铱星），如果你不能够完美钓鱼，那么可以选择使用优质浮标来钓出铱星品质的鱼。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3620, 'craft', 'DE0018', '', '01', '<p></p><p><b>树液采集器</b>是一种精炼设备，可放置在树上以生成枫糖浆，橡树树脂或松焦油。它也可以放在桃花心木树上产生树液，或放在蘑菇树上以生产不同的蘑菇。</p><p>可以使用斧头或十字镐敲击一次来移除树液采集器以供重复使用。</p><p>如果被闪电或炸弹击中的树上有树液采集器，则采集器及其产出物品均会被破坏。</p><p>放置在枫树、橡树、松树和桃花心木树上的树液采集器在冬季可继续工作。由于蘑菇树在冬季会变成树桩，因此放在上面的树液采集器在冬季不会继续工作。</p>', NULL),
	(3621, 'craft', 'DE0023', '', '01', '<p></p><p><b>太阳能板</b>是一种精炼设备，放置在室外时每经过7个晴天（包括刚放置的那天）都会在次日早晨产出一个 电池组。其打造配方在完成特别任务“岛屿食材”后由卡洛琳赠送。</p><p>太阳能板仅会根据其放置的位置记录晴天的天数。例如，放置在姜岛上的太阳能板并不会受星露谷下雨天气的影响。而由于沙漠不会下雨，放置在沙漠中的太阳能板可以每 7 天稳定产出一个电池组。</p>', NULL),
	(3622, 'craft', 'DE0103', '', '01', '<p><b>超级炸弹</b>是一种一次性的爆炸物。它会破坏其爆炸半径内的某些物品。另一些则不受影响。</p><p>超级炸弹可以通过打造获得，或是以1,600g的价格在矿井入口的矮人处和火山地牢商店购买。在沙漠商人处可以用5个铱矿石换取1个超级炸弹。沙漠节的卡利科三花蛋商人也会以 15 卡利科三花蛋的价格出售。鱿鱼娃、铱蝠、幽灵头骨和熔岩掘地虫也可能在被击杀时掉落超级炸弹。在任何季节，都可以从电影院内的抓娃娃游戏中赢得它。在骷髅洞穴的宝藏房间中偶尔可以找到5个超级炸弹。如果玩家选择了海滩农场并且至少升级了两次农舍，那么偶尔可以在补给箱中找到3~5个超级炸弹。旅行货车会以150g ~ 1,000的价格随机出售1个超级炸弹。谜之盒或金色谜之盒也有概率开出5个超级炸弹。兑奖机奖励也有可能获得6个或8个超级炸弹。肯特会随机寄给玩家1个超级炸弹作为随信礼物。</p><p>提醒：由于爆炸面积很大，使用时往往会受伤。使用时请确保有足够的血量以及治疗用品。如果玩家已经阅读了能力之书《矮人安全手册》，那么超级炸弹对其造成的伤害将减少25%。</p>', NULL),
	(3623, 'craft', 'DE0103', '影响', '01', '<ul><li>爆炸范围内的岩石、矿石和宝石节点会掉落相应物品，无法摧毁大岩石。</li><li>爆炸范围内的可采集矿物和蘑菇会直接被摧毁。</li><li>爆炸无法摧毁陨石跟巨大作物。</li><li>一定范围内（范围比爆炸半径要小）未开垦的地块和远古斑点都会变成已被开垦的状态，且可以掉落相应的掉落物（例如粘土），效果与玩家使用锄头时相同。</li><li>爆炸范围内放置的大部分可打造物品（例如小桶、宝石复制机等）都将会<b>直接消失，没有掉落物</b>。<li class="ql-indent-1">爆炸范围内的地板和小径仍可掉落并被玩家重新拾起。</li><li class="ql-indent-1">蟹笼不会被炸弹破坏。</li><li class="ql-indent-1">宝箱、石箱、祝尼魔箱、自动采集器不会被炸弹破坏，无论其中是否存有物品。</li><li class="ql-indent-1">加热器会被炸弹破坏。</li></li><li>摧毁一棵树一共需要6次爆炸：炸倒需要4次，摧毁树桩需要2次。</li></ul>', NULL),
	(3624, 'craft', 'DE0103', '半径', '01', '<p>超级炸弹的半径为7格。整个方形为造成伤害的区域，去掉紫色部分即为爆炸范围，红色部分为可能开垦出的耕地范围。</p><p></p>', NULL),
	(3625, 'craft', 'DE0103', '收集包', '01', '<p>超级炸弹不用于任何收集包。</p>', NULL),
	(3626, 'craft', 'DE0103', '裁制', '01', '<p>超级炸弹可通过缝纫机使用布料制成轰炸机夹克。超级炸弹还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(3627, 'craft', 'DE0103', '任务', '01', '<p>鱼塘内的岩浆鳗鱼有可能会需要两个超级炸弹来把鱼的容量从5扩充到7。</p>', NULL),
	(3628, 'craft', 'DE0103', '趣闻', '01', '<p>如果玩家想强行穿过门卫进入赌场，他就会掏出超级炸弹把你炸伤。</p>', NULL),
	(3629, 'craft', 'DE0019', '', '01', '<p></p><p><b>重型树液采集器</b>是一种精炼设备，以普通树液采集器两倍的速度工作。可以在齐先生的核桃房花费20颗 齐钻购买打造配方。重型树液采集器可以放置在树上用来生产枫糖浆、橡树树脂、松焦油，也可以放在桃花心木树上产生树液，或放在蘑菇树上以生产不同的蘑菇。</p><p>可以使用斧头或十字镐敲击一次来移除重型树液采集器以供重复使用。</p><p>如果被闪电或炸弹击中的树上有树液采集器，则采集器及其产出物品均会被破坏。</p><p>放置在枫树、橡树、松树和桃花心木树上的重型树液采集器在冬季可继续工作。由于蘑菇树在冬季会变成树桩，因此放在它们上面的重型树液采集器在冬季不会继续工作。</p>', NULL),
	(3630, 'craft', 'DE0110', '', '01', '<p></p><p><b>骷髅火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3631, 'craft', 'DE0020', '', '01', '<p></p><p><b>虫饵盒</b>是一种精炼设备，用于每天稳定产出钓鱼用的普通鱼饵，其打造配方在达到钓鱼等级 4 时解锁。玩家完成茶水间（重新混合后）的 渔夫收集包后也可以获得一个虫饵盒。</p><p>虫饵盒可以在每日开始时产出4-5个鱼饵，不需要投入任何原料。</p>', NULL),
	(3632, 'craft', 'DE0020', '价值', '01', '<p>在<b>没有技能加成</b>的情况下，虫饵盒的原料可以卖出845g。也就是说，你要是想回本，虫饵盒得工作42天左右。如果我们按照<b>第一年的物价</b>来看，全部原料都买回来的话，则要花费4,675g，你得让它工作267天才能回本！</p><p>不过这些没算上你去威利那边购买鱼饵所需要的时间，如果你天天走着去买，损失的时间也是很多的。也就是说，你可以通过多做几个虫饵盒来获取鱼饵，还是蛮有用的。</p>', NULL),
	(3633, 'craft', 'DE0133', '', '01', '<p><strong>木地板</strong>是一种装饰物，会因被闪电击中而掉落。</p><p>木地板的配方可以在木匠的商店中花费100g购买以解锁。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场上时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3635, 'craft', 'DE0016', '', '01', '<p></p><p><b>避雷针</b>是一种精炼设备，用于在雷雨天气产出 电池组，其打造配方在达到采集等级 6 后解锁。</p><p>在雷雨天气期间，被闪电击中的避雷针会进入充能状态，并在第二天早上生成一个电池组。闪电发生时，玩家不需要在呆在农场中。</p><p>避雷针还可以用于保护农场。当所有避雷针都处在充能状态时，未被拦截的闪电有概率击中树、果树、农作物和地板。但避雷针无法用来保护指定的区域，因此放置的位置不会影响闪电击中的概率。一个未拦截过闪电的避雷针有一定的概率成功拦截闪电，而已经处于充能状态的避雷针无法再次拦截闪电，因此玩家可能会需要多根避雷针。即使已存在未拦截的避雷针，也有一定概率拦截失败。一天内的雷击次数是随机的，并且会被每日运气影响概率。</p><p>玩家也可以通过完成金库的 10,000金收集包获取一个避雷针。</p>', NULL),
	(3636, 'craft', 'DE0016', '拦截概率', '01', '<p>避雷针并不是每次都能接到雷击。这个数值的计算如下</p><p><code>拦截概率=1-(已充能的避雷针数量/避雷针总数)²</code></p><p>依据这一公式，可以得出：</p><ul><li>如果没有避雷针被充能，那么下一个雷击被拦截的概率为 100%</li><li>如果50%的避雷针被充能，那么下一个雷击被拦截的概率是 75%</li><li>如果90%的避雷针被充能，那么下一个雷击被拦截的概率只有 19%</li></ul><p>受每日运气和运气增益的影响，未拦截的雷击有(0.25-每日运气-幸运等级/100)的概率击中农场上的农作物、树和地板。未拦截的雷击会优先击中果树和耕地，其次是器械，地板和其它树。</p><p>如果提前睡觉则睡觉期间每小时尝试劈一次闪电，生成和拦截逻辑与上面相同。</p>', NULL),
	(3637, 'craft', 'DE0016', '备注', '01', '<ul><li>避雷针可以放置在农场外，但不会生产电池组。</li><li>鹈鹕镇的冬季不会有雷雨天气，姜岛上不存在雷雨天气。</li><li>雷雨当天游玩的时间越长，产生的闪电越多，运气越好，闪电生成的概率越高。</li><li>避雷针可以阻止动物吃该格的草，以帮助草的保留扩张。</li></ul>', NULL),
	(3638, 'craft', 'DE0081', '', '01', '<p><strong>传送图腾：山岭</strong>是一种传送图腾，在物品栏中选中并右键点击屏幕中无法直接交互的任意处，便可传送玩家至深山。它可在达到采集等级 7 解锁配方后打造获得。也可通过挖掘远古斑点获得。</p><p>打开谜之盒有概率获得一个传送图腾：山岭。</p><p>饲养小龙虾、玉黍螺或蜗牛的鱼塘，在鱼的数量达到至少9条后，每天都有小概率产出1个传送图腾：山岭。这三种鱼的鱼塘对于传送图腾：山岭的每日产出概率是相同的（9条鱼和10条鱼皆为7%）。</p><p>第1年夏季1日开始，传送图腾：山岭有10%概率在远古斑点中替换粘土出现。</p>', NULL),
	(3640, 'craft', 'DE0081', '收集包', '01', '<p>传送图腾：山岭不用于任何收集包。</p>', NULL),
	(3641, 'craft', 'DE0081', '裁制', '01', '<p>传送图腾可通过缝纫机使用布料制成图腾面具。传送图腾可用于染色。放置在染料罐内时，传送图腾可用作<strong>橙色</strong>染料。放置在缝纫机内时，传送图腾还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3642, 'craft', 'DE0081', '任务', '01', '<p>传送图腾：山岭不用于任何任务。</p>', NULL),
	(3643, 'craft', 'DE0140', '', '01', '<p></p><p><b>木牌</b>是一种可以 打造 的物品。 玩家可以将 物品栏中物品的图片印在木牌上.玩家可以将通过拿着物品并右击鼠标将物品的图片放在木牌上。物品不会因此被消耗。</p><p>木牌可以插在星露谷的任何地方。如果放在村民路线中，木牌会被摧毁。另见"农场外打造物品" 了解地图上不会被NPC经过而破坏放置物品的安全区域。</p><p>把木牌放置在鱼塘上，木牌会变成可以显示鱼塘内有多少鱼的计数器。木牌上可以看到鱼的图像，右下角有一个数字。</p>', NULL),
	(3644, 'craft', 'DE0038', '', '01', '<p></p><p><b>碎木机</b>可用于将硬木或浮木加工成其他材料，可在第一年冬季的第2天收到罗宾的信后前往木匠的商店以1,000g购买。沙漠节期间，肯特会在其商铺以 25 卡利科三花蛋的价格出售碎木机（限1台）。</p>', NULL),
	(3645, 'craft', 'DE0119', '', '01', '<p></p><p><b>草籽</b>放置在地上会立即成为一丛草。随着时间推移，草丛会在农场中生长和扩散。当筒仓建好后，用镰刀收割草丛会获得干草。在冬季所有草均不会在农场中生长和扩散，收割的干草产量低于其他季节。</p>', NULL),
	(3646, 'craft', 'DE0119', '提示', '01', '<ul><li>为避免动物吃掉你种下的草籽，你可以在种下草籽后在其位置上放置一个栅栏或避雷针。这样你的草籽来源就不会被动物吃掉，每天都会往旁边衍生，可以用更少的地块满足更多的动物放牧需求（俗称“压草”）。</li><li>草籽本用于种植在室外以供农场的动物食用，但你也可以将其种植在农场建筑内，或是农场外的任意地方。动物不会吃掉这些地方的草，而草也不会生长扩散。但可以使用镰刀收割获得干草.</li><li>将草籽放入分解机可以快速获得10个纤维，相当于每个纤维10金，这是后期获取纤维的高效手段。</li><li>玛妮在沙漠节上以2 卡利科三花蛋的价格出售草籽（限100个）。</li></ul>', NULL),
	(3647, 'craft', 'DE0084', '', '01', '<p><b>仙尘</b>是一种可打造的一次性物品，允许玩家使精炼设备的生产过程立刻完成。玩家完成“海盗的妻子”任务后，可从贝啼处获得仙尘配方。</p><p>兑奖机第22个奖励后，循环奖励档位的第7档可固定获得1-2个仙尘。仙尘也有7%的概率在金宝箱中发现。完成浣熊的第四个任务后，可获得5个仙尘的奖励；完成第六个以及之后的任务后，7个仙尘是随机奖励之一。</p><p>书摊老板处可用1本星之书兑换8个仙尘。一旦解锁浣熊的第六个任务，浣熊妻子的商店可以1个神秘糖浆的价格兑换1个仙尘。</p><p>仙尘不可作用于孵化器、鸵鸟孵化器和史莱姆孵化器。对木桶使用仙尘时，其内的产品不会直接达到铱星品质，除非产品已经是黄金品质。每次使用一次仙尘，产品将会达到下一个品质，直到成为铱星品质。</p>', NULL),
	(3648, 'craft', 'DE0084', '裁制', '01', '<p>仙尘不用于任何裁缝或染色。</p>', NULL),
	(3649, 'craft', 'DE0084', '任务', '01', '<p>仙尘不用于任何任务。</p>', NULL),
	(3650, 'craft', 'DE0116', '', '01', '<p></p><p><b>石头火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3651, 'craft', 'DE0085', '', '01', '<p><b>雨水图腾</b>是一种可打造物品，可以让明天的天气强制下雨。在物品栏中选中并在屏幕中无法直接交互的任意处点击查看键（电脑端为，任天堂Switch为<b>Y</b>键），便可激活雨水图腾。它可在达到采集等级 9 解锁配方后打造获得。骷髅洞穴宝藏房间的宝箱偶尔也能开出1-3个雨水图腾。</p>', NULL),
	(3652, 'craft', 'DE0085', '注意事项', '01', '<ul><li>由于天气有优先级，有些日子是强制天气，比如节日或者季节第一天，那么使用雨水图腾就无法强制下雨。</li><li>冬天也可下雨（雨夹雪），可以使用这个特性在冬季购买美人鱼吊坠。</li><li>姜岛和鹈鹕镇不共享天气，在姜岛和鹈鹕镇使用分别影响当地的天气。</li></ul>', NULL),
	(3653, 'craft', 'DE0085', '收集包', '01', '<p>雨水图腾不用于任何收集包。</p>', NULL),
	(3654, 'craft', 'DE0085', '裁制', '01', '<p>雨水图腾可通过缝纫机使用布料制成图腾面具。雨水图腾可用于染色。放置在染料罐内时，雨水图腾可用作<b>橙色</b>染料。放置在缝纫机内时，雨水图腾还可用作<b>棕色</b>染料。</p>', NULL),
	(3655, 'craft', 'DE0085', '任务', '01', '<p>雨水图腾不用于任何任务。</p>', NULL),
	(3656, 'craft', 'DE0026', '', '01', '<p></p><p><b>晶球破开器</b>是一种精炼设备，用于在农场打开晶球，其打造配方在完成特别任务“洞穴巡查”后由克林特通过邮件赠送获得。</p><p>晶球破开器的处理时长为 60分钟 (1小时)，且不能打开古物宝藏、金色椰子、谜之盒以及金色谜之盒。</p><p>此物品提供了一种打开晶球的替代方法，此前玩家只能选择花费25g请克林特打开晶球。</p>', NULL),
	(3657, 'craft', 'DE0111', '', '01', '<p></p><p><b>镂空火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3658, 'craft', 'DE0040', '', '01', '<p><br></p><p><strong>缝纫机</strong>可以用来缝制衣服或给衣服染色，在完成艾米丽的“宝石能量”特别任务后通过邮件获得。</p><p>在柳巷2号中，也有一台缝纫机。在获得自己的缝纫机之前，玩家只能在柳巷2号中进行衣服的裁缝。</p><p>注意，缝纫机可以被炸弹爆炸破坏。</p>', NULL),
	(3660, 'craft', 'DE0130', '', '01', '<p><strong>风化地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>风化地板的配方可在矮人处以500g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3662, 'craft', 'DE0024', '', '01', '<p></p><p><b>鸵鸟孵化器</b>是一种用来把鸵鸟蛋孵化成鸵鸟的机器。其制作配方在玩家完成岛屿办事处捐赠和岛屿调查任务后由蜗牛教授给予。孵化器必须放置在畜棚，一个畜棚里可以放置多个孵化器。它孵化一个蛋需要花费 15,000分钟（9.5天），如拥有鸡舍大师职业则缩短一半至 7,500分钟（4.75天）。</p><p>要开始孵化过程，需要拿着鸵鸟蛋并右键点击机器。</p><p>如果多个鸵鸟蛋已经孵化完毕，玩家进入畜棚时只会孵出一个，玩家需要离开并重新进入畜棚使更多蛋孵出来。</p><p>如果畜棚已经满员（普通畜棚4只动物，大畜棚8只动物，高级畜棚12只动物），鸵鸟蛋的孵化将无法完成，直到腾出空间来容纳新鸵鸟。可以通过卖出动物或迁移动物的方式腾出空间。一旦畜棚不再满员，进入畜棚会使鸵鸟蛋立刻完成孵化。</p>', NULL),
	(3663, 'craft', 'DE0007', '', '01', '<p></p><p><b>压酪机</b>是一种用于制造工匠物品的工匠设备。它可以将奶变成奶酪。</p><p>任意品质的牛奶会转化为普通品质的奶酪，任意品质的羊奶会转化为普通品质的山羊奶酪。大壶牛奶和大瓶羊奶则会生产对应的金星品质的奶酪。</p><p>完成茶水间的 动物收集包后，会获得一个压酪机作为奖励。</p>', NULL),
	(3664, 'craft', 'DE0087', '', '01', '<p><b>木围栏</b>是一件可打造物品，可以让玩家在农场中隔开某些区域。可以维持完好状态48~52天，玩家的配偶有时会修理损坏的围栏。</p>', NULL),
	(3665, 'craft', 'DE0150', '', '01', '<p></p><p><b>荆棘戒指</b>是一种戒指，可以在达到战斗等级7 解锁配方后通过打造获得。</p><p>玩家在佩戴荆棘戒指时，被怪物攻击时会将受到的伤害反弹（伤害值等于其攻击的未减轻伤害减去怪物的防御力）。</p><p>注意：如果怪物对玩家造成的伤害小于10点，反弹给怪物的伤害改为未减轻伤害和实际造成伤害的平均值。</p>', NULL),
	(3666, 'craft', 'DE0150', '染色', '01', '<p>荆棘戒指可用作染料罐的<b>黄色</b>染料，放置在缝纫机内时还可用作<b>沙色</b>染料。</p>', NULL),
	(3667, 'craft', 'DE0064', '', '01', '<p><b>初级保湿土壤</b>是一种可以让土壤保持湿润的肥料。可以手工打造或在第一年春季15号后前往皮埃尔的杂货店用 100g 购买。</p><p>初级保湿土壤可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。土壤保湿的几率是33%。</p>', NULL),
	(3668, 'craft', 'DE0064', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(3669, 'craft', 'DE0064', '裁制', '01', '<p>初级保湿土壤可通过缝纫机使用布料制成下半部分可染色的高腰上衣。初级保湿土壤可用于染色。放置在染料罐内时，初级保湿土壤可用作<b>橙色</b>染料。放置在缝纫机内时，初级保湿土壤还可用作<b>棕色</b>染料。</p>', NULL),
	(3670, 'craft', 'DE0064', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3671, 'craft', 'DE0105', '', '01', '<p><b>炸弹</b>是一种一次性的爆炸物，它会对爆炸范围内的部分物品（例如：岩石，矿脉，宝石矿点等）造成伤害，对另一些物品则没有影响。</p><p>炸弹可以通过打造获得，也可以花费1,000g从矿井中的矮人处或火山地牢商店中购买。在沙漠商人处可以用5个石英交换1个炸弹。击杀蝙蝠、熔岩蟹、鱿鱼娃、飞蛇时都有可能掉落炸弹。在骷髅洞穴的宝箱里偶尔可以找到10个炸弹。打破危险矿井或骷髅洞穴中的木箱和木桶有概率掉落1个炸弹。如果玩家选择了海滩农场，且农舍只进行过一次升级，则会有概率从补给箱中获得3-5个炸弹。旅行货车会以150g ~ 1,000的价格随机出售1个炸弹。打开谜之盒有概率获得5个炸弹。兑奖机奖品有可能获得12个或15个炸弹。肯特会随机寄给玩家1个炸弹作为随信礼物。</p><p>如果玩家阅读过《矮人安全手册》，那么将减少25%来自炸弹的伤害。</p>', NULL),
	(3672, 'craft', 'DE0105', '影响', '01', '<ul><li>爆炸范围内的岩石、矿石和宝石节点会掉落相应物品，无法摧毁大岩石。</li><li>爆炸范围内的可采集矿物和蘑菇会直接被摧毁。</li><li>爆炸无法摧毁陨石跟巨大作物。</li><li>一定范围内（范围比爆炸半径要小）未开垦的地块和远古斑点都会变成已被开垦的状态，且可以掉落相应的掉落物（例如粘土），效果与玩家使用锄头时相同。</li><li>爆炸范围内放置的大部分可打造物品（例如小桶、宝石复制机等）都将会<b>直接消失，没有掉落物</b>。<li class="ql-indent-1">爆炸范围内的地板和小径仍可掉落并被玩家重新拾起。</li><li class="ql-indent-1">蟹笼不会被炸弹破坏。</li><li class="ql-indent-1">宝箱、石箱、祝尼魔箱、自动采集器不会被炸弹破坏，无论其中是否存有物品。</li><li class="ql-indent-1">加热器会被炸弹破坏。</li></li><li>摧毁一棵树一共需要8次爆炸：炸倒需要5次，摧毁树桩需要3次。</li></ul>', NULL),
	(3673, 'craft', 'DE0105', '半径', '01', '<p>炸弹的爆炸半径为5。整个方形为造成伤害的区域，去掉紫色部分即为爆炸范围，红色部分为可能开垦出的耕地范围。</p><p></p>', NULL),
	(3674, 'craft', 'DE0105', '收集包', '01', '<p>炸弹不用于任何收集包。</p>', NULL),
	(3675, 'craft', 'DE0105', '裁制', '01', '<p>炸弹可通过缝纫机使用布料制成轰炸机夹克。炸弹可用于染色。放置在缝纫机内时，炸弹可用作<b>黑色</b>染料。</p>', NULL),
	(3676, 'craft', 'DE0105', '任务', '01', '<p>炸弹不用于任何任务。</p>', NULL),
	(3677, 'craft', 'DE0148', '', '01', '<p><b>结婚戒指</b>是一种可打造的物品，用于在多人游戏中向另一名未婚玩家求婚。配方从旅行货车以500g购买获得。</p><p>若要向另一名玩家求婚，必须将结婚戒指交给对方，对方玩家可以选择接受或拒绝。如果选择接受，两人就订婚了。和与村民的婚姻一样，婚礼通常在3天内举行。订婚后，则不能再次使用戒指求婚。</p><p>结婚戒指可以像其他戒指一样装备，但没有任何加成效果。它不能给予村民。</p><p>像其他戒指一样，结婚戒指可以卖给冒险家公会。</p>', NULL),
	(3678, 'craft', 'DE0148', '成就', '01', '<p>结婚戒指不再计入任何制作类成就以及“完美度”的“制作的制造设计图”，即便在多人游戏中亦是如此。</p>', NULL),
	(3679, 'craft', 'DE0148', '染色', '01', '<p>结婚戒指可用作染料罐的<b>紫色</b>染料，放置在缝纫机内时还可用作<b>亮紫色</b>染料。</p>', NULL),
	(3680, 'craft', 'DE0035', '', '01', '<p></p><p><b>农场电脑</b>可以用来显示你的农场的有关信息。在完成德米特里厄斯“生态平衡”或“水生物泛滥”特别任务后，玩家会收到其制造配方。</p><p>电脑显示的信息包括:</p><ul><li>筒仓干草储存量</li><li>农作物总量</li><li>可收成农作物</li><li>未浇水的农作物</li><li>温室中可收成农作物</li><li>开耕土壤</li><li>采集道具数</li><li>已完成机器</li><li>山洞状态</li></ul><p>农场电脑可以放置在姜岛农场。若如此做，农场电脑将会显示姜岛农场的信息。</p><p>即使农场上存在采集品（例如： 松露 ），“采集道具数”也只会在森林农场或者海滩农场中的电脑上显示。使用季节种子种植的作物会显示在“农作物”相关条目下。</p>', NULL),
	(3681, 'craft', 'DE0070', '', '01', '<p><strong>高级肥料</strong>是一种可以用于培育高品质作物的肥料。高级肥料可以经打造或第二年开始后在皮埃尔的杂货店花费150g购买。高级肥料在任意年份都有可能会出现在旅行货车的售卖货物中，售价100g ~ 1,000。完成金库的 5,000金收集包可以获得 30 个高级肥料作为奖励。打开谜之盒或金色谜之盒有概率获得10个或20个高级肥料。</p><p>高级肥料必须在种子发芽之前放置到土壤方格之中。</p><p>高级肥料只影响基础的作物收获，对于可以多次收割农产品的农作物（例如土豆、蓝莓或蔓越莓）单次收割获得的额外农产品一般是普通品质。</p>', NULL),
	(3683, 'craft', 'DE0070', '何时把鱼制成高级肥料', '01', '<p>下表是基于鱼的品质的快速查阅表格，展示了何时应将鱼制成高级肥料。</p><ul><li>该表认为售价正好是300g的鱼是“不好的”，不适合制成高级肥料，因为从技术上讲，四个树液（8g）让直接购买比手工制造来的更加便宜。</li></ul>', NULL),
	(3684, 'craft', 'DE0070', '裁制', '01', '<p>高级肥料可通过缝纫机使用布料制成上半部分可染色的高腰上衣。高级肥料可用于染色。放置在染料罐内时，高级肥料可用作<strong>橙色</strong>染料。放置在缝纫机内时，高级肥料还可用作<strong>棕色</strong>染料。</p>', NULL),
	(3685, 'craft', 'DE0070', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(3686, 'craft', 'DE0139', '', '01', '<p><strong>乡村木地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>乡村木地板的配方可在木匠的商店以200g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3688, 'craft', 'DE0034', '', '01', '<p></p><p><b>迷你图腾尖塔</b>是一种可打造物品，可以放置在星露谷农场范围内的任意位置（姜岛农场不可放置）。迷你图腾尖塔为成对使用，可将玩家从其中一个传送到另一个。</p><p>其打造配方在完成法师的特别任务“奇特物质”后获得。</p>', NULL),
	(3689, 'craft', 'DE0034', '限制', '01', '<ul><li>每座农场只限一对，超过一对不能放置。</li><li>只放置一个亦可，但没有效果。</li><li>不能放在农场外面和建筑（包括农舍和小屋）里面。</li><li>不能放在姜岛农场使用。</li><li>不可以带着马一起传送。</li></ul>', NULL),
	(3690, 'craft', 'DE0088', '', '01', '<p><b>石围栏</b>是一件可打造物品，可以让玩家在农场中隔开某些区域。可以维持完好状态106~109天。</p>', NULL),
	(3691, 'craft', 'DE0128', '', '01', '<p><strong>垫脚石小径</strong>是一种可打造的装饰物品，是地板的一种。可用于在农场铺饰走道。会因被闪电击中而从地上脱落（脱落后可重新捡起并安放，但超过一定天数后仍未被捡起的垫脚石小径会消失），也会因为被蔓延的杂草覆盖而直接消失。</p><p>垫脚石小径的配方可以在木匠的商店以100g购买。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，从而可以避免草或者树对玩家行动造成减速或阻碍。</p>', NULL),
	(3693, 'craft', 'DE0131', '', '01', '<p><strong>红砖地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>红砖地板的配方可在木匠的商店以500g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3695, 'craft', 'DE0120', '', '01', '<p></p><p><br/><b>茶苗</b>需要20日生长成为茶树，之后会在春季、夏季、秋季（如果在室内则包括冬季）的最后一周里每天产出一个茶叶。</p><p>茶苗可以像普通的树一样种在地上，也可以种在花盆中。茶苗不需要浇水就可以生长，在冬天也可以在室外生长。</p><p>茶苗可以在旅行货车购买到，沙漠节期间卡洛琳也会在其商铺以 30 卡利科三花蛋的价格出售茶苗（限10个）。除此之外，只能由打造获得。制造配方由触发卡洛琳的2心事件后第二天的信件中获得。</p>', NULL),
	(3696, 'craft', 'DE0120', '注意', '01', '<ul><li>茶树可当作障碍物以阻挡动物。</li></ul>', NULL),
	(3697, 'craft', 'DE0039', '', '01', '<p><strong>蟹笼</strong>是一种工具，用于收集蟹笼鱼类。可以通过达到钓鱼等级 3 后打造获得，或者在鱼店以1,500g的价格购买。完成 蟹笼收集包会获得3个蟹笼作为奖励。也是鳟鱼大赛的奖品之一。</p><p>将蟹笼放置在任意水域边，然后放入任意鱼饵，第二天即可收获对应水域的小鱼小虾或是垃圾，选择技能诱饵大师后则不再需要鱼饵。放置蟹笼、往蟹笼中投放鱼饵、收获蟹笼中的物品均不会消耗能量。</p><p>收获蟹笼能获得 5 点钓鱼经验值，即便获得的物品是垃圾或是普通的沙滩采集物品。放置蟹笼、往蟹笼中投放鱼饵不会获得经验值。经蟹笼捞上来的鱼也能被算入和钓鱼相关的四个成就（渔夫、老海员、垂钓大师和捕鱼大师）的数量之中。</p><p>玩家阅读书籍捕蟹秘籍后，收获蟹笼时有 25% 的概率获得双倍捕捞物。</p>', NULL),
	(3698, 'craft', 'DE0039', '特殊鱼饵', '01', '<p>任何类型的鱼饵都可以用于蟹笼，然而部分鱼饵有特殊效果，具体为：</p><ul><li>万能鱼饵可以降低一半获取垃圾的概率。</li><li>高级鱼饵可以降低一半获取垃圾的概率，并将蟹笼捕获的鱼的品质提升为银星。</li><li>针对性鱼饵可以降低一半获取垃圾的概率，并且增加捕获指定的蟹笼鱼类的概率。</li><li>普通鱼饵、磁铁、魔法鱼饵、挑战鱼饵皆没有任何特殊效果。</li></ul>', NULL),
	(3699, 'craft', 'DE0039', '阶段', '01', '<p>蟹笼有三个工作阶段，可以直观地区分：</p>', NULL),
	(3700, 'craft', 'DE0039', '捕捞物', '01', '<p>在放置蟹笼后的第二天，可捕获以下物品：</p><h3>任意水域</h3><p>除非你具有水手技能，否则以下垃圾物品在任何水域都有相同的几率收到。不考虑运气和钓鱼技能，垃圾出现的几率是38%，所有种类的垃圾出现的几率是相同的。这些垃圾可以使用回收机转化为更有价值的物品。</p><h3>海洋</h3><p>以下水产可以在任意海洋中放置蟹笼进行捕捞。注意，蟹笼只能捕获普通品质的鱼。在1.6.3版本更新后使用高级鱼饵后蟹笼产出的鱼均为银星。</p><p><strong>注意：</strong>如果玩家拥有水手职业，那么无论蟹笼放置在何处、使用何种鱼饵，获得每种鱼的概率都是相等的（ 14.3% ）。</p><p>如果玩家没有水手职业并且在蟹笼中使用高级鱼饵、万能鱼饵或是任何除海洋蟹笼鱼类外的针对性鱼饵，则获得每种蟹笼鱼类的概率在上表基础上乘以 1.125 。如果玩家没有水手职业并使用了某种海洋蟹笼鱼类的针对性鱼饵，则获得对应鱼类的概率会提升，详见下表。</p><h3>淡水</h3><p>将蟹笼放置在任意的淡水水域中，能捕获以下生物。注意，蟹笼只能捕获到普通品质的鱼。在1.6.3版本更新后使用高级鱼饵后蟹笼产出的鱼均为银星。</p><p>特别地，农场池塘也能产出蟹笼鱼类，即便是钓鱼竿只能钓到垃圾的池塘也可能用蟹笼捕鱼。蟹笼也可以放置在地下水体（例如：下水道）进行捕鱼。矿井中的池塘则是例外：蟹笼可以被放置在矿井池塘（包括火山的岩浆）之中，但一旦你离开这层，蟹笼会彻底消失。另外，不能把蟹笼放置在仅用于装饰的水体中（例如鹈鹕镇的喷泉，或温泉外边的小水池）。</p><p><strong>注意：</strong>如果玩家拥有水手职业，那么无论蟹笼放置在何处、使用何种鱼饵，获得每种鱼的概率都是相等的（33.3%）。</p><p>在海滩农场的水池里，当玩家没有水手职业时，获得每种蟹笼鱼类的概率在上表基础上乘以 0.69 ，因为那里相较其他地方更可能得到垃圾。如果玩家没有水手职业并且在蟹笼中使用高级鱼饵、万能鱼饵或是任何除淡水蟹笼鱼类外的针对性鱼饵，则获得每种蟹笼鱼类的概率在上表基础上乘以 1.125 。在海滩农场的水池里，获得每种蟹笼鱼类的概率在上表基础上乘以 0.97 ，或是在之前已乘以 0.69 的基础上再乘以 1.405 。如果玩家没有水手职业并使用了某种淡水蟹笼鱼类的针对性鱼饵，则获得对应鱼类的概率会提升，下表详细给出了不同的针对性鱼饵在除海滩农场水池外其他所有淡水水域使用时获得各种鱼类的概率。在海滩农场的水池，获得每种蟹笼鱼类的概率在下表基础上乘以 0.861 ，获得垃圾的概率相应上升。</p>', NULL),
	(3701, 'craft', 'DE0039', '染色', '01', '<p>蟹笼可用于染色。放置在染料罐内时，蟹笼可用作<strong>橙色</strong>染料。</p>', NULL),
	(3702, 'craft', 'DE0039', '收益计算', '01', '<ul><li>如果将蟹笼产出的所有垃圾都用回收机回收且售出，一个蟹笼在淡水中平均每天收益是47g，海水中则是50.5。</li><li>如果你选择了水手这一职业并且把所有价格低于75g的鱼做成生鱼片，那么淡水中的收益将达到每天75g，海水中的收益将达到每天81.4</li></ul>', NULL),
	(3703, 'craft', 'DE0039', '注意', '01', '<ul><li>因为蟹笼在淡水水域捕获的鱼类价值很低，且易于被大量捕获，所以它们是用于制造高级肥料或生鱼片的理想原料。在游戏早期阶段，你不妨购买一些蟹笼放置在你的农场池塘中（因为离家近，方便每天收集捕获物），这样你就可以用它们的捕获物来制作高级肥料或生鱼片，并获得一点钓鱼经验。</li><li>当你选择了诱饵大师后，很容易在收获蟹笼捕获物后立即不小心捡起蟹笼。自1.5版本，可以轻松避免此情况：光标对准还未收获的蟹笼，然后长按收获键。只要按键未松开，就不会不小心捡起蟹笼。<li class="ql-indent-1">如果没有选择这个职业，最简单的方法就是手持鱼饵，在收获蟹笼的同时放入鱼饵。装有鱼饵的蟹笼是不能被捡起的。</li></li><li>如果没有捕猎者这一职业，纯靠钱来购买蟹笼材料需要3,100g。有了这一职业也需要花费1,300g。并且，这只是第一年的价格。第二年时原材料价格上涨，而威利鱼店的购买价格仍固定为1,500g。因此，在第二年后直接购买蟹笼会更划算，即使玩家选择了捕猎者职业。</li><li>节日期间的沙滩场景有单独的地图，因此，放置在沙滩上的蟹笼、宝箱等物品会在夜市期间消失，并在冬季18日重新回来。</li></ul>', NULL),
	(3704, 'craft', 'DE0058', '', '01', '<p><b>陷阱浮标</b>是一种渔具，可以使玩家在钓鱼时，如果鱼不在绿条内，右侧进度条的下降速度降低 33%。达到钓鱼等级6 后可以通过打造获得，同时解锁鱼店以500g出售。陷阱浮标也会随机出现在旅行货车的货物中，售价为600g ~ 1,000。</p><p>如果在高级铱金鱼竿中装备两个陷阱浮标，则进度条的下降速度降低 50%。</p><p>只有铱金鱼竿和高级铱金鱼竿可以装备渔具。铱金鱼竿每次只能装备一个渔具，而高级铱金鱼竿能装备两个。要装上渔具时，先鼠标左键点击渔具，然后鼠标右键点选鱼竿。要卸下渔具时，先鼠标右键点击鱼竿卸下鱼饵，再次右键点击鱼竿就可以卸下渔具。如果是移除高级铱金鱼竿的钓具，会先移除左边的。</p><p>渔具不能在物品栏或箱子中堆叠，每个渔具占据一个空格。</p>', NULL),
	(3705, 'craft', 'DE0058', '收集包', '01', '<p>陷阱浮标不用于任何收集包。</p>', NULL),
	(3706, 'craft', 'DE0058', '任务', '01', '<p>陷阱浮标不用于任何任务。</p>', NULL),
	(3707, 'craft', 'DE0058', '收益计算', '01', '<ul><li>制作陷阱浮标并出售是有收益的，每个可以获得120g的利润。如果玩家拥有“铁匠”职业，则只有90g（因为这不如直接出售铜锭）。</li></ul>', NULL),
	(3708, 'craft', 'DE0104', '', '01', '<p><b>樱桃炸弹</b>是一种一次性的爆炸物。它会摧毁爆炸范围内的所有物品（例如，岩石、矿脉、宝石矿等）。用一个樱桃炸弹的威力就足够炸毁前往矮人住处的岩石障碍。</p><p>樱桃炸弹可以在达到采矿等级1 后可以通过打造获得，还可以以450g在矮人或火山地牢的商店处购买。</p>', NULL),
	(3709, 'craft', 'DE0104', '影响', '01', '<ul><li>爆炸范围内的岩石、矿石和宝石节点会掉落相应物品，无法摧毁大岩石。</li><li>爆炸范围内的可采集矿物和蘑菇会直接被摧毁。</li><li>爆炸无法摧毁陨石跟巨大作物。</li><li>一定范围内（范围比爆炸半径要小）未开垦的地块和远古斑点都会变成已被开垦的状态，且可以掉落相应的掉落物（例如粘土），效果与玩家使用锄头时相同。</li><li>爆炸范围内放置的大部分可打造物品（例如小桶、宝石复制机等）都将会<b>直接消失，没有掉落物</b>。<li class="ql-indent-1">爆炸范围内的地板和小径仍可掉落并被玩家重新拾起。</li><li class="ql-indent-1">蟹笼不会被炸弹破坏。</li><li class="ql-indent-1">宝箱、石箱、祝尼魔箱、自动采集器不会被炸弹破坏，无论其中是否存有物品。</li><li class="ql-indent-1">加热器会被炸弹破坏。</li></li><li>摧毁一棵树一共需要15次爆炸：炸倒需要10次，摧毁树桩需要5次。</li></ul>', NULL),
	(3710, 'craft', 'DE0104', '半径', '01', '<p>樱桃炸弹的爆炸半径是3。整个方形为造成伤害的区域，去掉紫色部分即为爆炸范围，红色部分为可能开垦出的耕地范围。</p><p></p>', NULL),
	(3711, 'craft', 'DE0104', '裁制', '01', '<p>樱桃炸弹可通过缝纫机使用布料制成轰炸机夹克。樱桃炸弹还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(3712, 'craft', 'DE0142', '', '01', '<p></p><p><b>石牌</b>是一种可打造物品，用于显示可放入物品栏的物品的图像。玩家只要让角色双手持有一个物品，然后鼠标右键点击石碑，就能让石碑显示该物品的图像。该过程不消耗物品。</p><p>石牌可以放在星露谷的任何地方。如果把石牌放在村民活动的路径上，石牌会被村民破坏。详见：农场外的打造物品。</p><p>把石牌放置在鱼塘上，石牌会变成可以显示鱼塘内有多少鱼的计数器。石牌上可以看到鱼的图像，右下角有一个数字。</p>', NULL),
	(3713, 'craft', 'DE0134', '', '01', '<p><strong>木头小径</strong>是一种打造的装饰品，可以在农场中放置来装饰地板，可以被闪电击落和被杂草蔓延而破坏。</p><p>配方是玩家初始即拥有的。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3715, 'craft', 'DE0076', '', '01', '<p><b>生命药水</b>是一种可食用的制造物品，达到战斗等级2 后可以通过打造获得，还可以以2,000g在矮人处购买。铱蝠有 5% 的概率掉落生命药水，骷髅洞穴的宝箱层也可能会发现 2-4 个生命药水。打开谜之盒或金色谜之盒也有概率获得2瓶生命药水。</p>', NULL),
	(3716, 'craft', 'DE0076', '裁制', '01', '<p>生命药水可通过缝纫机使用布料制成可染色的 男款深色荧光衬衫 或女款深色荧光衬衫。生命药水可用于染色。放置在染料罐内时，生命药水可用作<b>紫色</b>染料。放置在缝纫机内时，生命药水还可用作<b>亮粉色</b>染料。</p>', NULL),
	(3717, 'craft', 'DE0076', '任务', '01', '<p>生命药水不用于任何任务。</p>', NULL),
	(3718, 'craft', 'DE0076', '趣闻', '01', '<ul><li>1.6.4以前单独食用普通品质的紫蘑菇+羊肚菌+鸡油菌可恢复 220 点能量，这比生命药水能提供的能量还要多。然而，食用这些蘑菇只能回复 98 点生命值并不能补满。</li><li>制作并出售这个物品会导致亏损，因为原材料的售价更高（总共 645 金）。</li></ul>', NULL),
	(3719, 'craft', 'DE0129', '', '01', '<p><strong>鹅卵石小径</strong>是一种可打造的装饰物品，是地板的一种。可用于在农场铺饰走道。会因被闪电击中而从地上脱落（脱落后可重新捡起并安放，但超过一定天数后仍未被捡起的鹅卵石小径会消失），也会因为被蔓延的杂草覆盖而直接消失。</p><p>鹅卵石小径的配方是玩家初始即拥有的配方之一。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农场外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，从而可以避免草或者树对玩家行动造成减速或阻碍。</p>', NULL),
	(3721, 'craft', 'DE0086', '', '01', '<p><b>大门</b>是一件可打造物品，用于让玩家穿过围栏。一个大门可以维持完好状态 360 天（然后就会因逐渐破损而消失）。</p>', NULL),
	(3722, 'craft', 'DE0086', '注意', '01', '<ul><li>如果你打开大门并站在大门所在的方格上，然后再关闭大门。继续占据大门所在方格并再次打开大门的时候，大门会被破坏成为掉落物。</li><li>虽然大门只有一格宽，但是依旧可以骑着马从任何方向穿越任何朝向的大门。</li><li>用十字镐敲击大门不会破坏它，所以你可以用镐子将大门取下来，然后重新放置它。</li><li>大门可用于替换一个已经放置在地上的围栏（把大门放到工具栏，然后点击待替换的围栏方格）。如果大门被其他围栏环绕，大门的颜色会变成和围栏一样的颜色，这个效果会持续到退出游戏为止。</li></ul>', NULL),
	(3723, 'craft', 'DE0022', '', '01', '<p></p><p><b>史莱姆孵化器</b>是一种精炼设备，用于孵化史莱姆，其打造配方在达到战斗等级 8 时解锁。</p><p>建好的史莱姆屋会自带一个史莱姆孵化器，孵化后的史莱姆只会在建筑内活动。也可以把史莱姆孵化器放置在室外，这样就能在室外饲养史莱姆。史莱姆屋内最多可容纳 20 只史莱姆，如果空间已满则无法成功孵化，除非击杀现有史莱姆以腾出空位。但在其他区域则没有数量限制。</p><p>史莱姆蛋需要 4,000分钟（2天13小时20分钟）孵化，拥有鸡舍大师职业可以把孵化时间减半。如果孵化处理完成，需要过夜才能将史莱姆孵化。</p><p>注：仙尘不能使孵化立即完成。</p>', NULL),
	(3724, 'craft', 'DE0124', '', '01', '<p></p><p><b>上古种子</b>是一种种子，不要与古代种子（古物）混淆。上古种子成熟后结出上古水果。</p><p>如果你把古物古代种子捐赠给博物馆，可以向冈瑟索要一包上古种子作为奖励。你还将得到一个工艺配方，通过它你可以将古物古代种子制作为上古种子。</p><p>上古种子也可以通过将上古水果放置到种子生产器中获得。将除了上古水果以外的任何作物放进种子生产器时，也有很小的机会（0.5%）获得1-3个上古种子而不是该作物的种子。</p><p>上古种子不能种在花盆里。如果尝试将上古种子种入花盆中，会显示消息“她的根长得很深......她在那里不会开心。”</p><p>上古种子可以种植在温室中。</p><p>你也可以通过将上古种子放入分解机来制作古物古代种子。 如果玩家有一包可用的上古种子，但尚未将古物古代种子捐赠给博物馆，这非常有用。</p>', NULL),
	(3725, 'craft', 'DE0025', '', '01', '<p></p><p><b>碎骨机</b>是一种精炼设备，可以将骨头碎片或任何骨头类物品（见下文）转化为肥料。一次处理需要240 分钟（4小时） 的游戏内时间。</p><p>碎骨机可以通过打造获得，需要先完成冈瑟的特别任务：历史的碎片，取得其配方。</p>', NULL),
	(3726, 'craft', 'DE0025', '可能的产物', '01', '<p>碎骨机可以随机产出以下物品，有10%的概率获取双倍物品：</p><ul><li> 高级生长激素（3）</li><li> 高级肥料（10）</li><li> 生长激素（5）</li><li> 树肥（5）</li></ul>', NULL),
	(3727, 'craft', 'DE0089', '', '01', '<p><b>铁围栏</b>是一件可打造物品，可以让玩家在农场中隔开某些区域。可以维持完好状态223~226天。</p>', NULL),
	(3728, 'craft', 'DE0143', '', '01', '<p><b>鼓块</b>是家具一种。打造鼓块的配方可以在罗宾的六心事件中获得。当玩家向博物馆捐赠三件古物（包含古代鼓）的时候，冈瑟会给玩家一个鼓块作为奖励。 </p><p>如果玩家用鼠标右键单击鼓块，可以改变从鼓块旁经过时触发的声音。</p><p>鼓块共有七种音色，按右键次数从0-6分别是底鼓，军鼓，闭镲，开镲，落地嗵鼓，中音嗵鼓，节拍器。</p>', NULL),
	(3729, 'craft', 'DE0030', '', '01', '<p></p><p><b>稻草人</b>是一种可打造的物品，它可以防止乌鸦吃掉农作物。稻草人的制作配方可以在耕种等级达到1级时获得。</p><p>乌鸦不会攻击野生种子种植出的作物，所以野生种子不需要稻草人来保护。</p><p>姜岛上没有乌鸦，因此姜岛农场上也不需要放置稻草人。</p>', NULL),
	(3730, 'craft', 'DE0030', '范围', '01', '<p>稻草人会保护周围248个地块的圆形区域：东、南、西、北方向上8格，东北、东南、西南和西北各6格。换句话说，它保护一片17x17的区域，但不包括每个角落的10个空格。想要以正方形计算方便规划领域的话，那就是13x13的区域。</p><p></p>', NULL),
	(3731, 'craft', 'DE0030', '稀有稻草人', '01', '<p>稀有稻草人的作用与普通的稻草人相同。<b>“稀有稻草人”和“珍奇乌鸦”是对同一类物品的不同称呼。</b></p><p><b>注：</b>仅有3号稀有稻草人的头上可以戴帽子。</p><p>集齐8个稀有稻草人后，“Z.C.珍奇乌鸦协会”会通过信件送给玩家豪华稻草人的制作配方。</p>', NULL),
	(3732, 'craft', 'DE0030', '任务', '01', '<ul><li>为了完成“进阶”任务，玩家需要达到耕种等级1，并制作一个稻草人。</li></ul>', NULL),
	(3733, 'craft', 'DE0030', '趣闻', '01', '<ul><li>珍奇乌鸦 #1来自宫崎骏的《哈尔的移动城堡》中的角色“菜头”。</li><li>与稻草人互动的时候，会出现一条信息，显示它已经赶走了多少乌鸦。当拾起稻草人的时候，计数会被重置。</li></ul>', NULL),
	(3734, 'craft', 'DE0030', '秘密', '01', '<p>3号珍奇乌鸦（外星人）的头上可以戴帽子。</p>', NULL),
	(3735, 'craft', 'DE0017', '', '01', '<p></p><p><b>回收机</b>是一种精炼设备，用于将大部分垃圾（Joja可乐和腐烂的植物除外）转变成有用的材料，处理时间为 60分钟（1小时） 。其打造配方在达到钓鱼等级 4 后解锁。</p><p>玩家完成布告栏的 土地研究收集包后也可以获得一个回收机。</p>', NULL),
	(3736, 'craft', 'DE0107', '', '01', '<p></p><p><b>篝火</b>是一种可打造物品。它能在夜晚提供照明。用鼠标右键单击篝火能点亮或熄灭火种。</p>', NULL),
	(3737, 'craft', 'DE0107', '趣闻', '01', '<ul><li>玩家可以和莱纳斯帐篷外的篝火互动，但是篝火熄灭与否不影响玩家与莱纳斯的谈话内容和莱纳斯的活动。</li></ul>', NULL),
	(3738, 'craft', 'DE0009', '', '01', '<p></p><p><b>小桶</b>是一种用来制作工匠物品的工匠设备，打造配方在耕种等级8级时解锁。完成茶水间的 工匠收集包后，可以获得一个作为奖励。4个小桶会作为兑奖机的第19个奖品（50%概率）。</p>', NULL),
	(3739, 'craft', 'DE0009', '产品', '01', '<p>注意：加工时间是近似值，因为从上午2点到6点的时间流速不同。正常情况下1小时 = 60分钟，但上午2点到6点的1小时 = 100分钟。一整天是 1600 分钟，其中白天是 1200 分钟，晚上是 400 分钟。</p>', NULL),
	(3740, 'craft', 'DE0009', '罐头瓶 vs. 小桶', '01', '<p>从原料类型上区分，罐头瓶可以加工水果、蔬菜和鱼籽，而小桶可以加工水果、蔬菜、咖啡豆、蜂蜜和茶叶。在加工水果和蔬菜时，由于价格公式的不同，二者在加工利润方面有所区别。使用罐头瓶加工的增值公式为：（2 × 基础农作物价格+ 50）; 而小桶则是将水果的基础价格 × 3，蔬菜价格 × 2.25。因此，如玉米或西红柿等低价值、高产量作物放入罐头瓶加工会更有价值。而小桶则更合适售价较高的作物，如上古水果或甜瓜。二者的平衡点，对于水果是50g，对于蔬菜是200g。下面是一个放小桶加工会更有价值（仅从单价来看）的作物列表，列出了作物的基础价格。<b>粗体字</b>标识出来的作物基础售价超过200g。</p><p>注意，上述计算只考虑了最大利润方面；如果根据所需加工时间来看每日盈利，无论作物基础价值如何，罐头瓶 的盈利效率始终超过 小桶，因为罐头瓶的加工速度要快得多。尽管如此，在游戏的第一年，考虑到制造材料的成本，实际上小桶比罐头瓶更适合大规模生产。如果木材、石头、矿石和煤炭全部从 木匠的商店 和 铁匠铺 购买，1个小桶的制造成本为1725金加1个 橡树树脂（不包括 树液采集器 的成本），而1个罐头瓶的制造成本为2500金。如果不购买材料，游戏中获取木材比石头容易，获取树脂比煤炭容易得多（使用 树肥 种橡树）。另外，啤酒花 是第一年收益最高的作物，淡啤酒 只能用小桶加工。</p>', NULL),
	(3741, 'craft', 'DE0009', '存放小桶的方案', '01', '<h3>在农场建筑内</h3><p>玩家可在农场建筑内放置小桶。</p><ul><li>小屋占地21格，最优方案下可放置67个小桶。升级为大屋子后，占地不变，最优方案下可放置137个小桶。</li><li>畜棚占地28格，相较小屋造价更低，并且最多可放置90个小桶。升级为高级畜棚后，占地和畜棚一致，最多可放置136个小桶，但建造成本相当高。</li><li>鸡舍造价较低，但是可用的空间非常有限，甚至不如直接放置在室外。</li><li>联机小屋造价最低，在无人入住并且必须保留床和初始赠送的防风草种子的情况下，联机小屋只占地15格，无需升级就能够放置42个小桶；第一次升级后，联机小屋能够放置113个小桶，在各个方案中成本最优；第二次升级后，联机小屋能够放置352个小桶，但建造的成本也显著提高。</li></ul><ul class="gallery mw-gallery-packed"><li class="gallerybox" style="width: 134.66666666667px"><div style="width: 134.66666666667px"><div class="thumb" style="width: 132.66666666667px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>小屋可放置67个设备</p></div></div></li><li class="gallerybox" style="width: 165.33333333333px"><div style="width: 165.33333333333px"><div class="thumb" style="width: 163.33333333333px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>大屋子可放置137个设备</p></div></div></li><li class="gallerybox" style="width: 178px"><div style="width: 178px"><div class="thumb" style="width: 176px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>畜棚可放置90个设备</p></div></div></li><li class="gallerybox" style="width: 250.66666666667px"><div style="width: 250.66666666667px"><div class="thumb" style="width: 248.66666666667px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>高级畜棚可放置136个设备</p></div></div></li></ul><ul class="gallery mw-gallery-packed"><li class="gallerybox" style="width: 145.33333333333px"><div style="width: 145.33333333333px"><div class="thumb" style="width: 143.33333333333px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>联机小屋可放置42个设备</p></div></div></li><li class="gallerybox" style="width: 378px"><div style="width: 378px"><div class="thumb" style="width: 376px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>升级一次后，联机小屋可放置113个设备</p></div></div></li><li class="gallerybox" style="width: 176.66666666667px"><div style="width: 176.66666666667px"><div class="thumb" style="width: 174.66666666667px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>升级两次后，联机小屋可放置351个设备</p></div></div></li></ul><h3>在农场外面</h3><p>小桶可以被放置在农场外的大多地方。在某些地方可能会因为NPC人物路过而遭到破坏。在沙漠 和 铁路暂没有图片来展示较好的方案。</p><ul class="gallery mw-gallery-packed"><li class="gallerybox" style="width: 172px"><div style="width: 172px"><div class="thumb" style="width: 170px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>在 采石场可放置374个小桶。</p></div></div></li><li class="gallerybox" style="width: 588.66666666667px"><div style="width: 588.66666666667px"><div class="thumb" style="width: 586.66666666667px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>在隧道中可放置131个小桶。</p></div></div></li><li class="gallerybox" style="width: 416px"><div style="width: 416px"><div class="thumb" style="width: 414px;"><div style="margin:0px auto;"></div></div><div class="gallerytext"><p>在路边可放置90个小桶。其中两个在隧道中未显示。</p></div></div></li></ul>', NULL),
	(3742, 'craft', 'DE0106', '', '01', '<p></p><p><b>大理石火炬</b>是可打造光源之一，它可以在晚上用于照明。鼠标右键单击火炬可以点燃或熄灭火炬。当火炬处于开启状态，会照亮半径约 7 格的区域。</p><p>所有火炬的配方都可以在木匠的商店购买，买齐所有火炬的配方需要花费共13,250g。</p><ul><li> 木头火炬（250g）</li><li> 石头火炬（400g）</li><li> 木桶火炬（800g）</li><li> 树桩火炬（800g）</li><li> 金制火炬（1,000g）</li><li> 镂空火炬（2,000g）</li><li> 骷髅火炬（3,000g）</li><li> 大理石火炬（5,000g）</li></ul>', NULL),
	(3743, 'craft', 'DE0068', '', '01', '<p><b>顶级生长激素</b>是一种肥料，可以促进作物生长速度加快 33%（在有农业学家技能时共加速 43%）。可以在齐先生的核桃房花费30颗齐钻 购买配方后打造。</p><p>顶级生长激素可以在种植种子之前或之后，或是作物的任何生长阶段施在犁过的土壤中。对于可多次收获的作物，只有第一次收获的周期会缩短，顶级生长激素对后续收获没有作用。</p>', NULL),
	(3744, 'craft', 'DE0042', '', '01', '<p></p><p>玩家可通过<b>电话</b>查询商店的营业时间和库存。电话可在木匠的商店中购买，价格为2,000g。电话的放置没有任何的限制。</p><p>给商店老板打电话不会算作与他们聊天，因此不会增加好感度。</p><p>玩家可能会接到诈骗电话，获得“熊的知识”后熊打来的电话，文森特、皮埃尔、刘易斯的电话，帽子老鼠的电话，或是语言不通的外国人的电话。</p><p>在苔雨期间，玩家无法使用电话。</p>', NULL),
	(3745, 'craft', 'DE0042', '打电话', '01', '<p>商店老板（或是他们的答录机）会告知玩家商店营业时间。此外，不同的商店会有不同的选项供玩家选择。答录机的信息会因店主是否有空、节日等因素变化。</p><h3>罗宾</h3><p>打电话给罗宾可以确认木匠商店的营业时间和库存。</p><ul><li>确认商店库存</li><li>确认房屋升级费用（也适用于小屋）</li><li>确认建筑升级费用</li></ul><h3>克林特</h3><p>打电话给克林特可以确认铁匠铺的营业时间。</p><ul><li>确认工具升级费用</li><li>注意：不能查询矿石的价格。</li></ul><h3>皮埃尔</h3><p>打电话给皮埃尔可以确认皮埃尔的杂货店的营业时间和库存。</p><ul><li>确认商店库存</li></ul><h3>玛妮</h3><p>打电话给玛妮可以确认玛妮的牧场的营业时间。</p><ul><li>确认牲畜价格</li><li>注意：不能查看商店库存</li></ul><h3>格斯</h3><p>打电话给格斯可以确认星之果实餐吧的营业时间。</p><ul><li>确认餐吧每天的特色菜。</li><li>注意：不能查询特色菜外的其他库存。</li></ul><h3>马龙</h3><p>在完成消灭怪物的目标：击杀150个熔岩精灵或熔岩火球后，玩家将获得马龙的电话号码，允许玩家通过电话使用物品找回服务。</p>', NULL),
	(3746, 'craft', 'DE0012', '', '01', '<p></p><p><b>熔炉</b>是一种精炼设备，用于将矿石熔炼为锭，以便打造各种配方。当你获取至少一个铜矿石后，克林特会在早上出现在家门口提供熔炉的制作配方。通过采矿、开晶球，钓鱼宝箱或者从克林特处购买获得的铜矿石都会触发这个事件。你必须在上午6:00到上午11:30之间离开你的房子才会触发该事件。</p><p>如果克林特前来提供蓝图时你还没有见过他，那么本次事件会为你们互相介绍对方。</p><p>玩家完成锅炉房的 铁匠的收集包也可以获得一个熔炉。</p><p>熔炉会不时发出放置墙纸或地板时的声音。</p>', NULL),
	(3747, 'craft', 'DE0012', '熔炼', '01', '<p>只要玩家物品栏中有煤炭，将材料拿在手上并点击熔炉，就可以进行熔炼。无论材料或时间长短，每次熔炼都需要一个煤炭。</p>', NULL),
	(3748, 'craft', 'DE0075', '', '01', '<p><b>工作小食</b>是一种可食用的打造物品。制作配方可在采集技能等级达到1级时获得。在任何季节的任何时间，翻动垃圾桶也有概率找到工作小食。</p>', NULL),
	(3749, 'craft', 'DE0075', '裁制', '01', '<p>工作小食可通过缝纫机使用布料制成牛仔夹克。工作小食可用于染色。放置在染料罐内时，工作小食可用作<b>橙色</b>染料。放置在缝纫机内时，工作小食还可用作<b>棕色</b>染料。</p><p>可以用作橙色染料。</p>', NULL),
	(3750, 'craft', 'DE0075', '任务', '01', '<p>饲养有小龙虾、玉黍螺或蜗牛的鱼塘在容量从7条鱼提升到10条鱼的时候，会出现要求玩家提供一个工作小食的鱼塘任务。</p>', NULL),
	(3751, 'craft', 'DE0135', '', '01', '<p><strong>石头地板</strong>是一种装饰物。可用于在农场铺饰走道。会因被闪电击中而掉落。</p><p>石头地板的配方可在木匠的商店处以100g购买到。</p><p>所有类型的地板都可以提供0.1的玩家速度加成，但只有放置在农舍外时才能提供该加成。此外，它可以阻止草蔓延到放置地板的区域，也能防止树在该区域内散播种子，还可以避免草或者树对玩家行动造成减速或阻挡。</p>', NULL),
	(3753, 'craft', 'DE0211', '', '01', '<p><b>淘盘</b>是一种工具，可用于从水体中收集矿石（和其他物品）。在矿井入口左侧的“闪闪发光的巨石”被移除后，玩家进入深山时，会触发一段威利赠送淘盘的过场动画。“闪闪发光的巨石”可通过完成鱼缸收集包或从Joja社区发展申请书用20,000g购买“淘金碗”来移除。</p>', NULL),
	(3754, 'craft', 'DE0211', '获得方式', '01', '<p>在闪闪发光的巨石被移除后，前往深山，会触发一段过场动画，显示威利正在思考巨石的消失。他告诉玩家，水中有闪烁的光，说明溪流正在将矿石和矿物带出山外。然后他送给你一个他备用的淘盘。</p><p>动画结束后，玩家可以在鱼店花费2,500g购买淘盘。</p>', NULL),
	(3755, 'craft', 'DE0211', '如何使用', '01', '<p>“闪烁的光”实际上为淘盘专用的洗矿点。它会随机出现在水体中，出现时还会播放“有东西掉进水里”的音效。如果你可以与出现洗矿点的图格交互，使用淘盘右击洗矿点即可进行淘金。使用淘盘不消耗能量。在每个场景中出现的洗矿点的位置，以及淘金能获得的物品，是在这一天开始时就决定好的。洗矿点可能出现在任何湖泊、池塘、河流中，以及姜岛南部和夜市期间的沙滩，并且可以被追踪者技能定位到（以绿色小箭头表示）。</p><p>你也可以把淘盘当做帽子戴在头上。当你把淘盘从物品栏放入玩家角色的帽子服装格时，它就会变成淘盘（帽子）。</p>', NULL),
	(3756, 'craft', 'DE0211', '可获得的物品', '01', '<p>每次淘金都会得到随机数目的金属矿石，该数目范围从普通淘盘的3~9到铱淘盘的6~12不等。每日运气和运气增益也会略微影响淘金时获得的矿石数量 (5级运气增益下必定额外获得一块矿石，完美的每日运气下每次平均多得0.2块矿石)。</p><p>矿石的种类是随机的，取决于淘盘的等级、每日运气和运气增益。基本的可能性是：</p><ul><li> 铱矿石（1%）</li><li> 黄金矿石（20.8%）</li><li> 铁矿石（32.3%）</li><li> 铜矿石（36%）</li><li> 骨头碎片（10%，拥有考古学家附魔时提升至20%，减少获得铜、铁、金矿石的概率）</li></ul><p>淘盘每提升一级，获得铱矿石的概率提高5％，运气增益每一级增加0.1%，每日运气增加±10%。获得铜矿石的概率相应地减少。</p><p>此外，淘金还能获得一些特殊物品。每次淘金获得的特殊物品个数受淘盘等级、每日运气和运气增益影响。每次淘金能获得的特殊物品个数最大值与淘盘等级有关，普通淘盘最多1种，铱淘盘最多4种。丰富附魔会将个数上限加2。</p><p>获得特殊物品的基础概率为40%，每日运气增加±10%，每1级运气增益增加4%，淘盘每提升1级增加4%（例如铱淘盘增加12%）。如果淘盘已附魔（无论哪一种），每1级淘盘增加5%（铱淘盘将增加15%）。在获得一个特殊物品失败后将不会再继续判定下一个物品。</p><p>例如，一个拥有丰富附魔的铱淘盘有55%的基础概率获得一个特殊物品（实际依运气调整）。如果成功获得，则判定下一个特殊物品，依此类推，直到6个判定全部成功或是有一个失败为止。因此，在此例中每次淘金平均获得1.18个特殊物品。如果加上2级运气增益和完美的每日运气，成功概率增加到73%，平均每次淘金获得2.29个特殊物品。</p><p>每次判定获得特殊物品时，都会从下面选择一项：</p><ul><li> 钻石（1.5%，每日运气影响±7.5%，每升一级淘盘+0.37%）</li><li> 紫水晶（1%）</li><li> 海蓝宝石（1%）</li><li> 绿宝石（1%）</li><li> 红宝石（1%）</li><li> 黄水晶（1%）</li><li> 万象晶球（28%，一次获得1-3个）</li><li> 火水晶（4.7%）</li><li> 地晶（4.7%）</li><li> 泪晶（4.7%）</li><li> 煤炭（50%, 一次获得1-7个，概率依每日运气和淘盘等级的提升而降低）</li></ul><p>其中，降低的煤炭概率值与提升的钻石概率值相对应。此外，运气增益也会轻微提高钻石的概率同时减少其他宝石的概率。</p><p>除上表所列各项外，每次判定获得特殊物品时，还可能额外获得以下一项物品：</p><ul><li>随机一件家具或衣服。(0.5%，受每日运气影响，最高到5%)</li><li>随机一条当前水域的鱼。(10%，仅当淘盘附魔捕鱼)</li><li>随机古物。(2%，附魔考古学家时为7%)</li><li> 谜之盒 或金色谜之盒 (3.3%，阅读谜之书后为4.4%)</li><li> 幸运戒指 (中性每日运气下概率为0%，完美每日运气下为3.3%，每1级运气增益提高0.06%)</li></ul><p>除上述物品外，还有一些物品单独判定。每次淘金有5%概率获得一个古物宝藏, 拥有考古学家附魔后提升至20%。单次淘金时该判定将重复进行直至其失败，因此每次平均会得到0.05个宝藏，拥有考古学家附魔后每次平均0.25个。</p><p>在姜岛上淘金时还可能会收到两种物品：20%概率获得尾巴化石（只在挖掘场小河）、20%概率获得芋头块茎（其他地方）。</p>', NULL),
	(3757, 'craft', 'DE0211', '帽子', '01', '<p>淘盘也是一种帽子。当玩家将淘盘放在物品栏的帽子卡槽中时，淘盘就会变成一顶帽子，戴在玩家的头上。</p>', NULL),
	(3758, 'craft', 'DE0211', '提示', '01', '<ul><li>如果选择了“追踪者”职业，当所在地图中出现“闪烁的光”位置在可视范围以外时，屏幕边缘会出现一个绿色的小箭头。</li></ul>', NULL),
	(3759, 'craft', 'DE0210', '', '01', '<p>高级铱金鱼竿是一种用于钓鱼的工具，选择钓鱼精通后从精通山洞获得。</p><p>高级铱金鱼竿可以同时装配鱼饵和两个渔具，是目前最高等级的鱼竿。在从精通山洞获得后，便可不限次数地在威利的鱼店处以25,000g购买更多高级铱金鱼竿。</p>', NULL),
	(3760, 'craft', 'DE0210', '提示', '01', '<p>两个渔具的效果可以叠加（部分渔具除外，如声纳浮漂）。比如，使用两个软木塞浮标会使钓鱼条长度增加48个像素，而只使用一个则为24个像素。</p>', NULL),
	(3761, 'craft', 'DE0209', '', '01', '<p>铱金鱼竿是一种用于钓鱼的工具，玩家在达到钓鱼等级6 后可以在鱼店以7,500g的价格购买。</p><p>铱金鱼竿可以同时使用鱼饵和渔具。</p>', NULL),
	(3762, 'craft', 'DE0208', '', '01', '<p><b>玻璃纤维钓竿</b>是一种用于钓鱼的工具，玩家在达到钓鱼等级2 后可以在鱼店以1,800g的价格购买。</p><p>玻璃纤维钓竿可以使用鱼饵，但无法装备渔具。</p>', NULL),
	(3763, 'craft', 'DE0207', '', '01', '<p><b>竹鱼竿</b>是一种用于钓鱼的工具，在“前往沙滩”任务的过场动画中会从威利处免费获得一个，之后可以在鱼店里以500g的价格购买。</p><p>竹鱼竿不能装备鱼饵或渔具。</p>', NULL),
	(3764, 'craft', 'DE0206', '', '01', '<p><b>训练用鱼竿</b>是一种用于钓鱼的工具，可以在鱼店以25g的价格购买。</p><p>或者，在第1年春季28日或之后，如果玩家已获得竹鱼竿但尚未达到钓鱼等级1，在晴朗的一天上午11:30之前进入农场将导致威利来访并给玩家一根训练用鱼竿和一节钓鱼课，使玩家达到钓鱼等级1。</p><p>如果玩家在钓鱼小游戏中遇到困难，此鱼杆非常有用，因为它将玩家的钓鱼技能设置为5级，以便在尚未达到5级的情况下确定绿色条（136像素）的大小。它不能装备鱼饵或渔具。但是，使用训练用鱼竿会导致进度条比其他鱼杆慢33%，与陷阱浮标的效果相同。</p><p>训练用鱼竿只能钓到基本的鱼（难度低于50的鱼），所有钓到的鱼都是普通品质的。然而，用此竿钓到的鱼会获得与其他任何钓竿相同的经验值，并计入成就和鱼类收集图鉴。宝箱也可以用此竿钓到。所有符合条件的鱼类的生成几率也增加了10%。</p>', NULL),
	(3765, 'craft', 'DE0206', '基础鱼类', '01', '<p>训练用鱼竿只能钓到以下鱼类：</p><p>鳀鱼、鲷鱼、大头鱼、鲤鱼、鲢鱼、鲱鱼、河鲈、虹鳟鱼、红鲷鱼、沙丁鱼、海参、西鲱、小嘴鲈鱼、太阳鱼、大眼鱼。</p><p>同样地，训练用鱼竿也会钓到垃圾、海草、绿藻、白藻、海凝胶、河凝胶和洞穴凝胶。</p>', NULL),
	(3766, 'craft', 'DE0206', '备注', '01', '<ul><li>在矿井中使用训练用鱼竿只会产生垃圾，因为所有的一般渔获，包括绿藻、白藻和洞穴凝胶，都被排除在掉落池之外。</li></ul>', NULL),
	(3767, 'craft', 'DE0205', '', '01', '<p></p><p><b>铱镰刀</b>是选择耕种精通后从精通山洞获得的工具。铱镰刀是最高品质的镰刀。</p><p>铱镰刀的升级效果如下:</p><ul><li>可以收获其作用范围内的成熟作物，包括常规作物、生长在花盆中的作物、大葱、从野生种子中生长出来的作物。</li><li>铱镰刀的作用范围比金色的镰刀更大，并且在挥舞时有更高的几率收割范围内的所有杂草。</li><li>铱镰刀收获干草的几率从金色的镰刀的 75% 提升到了 100%。</li><li>作为武器时，铱镰刀的伤害提升到了 20。</li></ul>', NULL),
	(3768, 'craft', 'DE0204', '', '01', '<p></p><p><b>金色的镰刀</b>是一种从采石场矿井获得的工具。解锁采石场后，可以在采石场侧的桥头北边找到采石场矿井的入口。矿井内部是延伸进入山洞内的单层空间。矿井尽头有一个拿着黄金镰刀的死神雕像，与之互动即可获得金色的镰刀。</p><p>拿走镰刀后再与雕像互动将会把玩家传送到采石场矿井入口。</p><p>对比普通镰刀，金色的镰刀有2格额外半径，并且在用作武器时造成10点额外伤害。在其他方面，它和普通镰刀拥有相同的功能。被用于割草时，它有75%概率将草转化为干草，而普通镰刀为50%概率。。</p><p>一旦获得耕种精通，玩家便可解锁最高品质的镰刀“铱镰刀”。</p>', NULL),
	(3769, 'craft', 'DE0203', '', '01', '<p></p><p><b>镰刀</b>是游戏的初始工具之一，可以用于清除杂草和小树苗，并且收割草之后有50%的概率将其转化为干草并储存到筒仓里，如果收割的是蓝草则是两份干草。收获某些作物的时候也必须要用镰刀收割（如苋菜、甘蓝菜、小麦）。镰刀使用时与剑类似，且不会消耗任何能量，但是可造成的伤害非常小。</p><p>镰刀无法在铁匠铺升级，但可以在采石场矿井中找到金色的镰刀，或者解锁耕种精通后获取铱镰刀。</p>', NULL),
	(3770, 'craft', 'DE0045', '', '01', '<p><b>加热器</b>是一种工具，用于在冬季使鸡舍或畜棚的动物保持温暖。加热器可以在玛妮的牧场处以2,000g购买，也可以通过完成饲料收集包获得一个。</p><p>在冬季的下午6点之后，加热器会使鸡舍或畜棚内动物的<b>心情</b>（不是友谊）达到150或以上。如果动物没有被困在室外而是呆在室内，游戏内每10分钟它们会持续增加10点心情，直到达到最高值255，或是玩家睡觉为止。</p><p>多余的加热器不会带来额外的效果。</p>', NULL),
	(3771, 'craft', 'DE0169', '', '01', '<p><b>十字镐</b>是游戏的初始工具之一，主要用于敲碎石头，在采矿中尤为重要。十字镐也可以用于收取放置的设备或家具物品（比如地板和洒水器）。</p><p>十字镐可以用来恢复由锄头开垦过的土地。如果土地上种植有农作物，这一过程将分为两个阶段：第一次敲击会破坏土地上的任何农作物，而土地本身（包括其上施加的肥料）不受影响；再次敲击会将土地恢复到未开垦状态，并移除其上施加的肥料。对于放置在地板上的物品，十字镐同样需要两次敲击来分别移除两者，而斧头只需要一次。</p><p>与其他武器类似，十字镐可以对怪物造成轻微的伤害。特别地，如果用十字镐敲击岩石蟹五次，会把它的壳从背上敲下来，并会使它逃走。</p>', NULL),
	(3772, 'craft', 'DE0169', '十字镐等级', '01', '<p>十字镐可以在铁匠铺升级。每次升级所需的费用和资源如下表所示。每次工具升级都会减少敲碎石块与采矿点所需的次数，并允许玩家敲碎更为坚硬和巨大的岩石。</p>', NULL),
	(3775, 'craft', 'DE0046', '', '01', '<p><b>孵化器</b>是一种工具，用于孵化鸡舍动物的蛋。孵化器无法购买，升级为大鸡舍或高级鸡舍会自带一个，位于喂料斗的左侧。 </p><p>拿着蛋的时候使用孵化器就可以将蛋放进孵化器中。一旦孵化器开始孵化，蛋就无法再次取出，孵化过程也无法取消。</p><p>如果鸡舍已经达到了容量上限（大鸡舍8只，高级鸡舍12只），在鸡舍有空位之前，蛋不会被孵化，并且可以取出。要腾出空间，可以出售一只动物，或把动物转移到其他鸡舍中。一旦鸡舍中有了空位，玩家进入鸡舍后，孵化器就会立刻开始孵化。</p>', NULL),
	(3776, 'craft', 'DE0046', '孵化时间', '01', '<p>下列是孵化器孵化一颗蛋需要的时间。</p><p>如果选择了“鸡舍大师”职业，则孵化时间减半。</p>', NULL),
	(3777, 'craft', 'DE0049', '', '01', '<p><b>喂料斗</b>是一种工具，用于在筒仓和玩家背包之间转移干草。所有的畜棚和鸡舍里面都自带一个喂料斗，玩家无法将它放入背包或改变它的位置。</p><p>当饲料槽中有空位置时，玩家用鼠标右键点击喂料斗，便可以获得筒仓内的干草。将干草放在饲料槽上，动物便可以进食。要想把干草放进筒仓，可以先将物品栏中的干草举在手中，然后右键单击喂料斗。</p><p>高级畜棚或高级鸡舍具有自动喂食系统，因此饲料槽中的干草会自动补充。这样一来，玩家就无需手动从喂料斗中取出干草。不过，有了自动喂食系统后，还是可以把干草放进喂料斗。</p>', NULL),
	(3778, 'craft', 'DE0049', '提示', '01', '<ul><li>干草也可以储存在箱子里，不必全部储存在筒仓中。要想将筒仓中的干草储存在箱子里，只需将干草从喂料斗中取出，然后放入箱子中。这就要求至少有一个畜棚或鸡舍的饲料槽上有空位。高级畜棚或高级鸡舍会自动填满饲料槽，但饲料槽上的干草可以用炸弹破坏掉，以重新腾出空位，方便取出干草。</li><li>即使玩家没有升级到高级畜棚或高级鸡舍，如果玩家已婚，有时候玩家的配偶会帮玩家喂养农场动物，此时每个鸡舍或畜棚的饲料槽会自动填满。在喂料斗和饲料槽前放置箱子或其他设备以阻挡配偶的进入也无法阻止饲料槽自动填满。但是可以用炸弹清空饲料槽，以重新取出干草。</li></ul>', NULL),
	(3784, 'craft', 'DE0159', '', '01', '<p><b>斧头</b>是游戏的初始工具之一，用于砍树枝、树和大树桩，也可以清除或者收割某些东西，尤其是获取木材。斧头也可以用作武器来造成少量伤害，收获巨型作物需要使用斧头。</p><p>最初的斧头是在游戏开始时获得的，可以在铁匠铺升级。</p><p>升级之后，它能够砍伐大树桩，最终能够砍伐大圆木来获得硬木。需要钢斧头来清除阻挡秘密森林入口的大圆木。</p><p>在河流和森林农场地图上，一些灌木丛可以用铜或更高级的斧头摧毁。</p>', NULL),
	(3785, 'craft', 'DE0159', '备注', '01', '<ul><li>无论其升级等级如何，在锻造台附魔强力的斧头都可以移除大树桩和大圆木。</li></ul>', NULL),
	(3786, 'craft', 'DE0051', '', '01', '<p><b>自动抚摸机</b>是一种工具，可以用来每天自动抚摸畜棚和鸡舍里的动物。完成Joja社区发展申请书后，可从Joja超市以50,000g的价格购买。也可以在骷髅洞穴宝箱和金色谜之盒中找到，概率分别为3.6%和0.5%。此外，在深处的危险任务期间或是开启挑战神龛后，击杀矿井里的怪物、打破板条箱或木桶也有概率掉落自动抚摸机，但概率极低。</p><p>自动抚摸机可以放置在任何地方，但只会在畜棚和鸡舍中起作用。要拾起自动抚摸机，必须使用工具敲击或者反复左键点击。</p><p>自动抚摸机不可出售，也不能被分解机分解。</p>', NULL),
	(3787, 'craft', 'DE0051', '动物友谊', '01', '<p>自动抚摸机将防止动物因没有抚摸而降低友谊值或心情值，并且与手动抚摸相比，动物将获得大致相同的心情提升。但是，通过这种方式抚摸，它们只会获得7点友谊值（手动抚摸则是15点）。自动抚摸机的心情提升可以与玩家的常规抚摸叠加，但友谊值提升不可以。即：已经被自动抚摸机的动物，再手动抚摸，总共只能获得15点友谊值。</p>', NULL),
	(3788, 'craft', 'DE0047', '', '01', '<p><b>挤奶桶</b>是一种工具，用于从牛或山羊处收集牛奶或羊奶。挤奶桶只能从玛妮的牧场处以1,000g购买。</p><p>挤奶桶只能对已生长成熟并且当前有产出的牛或山羊使用，成熟的牛每天都会产出牛奶，而山羊则是每两天产出一次羊奶。对未成熟的牛或山羊使用会显示“[动物名字]太小了还不能产奶”的信息，而对其他动物使用会显示“[动物名字]不产奶”。如果该动物已经挤过奶并且还没有产出，会显示“[动物名字]现在没有奶了。”</p><p>玩家每次<b>成功</b>使用挤奶桶会消耗4点能量，并增加5点耕种经验值。</p><p>玩家达到耕种等级10后，可以从玛妮的牧场处以25,000g购买自动采集器，放置在畜棚内每天都会自动收集所有牛和山羊的奶。</p>', NULL),
	(3789, 'craft', 'DE0048', '', '01', '<p><b>剪刀</b>是一种工具，用于从绵羊身上收集动物毛。剪刀只能从玛妮的牧场处以1,000g购买。</p><p>剪刀只能对已生长成熟并且当前有产出的绵羊使用，成熟的绵羊每三天产出一次动物毛。对未成熟的绵羊使用会显示“[动物名字]太小了，还不能从其身上剪毛”的信息，而对其他动物（包括山羊）使用会显示“[动物名字]不产羊毛”。如果该绵羊已经剪过毛并且还没有产出，会显示“[动物名字]现在没有羊毛。”</p><p>玩家每次<b>成功</b>使用剪刀都会消耗4点能量，并增加5点耕种经验值。</p><p>玩家达到耕种等级10后，可以从玛妮的牧场处以25,000g购买自动采集器，放置在畜棚内每天都会自动收集所有绵羊的动物毛。</p>', NULL),
	(3796, 'craft', 'DE0154', '', '01', '<p><b>锄头</b>是游戏的初始工具之一，主要被用于开垦耕种需要的土地，以及挖掘远古斑点。</p><p>锄头也可以用在矿井、沙滩、沙漠以及其他有裸露土壤的区域来挖掘沙子或土块。在矿井中可以挖掘出古物、山洞萝卜以及其他物品。在地上区域，可以挖掘出粘土、金属矿石或者混合种子。在冬季，会有更多的可挖掘区域，并且可以挖到雪山药和冬根。</p><p>锄头可以在铁匠铺升级。每次升级所需的费用和资源如下表所示。在锄头升级后，按住鼠标左键蓄力，然后松开，即可一次挖掘多个格子。</p>', NULL),
	(3797, 'craft', 'DE0154', '注意事项', '01', '<p>当挖掘范围内存在物品（例如小桶或洒水器）时，物品会被取消放置。这一特性在玩家试图回收大量放置的物品（例如小桶和罐头瓶）时是有利的，但在开垦耕地时是不利的。但是，锄头不会移除地板和小径上的物品。因此，可以把稻草人等物品放置在地板/小径之上来避免锄地时被意外取下，洒水器不会被意外取下，所以不用铺地板。</p>', NULL),
	(3798, 'craft', 'DE0164', '', '01', '<p><b> 喷壶</b>是游戏的初始工具之一，用于灌溉农作物。它可以在任何水源装满水，包括厨房的水槽（如果玩家拥有厨房）。</p><p>作物在完全成熟之前，每天都需要浇水。未灌溉的作物虽然不会枯萎，但也不会成长。可多次收获的成熟作物同样需要每天浇水。</p><p>作物在雨天则不需要浇水。在冬天，由冬季种子种出的作物也需要浇水，即使那天下雪也一样。</p><p>只可收获一次的成熟作物不需要灌溉，如果玩家不收获这些作物，他们将一直留在那个地方。（注意：巨大作物的形成需要每天灌溉已成熟的作物。) </p><p>提示：使用保湿土壤施肥会使得土壤额外保持湿润几天。</p><p>喷壶可以在铁匠铺那里升级。每次升级所需的花费和资源需求见下表。若想一次灌溉不止一块地，只需按住左键为升级后的喷壶蓄力，之后放开左键进行灌溉。</p>', NULL),
	(3799, 'craft', 'DE0164', '灌溉多个方格', '01', '<p>你只能在从你当前位置开始的一条直线上（上/下 或 左/右）浇灌多个土壤方格。浇灌区域分阶段扩大，先是3个方块，然后扩大到5个方格，再扩大到3×3的方框，再扩大到3×6的方框。</p><p>在PC上，按住左键可以分阶段“启动”升级后的喷壶，当你选择的浇灌区域被高亮显示时，再松开左键。在移动设备上，点击距离最近的要浇水的方块，然后等待（非常短暂），直到头像缩回，然后拖出到最远的要浇水的方块，当要浇水的区域高亮时释放。</p>', NULL),
	(3800, 'craft', 'DE0164', '升级后水量消耗变化', '01', '<p>起始喷壶浇灌一格地面会消耗1点水量。满蓄力的铜喷壶灌溉3格地面会消耗2点水量。满蓄力的钢喷壶消耗3点水量，金喷壶满蓄力消耗4点水量，铱喷壶则消耗5点水量。</p><p>附:即使剩余水量不足以灌溉满蓄力所浇灌的范围，玩家还是能完成满蓄力灌溉的操作。</p><ul><li>升级喷壶需要度过两个夜晚。例如，如果在季节的第十天把喷壶给了克林特，他将在第十二天完成。</li><li>最好在冬季升级喷壶，因为冬季不需要浇水。</li><li>如果想要在季节中期升级喷壶，建议提前看电视天气预报是否下雨，如果预报明天有雨，在当日给所有作物浇水后，当日将喷壶交给克林特，第二天下雨不需要浇水，第三天喷壶将完成升级。</li><li>也可以在任意季节的第二十七天升级喷壶，大多数作物不需要在第二十八天浇水，因为大多数作物将会在换季时枯萎，而喷壶将在下个季节的第一天升级完成。</li></ul>', NULL),
	(3801, 'craft', 'DE0164', '趣闻', '01', '<ul><li>如果你向火把、篝火或其他可发光的物品浇水，它们会持续发亮（不会被熄灭）。</li><li>升级后的农舍内厨房的水槽也算作水源，但是村民家里的不算。</li></ul>', NULL),
	(3802, 'craft', 'DE0050', '', '01', '<p><b>自动采集器</b>是一种工具，用于自动收集动物产品。玩家达到耕种等级10 后可以从玛妮的牧场处以25,000g购买，有时也可以从骷髅洞穴中的宝箱获得。</p><p>将自动采集器放置在畜棚会自动采集奶牛和山羊的奶产品、绵羊的羊毛，以及鸵鸟的鸵鸟蛋；放置在鸡舍时会自动采集鸡舍动物的所有产品。</p><p>每天早晨，自动采集器将会自动收集畜棚和鸡舍里的所有动物产品，但收集自动采集的物品不会增加玩家的耕种经验值或精通经验值。</p><p>自动采集器与宝箱相同，最多可以存放36种不同的收获物品。右键点击自动采集器可以查看或移除物品。但如果其中未放置任何物品，则无法打开它。</p><p>玩家可以将自动采集器放在任何地方，但是放在畜棚或鸡舍之外的地方不会收获任何物品。要移除自动采集器，必须使用工具 （重复点击左键或长按左键无法移除）。</p><p>自动采集器无法被售卖或分解。</p>', NULL),
	(3803, 'craft', 'DE0050', '动物友谊', '01', '<p>自动采集器使玩家无法通过手动挤奶或剪羊毛获得+5友谊。如果玩家与动物的友谊还未达到5心，且动物居住的畜棚或鸡舍里有自动采集器，则动物需要更多的时间来达到5心。</p><p><b>注意：</b>如果玩家在一天中都没有与动物互动，会在每天结算时失去5-10点与动物的友谊。这意味着，即使保证了动物的喂食和外出活动，但没有手动抚摸、挤奶或剪毛的动物还是会逐渐减少友谊值。</p><p>这个负面影响可以通过使用自动抚摸机来抵消。</p>', NULL),
	(3804, 'craft', 'DE0050', '邮件', '01', '<p>耕种等级到达10级后，第二天玩家会收到一封玛妮寄来的信。信件的阅读与否不影响商品库存，耕种等级满足后就可立即购买。</p>', NULL),
	(3805, 'craft', 'DE0174', '', '01', '<p><b>垃圾桶</b>是一种在物品栏菜单中用来丢弃物品的工具。升级后，丢弃物品时会返还一部分物品价值。</p><p>游戏开始时玩家的物品栏里有一个基础垃圾桶。垃圾桶可以在铁匠铺升级。每次升级所需的费用和资源如下表所示。</p>', NULL),
	(3806, 'craft', 'DE0174', '注意事项', '01', '<ul><li>与其他工具不同，在铁匠铺升级垃圾桶期间，原有的垃圾桶仍然可以使用。</li><li>垃圾桶回收物品的价值会因为职业对物品价值的影响而有所变化（如渔夫、农耕人等）。</li><li>某些不可出售的物品（如咖啡机、汽水机、缝纫机等）通过垃圾桶删除后，被删除的物品将无法通过正常方式获取。</li><li>垃圾桶升级后，丢弃（包括无法从目录和家具目录获取的）地板、家具、墙纸仍然不会返还金币，尽管它们在游戏数据中有“价值”数据。</li></ul>', NULL),
	(3831, 'special', 'SP0006', '', '01', '<p><b>糖</b>是烹饪的一种材料，可以用来制造许多食物。它全年都可以在皮埃尔的杂货店花100买到，或在Joja超市花125买到.</p><p>玩家可以把甜菜放在磨坊里制成糖。一份甜菜可以制出三份糖。 将甜菜放在磨坊里，第二天早上糖会存在右边的盒子里。</p>', NULL),
	(3833, 'special', 'SP0006', '裁制', '01', '<p>糖可通过缝纫机使用布料制成砂糖T恤。糖可用于染色。放置在缝纫机内时，糖可用作<b>白色</b>染料。</p>', NULL),
	(3834, 'special', 'SP0006', '注', '01', '<p>用无星或银星品质的甜菜制糖，是有利润的，它的售价是150，比无星或银星品质的甜菜还要贵，即使你选择了农耕人技能。但是，这远远低于用罐头瓶制成的价值250的腌甜菜。</p>', NULL),
	(6501, 'fish', 'FH0057', '', '01', '<p><br></p><p><strong>雌鮟鱇鱼</strong>是用于完成齐先生的“大家族”任务的五条传奇鱼之一。它的售价、治疗效果和钓鱼地点都与鮟鱇鱼一样，但是它只能在任务期间被钓到。</p>', NULL),
	(6502, 'fish', 'FH0057', '地图', '01', '<p>雌鮟鱇鱼可以在Joja超市的北方被找到。</p>', NULL),
	(6503, 'fish', 'FH0057', '收集包', '01', '<p>雌鮟鱇鱼不用于任何收集包。</p>', NULL),
	(6505, 'fish', 'FH0057', '鱼塘', '01', '<p>雌鮟鱇鱼可以通过鱼塘养殖，最高容量即为 1 条，不可繁殖。</p>', NULL),
	(6506, 'fish', 'FH0057', '任务', '01', '<p>雌鮟鱇鱼只能在齐先生的“大家族”任务期间被钓到。</p>', NULL),
	(6507, 'fish', 'FH0130', '', '01', '<p><b>洞穴凝胶</b>是一种可在任意季节的矿井第20、60和100层的池塘中使用钓鱼竿钓鱼获得的物品。钓上洞穴凝胶的概率会随运气增益等级的提升而增大，而与每日运气无关。</p><p>洞穴凝胶在容量至少为8的岩浆鳗鱼鱼塘中有概率产出。玩家有概率可以在书摊老板处用1本海之宝石交换3个洞穴凝胶，前提是玩家已经阅读过这本书。好感度达到最大后，宠物乌龟可能会给予玩家洞穴凝胶作为礼物。</p><p>洞穴凝胶会显示在收集品 - 鱼的页面之中，也会计入四项和钓鱼相关的成就。然而，洞穴凝胶在分类上并不属于鱼，所以也不能在鱼店卖给威利。</p>', NULL),
	(6508, 'fish', 'FH0130', '收集包', '01', '<p>洞穴凝胶不用于任何收集包。</p>', NULL),
	(6509, 'fish', 'FH0130', '攻略', '01', '<p>在所有地下水体中，从矿井第100层的池塘钓上洞穴凝胶的概率是最大的，因为这里只可能钓上洞穴凝胶、岩浆鳗鱼或者垃圾。在这一层，每次钓到的垃圾均有5%的几率被替换为洞穴凝胶。每提升1级运气增益，此几率就会增加5%（每日运气不影响替换几率）。例如运气+5时，垃圾就会有30%的几率被替换为洞穴凝胶。因此，在较为靠岸的地方抛竿可以降低岩浆鳗鱼上钩的概率、提升钓到垃圾的概率，从而略微增加钓上洞穴凝胶的概率。</p><p>不同于其他水体，只有在矿井第100层的池塘中，钓上凝胶与否不会取决于游戏存档的预设数值。</p><p>训练用鱼竿无法钓上洞穴凝胶。</p>', NULL),
	(6510, 'fish', 'FH0131', '', '01', '<p><b>河凝胶</b>是一种可在任意季节的河流、湖泊（包括沙漠湖泊）中使用钓鱼竿钓鱼获得的物品。每提升1点运气增益都会显著增加捕获河凝胶的机会。在午夜鲤鱼鱼塘数量达到7后也有概率产出。</p><p>玩家有概率可以在书摊老板处用1本海之宝石交换3个河凝胶，前提是玩家已经阅读过这本书。</p><p>河凝胶会显示在收集品 - 鱼的页面之中，也会计入四项和钓鱼相关的成就。然而，河凝胶在分类上并不属于鱼，所以也不能在鱼店卖给威利。</p>', NULL),
	(6511, 'fish', 'FH0131', '收集包', '01', '<p>河凝胶不用于任何收集包。</p>', NULL),
	(6512, 'fish', 'FH0132', '', '01', '<p><b>海凝胶</b>是一种可在任意季节的沙滩水域中使用钓鱼竿钓鱼获得的物品。在夜市的潜水艇内或在姜岛海域也可以钓出。每提升1点运气增益都会显著增加捕获海凝胶的机会。在比目鱼鱼塘数量达到7后也有概率产出。</p><p>玩家有概率可以在书摊老板处用1本海之宝石交换3个海凝胶，前提是玩家已经阅读过这本书。</p><p>海凝胶会显示在收集品 - 鱼的页面之中，也会计入四项和钓鱼相关的成就。然而，海凝胶在分类上并不属于鱼，所以也不能在鱼店卖给威利。</p>', NULL),
	(6513, 'fish', 'FH0132', '收集包', '01', '<p>海凝胶不用于任何收集包。</p>', NULL),
	(6514, 'fish', 'FH0132', '趣闻', '01', '<p>从第二年的月光水母起舞中和德米特里厄斯的对话可以得知，海凝胶的触感和月光水母差不多。</p>', NULL),
	(6540, 'fish', 'FH0073', '', '01', '<p><b>白藻</b>是一种能在任何季节、任何地下水域（矿井、下水道、突变虫穴或巫婆沼泽）用钓鱼竿钓到的物品。</p><p>白藻同样可以作为怪物的掉落物出现，可掉落该物品的怪物有：蛆、突变蛆、苍蝇、突变苍蝇、臭虫和史莱姆（仅限红色、紫色、铜和铁四种变种史莱姆）。饲养有9条及以上鬼鱼的鱼塘的每日产出物品中，可能包含有5个白藻。</p><p>当获得白藻以后，白藻会出现在收集品 - 鱼页面之中。白藻也计入4个钓鱼相关的成就之中（渔夫、老海员、垂钓大师和捕鱼大师）。然而，除了上述情况之外，白藻不会被分类为鱼类，这就表明玩家不能把白藻卖给鱼店的威利、售出价格不受钓鱼职业影响、不能在食谱中代替“任意鱼”，也不能放进鱼饵制造机、熏鱼机或是养在鱼塘。 </p>', NULL),
	(6541, 'fish', 'FH0073', '收集包', '01', '<p>5 个白藻是工艺室（重新混合后）的 野生药材收集包的一个可选项。</p>', NULL),
	(6542, 'fish', 'FH0073', '裁制', '01', '<p>白藻可通过缝纫机使用布料制成可染色的 有领衬衫。白藻可用于染色。放置在缝纫机内时，白藻可用作<b>白色</b>染料。</p>', NULL),
	(6543, 'fish', 'FH0073', '任务', '01', '<ul><li>鬼鱼会在鱼塘扩容任务中要求 5 个白藻，以将鱼塘的容量从 5 增加到 7。</li><li>在浣熊的请求的第三个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个白藻。</li></ul>', NULL),
	(6544, 'fish', 'FH0072', '', '01', '<p><b>绿藻</b>是一种可在任意季节、任意淡水水体（标准农场上的池塘除外）中使用钓鱼竿钓鱼获得的物品。蟹笼不能捕获绿藻。钓上绿藻后，玩家会获得3点钓鱼经验。</p><p>绿藻是绿色史莱姆的一种稀有掉落物，也可由史莱姆鱼、小龙虾、蜗牛、玉黍螺在鱼塘中产出。在任何季节的任何时间，翻动垃圾桶也有概率找到绿藻。</p><p>绿藻会显示在收集品 - 鱼的页面之中，并且捕获的绿藻会计入四项和钓鱼相关的成就之中。除了上述情况之外，绿藻在分类上并不属于鱼，所以也不能被卖给鱼店的威利。</p>', NULL),
	(6545, 'fish', 'FH0072', '收集包', '01', '<p>绿藻不用于任何收集包。</p>', NULL),
	(6546, 'fish', 'FH0072', '裁制', '01', '<p>绿藻可通过缝纫机使用布料制成可染色的有领衬衫。绿藻可用于染色。放置在染料罐内时，绿藻可用作<b>绿色</b>染料。放置在缝纫机内时，绿藻还可用作<b>深绿色</b>染料。</p>', NULL),
	(6547, 'fish', 'FH0072', '任务', '01', '<ul><li>绿藻会在任何季节，随机出现在皮埃尔的杂货店外边的布告栏上。任务完成的奖励是45和150点友谊。</li><li>大头鱼、鲤鱼、大嘴鲈鱼以及午夜鲤鱼的鱼塘容量从3提升到5的时候，可能会出现需要2~3个绿藻的鱼塘任务。</li></ul>', NULL),
	(6548, 'fish', 'FH0071', '', '01', '<p><b>海草</b>可以在任何季节，通过在海边钓鱼获得。在冬季的夜市的钓鱼潜水艇上钓鱼，将有41%的机率钓到海草。蟹笼不能捕获海草。</p><p>海草会在海滩的潮汐池南边狭长的海岸上生成，但此处生成海草的概率相当低（每晚只有3%的几率）。当鱼塘内饲养的鸟蛤、螃蟹、龙虾、蚌、牡蛎或虾等咸水鱼（蟹笼鱼类）数量至少1个的时候，鱼塘产出1~3个海草的几率是8.8%。</p><p>使用钓鱼竿钓到海草只能获得3点钓鱼经验值，以及一个普通品质的海草。从沙滩上经采集捡起一个海草能获得7点钓鱼经验值，海草的品质则会因为采集技能的等级不同而不同（可能会捡到铱星品质的海草）。</p><p>在任何季节的任何时间，翻动垃圾桶也有概率找到海草。</p><p>海草会显示在收集品 - 鱼的页面之中，并计入和钓鱼相关的四个成就之中。然而，除了上述情况之外，海草不会被分类为鱼类，因此玩家不能把海草卖给鱼店的威利。 </p>', NULL),
	(6549, 'fish', 'FH0071', '地图', '01', '<p>海草可以在海洋和沙滩上找到。</p>', NULL),
	(6550, 'fish', 'FH0071', '收集包', '01', '<p>海草不用于任何收集包。</p>', NULL),
	(6551, 'fish', 'FH0071', '裁制', '01', '<p>海草可通过缝纫机使用布料制成海藻上衣。海草还可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
	(6552, 'fish', 'FH0071', '任务', '01', '<ul><li>海草会在任意季节，出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中。奖励为 60和150友谊点。</li><li>当鱼塘的养殖数量从3提高到5的时候，鱼塘的求助任务会出现有些种类的鱼会要求玩家提供1~2个海草的情况。</li></ul>', NULL),
	(6553, 'crop', 'CR0008', '', '01', '<p><b>草莓</b>是一种水果类农作物，由草莓种子生长 8 天后成熟。成熟后，每 4 天可以收获一次。</p>', NULL),
	(6554, 'crop', 'CR0008', '生长阶段', '01', '<p>收获时，每株草莓每4天产出1颗草莓，并且有小几率产出更多的草莓。</p>', NULL),
	(6555, 'crop', 'CR0008', '收集包', '01', '<p>不用于任何收集包中。</p>', NULL),
	(6556, 'crop', 'CR0008', '配方', '01', '<p>不用于任何配方。</p>', NULL),
	(6557, 'crop', 'CR0008', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(6558, 'crop', 'CR0008', '裁制', '01', '<p>草莓可通过缝纫机使用布料制成可染色的农夫裤。草莓可用于染色。放置在染料罐内时，草莓可用作<b>红色</b>染料。放置在缝纫机内时，草莓还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6559, 'crop', 'CR0008', '趣闻', '01', '<ul><li>第一年理论上最多可收获三次草莓 。这需要在春13日种植并使用生长激素 ，获取生长激素最简单的方法是完成 春季作物收集包（但仅奖励20个）；这需要在春1日种植花椰菜并在复活节结束后当天访问社区中心 。另外两种获得生长激素的办法分别是制作和皮埃尔处购买 ，前者需要在春6日前达到采集4级以及时获取松焦油和获取苔藓 ，更为困难。</li></ul>', NULL),
	(6560, 'crop', 'CR0053', '', '01', '<p><strong>黑莓</strong>是一种能在星露谷通过采集而找到的水果。它能够在黑莓收获季（秋季8~11）户外的灌木丛或地上中找到。它也能通过种植秋季种子得到。在秋季翻动垃圾桶也有概率找到黑莓。</p><p>在黑莓收获季时，在每个生产黑莓的灌木丛中获取的数量随着采集的等级的上升而增加。等级4时，每个灌木丛能得到2个黑莓。等级8时，每个灌木丛会得到3个黑莓。如果采集等级通过进食烹饪的食物被暂时地提高到12或13级，那么每个灌木丛就能得到4个黑莓。每收获1个黑莓会获得1点采集经验值。</p>', NULL),
	(6561, 'crop', 'CR0053', '品质', '01', '<p>黑莓在黑莓收获季时，被玩家从灌木丛中摇下来时为普通品质。除非在植物学家的影响下，它们会永远呈现为铱星品质。除了在植物学家的影响下，从地上拾起或种植秋季种子而得到的黑莓会表现为普通、银星、金星品质。</p>', NULL),
	(6562, 'crop', 'CR0053', '收集包', '01', '<p>被用于工艺室的 秋季采集收集包.</p>', NULL),
	(6564, 'crop', 'CR0053', '裁制', '01', '<p>黑莓可通过缝纫机使用布料制成上衣。黑莓可用于染色。放置在染料罐内时，黑莓可用作<strong>蓝色</strong>染料。放置在缝纫机内时，黑莓还可用作<strong>深蓝色</strong>染料。</p>', NULL),
	(6565, 'crop', 'CR0053', '任务', '01', '<ul><li>黑莓可能会随机地出现在皮埃尔的杂货店外的"需要帮助" 公告板中，完成奖励为60和150友谊点数。</li></ul>', NULL),
	(6566, 'crop', 'CR0050', '', '01', '<p><b>普通蘑菇</b>是一种在秋季遍布星露谷的蘑菇，可通过采集获得。春季和秋季的秘密森林，夏季和秋季的森林农场以及姜岛蘑菇洞都可以找到它们。在秋季翻动垃圾桶也有概率找到。普通蘑菇也可以使用秋季种子种出。如果你选择的是蘑菇洞穴，可以在山洞的蘑菇箱中收获。在蘑菇树上放置树液采集器也可以获得普通蘑菇。它们也可以由蘑菇树桩产出。</p><p>在种子生产器中放入普通蘑菇将产生 1~3 个秋季种子。</p>', NULL),
	(6567, 'crop', 'CR0050', '收集包', '01', '<p>普通蘑菇可用于工艺室的 秋季采集收集包。</p>', NULL),
	(6568, 'crop', 'CR0050', '配方', '01', '<h3>烹饪</h3><h3>打造</h3>', NULL),
	(6569, 'crop', 'CR0050', '裁制', '01', '<p>普通蘑菇可通过缝纫机使用布料制成上衣。普通蘑菇可用于染色。放置在染料罐内时，普通蘑菇可用作<b>橙色</b>染料。放置在缝纫机内时，普通蘑菇还可用作<b>深棕色</b>染料。</p>', NULL),
	(6570, 'crop', 'CR0050', '任务', '01', '<ul><li>普通蘑菇可能在秋季期间随机出现在皮埃尔的杂货店门外的"需要帮助" 公告板上，完成任务能获得120和 150 点友谊值。</li><li>饲养有大头鱼、大嘴鲈鱼或木跃鱼的鱼塘会出现要求玩家提供2-3个普通蘑菇的鱼塘任务。</li></ul>', NULL),
	(6571, 'crop', 'CR0050', '趣闻', '01', '<ul><li>秋季可采集物品中，仅有普通蘑菇可以直接放入种子生产器。</li></ul>', NULL),
	(6572, 'crop', 'CR0049', '', '01', '<p><b>甜豌豆</b>是花的一种，可以在夏季通过采集获得。它在鹈鹕镇最为常见，夏季可采集物品中仅有它能在镇里找到。甜豌豆也会在秘密森林、巴士站、铁路和森林农场地图中生长。它也可以通过种植夏季种子获得。在夏季翻动垃圾桶也有概率找到甜豌豆。</p>', NULL),
	(6573, 'crop', 'CR0049', '治疗效果', '01', '<p>甜豌豆可以食用，但是不会增加生命值和能量。</p>', NULL),
	(6574, 'crop', 'CR0049', '收集包', '01', '<p>工艺室的 夏季采集收集包需要一个甜豌豆。</p>', NULL),
	(6575, 'crop', 'CR0049', '裁制', '01', '<p>甜豌豆可通过缝纫机使用布料制成上衣。甜豌豆可用于染色。放置在染料罐内时，甜豌豆可用作<b>紫色</b>染料。放置在缝纫机内时，甜豌豆还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6576, 'crop', 'CR0049', '任务', '01', '<ul><li>在夏季，甜豌豆会出现在皮埃尔的杂货店外边的布告栏上的随机任务之中，完成该任务的奖励是 150 和150 友谊点 。</li></ul>', NULL),
	(6577, 'crop', 'CR0035', '', '01', '<p><b>玫瑰仙子</b>是一种花，是由玫瑰仙子种子经过12天生长发育所成的成熟植株。</p><p>如果将玫瑰仙子种植在蜂房的作用范围内，蜂房会产生玫瑰仙子蜂蜜，将蜂蜜的基础价从100增加到680。虽然玫瑰仙子本身的收益并不高，但玫瑰仙子蜂蜜是价值最高的蜂蜜。</p><p>偶尔可以从旅行货车以870 ~ 1,450买到玫瑰仙子。</p>', NULL),
	(6578, 'crop', 'CR0035', '收集包', '01', '<p>玫瑰仙子是茶水间（重新混合后）的 花园收集包的一个可选项。</p>', NULL),
	(6579, 'crop', 'CR0035', '裁制', '01', '<p>玫瑰仙子可通过缝纫机使用布料制成可染色的长裙。玫瑰仙子可用于染色。放置在染料罐内时，玫瑰仙子可用作<b>红色</b>染料。放置在缝纫机内时，玫瑰仙子还可用作<b>粉色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6580, 'crop', 'CR0035', '任务', '01', '<p>玫瑰仙子不用于任何任务。</p>', NULL),
	(6581, 'crop', 'CR0035', '注意事项', '01', '<ul><li>成熟的玫瑰仙子出现六种不同花色的机会是均等的。</li></ul><ul><li>不同颜色的玫瑰仙子会占据物品栏或宝箱中不同的位置。依照颜色和作物品质等级的不同，玩家最多可以收获24种品质颜色各不相同的玫瑰仙子。</li><li>虽然玫瑰仙子没有正红色的版本，但仍可用作染料罐的红色染料。</li><li>在一天结束时，农场中至少有一朵成熟的玫瑰仙子，增加70%的农作物仙子事件的机会(从1%增加到1.7%)。</li></ul>', NULL),
	(6582, 'crop', 'CR0006', '', '01', '<p><b>蒜</b>是一种蔬菜类农作物，由大蒜种子经过4天的时间长成。从第二年开始，种子可以在皮埃尔的杂货店买到。</p>', NULL),
	(6583, 'crop', 'CR0006', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(6584, 'crop', 'CR0006', '配方', '01', '<h3>烹饪</h3><h3>制造</h3>', NULL),
	(6585, 'crop', 'CR0006', '裁制', '01', '<p>蒜可通过缝纫机使用布料制成可染色的衬衫皮带。蒜可用于染色。放置在缝纫机内时，蒜可用作<b>白色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6586, 'crop', 'CR0006', '任务', '01', '<ul><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个蒜（仅在春季）。</li><li>可用于解开饕餮青蛙的任务。</li></ul>', NULL),
	(6587, 'crop', 'CR0005', '', '01', '<p><b>甘蓝菜</b>是一种蔬菜类农作物，由甘蓝种子生长6天后成熟。</p><p>甘蓝菜需要使用镰刀来收获。</p>', NULL),
	(6588, 'crop', 'CR0005', '收集包', '01', '<p>甘蓝菜可用于重新混合的收集包中茶水间的 春季作物收集包。</p>', NULL),
	(6589, 'crop', 'CR0005', '裁制', '01', '<p>甘蓝菜可通过缝纫机使用布料制成可染色的衬衫皮带。甘蓝菜可用于染色。放置在染料罐内时，甘蓝菜可用作<b>绿色</b>染料。放置在缝纫机内时，甘蓝菜还可用作<b>绿色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6590, 'crop', 'CR0005', '任务', '01', '<ul><li>甘蓝菜可能会在春季(2+年)皮埃尔的杂货店外面的"求助"板中出现，回报是330和150点友谊值。</li></ul>', NULL),
	(6591, 'crop', 'CR0033', '', '01', '<p><b>蔓越莓</b>是一种水果类农作物，由蔓越莓种子生长7天后成熟。成熟后，每5天可以收获一次。</p>', NULL),
	(6592, 'crop', 'CR0033', '生长阶段', '01', '<p>收获时，每株蔓越莓每5天可以收获2个蔓越莓，10%的机会多收获1个或1个以上蔓越莓(平均增加0.11个)</p>', NULL),
	(6593, 'crop', 'CR0033', '作物生长日历', '01', '<h3>收获数量</h3><p>相对于使用高级生长激素，使用肥料或高级肥料将更有优势，因为生长激素不会缩短收获之间的时间（依然是每5天成熟一次）。而且，虽然第一次的蔓越莓成熟时间会从7天下降到5.25天，但是在整个秋季中你还是只能收获5次蔓越莓。</p>', NULL),
	(6594, 'crop', 'CR0033', '收集包', '01', '<p>任何收集包都不需要蔓越莓。</p>', NULL),
	(6595, 'crop', 'CR0033', '裁制', '01', '<p>蔓越莓可通过缝纫机使用布料制成可染色的农夫裤。蔓越莓可用于染色。放置在染料罐内时，蔓越莓可用作<b>红色</b>染料。放置在缝纫机内时，蔓越莓还可用作<b>红色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6596, 'crop', 'CR0033', '任务', '01', '<p>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个蔓越莓（仅在秋季）。</p>', NULL),
	(6597, 'crop', 'CR0033', '提示', '01', '<ul><li>请不要被蔓越莓的第二个生长阶段的外表所欺骗，因为它看起来会有些像因换季而枯萎的农作物。</li></ul>', NULL),
	(6598, 'crop', 'CR0034', '', '01', '<p><b>南瓜</b>是一种蔬菜类农作物，由南瓜种子生长13天后成熟。</p><p>南瓜是仅有的五种可以长成巨大作物的农作物之一。它具有所有正常作物中第二高的单位基价，仅次于杨桃。</p>', NULL),
	(6599, 'crop', 'CR0034', '生长阶段', '01', '<p>南瓜可以形成巨型作物。</p>', NULL),
	(6600, 'crop', 'CR0034', '收集包', '01', '<p>它用于茶水间的 秋季作物收集包，是 茶水间的 高品质作物收集包的选项之一。</p>', NULL),
	(6601, 'crop', 'CR0034', '裁制', '01', '<p>南瓜可通过缝纫机使用布料制成可染色的纽扣衬衫。南瓜可用于染色。放置在染料罐内时，南瓜可用作<b>橙色</b>染料。放置在缝纫机内时，南瓜还可用作<b>橙色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6602, 'crop', 'CR0034', '任务', '01', '<ul><li>“雕刻南瓜”：卡洛琳会在第1年秋季19日给你写信说，需要一个南瓜。奖励是500和1心友谊。</li><li>南瓜在秋季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励为960和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个南瓜（仅在秋季）。</li></ul>', NULL),
	(6603, 'crop', 'CR0022', '', '01', '<p><b>蓝莓</b>是一种水果类农作物，由蓝莓种子生长13天后成熟。成熟后，每4天可以收获一次。</p>', NULL),
	(6604, 'crop', 'CR0022', '生长阶段', '01', '<p>每次收获得到3个蓝莓，还有2%机会收获到更多的蓝莓。</p>', NULL),
	(6605, 'crop', 'CR0022', '施肥', '01', '<p>初级肥料和高级肥料只影响每一次收获的第一个浆果，这意味着每丛收获的3个浆果中有2个将不会受到肥料加成。因此，生长激素是该作物的最佳肥料——假设在夏季的第一天种下蓝莓，使用生长激素后可在秋季前额外收获一次，使收益增加25%。</p><p>假设每一次收获的第一个浆果为金星，使用高级肥料可使收益增加17%。需要注意的是如果使用高级生长激素，也只能额外多收获一次，而且成本更高；但也存在例外：从绿洲购买高级生长激素，价格为80；从皮埃尔的杂货店购买生长激素，价格为100。</p>', NULL),
	(6606, 'crop', 'CR0022', '收集包', '01', '<p>茶水间的 夏季作物收集包需要提交一个蓝莓.</p>', NULL),
	(6607, 'crop', 'CR0022', '裁制', '01', '<p>蓝莓可通过缝纫机使用布料制成可染色的短裤。蓝莓可用于染色。放置在染料罐内时，蓝莓可用作<b>蓝色</b>染料。放置在缝纫机内时，蓝莓还可用作<b>蓝色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6608, 'crop', 'CR0022', '任务', '01', '<ul><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个蓝莓（仅在夏季）。</li></ul>', NULL),
	(6609, 'crop', 'CR0022', '趣闻', '01', '<ul><li>如果在夏季的星期天与刘易斯对话，他可能会说：“你有没有在你的农场里种蓝莓呢？我是真的很喜欢新鲜的蓝莓。”蓝莓也确实是他喜欢的礼物。</li></ul>', NULL),
	(6610, 'crop', 'CR0039', '', '01', '<p><b>纤维</b>是一种通过破坏杂草或者收获被种植的纤维种子获得的资源。用镰刀、斧头、十字镐、锄头、剑或炸弹清理杂草，每株野草有50%的概率掉落一个纤维。在星期二，沙漠商人会以每五个石头一个纤维的交换价格出售纤维。沙漠节期间，文森特会在其商铺以 1 卡利科三花蛋的价格出售一组（10个）纤维。</p><p>将草籽放入分解机可以快速获得10个纤维，相当于每个纤维10金，这是后期获取纤维的高效手段。虽然要花费一些时间，但是比起用石头交换要便宜得多。</p><p>可掉落纤维的杂草会频繁地出现在矿井的第25~29层和105层~109层。在游戏后期阶段，突变虫穴也是纤维的一个丰富的来源，它每次会产出40~70个纤维，每隔几天就刷新一次。</p><p>出现在荒野农场的荒野石魔被击杀后有90%的几率掉落一个纤维，有50%的几率掉落第二个纤维。</p><p>纤维总是普通品质。</p>', NULL),
	(6611, 'crop', 'CR0039', '生长阶段', '01', '<p>下表即为 纤维种子生长的各个阶段。Fiber</p>', NULL),
	(6612, 'crop', 'CR0039', '建筑', '01', '<p>纤维可用于建造祝尼魔屋。玩家可以在完成了哥布林问题任务后，去法师塔向法师购买。</p>', NULL),
	(6613, 'crop', 'CR0039', '收集包', '01', '<p>200个纤维可用于 工艺室（重新混合后）的 森林收集包。</p>', NULL),
	(6614, 'crop', 'CR0039', '裁制', '01', '<p>纤维可通过缝纫机使用布料制成可染色的 男款普通套头衫 或女款普通套头衫。纤维还可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
	(6615, 'crop', 'CR0039', '任务', '01', '<p>在鱼塘求助任务中，鱼塘的容量达到从3提升到5时，小龙虾、蜗牛或者玉黍螺会要求玩家提供15个纤维。</p>', NULL),
	(6616, 'crop', 'CR0039', '交易', '01', '<p>一旦解锁完成，便可在浣熊妻子的商店使用纤维以物易物。</p>', NULL),
	(6617, 'crop', 'CR0013', '', '01', '<p><b>玉米</b>是一种蔬菜类农作物，由玉米种子生长14天后成熟。成熟后，每4天可以收获一次。</p>', NULL),
	(6618, 'crop', 'CR0013', '作物生长日历', '01', '<h3>多季作物</h3><p>玉米可以在 夏季 和 秋季生长。它在两个季节没有区别。如果一株玉米(任何生长阶段)在夏28日种在地里，它会在秋1日继续生长，就好像什么都没有发生一样。 夏天玉米地里下的肥料不会在秋1日消失。</p>', NULL),
	(6619, 'crop', 'CR0013', '收集包', '01', '<p>玉米可用于完成 茶水间的 秋季作物收集包 。5个金星品质的玉米也是 茶水间的 高品质作物收集包 以及 茶水间（重新混合后）的 高品质作物收集包的可选项之一。</p>', NULL),
	(6620, 'crop', 'CR0013', '裁制', '01', '<p>玉米可通过缝纫机使用布料制成可染色的条纹衫。玉米可用于染色。放置在染料罐内时，玉米可用作<b>黄色</b>染料。放置在缝纫机内时，玉米还可用作<b>黄色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6621, 'crop', 'CR0013', '工匠物品', '01', '<p>请注意，本游戏并没有把油认定为工匠物品，所以油实际上并没有受益于工匠职业（即玩家并不能使用工匠技能提高油的售价）。</p>', NULL),
	(6622, 'crop', 'CR0013', '任务', '01', '<ul><li>玉米在秋季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有150和150友谊点。</li></ul>', NULL),
	(6623, 'crop', 'CR0013', '趣闻', '01', '<ul><li>普通品质的玉米只能卖出50，但2包玉米种子总共能卖出150的价格。这和玉米腌菜的价格相同(假设不受工匠技能加成)，但是在罐头瓶中生产玉米腌菜需要4000分钟，使用种子生产器生产玉米种子只需要20分钟。</li></ul>', NULL),
	(6624, 'crop', 'CR0030', '', '01', '<p><b>甜菜</b>是一种蔬菜类农作物，由甜菜种子生长6天后成熟。甜菜种子可以从绿洲商店购买。</p><p>一颗甜菜可以通过磨坊制成3份糖。</p>', NULL),
	(6625, 'crop', 'CR0030', '收集包', '01', '<p>均不需要。</p>', NULL),
	(6626, 'crop', 'CR0030', '裁制', '01', '<p>甜菜可通过缝纫机使用布料制成可染色的纽扣衬衫。甜菜可用于染色。放置在染料罐内时，甜菜可用作<b>紫色</b>染料。放置在缝纫机内时，甜菜还可用作<b>深紫色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6627, 'crop', 'CR0030', '任务', '01', '<ul><li>在神秘的齐任务的第二阶段，玩家需要将10个甜菜放在刘易斯镇长的冰箱里。</li></ul>', NULL),
	(6628, 'crop', 'CR0011', '', '01', '<p><b>咖啡豆</b>既是农作物也是可以种植的种子。咖啡豆在春季或夏季种植后，生长十天即可成熟（之后每两天可收获一次）。将5个咖啡豆放进小桶即可制作出咖啡。</p><p>灰尘精灵有很小的几率会掉落咖啡豆（大约1%）。旅行货车也会售卖咖啡豆，价格为2,500（25%的概率）或100 ~ 1,000（1.26%的概率）。</p><p>成熟后，每两天可以从每棵作物收获4个咖啡豆，有很小的随机概率获得更多的咖啡豆。初级肥料和高级肥料只影响第一次收获的咖啡豆。</p><p>要注意的是，游戏中咖啡豆不属于标准农作物。咖啡豆作为种子，它的售价不会因农耕人职业而受益。</p>', NULL),
	(6629, 'crop', 'CR0011', '多季节作物', '01', '<p>咖啡豆可以在春天和夏天种植。它在这两个季节的状态没有区别。</p><p>如果春季28日时地里有一棵咖啡豆植株（任何一个生长阶段），它将在夏季1日继续生长，就像没有发生什么变化一样。春季28日咖啡豆植株地下的肥料，在夏季1日不会消失。</p>', NULL),
	(6630, 'crop', 'CR0011', '工匠物品', '01', '<p>游戏不认为咖啡是工匠物品，所以它不会因工匠职业而受益。</p><p>为了制造出咖啡, 5个咖啡豆必须<b>从一个物品堆里</b>放入小桶。例如，不能将3个银质咖啡豆和2个金质咖啡豆放入小桶，因为不同品质不能互相叠加，而小桶一次要放入5个咖啡豆。</p>', NULL),
	(6631, 'crop', 'CR0011', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(6632, 'crop', 'CR0011', '裁制', '01', '<p>咖啡豆可通过缝纫机使用布料制成可染色的法兰绒上衣。咖啡豆可用于染色。放置在染料罐内时，咖啡豆可用作<b>橙色</b>染料。放置在缝纫机内时，咖啡豆还可用作<b>棕色</b>染料。</p>', NULL),
	(6633, 'crop', 'CR0011', '任务', '01', '<p>饲养有水滴鱼的鱼塘在数量从3提升到5的时候，可能会出现要求玩家提供5个咖啡豆的鱼塘任务。</p>', NULL),
	(6634, 'crop', 'CR0011', '趣闻', '01', '<ul><li>在春季的第一天种植一个豆子，然后将你所收获的豆子全部再次种植。到夏季13日的时候，咖啡豆将填满标准农场的3427个地块。如果你在第一年春季5日开始种植（可以在旅行商人那里买到咖啡豆的第一天），你能够在夏季17日（无加速效果下能够种植的最后一天）填满整个农场。</li><li>计算可以收获多少豆子的公式为 [2天前收获的豆子] + 4 × [10天前种下的种子数]</li></ul>', NULL),
	(6635, 'crop', 'CR0025', '', '01', '<p><b>夏季亮片</b>是一种花类农作物，由夏季亮片种子生长8天后获得。</p><p>如果将夏季亮片种植在蜂房的作用范围内，蜂房会产生夏季亮片蜂蜜，将蜂蜜的基础价从100增加到280。</p><p>偶尔可以从旅行货车以270 ~ 1,000的价格购买到夏季亮片。</p>', NULL),
	(6636, 'crop', 'CR0025', '收集包', '01', '<p>夏季亮片是茶水间（重新混合后）的 花园收集包的一个可选项。</p>', NULL),
	(6637, 'crop', 'CR0025', '裁制', '01', '<p>夏季亮片可通过缝纫机使用布料制成可染色的褶裙。夏季亮片可用于染色。放置在染料罐内时，夏季亮片可用作<b>黄色</b>染料。放置在缝纫机内时，夏季亮片还可用作<b>黄色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6638, 'crop', 'CR0025', '任务', '01', '<p>夏季亮片不用于任何任务。</p>', NULL),
	(6639, 'crop', 'CR0025', '提示', '01', '<ul><li>完全生长的夏季亮片有相同的概率变成六种不同颜色中的一种。不同颜色的夏季亮片会在物品栏或箱子中占用不同的槽位。六种不同的颜色和四种不同的作物品质使它最多可能占用24个槽位。</li></ul>', NULL),
	(6640, 'crop', 'CR0010', '', '01', '<p><b>郁金香</b>是一种花，也是一种农作物，可以由郁金香球茎生长6天后获得。</p><p>如果将郁金香种植在蜂房的作用范围内，蜂房会产生郁金香蜂蜜，将蜂蜜的基础价从100增加到160。</p><p>也可以用100 ~ 1,000向旅行货车购买。</p>', NULL),
	(6641, 'crop', 'CR0010', '收集包', '01', '<p>郁金香是茶水间（重新混合后）的 花园收集包的一个可选项。</p>', NULL),
	(6642, 'crop', 'CR0010', '配方', '01', '<p>郁金香不用于任何配方。</p>', NULL),
	(6643, 'crop', 'CR0010', '裁制', '01', '<p>郁金香可通过缝纫机使用布料制成可染色的短裙。郁金香可用于染色。放置在染料罐内时，郁金香可用作<b>红色</b>染料。放置在缝纫机内时，郁金香还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6644, 'crop', 'CR0010', '注意', '01', '<ul><li>郁金香有5种不同的颜色，长成各颜色的概率是相等的。不同颜色的郁金香会在箱子以及背包中占用不同的格子。五种颜色 ×四种品质 = 总共占用20个格子。</li></ul><ul><li>由于其利润率相对较小，所以最好以观赏或者送礼为目的种植郁金香。</li><li>郁金香低廉的价格、较短的生长周期、高额的能量值使其在游戏前期作为能量回复资源十分有用。</li></ul>', NULL),
	(6645, 'crop', 'CR0010', '任务', '01', '<p>郁金香不用于任何任务。</p>', NULL),
	(6646, 'crop', 'CR0043', '', '01', '<p><b>菠萝</b>是一种水果类农作物，是由菠萝种子经过14日生长成熟后结出的果实。成熟后，每隔7天都能收获一颗菠萝。</p><p>可以在每周二向姜岛商人以一个菠萝交易一个棕榈树墙饰。</p>', NULL),
	(6647, 'crop', 'CR0043', '任务', '01', '<ul><li>卡洛琳的特别任务："岛屿食材"任务期间可能会要求玩家收集并提交100个菠萝。</li><li>狮子鱼在鱼塘中的数量达到3时可能会需要1个菠萝。</li></ul>', NULL),
	(6648, 'crop', 'CR0043', '裁制', '01', '<p>菠萝可通过缝纫机使用布料制成可染色的农夫裤。菠萝还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(6649, 'crop', 'CR0043', '趣闻', '01', '<p>菠萝可以作为装饰品放入鱼缸中，这疑似是致敬了海绵宝宝。</p>', NULL),
	(6650, 'crop', 'CR0055', '', '01', '<p><b>水晶果</b>可在冬季通过采集获得，或经杀死矿井（40-80层）的灰尘精灵的掉落物获得，或是种植冬季种子获得。在冬季翻动垃圾桶也有概率找到水晶果。</p>', NULL),
	(6651, 'crop', 'CR0055', '收集包', '01', '<p>工艺室的 冬季采集收集包需要一个水晶果。</p>', NULL),
	(6652, 'crop', 'CR0055', '裁制', '01', '<p>水晶果可通过缝纫机使用布料制成上衣。水晶果还可用作染料罐和缝纫机的<b>蓝色</b>染料。</p>', NULL),
	(6653, 'crop', 'CR0055', '任务', '01', '<ul><li>水晶果可能在冬季期间随机出现在皮埃尔的杂货店门外的“布告栏”上，完成任务能获得 450 和 150 友谊点。</li><li>饲养有冰柱鱼的鱼塘在数量从5提升到7的时候，会出现要求玩家提供一个冬根的鱼塘求助任务。</li></ul>', NULL),
	(6654, 'crop', 'CR0045', '', '01', '<p><b>黄水仙</b>可以在春季的野外采集采集到，或是从春季种子里种出来。皮埃尔也会在花舞节以50的价格出售（限10个）。沙漠节期间，艾芙琳会以 15卡利科三花蛋的价格在商铺出售黄水仙（限5个）。在春季翻动垃圾桶也有概率找到黄水仙。无尽财富之雕像每年会在桑迪生日时（秋15日）生产一个普通品质的黄水仙。</p><p>尽管在现实中，黄水仙是一种花，但在游戏中，它被分类为“采集品”而不是“花”。</p>', NULL),
	(6655, 'crop', 'CR0045', '效果', '01', '<p>黄水仙可以吃，但它不会提供任何能量或生命值。</p>', NULL),
	(6656, 'crop', 'CR0045', '收集包', '01', '<p>它用于工艺室的 春季采集收集包.</p>', NULL),
	(6657, 'crop', 'CR0045', '裁制', '01', '<p>黄水仙可通过缝纫机使用布料制成上衣。黄水仙还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(6658, 'crop', 'CR0045', '任务', '01', '<ul><li>在春季，黄水仙会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励是 90 和 150友谊点。</li></ul>', NULL),
	(6659, 'crop', 'CR0016', '', '01', '<p><b>萝卜</b>是一种蔬菜农作物，由萝卜种子生长6天后成熟。</p>', NULL),
	(6660, 'crop', 'CR0016', '裁制', '01', '<p>萝卜可通过缝纫机使用布料制成可染色的 男款背心 或女款背心。萝卜可用于染色。放置在染料罐内时，萝卜可用作<b>红色</b>染料。放置在缝纫机内时，萝卜还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6661, 'crop', 'CR0016', '任务', '01', '<ul><li>萝卜在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励为270和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个萝卜（仅在夏季）。</li></ul>', NULL),
	(6662, 'crop', 'CR0032', '', '01', '<p><b>葡萄</b>是一种水果，既属于标准农作物，也是一种采集品。</p><ul><li>在夏季，葡萄可以在星露谷中采集得到，或用夏季种子种植得到。具体出现概率为：边远森林（占该地点所有可采集物品的62%）、深山（62%）、巴士站（27%）、铁路（27%）、森林农场地图（25%）。在夏季翻动垃圾桶也有概率找到葡萄。</li><li>在秋季，葡萄可以由葡萄种子种植得到，成熟需要十天，之后每三天收获一次。</li></ul><p>秋季作为农作物生长而成的葡萄售价和其他农作物一样受到农耕人职业加成影响。然而，夏季由采集或夏季种子得来的葡萄售价不受农耕人职业影响。如果将农作物葡萄和采集葡萄堆叠后售卖，将全部视为农作物葡萄。（详见水果条目。</p><p>除此之外，两种来源的葡萄没有其他区别，在用于制作、礼物、收集包、食谱和任务时可以通用。</p>', NULL),
	(6663, 'crop', 'CR0032', '生长阶段', '01', '<p>葡萄植株在任何生长阶段都不能行走通过，除了枯萎状态。</p>', NULL),
	(6664, 'crop', 'CR0032', '收集', '01', '<p>葡萄可用于工艺室的 夏季采集收集包.</p>', NULL),
	(6665, 'crop', 'CR0032', '裁制', '01', '<p>葡萄可通过缝纫机使用布料制成可染色的短裤。葡萄还可用作染料罐和缝纫机的<b>紫色</b>染料。</p>', NULL),
	(6666, 'crop', 'CR0032', '任务', '01', '<ul><li>葡萄在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有240和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个葡萄（仅在秋季）。</li></ul>', NULL),
	(6667, 'crop', 'CR0042', '', '01', '<p><b>齐瓜</b>是一种水果，由齐豆生长4天而成。只有当接到“齐先生的作物”任务时才可以开始种植齐瓜。齐瓜可以生长为巨大作物。</p><p>任务完成或时间截止当天夜里，玩家所有宝箱和背包中的齐瓜及齐豆会全部消失，包括生长中的齐瓜作物、祝尼魔箱中、罐头瓶中、小桶中和马龙失物招领列表中等各个地方的齐豆和齐瓜，但巨大齐瓜除外。</p><p>齐瓜只有普通品质。</p>', NULL),
	(6668, 'crop', 'CR0042', '生长阶段', '01', '<p>齐瓜可以长成巨大作物。</p>', NULL),
	(6669, 'crop', 'CR0042', '收集包', '01', '<p>该作物不用于任何收集包中。</p>', NULL),
	(6670, 'crop', 'CR0042', '任务', '01', '<ul><li>齐先生要求玩家在28天内出售500个齐瓜。“齐先生的作物”任务奖励为100.</li></ul>', NULL),
	(6671, 'crop', 'CR0042', '裁制', '01', '<p>齐瓜可通过缝纫机使用布料制成齐氏面具。齐瓜还可用作染料罐和缝纫机的<b>蓝色</b>染料。</p>', NULL),
	(6672, 'crop', 'CR0042', '趣闻', '01', '<ul><li>在早期版本齐瓜的介绍栏中显示齐先生要求你出售1000个这种奇怪的瓜，但实际上他只要求你出售500个齐瓜。有可能是因为ConcernedApe在开发时降低了完成难度，现版本中已被修复。</li><li>完成任务不会移除皮埃尔的杂货店库存中的齐瓜，额外的齐瓜可以在完成任务前卖到杂货店，第二天以每个1的价格买回来，这使得未来齐先生的作物任务变得更加容易。<ul><li>上述bug已于1.6版本修复。</li></ul></li><li>通常收获巨大作物会掉落15~21个普通品质的作物，而收获巨大齐瓜则会获得20~25个齐瓜。（待测试）</li><li>不在齐先生的作物任务期间，巨大齐瓜也不会消失。但是此时收获巨大齐瓜只能获得一个谜之盒。<ul><li>第一次在非任务期间收获巨大齐瓜，左下角会弹出以下信息：“齐先生：哈哈，你以为随时都能拿到我的珍贵水果吗？年轻人啊，很遗憾......只有挑战期间可以。”</li><li>但之后重新接取齐先生的作物任务，巨大齐瓜还是可以正常收获。</li></ul></li></ul>', NULL),
	(6673, 'crop', 'CR0048', '', '01', '<p><b>香味浆果</b>是一种水果，可以在夏季经采集获得，或种植夏季种子获得。旅行货车也会以240 ~ 1,000的价格随机出售。沙漠节期间，德米特里厄斯会在其商铺以 10 卡利科三花蛋的价格出售香味浆果（限3个）。在夏季翻动垃圾桶也有概率找到香味浆果。</p><p>把一个香味浆果放入种子生产器可以获得1~3个夏季种子。</p>', NULL),
	(6674, 'crop', 'CR0048', '收集包', '01', '<p>工艺室的 夏季采集收集包需要一个香味浆果。</p>', NULL),
	(6675, 'crop', 'CR0048', '裁制', '01', '<p>香味浆果可通过缝纫机使用布料制成仅袖子可染色的 男款绿色纽扣汗衫 或女款绿色纽扣汗衫。香味浆果还可用作染料罐和缝纫机的<b>橙色</b>染料。</p>', NULL),
	(6676, 'crop', 'CR0048', '任务', '01', '<ul><li>香味浆果在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有 240 和150友谊点。</li></ul>', NULL),
	(6677, 'crop', 'CR0026', '', '01', '<p><b>苋菜</b>是一种蔬菜类农作物，由苋菜种子生长7天后成熟。</p><p>苋菜需要使用镰刀来收获。</p>', NULL),
	(6678, 'crop', 'CR0026', '收集包', '01', '<p>用于布告栏（重新混合后）的 染料收集包。</p>', NULL),
	(6679, 'crop', 'CR0026', '裁制', '01', '<p>苋菜可通过缝纫机使用布料制成可染色的短上衣。苋菜可用于染色。放置在染料罐内时，苋菜可用作<b>紫色</b>染料。放置在缝纫机内时，苋菜还可用作<b>苍紫罗兰色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6680, 'crop', 'CR0026', '任务', '01', '<ul><li>“讨牛欢心”：第1年秋季的第3天，玛妮会给玩家写一封信，说她需要一个苋菜。奖励为500和1心友谊。</li></ul>', NULL),
	(6681, 'crop', 'CR0051', '', '01', '<p><strong>野梅</strong>是一种水果，在秋季可以通过采集在星露谷找到。种植秋季种子也有几率获得野梅。在秋季翻动垃圾桶也有概率找到野梅。</p>', NULL),
	(6682, 'crop', 'CR0051', '收集包', '01', '<ul><li>野梅可用于工艺室的 秋季采集收集包，数量需求为1。</li><li>野梅是布告栏（重新混合后）的 采集者收集包的可选项之一，数量需求为20。</li></ul>', NULL),
	(6684, 'crop', 'CR0051', '裁制', '01', '<p>野梅可通过缝纫机使用布料制成可染色的农夫裤。野梅可用于染色。放置在染料罐内时，野梅可用作<strong>紫色</strong>染料。放置在缝纫机内时，野梅还可用作<strong>深紫色</strong>染料。</p>', NULL),
	(6685, 'crop', 'CR0051', '任务', '01', '<ul><li>野梅在秋季出现在皮埃尔的杂货店外边的布告栏上的随机任务中，完成任务的奖励是 240 和 150 友谊点 。</li></ul>', NULL),
	(6686, 'crop', 'CR0002', '', '01', '<p><b>土豆</b>是一种蔬菜农作物，由土豆种子生长6天后成熟。</p>', NULL),
	(6687, 'crop', 'CR0002', '生长阶段', '01', '<p>当收获时，每个土豆植物都会产生1个土豆，并且随机生产额外的土豆，额外的土豆基于每日运气。</p>', NULL),
	(6688, 'crop', 'CR0002', '收集包', '01', '<p>它用于茶水间的 春季作物收集包。</p>', NULL),
	(6689, 'crop', 'CR0002', '裁制', '01', '<p>土豆可通过缝纫机使用布料制成可染色的衬衫皮带。土豆可用于染色。放置在染料罐内时，土豆可用作<b>橙色</b>染料。放置在缝纫机内时，土豆还可用作<b>棕色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6690, 'crop', 'CR0002', '任务', '01', '<ul><li>土豆在春季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有240和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家用出货箱收获并售出100个土豆（仅在春季）。</li></ul>', NULL),
	(6691, 'crop', 'CR0019', '', '01', '<p><b>啤酒花</b>是一种农作物。它由啤酒花种子经过11天后长成。</p><p>啤酒花可以放进小桶里酿造淡啤酒。淡啤酒的日收益是游戏中最高之一，仅次于杨桃果酒，但是生产淡啤酒的工作量大得多。</p>', NULL),
	(6692, 'crop', 'CR0019', '生长阶段', '01', '<p>无法行走穿过任意生长阶段的啤酒花，但可以穿过已枯萎的啤酒花。</p>', NULL),
	(6693, 'crop', 'CR0019', '收集包', '01', '<p>可以用于完成工艺室（重新混合后）的 野生药材收集包。</p>', NULL),
	(6694, 'crop', 'CR0019', '配方', '01', '<p>不用于任何配方。</p>', NULL),
	(6695, 'crop', 'CR0019', '工匠物品', '01', '<p>啤酒花可以放入小桶中生产淡啤酒。淡啤酒可以放入木桶中陈酿来提升品质。</p>', NULL),
	(6696, 'crop', 'CR0019', '裁制', '01', '<p>啤酒花可通过缝纫机使用布料制成可染色的衬衫皮带。啤酒花还可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
	(6697, 'crop', 'CR0019', '提示', '01', '<p>啤酒花可以提供单个季节内第二高的耕种技能经验值（102至126，取决于成熟速度），仅次于成熟的上古水果植株。第一年夏种植大量啤酒花可以快速提升耕种等级。</p><p>用种子生产器将其制成啤酒花种子可以得到比直接出售更高的利润，而且有概率获得上古种子。制成淡啤酒或腌制啤酒花利润更高，但种子生产器耗时更短，仅需游戏内20分钟。</p>', NULL),
	(6698, 'crop', 'CR0024', '', '01', '<p><b>虞美人花</b>是一种农作物，由虞美人种子经过7天后长成。与其他的花不同的是，虞美人花是一种NPC普遍讨厌的礼物。潘妮是唯一的例外，她最爱虞美人花。</p><p>如果将虞美人花种植在蜂房的作用范围内 (水平方向5格，竖直方向5格)，蜂房会产生虞美人花蜂蜜，将蜂蜜的基础价从100增加到380。</p><p>偶尔可以从旅行货车以420 ~ 1,000购买。</p>', NULL),
	(6699, 'crop', 'CR0024', '收集包', '01', '<p>用于布告栏的 厨师收集包.</p>', NULL),
	(6700, 'crop', 'CR0024', '裁制', '01', '<p>虞美人花可通过缝纫机使用布料制成可染色的褶裙。虞美人花可用于染色。放置在染料罐内时，虞美人花可用作<b>橙色</b>染料。放置在缝纫机内时，虞美人花还可用作<b>橙色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6701, 'crop', 'CR0024', '提示', '01', '<ul><li>成熟的虞美人花有三种颜色，它们出现的几率是一样的。</li></ul><ul><li>不同颜色的虞美人花各自占用一个存储格，最多的情况下，它将占用3种颜色×4种品质=12个格子。</li></ul>', NULL),
	(6702, 'crop', 'CR0024', '趣闻', '01', '<ul><li>该物品名称曾发生变更。物品英文名“Poppy”、描述中的“有药用价值”以及外观均源自该物品的原名“罂粟”。</li></ul>', NULL),
	(6703, 'crop', 'CR0007', '', '01', '<p><b>大黄</b>是一种水果类农作物，是由大黄种子经过13日生长成熟后结出的果实。大黄种子只能从绿洲购买，或是偶尔能从旅行货车处购买。</p>', NULL),
	(6704, 'crop', 'CR0007', '收集包', '01', '<p>它不被用于任何收集包。</p>', NULL),
	(6705, 'crop', 'CR0007', '裁制', '01', '<p>大黄可通过缝纫机使用布料制成仅袖子可染色的 男款绿色纽扣汗衫 或女款绿色纽扣汗衫。大黄可用于染色。放置在染料罐内时，大黄可用作<b>红色</b>染料。放置在缝纫机内时，大黄还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6706, 'crop', 'CR0007', '任务', '01', '<p>大黄不被用于任何任务。</p>', NULL),
	(6707, 'crop', 'CR0007', '趣闻', '01', '<p>现实生活中，大黄是一种以茎为食的植物。</p>', NULL),
	(6708, 'crop', 'CR0027', '', '01', '<p><b>小白菜</b>是一种蔬菜类农作物，由小白菜种子生长4天后成熟。</p>', NULL),
	(6709, 'crop', 'CR0027', '收集包', '01', '<p>它不用于任何收集包。</p>', NULL),
	(6710, 'crop', 'CR0027', '裁制', '01', '<p>小白菜可通过缝纫机使用布料制成可染色的纽扣衬衫。小白菜可用于染色。放置在染料罐内时，小白菜可用作<b>绿色</b>染料。放置在缝纫机内时，小白菜还可用作<b>绿色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6711, 'crop', 'CR0027', '任务', '01', '<ul><li>小白菜可能会秋季在皮埃尔的杂货店之外的求助＂布告栏＂中随机请求，以获得240的奖励和150 友谊点。</li><li>刘易斯的特别任务：“农作物订单”在秋季可能会要求玩家收集并通过出货箱出售100个小白菜。</li></ul>', NULL),
	(6712, 'crop', 'CR0044', '', '01', '<p><b>野山葵</b>可在春季通过采集获得，或通过种植春季种子获得。在春季翻动垃圾桶也有概率找到。把野山葵放入种子生产器会产出1-3个春季种子。</p>', NULL),
	(6713, 'crop', 'CR0044', '收集包', '01', '<p>用于工艺室的 春季采集收集包</p>', NULL),
	(6715, 'crop', 'CR0044', '裁制', '01', '<p>野山葵可通过缝纫机使用布料制成"Good Grief" 衫。野山葵可用于染色。放置在染料罐内时，野山葵可用作<strong>橙色</strong>染料。放置在缝纫机内时，野山葵还可用作<strong>棕色</strong>染料。</p>', NULL),
	(6716, 'crop', 'CR0044', '任务', '01', '<ul><li>野山葵可能在春季期间随机出现在"需要帮助" 公告板上，完成任务能获得150和150点友谊值。</li></ul>', NULL),
	(6717, 'crop', 'CR0046', '', '01', '<p><strong>韭葱</strong>在春季可以通过采集找到，或者可以通过种植春季种子获得。在春季翻动垃圾桶也有概率找到韭葱。无尽财富之雕像会在乔治生日时（秋24日）生产一个普通品质的韭葱。</p><p>韭葱在深山和边远森林地区很常见，这两个地区58%的可采集物品都是韭葱。除此之外，巴士站（20%）、铁路（20%）和森林农场（25%）三个地点也能采集到韭葱。</p>', NULL),
	(6718, 'crop', 'CR0046', '收集包', '01', '<p>韭葱是工艺室的 春季采集收集包中的一个物品。</p>', NULL),
	(6720, 'crop', 'CR0046', '裁制', '01', '<p>韭葱可通过缝纫机使用布料制成黄绿色束腰上衣。韭葱可用于染色。放置在缝纫机内时，韭葱可用作<strong>白色</strong>染料。</p>', NULL),
	(6721, 'crop', 'CR0046', '任务', '01', '<ul><li>在任务"奶奶的礼物"中，艾芙琳会在第二年的春季的15号给玩家寄一封信，请求玩家帮忙提供1颗韭葱。奖励是500和1心友谊。</li><li>韭葱在春季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有180和150友谊点。</li><li>在刘易斯房子外的特别任务板上随机出现特别任务：给乔治的礼物，艾芙琳会要求玩家收集并提供12颗韭葱。</li></ul>', NULL),
	(6722, 'crop', 'CR0041', '', '01', '<p><b>芋头</b>是一种蔬菜类农作物，由芋头块茎经过10天后长成。如果在水边种植，则生长只需7天且不需要浇水。</p>', NULL),
	(6723, 'crop', 'CR0041', '裁制', '01', '<p>芋头可通过缝纫机使用布料制成可染色的衬衫皮带。芋头可用于染色。放置在染料罐内时，芋头可用作<b>橙色</b>染料。放置在缝纫机内时，芋头还可用作<b>棕色</b>染料。</p>', NULL),
	(6724, 'crop', 'CR0041', '任务', '01', '<ul><li>卡洛琳的特别任务："岛屿食材"任务期间可能会要求玩家收集并提交100个芋头。</li><li>蓝铁饼鱼在鱼塘中的数量达到3时会需要3个芋头，在数量达到5时会需要10个芋头。</li></ul>', NULL),
	(6725, 'crop', 'CR0041', '趣闻', '01', '<p>收获芋头后，芋头所在的地块将在一天的剩余时间内保持湿润。</p>', NULL),
	(6726, 'crop', 'CR0003', '', '01', '<p><b>花椰菜</b>是一种蔬菜类农作物，由花椰菜种子经过12天的时间长成。花椰菜是仅有的五种可以长成巨大作物的农作物之一。</p>', NULL),
	(6727, 'crop', 'CR0003', '生长阶段', '01', '<p>花椰菜可以长成巨大作物。</p>', NULL),
	(6728, 'crop', 'CR0003', '收集包', '01', '<p>花椰菜可用于茶水间的 春季作物收集包。</p><p>花椰菜也是茶水间（重新混合后）的 高品质作物收集包的一个可选项，需要5个金星的花椰菜。</p>', NULL),
	(6729, 'crop', 'CR0003', '裁制', '01', '<p>花椰菜可通过缝纫机使用布料制成可染色的衬衫皮带。花椰菜可用于染色。放置在缝纫机内时，花椰菜可用作<b>白色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6730, 'crop', 'CR0003', '任务', '01', '<ul><li>乔迪会在春季的第19天，通过信件向你请求一个新鲜的花椰菜（"乔迪的请求"任务）。回报是350和1心友谊。</li><li>春季时，花椰菜会随机出现在皮埃尔的杂货店外面的“求助”板上，回报是525和150点友谊值。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个花椰菜（仅在春季）。</li></ul>', NULL),
	(6731, 'crop', 'CR0014', '', '01', '<p><b>甜瓜</b>是一种水果类农作物，由甜瓜种子生长 12 天后成熟。</p><p>甜瓜是仅有的五种可以长成巨大作物的农作物之一。</p>', NULL),
	(6732, 'crop', 'CR0014', '收集包', '01', '<p>甜瓜可用于茶水间的 夏季作物收集包，它同时也是茶水间的 高品质作物收集包的可选收集项目之一。</p>', NULL),
	(6733, 'crop', 'CR0014', '裁制', '01', '<p>甜瓜可通过缝纫机使用布料制成可染色的短裤。甜瓜可用于染色。放置在染料罐内时，甜瓜可用作<b>红色</b>染料。放置在缝纫机内时，甜瓜还可用作<b>粉色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6734, 'crop', 'CR0014', '任务', '01', '<ul><li>在"谷物研究"中，在夏季第20天时，德米特里厄斯会给你写封信，希望你能给他带个甜瓜。奖励是550和1 心友谊。</li><li>甜瓜在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有 750和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个甜瓜（仅在夏季）。</li><li>可用于解开饕餮青蛙的任务。</li></ul>', NULL),
	(6735, 'crop', 'CR0020', '', '01', '<p><strong>小麦</strong>是一种蔬菜。在4天之后从小麦种子中生长。小麦需要使用镰刀来收获，收割时可能会产出干草。</p><p>小麦可以通过小桶制成啤酒，还可以通过磨坊制成1份大麦粉。但不能直接用于烹饪。</p>', NULL),
	(6736, 'crop', 'CR0020', '多季节作物', '01', '<p>小麦可以在夏季和秋季种植。</p><p>如果一个小麦（在任何生长阶段）在夏天的第二十八日在地上，它将在秋天的第一天继续生长。夏季的第二十八日地上的小麦不会消失。完全种植的小麦直到秋天第一天被收割，以防止肥料和耕种土壤消失。</p>', NULL),
	(6737, 'crop', 'CR0020', '收集包', '01', '<p>布告栏的 饲料收集包需要10个小麦。</p>', NULL),
	(6738, 'crop', 'CR0020', '手工品', '01', '<p>将小麦放入小桶中生产啤酒。啤酒放在木桶中可改进品质。</p>', NULL),
	(6739, 'crop', 'CR0020', '任务', '01', '<ul><li>小麦在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有75和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个小麦（仅在夏季）。</li><li>可用于解开饕餮青蛙的任务。</li></ul>', NULL),
	(6740, 'crop', 'CR0020', '裁制', '01', '<p>小麦可通过缝纫机使用布料制成可染色的 男款短背心 或女款短背心。小麦还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(6741, 'crop', 'CR0036', '', '01', '<p><b>宝石甜莓</b>是一种农作物，由稀有种子在24天后长成。它不属于水果和蔬菜，因此不能放入小桶或罐头瓶，也不受收集者或植物学家技能影响。</p><p>稀有种子可在旅行货车购买，春季和夏季价格1,000，其他季节600 ~ 1,000。也可以把宝石甜莓放入种子生产器来获得稀有种子。 </p><p>尽管宝石甜莓售价高昂，但作为礼物时，所有村民的反应都是一般。</p><p>如果你把一个宝石甜莓送给秘密森林的老师傅香炸奶酪卷，你将会得到一个星之果实，吃下后你的最大能量将永久提高34点。</p>', NULL),
	(6742, 'crop', 'CR0036', '提示', '01', '<p>种子生产器用每个宝石甜莓制造1-3个稀有种子的几率为97.5%。 因为每个宝石甜莓在普通，银星和金星品质下分别价值3,000，3,750，和4,500，而由种子生产器制造的稀有种子的成本至少是每个1,000(3,000 ÷ 3)，至多是每个4,500(4,500 ÷ 1)，所以从旅行货车处购买每个1,000的稀有种子更划算。</p>', NULL),
	(6743, 'crop', 'CR0036', '收集包', '01', '<p>宝石甜莓是茶水间（重新混合后）的 稀有作物收集包的一个可选项。</p>', NULL),
	(6744, 'crop', 'CR0036', '裁制', '01', '<p>宝石甜莓可通过缝纫机使用布料制成白色头巾。宝石甜莓还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(6745, 'crop', 'CR0036', '任务', '01', '<p>宝石甜莓不用于任何任务。</p>', NULL),
	(6746, 'crop', 'CR0038', '', '01', '<p><strong>仙人掌果子</strong>是一种可以在卡利科沙漠通过采集获得的水果。也可以在周二到绿洲以150的价格购买，或是随机在旅行货车以225 ~ 1,000的价格购买。解锁沙漠后，翻动任何垃圾桶也有概率找到。无尽财富之雕像会在山姆生日时（夏17日）生产一个普通品质的仙人掌果子。</p><p>仙人掌果子也是一种水果类农作物，由仙人掌种子生长12天后成熟，但只有通过种植收获的仙人掌果子才能受到农耕人职业的加成。</p>', NULL),
	(6747, 'crop', 'CR0038', '收集包', '01', '<p>仙人掌果子可用于工艺室的 异国情调采集收集包，也可用于布告栏（重新混合后）的 染料收集包。</p>', NULL),
	(6748, 'crop', 'CR0038', '配方', '01', '<p>仙人掌果子不用于任何配方中。</p>', NULL),
	(6749, 'crop', 'CR0038', '建筑', '01', '<p>仙人掌果子可用于在法师塔处建造沙漠图腾柱。</p>', NULL),
	(6750, 'crop', 'CR0038', '裁制', '01', '<p>仙人掌果子可通过缝纫机使用布料制成可染色的 男款80\'s 衬衣 或女款80\'s 衬衣。仙人掌果子还可用作染料罐和缝纫机的<strong>绿色</strong>染料。</p>', NULL),
	(6751, 'crop', 'CR0038', '任务', '01', '<ul><li>当玩家完成金库收集包后，有可能在皮埃尔的杂货店外面的布告栏处接取到有关仙人掌果子的求助任务，报酬是225和150点友谊。</li><li>沙鱼或蝎鲤鱼的鱼塘在容量从1提升到3的时候，可能会出现要求玩家提供3个仙人掌果子的鱼塘任务。</li></ul>', NULL),
	(6752, 'crop', 'CR0018', '', '01', '<p><b>西红柿</b>是一种蔬菜类农作物，由西红柿种子生长11天后成熟。成熟后，每4天可以收获一次。</p>', NULL),
	(6753, 'crop', 'CR0018', '生长阶段', '01', '<p>当收获时，每个西红柿植株每4天产出1个西红柿，有5%的概率产出更多的西红柿。</p>', NULL),
	(6754, 'crop', 'CR0018', '收集包', '01', '<p>它用于茶水间的 夏季作物收集包.</p>', NULL),
	(6755, 'crop', 'CR0018', '配方', '01', '<p>除调料、牛奶外西红柿是配方中使用最多的农作物，共八个配方。</p>', NULL),
	(6756, 'crop', 'CR0018', '任务', '01', '<ul><li>西红柿会随机出现在夏季皮埃尔的杂货店外面的"求助"板，可以获得180和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个西红柿（仅在夏季）。</li></ul>', NULL),
	(6757, 'crop', 'CR0018', '裁制', '01', '<p>西红柿可通过缝纫机使用布料制成番茄T恤。西红柿可用于染色。放置在染料罐内时，西红柿可用作<b>红色</b>染料。放置在缝纫机内时，西红柿还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6758, 'crop', 'CR0018', '趣闻', '01', '<ul><li>西红柿的模棱两可的定位——水果还是蔬菜，是德米特里厄斯的6心事件的主题。</li></ul><ul><li>遗失之书中的“由巴之书摘录”记载："种子萌发了...<b>11天之后，藤蔓结出了果子</b>...世界就是这样形成的。"</li></ul>', NULL),
	(6759, 'crop', 'CR0037', '', '01', '<p><b>上古水果</b>是一种水果类农作物，由上古种子生长28天后成熟。成熟后，每7天可以收获一次。</p><p>注：若要使其利润最大化，请在春季第一天种植或是在温室、姜岛中种植。</p>', NULL),
	(6760, 'crop', 'CR0037', '作物生长日历', '01', '<p style="margin: 0;">不考虑肥料和农业学家技能加成的情况下，如果在某个季节的第一天播种，那么需要到下一个季节的第一天才能首次收获。生长激素和技能仅能缩短第一次收获的时间。</p>', NULL),
	(6761, 'crop', 'CR0037', '收集包', '01', '<p>5个金星品质的上古水果是 失踪的收集包的一个可选项。</p><p>上古水果也是茶水间（重新混合后）的 稀有作物收集包的一个可选项。</p>', NULL),
	(6762, 'crop', 'CR0037', '配方', '01', '<p>它不用于任何配方。</p>', NULL),
	(6763, 'crop', 'CR0037', '裁制', '01', '<p>上古水果可通过缝纫机使用布料制成可染色的精灵裤。上古水果可用于染色。放置在染料罐内时，上古水果可用作<b>蓝色</b>染料。放置在缝纫机内时，上古水果还可用作<b>青色</b>染料。</p>', NULL),
	(6764, 'crop', 'CR0037', '任务', '01', '<p>它不用于任何任务。</p>', NULL),
	(6765, 'crop', 'CR0012', '', '01', '<p><b>未碾米</b>是一种蔬菜作物，由稻苗经6-8天长成。使用镰刀收获。种植在农场池塘周围三格内会被自动灌溉，无需浇水。</p><p>除了果汁和腌菜，玩家也可以使用磨坊来将未碾米加工成大米。置入磨坊后，每个未碾米产出1个大米。大米将在下一天早晨出现在风车右侧的容器中。</p>', NULL),
	(6766, 'crop', 'CR0012', '生长阶段', '01', '<p>水稻有10%的可能性会产出更多的未碾米。当玩家收割水稻时，每株水稻基础产出固定为1个未碾米（平均收获1.111个未碾米）。而当玩家耕种等级为10级或更高时，每株水稻基础产出变为概率相同的1或2个未碾米（平均收获1.611个未碾米）。</p>', NULL),
	(6767, 'crop', 'CR0012', '收集包', '01', '<p>未碾米不用于任何收集包。</p>', NULL),
	(6768, 'crop', 'CR0012', '裁制', '01', '<p>未碾米可通过缝纫机使用布料制成可染色的衬衫皮带。未碾米可用于染色。放置在染料罐内时，未碾米可用作<b>橙色</b>染料。放置在缝纫机内时，未碾米还可用作<b>棕色</b>染料。</p>', NULL),
	(6769, 'crop', 'CR0012', '任务', '01', '<ul><li>未碾米会在春季随机出现于“需要帮助”板，奖励为 90 和 150友谊点。</li></ul>', NULL),
	(6770, 'crop', 'CR0012', '备注', '01', '<ul><li>如果种植从皮埃尔的杂货店买到的稻苗，只有售卖金星的未碾米才能获得利润。在农耕人职业加成下，银星的未碾米也有1的利润。除此以外，用磨坊将未碾米制作成大米和用罐头瓶制作腌菜都是有利润的。</li><li>当你在已灌溉的土壤方格中收割未碾米的当天，该土壤方格会保留灌溉的水。当天你在那里种植的任何植物都不需要浇水，同样的作物还有芋头。</li></ul>', NULL),
	(6771, 'crop', 'CR0021', '', '01', '<p><b>杨桃</b>是一种水果类农作物，是由杨桃种子经过13日生长成熟后结出的果实。杨桃种子可从绿洲商店购买。以杨桃为原料制作的工匠产品在游戏中的售价是最高的。</p><p>在夏威夷宴会上，可以在皮埃尔的货摊上花费3,000购买1个杨桃。每次节日只能购买1个。</p>', NULL),
	(6772, 'crop', 'CR0021', '收集包', '01', '<p>杨桃是布告栏（重新混合后）的 染料收集包的收集项目之一。</p>', NULL),
	(6773, 'crop', 'CR0021', '建筑物', '01', '<p>修建祝尼魔屋需要杨桃，可以到法师塔向法师付款，让他给玩家建造一个祝尼魔屋。</p>', NULL),
	(6774, 'crop', 'CR0021', '裁制', '01', '<p>杨桃可通过缝纫机使用布料制成可染色的精灵裤。杨桃还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(6775, 'crop', 'CR0021', '任务', '01', '<ul><li>“士兵的星星”：在第2年夏季的15日，肯特会通过信件向玩家索要一个杨桃，报酬是500和1心友谊。</li></ul>', NULL),
	(6776, 'crop', 'CR0056', '', '01', '<p><b>雪山药</b>是一个只能在冬季获得的采集物品。自然生成的雪山药不会出现在地面上，而是深藏在雪泥之下，要获得雪山药，必须使用锄头进行挖掘（无法通过其他工具获取，甚至炸弹都不行）。</p><p>获得雪山药几率最高的方式是挖掘远古斑点（11%~16%，取决于地图）。在任意户外地点，挖掘可耕种地块时也有4%的几率获得一个雪山药。农场上例外，只能在挖掘远古斑点时获得雪山药，挖掘普通可耕种地块时无法获得雪山药；沙漠中也无法获得雪山药。经锄头挖掘获得的雪山药不会提供任何经验值。</p><p>雪山药也可以通过种植冬季种子获得，在这种情况下，它算作野生种子作物：此时雪山药生长在地面上，可以在不使用工具的情况下收获，每摘取一个雪山药获得7点采集经验值，并且受收集者和植物学家职业影响。</p><p>在冬季翻动垃圾桶也有概率找到雪山药。</p>', NULL),
	(6777, 'crop', 'CR0056', '收集包', '01', '<p>雪山药用于工艺室的 冬季采集收集包。</p>', NULL),
	(6778, 'crop', 'CR0056', '裁制', '01', '<p>雪山药可通过缝纫机使用布料制成上衣。雪山药可用于染色。放置在缝纫机内时，雪山药可用作<b>白色</b>染料。</p>', NULL),
	(6779, 'crop', 'CR0056', '任务', '01', '<ul><li>需要雪山药的求助任务可能在冬季期间随机出现在皮埃尔的杂货店外的“布告栏”上，报酬是300和150友谊点。</li></ul>', NULL),
	(6780, 'crop', 'CR0015', '', '01', '<p><b>辣椒</b>是一种水果类农作物，由辣椒种子生长5天后成熟。成熟后，每3天可以收获一次。</p>', NULL),
	(6781, 'crop', 'CR0015', '生长阶段', '01', '<p>当辣椒植株成熟后，每株辣椒每三天会产出1个辣椒，每株植株有随机的概率产出更多的辣椒。</p>', NULL),
	(6782, 'crop', 'CR0015', '收集包', '01', '<p>可以用来完成茶水间的 夏季作物收集包.</p>', NULL),
	(6783, 'crop', 'CR0015', '裁制', '01', '<p>辣椒可通过缝纫机使用布料制成可染色的短裤。辣椒可用于染色。放置在染料罐内时，辣椒可用作<b>红色</b>染料。放置在缝纫机内时，辣椒还可用作<b>红色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6784, 'crop', 'CR0015', '任务', '01', '<ul><li>在夏季的25号，乔治会写信给玩家，要求提供一个辣椒。任务完成的奖励是200和1心好感度。</li><li>在夏季，辣椒会出现在皮埃尔的杂货店外边的布告栏上的随机任务中。任务完成的奖励是120和 150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个辣椒（仅在夏季）。</li></ul>', NULL),
	(6785, 'crop', 'CR0040', '', '01', '<p><b>茶叶</b>是一种蔬菜类农作物。种植后，可以在春季、夏季、秋季（如果在室内种植则包括冬季）的最后一周（从22日周一开始），每天从茶树上收获。</p><p>茶叶可以放进小桶中制成绿茶，但如果要用于盈利且时间条件足够，放进罐头瓶收益更高。</p>', NULL),
	(6786, 'crop', 'CR0040', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(6787, 'crop', 'CR0040', '裁制', '01', '<p>茶叶可通过缝纫机使用布料制成可染色的衬衫皮带。茶叶还可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
	(6788, 'crop', 'CR0040', '工匠物品', '01', '<p>茶叶可以放进小桶中制作绿茶。</p>', NULL),
	(6789, 'crop', 'CR0040', '任务', '01', '<p>没有任何任务需要茶叶。</p>', NULL),
	(6790, 'crop', 'CR0009', '', '01', '<p><b>蓝爵</b>是一种花类农作物，由蓝爵士种子经过7天的时间长成。</p><p>如果将蓝爵种植在蜂房的作用范围内 (水平方向5格，竖直方向5格)，会产生蓝爵蜂蜜，将蜂蜜的基础价从100增加到200。</p><p>蓝爵可以从旅行货车以150 ~ 1,000购买。</p>', NULL),
	(6791, 'crop', 'CR0009', '收集包', '01', '<p>蓝爵是茶水间（重新混合后）的 花园收集包的一个可选项。</p>', NULL),
	(6792, 'crop', 'CR0009', '裁制', '01', '<p>蓝爵可通过缝纫机使用布料制成可染色的短裙。蓝爵可用于染色。放置在染料罐内时，蓝爵可用作<b>蓝色</b>染料。放置在缝纫机内时，蓝爵还可用作<b>蓝色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6793, 'crop', 'CR0009', '任务', '01', '<p>蓝爵不用于任何任务。</p>', NULL),
	(6794, 'crop', 'CR0009', '注释', '01', '<ul><li>蓝爵花有六种不同的颜色，长成不同颜色的概率是相等的。</li></ul><ul><li>不同颜色的蓝爵花会占用物品栏或宝箱里不同的格子。六种颜色 × 四种品质等级 = 24种可能使用的空间。</li></ul>', NULL),
	(6795, 'crop', 'CR0017', '', '01', '<p><b>红叶卷心菜</b>是一种蔬菜类农作物，由红叶卷心菜种子生长9天后成熟。</p><p>红叶卷心菜种子在第二年夏季开始才会在皮埃尔的杂货店中出售。在第二年以前，玩家可以通过旅行货车买到，或通过骷髅洞穴找到。</p>', NULL),
	(6796, 'crop', 'CR0017', '收集包', '01', '<p>红叶卷心菜可用于布告栏的 染料收集包。</p>', NULL),
	(6797, 'crop', 'CR0017', '裁制', '01', '<p>红叶卷心菜可通过缝纫机使用布料制成可染色的 男款背心 或女款背心。红叶卷心菜可用于染色。放置在染料罐内时，红叶卷心菜可用作<b>紫色</b>染料。放置在缝纫机内时，红叶卷心菜还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6798, 'crop', 'CR0017', '任务', '01', '<ul><li>红叶卷心菜会在夏季（第二年开始）随机出现在皮埃尔的杂货店外的“求助”板上，回报是780和150友谊值。</li></ul>', NULL),
	(6799, 'crop', 'CR0057', '', '01', '<p><b>番红花</b>是在冬季通过采集找到的花。番红花可在鹈鹕镇、铁路、巴士站、深山、煤矿森林或边远森林找到。也可通过种植冬季野生种子来获得番红花。在冬季翻动垃圾桶也有概率找到番红花。</p>', NULL),
	(6800, 'crop', 'CR0057', '治疗效果', '01', '<p>番红花是可食用的，但不会增加能量或健康。</p>', NULL),
	(6801, 'crop', 'CR0057', '收集包', '01', '<p>它用于工艺室的 冬季采集收集包.</p>', NULL),
	(6802, 'crop', 'CR0057', '裁制', '01', '<p>番红花可通过缝纫机使用布料制成上衣。番红花可用于染色。放置在染料罐内时，番红花可用作<b>紫色</b>染料。放置在缝纫机内时，番红花还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6803, 'crop', 'CR0057', '任务', '01', '<ul><li>番红花会在冬季出现在皮埃尔的杂货店外边的“布告栏”上的随机任务，完成任务可以获得 180 和 150 友谊点。</li></ul>', NULL),
	(6804, 'crop', 'CR0001', '', '01', '<p><b>防风草</b>是一种蔬菜农作物，由防风草种子生长4天后成熟。</p><p>无尽财富之雕像会在潘姆生日时（春18日）生产一个普通品质的防风草。</p>', NULL),
	(6805, 'crop', 'CR0001', '收集包', '01', '<p>防风草可以用于茶水间的 春季作物收集包和茶水间的 高品质作物收集包。</p>', NULL),
	(6806, 'crop', 'CR0001', '裁制', '01', '<p>防风草可通过缝纫机使用布料制成可染色的衬衫皮带。防风草可用于染色。放置在染料罐内时，防风草可用作<b>黄色</b>染料。放置在缝纫机内时，防风草还可用作<b>黄色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6807, 'crop', 'CR0001', '任务', '01', '<ul><li>你需要种植并收获一棵防风草来完成“开始”任务。</li><li>防风草会随机出现在春季皮埃尔的杂货店外面的"求助"板，回报是105和150友谊值。</li></ul>', NULL),
	(6808, 'crop', 'CR0001', '趣闻', '01', '<ul><li>游戏内的防风草实际上对应现实中的欧防风，而非中药防风草。二者名称相仿，但植物学上并无任何关联。</li></ul>', NULL),
	(6809, 'crop', 'CR0004', '', '01', '<p><b>青豆</b>是一种蔬菜类农作物，由青豆种子经过10天的时间长成。</p><p>这种作物需要使用一个棚架(自带)，并且成熟之后会继续结果。注意，棚架作物会阻挡玩家移动，所以种植时需要留有通道以便收获。</p>', NULL),
	(6810, 'crop', 'CR0004', '生长阶段', '01', '<p>在任何生长阶段，你都不能穿过青豆。除了已枯萎的青豆植株。</p>', NULL),
	(6811, 'crop', 'CR0004', '收集包', '01', '<p>青豆用于茶水间的 春季作物收集包。也可能是茶水间（重新混合后）的 高品质作物收集包的一个可选项，需要5个金星的青豆。</p>', NULL),
	(6812, 'crop', 'CR0004', '裁制', '01', '<p>青豆可通过缝纫机使用布料制成可染色的衬衫皮带。青豆可用于染色。放置在染料罐内时，青豆可用作<b>绿色</b>染料。放置在缝纫机内时，青豆还可用作<b>绿色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6813, 'crop', 'CR0004', '任务', '01', '<ul><li>春季时，青豆会随机出现在皮埃尔的杂货店外面的“求助”板上，回报是120和150点友谊值。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个青豆（仅在春季）。</li></ul>', NULL),
	(6814, 'crop', 'CR0029', '', '01', '<p><b>山药</b>是一种蔬菜类农作物，由山药种子生长10天后成熟。</p><p>山药也有3％的几率从掘地虫身上中掉落。</p>', NULL),
	(6815, 'crop', 'CR0029', '收集包', '01', '<p>它用于茶水间的 秋季作物收集包。</p>', NULL),
	(6816, 'crop', 'CR0029', '裁制', '01', '<p>山药可通过缝纫机使用布料制成可染色的纽扣衬衫。山药可用于染色。放置在染料罐内时，山药可用作<b>橙色</b>染料。放置在缝纫机内时，山药还可用作<b>棕色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6817, 'crop', 'CR0029', '任务', '01', '<ul><li>山药可能会秋季在皮埃尔的杂货店外面的“求助”布告栏上的随机请求，奖励为480和150 友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个山药（仅在秋季）。</li></ul>', NULL),
	(6818, 'crop', 'CR0047', '', '01', '<p><strong>蒲公英</strong>可以在春季通过采集获得，或者种下春季种子长成。也可以在花舞节中向皮埃尔购买（最多10个），沙漠节期间卡洛琳也会在其商铺以 10 卡利科三花蛋的价格出售蒲公英（限5个）。在春季翻动垃圾桶也有概率找到蒲公英。虽然它在现实生活中是一种花，但在游戏中，它被分类为“采集品”而不是“花”。它可以放入小桶制成果汁，售价为90金，如果选择了工匠技能则为126金。</p>', NULL),
	(6819, 'crop', 'CR0047', '收集包', '01', '<p>用于工艺室的 春季采集收集包</p>', NULL),
	(6821, 'crop', 'CR0047', '裁制', '01', '<p>蒲公英可通过缝纫机使用布料制成薄荷女式衬衫。蒲公英还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(6822, 'crop', 'CR0047', '任务', '01', '<ul><li>蒲公英可能在春季期间随机出现在皮埃尔的杂货店外的"需要帮助" 公告板上，报酬是120和150点友谊值。</li></ul>', NULL),
	(6823, 'crop', 'CR0052', '', '01', '<p><b>榛子</b>可在秋季通过采集获得，或通过种植秋季种子获得。在秋季15日-28日期间摇晃枫树，也有概率掉落榛子。沙漠节期间，肯特会在其商铺以 10 卡利科三花蛋的价格出售榛子（限5个）。在秋季翻动垃圾桶也有概率找到榛子。</p>', NULL),
	(6824, 'crop', 'CR0052', '收集包', '01', '<p>工艺室的 秋季采集收集包需要一个榛子。</p>', NULL),
	(6825, 'crop', 'CR0052', '配方', '01', '<h3>烹饪</h3><h3>打造</h3>', NULL),
	(6826, 'crop', 'CR0052', '裁制', '01', '<p>榛子可通过缝纫机使用布料制成棕色背带裤。榛子可用于染色。放置在染料罐内时，榛子可用作<b>橙色</b>染料。放置在缝纫机内时，榛子还可用作<b>棕色</b>染料。</p>', NULL),
	(6827, 'crop', 'CR0052', '任务', '01', '<ul><li>榛子可能在秋季期间随机出现在"需要帮助" 公告板上，完成任务能获得270和150点友谊值。</li></ul>', NULL),
	(6828, 'crop', 'CR0054', '', '01', '<p><strong>冬根</strong>是一个只能在冬季以采集的方式找到的物品。与地面上发现的其他可采集物品不同，要获得被掩盖在雪泥之下的冬根，必须使用锄头进行挖掘（其他工具不行，但是炸弹可以）。</p><p>挖掘远古斑点是找到冬根成功几率最高的方式（17%~24%，每张地图间的出现概率互相独立）。在任意地点，挖掘标准的可耕种方格有4%的几率找到一个冬根。有希望找到冬根的位置是出现在农场上的远古斑点，而不是可耕种土地。冬根不会在沙漠生成，并且经锄头挖掘获得的冬根不会提供任何经验值。</p><p>冬根可以通过种植冬季种子获得，在这种情况下，它们是一种标准的野生种子作物；此时冬根生长在地面上，可以在不使用工具的情况下收获，每摘取一个冬根获得7个采集经验值。</p><p>冬根偶尔也在冬季会出现在垃圾桶或旅行货车的售卖货物中。矿井（40层~80层）中或秘密森林的蓝色史莱姆有8%的掉落几率掉落一个冬根。</p><p>把冬根放入种子生产器会生成1~3个冬季种子。</p>', NULL),
	(6829, 'crop', 'CR0054', '收集包', '01', '<p>它用于工艺室的 冬季采集收集包。</p>', NULL),
	(6831, 'crop', 'CR0054', '裁制', '01', '<p>冬根可通过缝纫机使用布料制成橘红色领结衫。冬根还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(6832, 'crop', 'CR0054', '任务', '01', '<ul><li>冬根会随机出现在冬季皮埃尔的杂货店外的“求助”布告栏中，可以获得 140 和150友谊点。</li></ul>', NULL),
	(6833, 'crop', 'CR0028', '', '01', '<p><b>洋蓟</b>是蔬菜类农作物，由洋蓟种子生长8天后成熟。</p><p>洋蓟种子在第二年秋季开始才会在皮埃尔的杂货店中出售。在第二年以前，玩家也可以通过种植混合种子获得洋蓟。</p>', NULL),
	(6834, 'crop', 'CR0028', '收集包', '01', '<p>洋蓟不用于任何收集包。</p>', NULL),
	(6835, 'crop', 'CR0028', '裁制', '01', '<p>洋蓟可通过缝纫机使用布料制成可染色的纽扣衬衫。洋蓟可用于染色。放置在染料罐内时，洋蓟可用作<b>绿色</b>染料。放置在缝纫机内时，洋蓟还可用作<b>绿色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6836, 'crop', 'CR0028', '任务', '01', '<ul><li>洋蓟会在第二年开始的秋季开始出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励为480和150友谊点。</li><li>刘易斯的特别任务：“农作物订单”在秋季可能会要求玩家收集并通过出货箱出售100个洋蓟。</li></ul>', NULL),
	(6837, 'crop', 'CR0028', '趣闻', '01', '<p>洋蓟可能会于第一年出现在特别订单中，尽管第二年开始皮埃尔的杂货店才会开始出售洋蓟种子。但玩家可以通过种植混合种子来获取洋蓟。</p>', NULL),
	(6838, 'crop', 'CR0031', '', '01', '<p><b>茄子</b>是一种蔬菜类农作物，由茄子种子生长5天后成熟。成熟后，每5天可以收获一次。</p>', NULL),
	(6839, 'crop', 'CR0031', '生长阶段', '01', '<p>成熟后，每五天可再收获一个茄子，有0.2%的概率收获到更多个茄子。</p>', NULL),
	(6840, 'crop', 'CR0031', '收集包', '01', '<p>茄子被用于茶水间的 秋季作物收集包。</p>', NULL),
	(6841, 'crop', 'CR0031', '裁制', '01', '<p>茄子可通过缝纫机使用布料制成可染色的纽扣衬衫。茄子可用于染色。放置在染料罐内时，茄子可用作<b>紫色</b>染料。放置在缝纫机内时，茄子还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6842, 'crop', 'CR0031', '任务', '01', '<ul><li>秋季时，茄子偶尔会出现在皮埃尔的杂货店外面的布告栏求助任务中，奖励是180和150点友谊值。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个茄子（仅在秋季）。</li></ul>', NULL),
	(6843, 'crop', 'CR0023', '', '01', '<p><b>向日葵</b>是一种花类农作物，由向日葵种子生长8天后成熟。收获向日葵时会得到1朵向日葵和0-2份向日葵种子。</p><p>如果将向日葵种植在蜂房的作用范围内，蜂房会产生向日葵蜂蜜，将蜂蜜的基础价从100增加到260。</p><p>偶尔可以从旅行货车以240 ~ 1,000的价格购买到向日葵。</p>', NULL),
	(6844, 'crop', 'CR0023', '作物生长日历', '01', '<h3>多季节农作物</h3><p>向日葵可以在夏季和秋季种植，在两个季节的种植和收获都是同样的。</p><p>如果一株向日葵（任意生长阶段）生长在夏季28号，它会继续在秋季1号生长，季节变化没有带来任何的不同，肥料也不会在秋天到来后消失。</p>', NULL),
	(6845, 'crop', 'CR0023', '收集包', '01', '<ul><li>向日葵被用于布告栏的 染料收集包。</li><li>向日葵被用于茶水间（重新混合后）的 花园收集包。</li></ul>', NULL),
	(6846, 'crop', 'CR0023', '裁制', '01', '<p>向日葵可通过缝纫机使用布料制成可染色的长裙。向日葵可用于染色。放置在染料罐内时，向日葵可用作<b>黄色</b>染料。放置在缝纫机内时，向日葵还可用作<b>黄色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(6847, 'crop', 'CR0023', '工匠物品', '01', '<p>注意，油在游戏中不属于工匠物品，因此不会受工匠技能加成。</p>', NULL),
	(6848, 'crop', 'CR0023', '备注', '01', '<ul><li>如果被放置在蜂房的适当范围内（任意方向上的五格），向日葵将使蜂蜜生产变得更加盈利。</li><li>由于向日葵及其种子均可以使用产油机加工成油，因此在将向日葵制成油时，先用种子生产器将向日葵加工为向日葵种子会更加盈利。这将平均生产2个种子，平均每朵向日葵生产2份油，而非1份。</li></ul>', NULL),
	(6849, 'crop', 'CR0079', '', '01', '<p><b>胡萝卜</b>是一种农作物，由胡萝卜种子经过3天后长成。胡萝卜种子可以通过矿井中的木箱和木桶、兑奖机、金色钓鱼宝箱、浣熊妻子的商店以及挖掘绿色斑点等途径获得。</p><p>将胡萝卜喂给马，马会获得移动速度 +0.4 的增益效果，当天有效。</p>', NULL),
	(6850, 'crop', 'CR0079', '生长阶段', '01', '<p>收获时，每个胡萝卜可产 1 个胡萝卜。</p>', NULL),
	(6851, 'crop', 'CR0079', '收集包', '01', '<p>胡萝卜可用于重新混合的收集包中茶水间的 春季作物收集包。</p>', NULL),
	(6852, 'crop', 'CR0079', '任务', '01', '<p>胡萝卜不用于任何任务。</p>', NULL),
	(6853, 'crop', 'CR0079', '裁制', '01', '<p>胡萝卜可通过缝纫机使用布料制成可染色的衬衫皮带。胡萝卜可用于染色。放置在染料罐内时，胡萝卜可用作<b>橙色</b>染料。放置在缝纫机内时，胡萝卜还可用作<b>橙色</b>染料。</p>', NULL),
	(6854, 'crop', 'CR0079', '趣闻', '01', '<ul><li>胡萝卜是目前生长速度最快的农作物，只需3天便可成熟。</li></ul>', NULL),
	(6855, 'crop', 'CR0080', '', '01', '<p><b>金皮西葫芦</b>是一种蔬菜类农作物。它由金皮西葫芦种子经过6天后长成，成熟后每3天继续生产一次。种子的发现方式多种多样，包括矿井中的木箱和木桶、镇长的庄园中的兑奖机以及挖掘绿色斑点。</p>', NULL),
	(6856, 'crop', 'CR0080', '生长阶段', '01', '<p>收获时，每个金皮西葫芦可产 1 个金皮西葫芦。</p>', NULL),
	(6857, 'crop', 'CR0080', '收集包', '01', '<p>金皮西葫芦会被随机选中用于重新混合的收集包中的夏季作物收集包。</p>', NULL),
	(6858, 'crop', 'CR0080', '配方', '01', '<p>金皮西葫芦不用于任何配方。</p>', NULL),
	(6859, 'crop', 'CR0080', '裁制', '01', '<p>金皮西葫芦可通过缝纫机使用布料制成可染色的 男款背心 或女款背心。</p><p>金皮西葫芦可用于染色。放置在染料罐内时，金皮西葫芦可用作<b>黄色</b>染料。</p>', NULL),
	(6860, 'crop', 'CR0080', '任务', '01', '<p>金皮西葫芦不用于任何任务。</p>', NULL),
	(6861, 'crop', 'CR0081', '', '01', '<p><b>西蓝花</b>是一种农作物。它由西蓝花种子经过8天后长成，成熟后每4天继续生产一次。种子的发现方式多种多样，包括矿井中的木箱和木桶、镇长的庄园中的兑奖机以及挖掘绿色斑点。</p>', NULL),
	(6862, 'crop', 'CR0081', '收集包', '01', '<p>西蓝花可用于重新混合的收集包中茶水间的 秋季作物收集包。</p>', NULL),
	(6863, 'crop', 'CR0081', '配方', '01', '<p>西蓝花不用于任何配方。</p>', NULL),
	(6864, 'crop', 'CR0081', '任务', '01', '<p>西蓝花不用于任何任务。</p>', NULL),
	(6865, 'crop', 'CR0081', '裁制', '01', '<p>西蓝花可通过缝纫机使用布料制成可染色的纽扣衬衫。西蓝花可用于染色。放置在染料罐内时，西蓝花可用作<b>绿色</b>染料。放置在缝纫机内时，西蓝花还可用作<b>绿色</b>染料。</p>', NULL),
	(6866, 'crop', 'CR0082', '', '01', '<p><b>霜瓜</b>是一种水果类农作物，由霜瓜种子生长7天成熟后结出的果实。有多种方式获取霜瓜种子，包括在冬季挖掘绿色斑点，完成浣熊的请求后作为奖励获得，以及从浣熊妻子的商店中购买。霜瓜可能在冬日星盛宴商店以2,500出售（出现概率为25％）。</p>', NULL),
	(6867, 'crop', 'CR0082', '生长阶段', '01', '<p>霜瓜可以长成巨大作物。</p>', NULL),
	(6868, 'crop', 'CR0082', '收集包', '01', '<p>5个霜瓜是 布告栏（重新混合后）的 冬日之星收集包的可选项之一。</p>', NULL),
	(6869, 'crop', 'CR0082', '配方', '01', '<p>霜瓜不用于任何配方。</p>', NULL),
	(6870, 'crop', 'CR0082', '任务', '01', '<p>霜瓜不用于任何任务。</p>', NULL),
	(6871, 'crop', 'CR0082', '裁制', '01', '<p>霜瓜可通过缝纫机使用布料制成可染色的农夫裤。霜瓜可用于染色。放置在染料罐内时，霜瓜可用作<b>白色</b>染料。</p>', NULL),
	(6872, 'harvest', 'HA0002', '', '01', '<p><strong>野山葵</strong>可在春季通过采集获得，或通过种植春季种子获得。在春季翻动垃圾桶也有概率找到。把野山葵放入种子生产器会产出1-3个春季种子。</p>', NULL),
	(6873, 'harvest', 'HA0002', '收集包', '01', '<p>用于工艺室的 春季采集收集包</p>', NULL),
	(6875, 'harvest', 'HA0002', '裁制', '01', '<p>野山葵可通过缝纫机使用布料制成"Good Grief" 衫。野山葵可用于染色。放置在染料罐内时，野山葵可用作<strong>橙色</strong>染料。放置在缝纫机内时，野山葵还可用作<strong>棕色</strong>染料。</p>', NULL),
	(6876, 'harvest', 'HA0002', '任务', '01', '<ul><li>野山葵可能在春季期间随机出现在"需要帮助" 公告板上，完成任务能获得150和150点友谊值。</li></ul>', NULL),
	(6877, 'harvest', 'HA0009', '', '01', '<p><strong>野梅</strong>是一种水果，在秋季可以通过采集在星露谷找到。种植秋季种子也有几率获得野梅。在秋季翻动垃圾桶也有概率找到野梅。</p>', NULL),
	(6878, 'harvest', 'HA0009', '收集包', '01', '<ul><li>野梅可用于工艺室的 秋季采集收集包，数量需求为1。</li><li>野梅是布告栏（重新混合后）的 采集者收集包的可选项之一，数量需求为20。</li></ul>', NULL),
	(6880, 'harvest', 'HA0009', '裁制', '01', '<p>野梅可通过缝纫机使用布料制成可染色的农夫裤。野梅可用于染色。放置在染料罐内时，野梅可用作<strong>紫色</strong>染料。放置在缝纫机内时，野梅还可用作<strong>深紫色</strong>染料。</p>', NULL),
	(6881, 'harvest', 'HA0009', '任务', '01', '<ul><li>野梅在秋季出现在皮埃尔的杂货店外边的布告栏上的随机任务中，完成任务的奖励是 240 和 150 友谊点 。</li></ul>', NULL),
	(6882, 'harvest', 'HA0021', '', '01', '<p><b>羊肚菌</b>是一种可以在春季去秘密森林或森林农场采集得到的蘑菇，在选择蘑菇山洞选项后，它还可以在山洞中被找到。沙漠节期间乔治会以 30 卡利科三花蛋的价格出售羊肚菌（限1个）。周围存在橡树的蘑菇树桩也可能产出羊肚菌。</p>', NULL),
	(6883, 'harvest', 'HA0021', '收集包', '01', '<p>用于工艺室的 异国情调采集收集包。</p>', NULL),
	(6884, 'harvest', 'HA0021', '配方', '01', '<p>注：尽管提示中显示了具体数值，但是无论当前你的生命值多低，生命药水都能恢复至最大值。</p>', NULL),
	(6885, 'harvest', 'HA0021', '裁制', '01', '<p>羊肚菌可通过缝纫机使用布料制成羊肚菌T恤。羊肚菌可用于染色。放置在染料罐内时，羊肚菌可用作<b>橙色</b>染料。放置在缝纫机内时，羊肚菌还可用作<b>棕色</b>染料。</p>', NULL),
	(6886, 'harvest', 'HA0021', '任务', '01', '<p>羊肚菌不用于任何任务。</p>', NULL),
	(6887, 'harvest', 'HA0050', '', '01', '<p><b>恐龙蛋</b>是一种古物，也是一种动物产品。</p><p>恐龙蛋可以通过挖掘深山（包括采石场）的远古斑点，或打开钓鱼宝箱，杀死霸王喷火龙获得掉落物，采集于骷髅洞穴史前层获得，也可通过抓娃娃机获得。</p><p>把恐龙蛋放入大鸡舍的孵化器中，经过12天会孵化出一只恐龙（如果玩家具有鸡舍大师职业，则只需要6天）。</p><p>孵化后，恐龙会每七天产一次蛋，每个恐龙蛋的基础售价是 350。</p>', NULL),
	(6888, 'harvest', 'HA0050', '捐赠', '01', '<p>捐赠该物品可以增加博物馆的捐赠总数。</p><p><b>小提示：</b>将发现的第一个恐龙蛋放入孵化器中而不是捐献给博物馆，可以确保玩家既能获得一只恐龙，又能完成博物馆的收藏。</p>', NULL),
	(6889, 'harvest', 'HA0050', '工匠物品', '01', '<p>恐龙蛋可用于制作恐龙蛋黄酱。</p>', NULL),
	(6890, 'harvest', 'HA0050', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(6891, 'harvest', 'HA0050', '裁制', '01', '<p>恐龙蛋可通过缝纫机使用布料制成恐龙帽。恐龙蛋可用于染色。放置在染料罐内时，恐龙蛋可用作<b>绿色</b>染料。放置在缝纫机内时，恐龙蛋还可用作<b>绿色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6892, 'harvest', 'HA0050', '任务', '01', '<p>格斯的特别任务：格斯的著名煎蛋卷将会需要玩家在任务期间收集并提交24个蛋（除了金蛋以外任何的蛋都适用）。</p>', NULL),
	(6893, 'harvest', 'HA0050', '趣闻', '01', '<p>恐龙蛋与《超级马里奥》游戏系列中的“耀西蛋（Yoshi\'s Egg）”非常相似，可能是参考了它们。</p>', NULL),
	(6894, 'harvest', 'HA0068', '', '01', '<p><b>鹦鹉螺</b>可以通过采集在冬天的沙滩上、任何季节的海滩农场以及危险矿井寻获。玩家可以从德米特里厄斯邮件中随机收到一个。它也可以作为礼物在冬日星盛宴时收到。沙漠节期间德米特里厄斯会在其商铺以 50 卡利科三花蛋的价格出售鹦鹉螺（限1个）。</p><p>当鱼塘中饲养有蟹笼生物的数量达到9时，它们会有概率在鱼塘中生产1个鹦鹉螺。</p><p>鹦鹉螺可以作为装饰品放入鱼缸中。</p>', NULL),
	(6895, 'harvest', 'HA0068', '收集包', '01', '<p>它用于布告栏的 土地研究收集包。</p>', NULL),
	(6896, 'harvest', 'HA0068', '配方', '01', '<p>没有任何配方需要鹦鹉螺。</p>', NULL),
	(6897, 'harvest', 'HA0068', '裁制', '01', '<p>鹦鹉螺可通过缝纫机使用布料制成可染色的吊带上衣。鹦鹉螺还可用作染料罐和缝纫机的<b>橙色</b>染料。</p>', NULL),
	(6898, 'harvest', 'HA0068', '任务', '01', '<p>饲养有鲟鱼的鱼塘在数量从7提升到10的时候，会出现要求玩家提供1个鹦鹉螺的鱼塘任务。</p>', NULL),
	(6899, 'harvest', 'HA0026', '', '01', '<p><b>山洞萝卜</b>是通过矿井中的采集找到的。它也可以在垃圾桶中找到，或通过用锄头挖掘矿井（10-20层）的地下泥土获得。它也会在旅行货车中以100 ~ 1,000的价格随机出售。沙漠节期间，文森特会在其商铺以 2 卡利科三花蛋的价格出售1个山洞萝卜（限1个）。在冬日星盛宴上，皮埃尔的摊位也会以500的价格随机出售1个山洞萝卜。</p><p>它可以放入小桶制成果汁，也可以放入罐头瓶制成腌菜。</p>', NULL),
	(6900, 'harvest', 'HA0026', '收集包', '01', '<p>山洞萝卜用于工艺室的 异国情调采集收集包。</p>', NULL),
	(6901, 'harvest', 'HA0026', '裁制', '01', '<p>山洞萝卜可通过缝纫机使用布料制成绿色腰带衫。山洞萝卜可用于染色。放置在染料罐内时，山洞萝卜可用作<b>橙色</b>染料。放置在缝纫机内时，山洞萝卜还可用作<b>棕色</b>染料。</p>', NULL),
	(6902, 'harvest', 'HA0026', '任务', '01', '<ul><li>山洞萝卜会在任意季节出现在皮埃尔的杂货店外边的“布告栏”上的随机求助任务之中。任务完成的奖励是 75 和 150 友谊点 。</li><li>山洞萝卜会出现在鱼塘的鱼塘求助任务之中，具体需求数量因鱼类不同而不同。</li><li>当和玛妮的友谊达到3心的时候，她会请求玩家给她带一个山洞萝卜。任务的奖励是同玛妮的友谊增加 100友谊点。</li><li>山洞萝卜会出现在“浣熊的请求”第三个任务、第六个以及之后的任务中。具体需求数量为5个。</li></ul>', NULL),
	(6903, 'harvest', 'HA0026', '交易', '01', '<p>一旦解锁完成，便可在浣熊妻子的商店使用山洞萝卜以物易物。</p>', NULL),
	(6904, 'harvest', 'HA0018', '', '01', '<p><b>红蘑菇</b>可以在矿井 (27层以下)通过采集获得，在夏季或极偶尔在秋季的秘密森林、以及秋季的森林农场也会生成。如果玩家的总收入超过25,000以后选择了蘑菇作为德米特里厄斯的研究项目，那么玩家可以在农场的山洞中拾到红蘑菇。可以使用树液采集器从蘑菇树采集。可以使用蘑菇树桩采集，靠近枫树产量高。偶尔也见于垃圾桶、旅行货车和姜岛的蘑菇洞。</p>', NULL),
	(6905, 'harvest', 'HA0018', '收集', '01', '<p>布告栏的 染料收集包需要红蘑菇，同时红蘑菇是工艺室的 异国情调采集收集包的可选项目之一。</p>', NULL),
	(6906, 'harvest', 'HA0018', '配方', '01', '<p>无论位于用户界面右下角的提示框内数值为多少，生命药水都会把生命值恢复到最大值。</p>', NULL),
	(6907, 'harvest', 'HA0018', '裁制', '01', '<p>红蘑菇可通过缝纫机使用布料制成斑点头巾。红蘑菇还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(6908, 'harvest', 'HA0018', '任务', '01', '<ul><li>红蘑菇在秋季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务 之中，奖励为225和150 友谊点。</li><li>饲养有鲶鱼、幽灵鱼或木跃鱼的鱼塘在数量从3提升到5时，会出现要求玩家提供2~3个红蘑菇的鱼塘任务。</li></ul>', NULL),
	(6909, 'harvest', 'HA0005', '', '01', '<p><strong>蒲公英</strong>可以在春季通过采集获得，或者种下春季种子长成。也可以在花舞节中向皮埃尔购买（最多10个），沙漠节期间卡洛琳也会在其商铺以 10 卡利科三花蛋的价格出售蒲公英（限5个）。在春季翻动垃圾桶也有概率找到蒲公英。虽然它在现实生活中是一种花，但在游戏中，它被分类为“采集品”而不是“花”。它可以放入小桶制成果汁，售价为90金，如果选择了工匠技能则为126金。</p>', NULL),
	(6910, 'harvest', 'HA0005', '收集包', '01', '<p>用于工艺室的 春季采集收集包</p>', NULL),
	(6912, 'harvest', 'HA0005', '裁制', '01', '<p>蒲公英可通过缝纫机使用布料制成薄荷女式衬衫。蒲公英还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(6913, 'harvest', 'HA0005', '任务', '01', '<ul><li>蒲公英可能在春季期间随机出现在皮埃尔的杂货店外的"需要帮助" 公告板上，报酬是120和150点友谊值。</li></ul>', NULL),
	(6914, 'harvest', 'HA0049', '', '01', '<p><strong>仙人掌果子</strong>是一种可以在卡利科沙漠通过采集获得的水果。也可以在周二到绿洲以150的价格购买，或是随机在旅行货车以225 ~ 1,000的价格购买。解锁沙漠后，翻动任何垃圾桶也有概率找到。无尽财富之雕像会在山姆生日时（夏17日）生产一个普通品质的仙人掌果子。</p><p>仙人掌果子也是一种水果类农作物，由仙人掌种子生长12天后成熟，但只有通过种植收获的仙人掌果子才能受到农耕人职业的加成。</p>', NULL),
	(6915, 'harvest', 'HA0049', '收集包', '01', '<p>仙人掌果子可用于工艺室的 异国情调采集收集包，也可用于布告栏（重新混合后）的 染料收集包。</p>', NULL),
	(6916, 'harvest', 'HA0049', '配方', '01', '<p>仙人掌果子不用于任何配方中。</p>', NULL),
	(6917, 'harvest', 'HA0049', '建筑', '01', '<p>仙人掌果子可用于在法师塔处建造沙漠图腾柱。</p>', NULL),
	(6918, 'harvest', 'HA0049', '裁制', '01', '<p>仙人掌果子可通过缝纫机使用布料制成可染色的 男款80\'s 衬衣 或女款80\'s 衬衣。仙人掌果子还可用作染料罐和缝纫机的<strong>绿色</strong>染料。</p>', NULL),
	(6919, 'harvest', 'HA0049', '任务', '01', '<ul><li>当玩家完成金库收集包后，有可能在皮埃尔的杂货店外面的布告栏处接取到有关仙人掌果子的求助任务，报酬是225和150点友谊。</li><li>沙鱼或蝎鲤鱼的鱼塘在容量从1提升到3的时候，可能会出现要求玩家提供3个仙人掌果子的鱼塘任务。</li></ul>', NULL),
	(6920, 'harvest', 'HA0024', '', '01', '<p><strong>姜</strong>是姜岛上的一种经采集获得的食物，必须用锄头采集。老虎史莱姆也会掉落。收获姜不会得到任何经验值。它也可以放入小桶制成果汁，售价为135金，如果选择了工匠技能则为189金。</p><p>可以在姜岛商人处用20个姜购买热带床。</p>', NULL),
	(6921, 'harvest', 'HA0024', '秘密', '01', '<p><strong>详情</strong></p>', NULL),
	(6922, 'harvest', 'HA0024', '裁制', '01', '<p>姜可通过缝纫机使用布料制成采集者帽。姜还可用作染料罐和缝纫机的<strong>黄色</strong>染料。</p>', NULL),
	(6924, 'harvest', 'HA0024', '任务', '01', '<ul><li>卡洛琳的特别任务："岛屿食材"任务期间可能会要求玩家收集并提交100个姜。</li><li>狮子鱼在鱼塘中的数量达到3时可能会需要3个姜。</li></ul>', NULL),
	(6925, 'harvest', 'HA0006', '', '01', '<p><b>香味浆果</b>是一种水果，可以在夏季经采集获得，或种植夏季种子获得。旅行货车也会以240 ~ 1,000的价格随机出售。沙漠节期间，德米特里厄斯会在其商铺以 10 卡利科三花蛋的价格出售香味浆果（限3个）。在夏季翻动垃圾桶也有概率找到香味浆果。</p><p>把一个香味浆果放入种子生产器可以获得1~3个夏季种子。</p>', NULL),
	(6926, 'harvest', 'HA0006', '收集包', '01', '<p>工艺室的 夏季采集收集包需要一个香味浆果。</p>', NULL),
	(6927, 'harvest', 'HA0006', '裁制', '01', '<p>香味浆果可通过缝纫机使用布料制成仅袖子可染色的 男款绿色纽扣汗衫 或女款绿色纽扣汗衫。香味浆果还可用作染料罐和缝纫机的<b>橙色</b>染料。</p>', NULL),
	(6928, 'harvest', 'HA0006', '任务', '01', '<ul><li>香味浆果在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有 240 和150友谊点。</li></ul>', NULL),
	(6929, 'harvest', 'HA0017', '', '01', '<p><b>美洲大树莓</b>是一种水果，可以于每年树莓季(春季15-18日)在星露谷的灌木丛中采集获得。在其他的日子里野外不会发现它们。</p><p>采集技能到4级之后，从每个灌木丛中可以收获两个浆果。采集技能到8级之后，从每个灌木丛中可以收获3个浆果。如果暂时提高到13级可以一次收获4个浆果。每收获1个美洲大树莓会获得1点采集经验值。</p><p>在所有可采集的物品中，美洲大树莓的售价是最低的。所以送礼或吃掉比出售更有价值。这也是少有的制成果酱比制成果酒卖的更贵的水果之一。如果有了熊的知识，直接出售的价格甚至超过果酒。</p>', NULL),
	(6930, 'harvest', 'HA0017', '送礼', '01', '<h3>美洲大树莓</h3><h3>美洲大树莓果酒</h3><p>美洲大树莓果酒是同类中最便宜的。</p><h3>美洲大树莓果酱</h3><p>美洲大树莓果酱是同类中最便宜的。</p><p><br/></p>', NULL),
	(6931, 'harvest', 'HA0017', '收集包', '01', '<ul><li>布告栏（重新混合后）的 采集者收集包需要五十只美洲大树莓。</li><li>布告栏（重新混合后）的 儿童收集包需要十只美洲大树莓。</li><li>美洲大树莓可以被放进小桶来制作果酒，果酒可以用于布告栏的 魔法师收集包，这是最便宜的选择。</li><li>美洲大树莓果酒可以放在木桶陈酿至银星品质，可用于完成废弃乔家超市中的 失踪的收集包.</li></ul>', NULL),
	(6932, 'harvest', 'HA0017', '配方', '01', '<p>不用于任何配方，不过可以放入罐头瓶中，制成果酱。</p>', NULL),
	(6933, 'harvest', 'HA0017', '任务', '01', '<p>不能用在任何任务中</p>', NULL),
	(6934, 'harvest', 'HA0017', '裁制', '01', '<p>美洲大树莓可通过缝纫机使用布料制成上衣。美洲大树莓还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(6935, 'harvest', 'HA0017', '提示', '01', '<ul><li>尽管游戏提示出售美洲大树莓赚钱，但它很便宜，不如吃掉与送礼。尤其是第一年很多东西未解锁时美洲大树莓特别有效。 迅速将采集提升至4级可以在第一个树莓季收获400个美洲大树莓(超过10,000能量)。</li><li>在夜间更新时，每个灌木丛都有20%的几率生成美洲大树莓。在重新加载的存档中增加到50%，这意味着退出并重新加载游戏将平均多获得2.5倍的浆果。重新加载将在同一个位置始终生成相同的生成物，并且即使再次重新加载也不会改变。(详情可见 Reddit 上的帖子。)</li></ul>', NULL),
	(6936, 'harvest', 'HA0019', '', '01', '<p>蕨菜是唯一一种被划入蔬菜类别的可采集物品。所以作为一种蔬菜，蕨菜的售价会因为“农耕人”职业而获得10%的提升，并且可以放入小桶或者罐头瓶进一步加工成果汁或者腌菜。同时作为一种可采集物品，蕨菜也会受到多个相关的采集技能的增益影响。具体来说，每采集到一个蕨菜，可以获得7点采集经验值。蕨菜会受到采集等级 5（收集者）的双倍采集获得物和采集等级 10（植物学家）的所有采集物为铱星品质的增益效果。</p><p><b>蕨菜</b>可以通过以下方式获得：</p><ul><li>在夏季的秘密森林采集。</li><li>在姜岛的丛林区采集。</li><li>在骷髅洞穴的史前层采集。</li><li>当挑战之神庙被激活时，在矿井的41-69层采集。</li><li>偶尔出现在旅行货车的商品列表中，售价270 ~ 1,000。</li><li>在苔雨日期间砍伐苔雨树（种类3）。</li><li>把树液采集器放置在蕨菜形状的苔雨树上提取。</li><li>沙漠节期间在肯特的商铺以 10 卡利科三花蛋购买（限5个）。</li></ul>', NULL),
	(6937, 'harvest', 'HA0019', '收集包', '01', '<ul><li>布告栏的 厨师收集包需要蕨菜。</li><li>工艺室（重新混合后）的 野生药材收集包选项之一为5个蕨菜。</li></ul>', NULL),
	(6938, 'harvest', 'HA0019', '裁制', '01', '<p>蕨菜可通过缝纫机使用布料制成绿色背带裤。蕨菜可用于染色。放置在染料罐内时，蕨菜可用作<b>绿色</b>染料。放置在缝纫机内时，蕨菜还可用作<b>绿色</b>染料，染色强度为<b>强烈</b>。</p>', NULL),
	(6939, 'harvest', 'HA0019', '任务', '01', '<p>不用于任何求助任务。</p>', NULL),
	(6940, 'harvest', 'HA0063', '', '01', '<p><strong>海胆</strong>是沙滩采集获得的一种贝类，全季节都有。海胆的具体生成位置为沙滩的潮汐池，需要花费300木头修复位于主海滩最东边一条破损的小桥后才能到达该区域。在夏季12-14日，海滩上（包括潮汐池）会生成额外的海胆。</p><p>海滩潮汐池的海胆平均生成率为0.23只/天。在夏季12-14日期间，每天平均会额外生成0.8个海胆。此生成率不受海滩上其他可采集物品的影响；具体来说，海胆不算在每张地图最多6个可采集物品的限制中。每个海胆提供7采集值经验，并且所有采集加成都适用于海胆。</p><p>艾米丽偶尔会寄海胆作为礼物。此外，沙漠节期间艾米丽会以 5卡利科三花蛋的价格出售海胆（限5个）。所有咸水蟹笼生物的鱼塘在生物数量到达5时开始概率产生海胆。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落海胆。</p>', NULL),
	(6941, 'harvest', 'HA0063', '收集包', '01', '<p>用于布告栏的 染料收集包。</p>', NULL),
	(6942, 'harvest', 'HA0063', '裁制', '01', '<p>海胆可通过缝纫机使用布料制成可染色的吊带上衣。海胆还可用作染料罐和缝纫机的<strong>紫色</strong>染料。</p>', NULL),
	(6943, 'harvest', 'HA0063', '鱼塘', '01', '<p>海胆不被计为常规鱼类，不过仍然可以通过鱼塘养殖，每 4 天繁殖一次。鱼塘的初始容量为 10，不需要完成任务来提高容量。</p><p>鱼的数量及对应产物概率见下表。</p>', NULL),
	(6944, 'harvest', 'HA0063', '任务', '01', '<ul><li>个别海鱼的鱼塘任务可能会要求玩家提供1-2个海胆。</li><li>大树桩浣熊的第五个任务、第六个以及之后的任务可能会要求玩家提供2个海胆。</li></ul>', NULL),
	(6945, 'harvest', 'HA0063', '秘密', '01', '<ul><li>可以给鱼缸中的海胆戴帽子。</li></ul>', NULL),
	(6946, 'harvest', 'HA0064', '', '01', '<p>在沙滩上，<strong>珊瑚</strong>可以在任何季节通过采集获得。珊瑚的具体生成位置为沙滩的潮汐池，需要花费 300 木头修复位于主海滩最东边一条破损的小桥后才能到达该区域。在夏季 12-14 日，海滩上（包括潮汐池）会生成额外的珊瑚。</p><p>潮汐池的珊瑚的平均生成率为 0.93 个/天。在夏季 12-14 日期间，每天平均会额外生成 3.2 个珊瑚。这些生成率不受海滩上其他可采集物品的影响；具体来说，珊瑚不算在每张地图最多 6 个可采集物品的限制。每个珊瑚提供7采集值经验，并且所有采集加成都适用于珊瑚。</p><p>当鱼塘中饲养有蟹笼生物的数量达到 5 时，它们会有概率在鱼塘中生产 1 个珊瑚。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落珊瑚。</p>', NULL),
	(6947, 'harvest', 'HA0064', '收集包', '01', '<p>不用于任何收集包。</p>', NULL),
	(6948, 'harvest', 'HA0064', '裁制', '01', '<p>珊瑚可通过缝纫机使用布料制成可染色的吊带上衣。珊瑚还可用作染料罐和缝纫机的<strong>红色</strong>染料。</p>', NULL),
	(6949, 'harvest', 'HA0064', '建筑', '01', '<p>建造水之图腾柱需要支付 10 个珊瑚给法师塔。</p>', NULL),
	(6950, 'harvest', 'HA0064', '鱼塘', '01', '<p>珊瑚不被计为常规鱼类，不过仍然可以通过鱼塘养殖，每 2 天繁殖一次。鱼塘的初始容量为 10，不需要完成任务来提高容量。</p><p>鱼的数量及对应产物概率见下表。、</p>', NULL),
	(6951, 'harvest', 'HA0064', '任务', '01', '<ul><li>珊瑚可能会被用于鱼塘任务，具体为：<ul><li>在比目鱼的数量达到 3 时会需要 3 个。</li><li>在鲱鱼的数量达到 5 时会需要 1 个。</li><li>在鱿鱼、午夜鱿鱼的数量达到 3 时可能会需要 3 个。</li><li>在水滴鱼仅有 1 条时可能会需要 3 个。</li><li>在大海参、章鱼、虹鳟鱼、幽灵鱼的数量达到 3 时候可能会需要 3 个。</li><li>在鳀鱼、青花鱼、大比目鱼、红鲻鱼、红鲷鱼、沙丁鱼、海参、罗非鱼、鳗鱼、金枪鱼、河豚的数量达到 5 时候可能会需要 2 个。</li></ul></li><li>在浣熊的请求的第五个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 4 个珊瑚。</li></ul>', NULL),
	(6952, 'harvest', 'HA0061', '', '01', '<p><br></p><p><strong>牡蛎</strong>是一种鱼，可以在任何季节使用蟹笼在海洋中找到。它也可以从海滩采集。它也可能出现在旅行货车中，售价为120 ~ 1,000。像其他从蟹笼中捕获的鱼一样，牡蛎不能直接食用，但可以用于烹饪。</p>', NULL),
	(6953, 'harvest', 'HA0061', '收集包', '01', '<p>牡蛎是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(6954, 'harvest', 'HA0061', '裁制', '01', '<p>牡蛎可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。牡蛎可用于染色。放置在缝纫机内时，牡蛎可用作<strong>灰色</strong>染料。</p>', NULL),
	(6955, 'harvest', 'HA0061', '鱼塘', '01', '<p>牡蛎可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p><p>升级任务物品的需求、鱼的数量及对应产物概率见下表。</p>', NULL),
	(6956, 'harvest', 'HA0061', '任务', '01', '<ul><li>章鱼、虹鳟鱼、幽灵鱼以及大海参可能会在鱼塘扩容任务中要求 1 个牡蛎，以将鱼塘的容量从 3 增加到 5。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个牡蛎。</li></ul>', NULL),
	(6957, 'harvest', 'HA0027', '', '01', '<p><strong>椰子</strong>是一种水果，可在沙漠中通过采集获得，也可通过摇晃姜岛和沙漠中的棕榈树获得。每周一可在绿洲商店以200的价格购买，旅行货车也会随机出售椰子，售价为300 ~ 1,000。解锁沙漠后，翻动任何垃圾桶也有概率找到椰子。当桑迪对玩家的好感度大于0时，她可能会通过信件中送给玩家一个椰子作为礼物。无尽财富之雕像也会在莱纳斯生日时（冬3日）生产一个普通品质的椰子。</p><p>尽管椰子不可直接食用，但它可以放进罐头瓶做成果酱，或者放进小桶酿酒。</p><p>椰子不可用于种植棕榈树。</p><p>如果玩家已经打开过一个金色椰子，便可在姜岛商人处用10个椰子换1个金色椰子。</p>', NULL),
	(6958, 'harvest', 'HA0027', '收集包', '01', '<p>椰子是工艺室的 异国情调采集收集包的可选收集。</p>', NULL),
	(6960, 'harvest', 'HA0027', '建筑', '01', '<p>椰子可用于在法师塔处建造沙漠图腾柱。</p>', NULL),
	(6961, 'harvest', 'HA0027', '裁制', '01', '<p>椰子可通过缝纫机使用布料制成假日T恤。椰子可用于染色。放置在染料罐内时，椰子可用作<strong>橙色</strong>染料。放置在缝纫机内时，椰子还可用作<strong>深棕色</strong>染料。</p>', NULL),
	(6962, 'harvest', 'HA0027', '任务', '01', '<ul><li>"异域饮品"：格斯会在第2年冬季的19日给玩家写信说，需要一个椰子。奖励是600和增加1心好感度。</li><li>当玩家完成金库收集包后，有可能在皮埃尔的杂货店外面的布告栏处接取到有关椰子的求助任务，报酬是300和150点友谊。</li><li>沙鱼或蝎鲤鱼的鱼塘在容量从3提升到5的时候，可能会出现要求玩家提供3个椰子的鱼塘任务。</li></ul>', NULL),
	(6963, 'harvest', 'HA0027', '注意', '01', '<ul><li>要想通过摇晃或砍倒棕榈树以获得椰子，玩家必须达到采集技能1级。</li><li>从沙漠的地面上拾起的椰子不受农耕人职业加成影响，而从棕榈树上、从信件中、从绿洲或旅行商人购买的椰子受这一加成影响。详见：水果#农耕人职业适用范围详解。</li><li>从植物学的角度来说，椰子是一种核果。核果的特征为：外果皮薄，中果皮肥厚多汁，内果皮呈木质。同属于核果的还有樱桃和桃子。</li></ul>', NULL),
	(6964, 'harvest', 'HA0010', '', '01', '<p><strong>榛子</strong>可在秋季通过采集获得，或通过种植秋季种子获得。在秋季15日-28日期间摇晃枫树，也有概率掉落榛子。沙漠节期间，肯特会在其商铺以 10 卡利科三花蛋的价格出售榛子（限5个）。在秋季翻动垃圾桶也有概率找到榛子。</p>', NULL),
	(6965, 'harvest', 'HA0010', '收集包', '01', '<p>工艺室的 秋季采集收集包需要一个榛子。</p>', NULL),
	(6967, 'harvest', 'HA0010', '裁制', '01', '<p>榛子可通过缝纫机使用布料制成棕色背带裤。榛子可用于染色。放置在染料罐内时，榛子可用作<strong>橙色</strong>染料。放置在缝纫机内时，榛子还可用作<strong>棕色</strong>染料。</p>', NULL),
	(6968, 'harvest', 'HA0010', '任务', '01', '<ul><li>榛子可能在秋季期间随机出现在"需要帮助" 公告板上，完成任务能获得270和150点友谊值。</li></ul>', NULL),
	(6969, 'harvest', 'HA0001', '', '01', '<p><b>葡萄</b>是一种水果，既属于标准农作物，也是一种采集品。</p><ul><li>在夏季，葡萄可以在星露谷中采集得到，或用夏季种子种植得到。具体出现概率为：边远森林（占该地点所有可采集物品的62%）、深山（62%）、巴士站（27%）、铁路（27%）、森林农场地图（25%）。在夏季翻动垃圾桶也有概率找到葡萄。</li><li>在秋季，葡萄可以由葡萄种子种植得到，成熟需要十天，之后每三天收获一次。</li></ul><p>秋季作为农作物生长而成的葡萄售价和其他农作物一样受到农耕人职业加成影响。然而，夏季由采集或夏季种子得来的葡萄售价不受农耕人职业影响。如果将农作物葡萄和采集葡萄堆叠后售卖，将全部视为农作物葡萄。（详见水果条目。</p><p>除此之外，两种来源的葡萄没有其他区别，在用于制作、礼物、收集包、食谱和任务时可以通用。</p>', NULL),
	(6970, 'harvest', 'HA0001', '生长阶段', '01', '<p>葡萄植株在任何生长阶段都不能行走通过，除了枯萎状态。</p>', NULL),
	(6971, 'harvest', 'HA0001', '收集', '01', '<p>葡萄可用于工艺室的 夏季采集收集包.</p>', NULL),
	(6972, 'harvest', 'HA0001', '裁制', '01', '<p>葡萄可通过缝纫机使用布料制成可染色的短裤。葡萄还可用作染料罐和缝纫机的<b>紫色</b>染料。</p>', NULL),
	(6973, 'harvest', 'HA0001', '任务', '01', '<ul><li>葡萄在夏季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有240和150友谊点。</li><li>在刘易斯房子外边的特别任务版上出现的“农作物订单”任务中，刘易斯会要求玩家收获并运送100个葡萄（仅在秋季）。</li></ul>', NULL),
	(6974, 'harvest', 'HA0067', '', '01', '<p></p><p><b>蚌</b>是一种鱼，在任何季节都可以在海洋使用蟹笼捕获。它也可以在海滩上采集获得。它还可以在姜岛西部的海滩上采掘蚌矿获得。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落蚌。或者在旅行货车中以100 ~ 1,000的价格买到。像其他从蟹笼中捕获的鱼一样，蚌不能直接食用，但可以用于烹饪。</p><p>可以在姜岛商人处以75个蚌换1个芒果幼苗。</p>', NULL),
	(6975, 'harvest', 'HA0067', '收集包', '01', '<p>蚌是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(6976, 'harvest', 'HA0067', '裁制', '01', '<p>蚌可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。蚌可用于染色。放置在缝纫机内时，蚌可用作<b>灰色</b>染料。</p>', NULL),
	(6977, 'harvest', 'HA0067', '鱼塘', '01', '<p>蚌可以通过鱼塘养殖，每 1 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p><p>升级任务物品的需求、鱼的数量及对应产物概率见下表。</p>', NULL),
	(6978, 'harvest', 'HA0067', '任务', '01', '<ul><li>某些海鱼可能会在鱼塘扩容任务中要求 1 个蚌，以将鱼塘的容量从 7 增加到 10。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个蚌。</li></ul>', NULL),
	(6979, 'harvest', 'HA0023', '', '01', '<p><b>紫蘑菇</b>可通过以下方式找到：</p><ul><li>在矿井（81层开始）、骷髅洞穴和姜岛蘑菇洞采集。</li><li>为蘑菇树放置树液采集器或砍伐蘑菇树。</li><li>如果农场山洞选择为“蘑菇”，那么每次产出都有2.5%概率为紫蘑菇。</li><li>紫蘑菇可能会作为礼物在冬日星盛宴上收到。</li><li>玩家抵达矿井40层以后，翻动任何垃圾桶都有概率找到。</li><li>在旅行货车中以750 ~ 1,250的价格随机出售。</li><li>与法师的友谊值大于0时，紫蘑菇可能会作为其随信礼物。</li><li>秋季有25%概率出现在森林农场地图西侧。</li><li>蘑菇树桩产出。</li></ul>', NULL),
	(6980, 'harvest', 'HA0023', '收集包', '01', '<p>紫蘑菇用于</p><ul><li>布告栏的 土地研究收集包；</li><li>工艺室的 异国情调采集收集包可选收集项目之一 <b>或</b> 工艺室（重新混合后）的 野生药材收集包。</li></ul>', NULL),
	(6981, 'harvest', 'HA0023', '配方', '01', '<p>尽管生命药水的描述中列出了数字，但实际上无论生命值是多少，使用后都会将其完全恢复到最大值。</p>', NULL),
	(6982, 'harvest', 'HA0023', '裁制', '01', '<p>紫蘑菇可通过缝纫机使用布料制成上衣。紫蘑菇还可用作染料罐和缝纫机的<b>紫色</b>染料。</p>', NULL),
	(6983, 'harvest', 'HA0023', '任务', '01', '<ul><li>紫蘑菇可能会在任何季节出现在皮埃尔的杂货店外边的“布告栏”上的随机任务中。任务完成的奖励是 750 和 150友谊点。在你达到矿井40层及以上之前，紫蘑菇不会出现在布告栏中。</li><li>饲养有鲶鱼、章鱼、虹鳟鱼、幽灵鱼或大海参的鱼塘在数量从5提升到7时，会出现要求玩家提供2~3个紫蘑菇的鱼塘任务。</li></ul>', NULL),
	(6984, 'harvest', 'HA0048', '', '01', '<p><b>树液</b>是一种通过采集获得的物品，砍倒任意树（不包括桃花心木树）后会掉落。</p><p>树液可于多种制造配方。食用树液会减少2点能量。</p><p>树液可以通过树液采集器、重型树液采集器或击杀史莱姆（不包括巨型史莱姆）产出。</p><p>沙漠节期间，文森特会在其村民商铺以 1 卡利科三花蛋的价格出售1个树液（限1个）。</p>', NULL),
	(6985, 'harvest', 'HA0048', '收集包', '01', '<p>树液可用于工艺室（重新混合后）的 黏糊糊收集包。</p>', NULL),
	(6986, 'harvest', 'HA0048', '裁制', '01', '<p>树液可通过缝纫机使用布料制成棕黄色条纹衫。树液还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(6987, 'harvest', 'HA0048', '任务', '01', '<p>树液不能被用于任何任务。</p>', NULL),
	(6988, 'harvest', 'HA0048', '交易', '01', '<p>大树桩浣熊妻子的商店一旦解锁，玩家便可使用树液交易以下物品。</p>', NULL),
	(6989, 'harvest', 'HA0060', '', '01', '<p><b>彩虹贝壳</b>是一种较为值钱的沙滩采集物品，可通过以下途径获得：</p><ul><li>采集<ul><li>彩虹贝壳会在夏季的沙滩上生成。</li><li>如果玩家选择了海滩农场，任何季节的农场上都有可能生成彩虹贝壳。</li><li>如果玩家激活了挑战之神庙，或处于“深处的危险”任务期间，在矿井中也有可能生成彩虹贝壳。</li></ul></li><li>村民的赠礼<ul><li>当玩家和德米特里厄斯的友谊大于0时，德米特里厄斯就有可能会给玩家邮寄一份礼物。礼物有可能是彩虹贝壳。</li><li>在冬日星盛宴上，如果给玩家送礼物的村民是贾斯、文森特或雷欧，玩家也可能获得一个彩虹贝壳。</li></ul></li><li>彩虹贝壳偶尔会出现在旅行货车中，售价900 ~ 1,500。</li><li>当虹鳟鱼的鱼塘中有9条鱼时，每天有2%的几率产出1个彩虹贝壳。</li></ul>', NULL),
	(6990, 'harvest', 'HA0060', '收集包', '01', '<p>彩虹贝壳不用于任何收集包中。</p>', NULL),
	(6991, 'harvest', 'HA0060', '配方', '01', '<p>彩虹贝壳不用于任何配方中。</p>', NULL),
	(6992, 'harvest', 'HA0060', '裁制', '01', '<p>彩虹贝壳可通过缝纫机使用布料制成可染色的比基尼上装。彩虹贝壳可用于染色。放置在缝纫机内时，可以<b>自选颜色</b>。</p>', NULL),
	(6993, 'harvest', 'HA0060', '任务', '01', '<ul><li>“神秘的齐”任务中需要1个彩虹贝壳。</li><li>水滴鱼的鱼塘在容量从7提升到10的时候，可能会出现要求玩家提供1个彩虹贝壳的鱼塘任务。</li></ul>', NULL),
	(6994, 'harvest', 'HA0060', '注意', '01', '<ul><li>有一个获得彩虹贝壳的小技巧：在收到德米特里厄斯的寄来的礼物后，不断重新读档直到信件中包含彩虹贝壳。</li><li>彩虹贝壳的图像并非是彩虹色的，而五彩碎片的图像是彩虹色的。</li><li>彩虹贝壳可以作为鱼缸的装饰品。</li></ul>', NULL),
	(6995, 'harvest', 'HA0008', '', '01', '<p><strong>普通蘑菇</strong>是一种在秋季遍布星露谷的蘑菇，可通过采集获得。春季和秋季的秘密森林，夏季和秋季的森林农场以及姜岛蘑菇洞都可以找到它们。在秋季翻动垃圾桶也有概率找到。普通蘑菇也可以使用秋季种子种出。如果你选择的是蘑菇洞穴，可以在山洞的蘑菇箱中收获。在蘑菇树上放置树液采集器也可以获得普通蘑菇。它们也可以由蘑菇树桩产出。</p><p>在种子生产器中放入普通蘑菇将产生 1~3 个秋季种子。</p>', NULL),
	(6996, 'harvest', 'HA0008', '收集包', '01', '<p>普通蘑菇可用于工艺室的 秋季采集收集包。</p>', NULL),
	(6998, 'harvest', 'HA0008', '裁制', '01', '<p>普通蘑菇可通过缝纫机使用布料制成上衣。普通蘑菇可用于染色。放置在染料罐内时，普通蘑菇可用作<strong>橙色</strong>染料。放置在缝纫机内时，普通蘑菇还可用作<strong>深棕色</strong>染料。</p>', NULL),
	(6999, 'harvest', 'HA0008', '任务', '01', '<ul><li>普通蘑菇可能在秋季期间随机出现在皮埃尔的杂货店门外的"需要帮助" 公告板上，完成任务能获得120和 150 点友谊值。</li><li>饲养有大头鱼、大嘴鲈鱼或木跃鱼的鱼塘会出现要求玩家提供2-3个普通蘑菇的鱼塘任务。</li></ul>', NULL),
	(7000, 'harvest', 'HA0008', '趣闻', '01', '<ul><li>秋季可采集物品中，仅有普通蘑菇可以直接放入种子生产器。</li></ul>', NULL),
	(7001, 'harvest', 'HA0020', '', '01', '<p><b>冬青树</b>是在冬季通过采集找到的一种物品。在整个星露谷中都能找到冬青树，包括秘密森林。</p>', NULL),
	(7002, 'harvest', 'HA0020', '收集包', '01', '<p>冬青树可能会出现在工艺室（重新混合后）的 冬季采集收集包当中。5个冬青树也是 布告栏（重新混合后）的 冬日之星收集包的可选项之一。</p>', NULL),
	(7003, 'harvest', 'HA0020', '裁制', '01', '<p>冬青树可通过缝纫机使用布料制成靓红女式衬衫。冬青树还可用作染料罐和缝纫机的<b>红色</b>染料。</p>', NULL),
	(7004, 'harvest', 'HA0020', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(7005, 'harvest', 'HA0025', '', '01', '<p><b>熔岩菇</b>是一种在火山地牢经采集获得的蘑菇。 可用其在姜岛商人处购买菠萝种子（1个熔岩菇换1个菠萝种子）。</p><p>玩家在远处容易把它和假熔岩菇搞混。击杀假熔岩菇有99%几率会掉落熔岩菇。</p>', NULL),
	(7006, 'harvest', 'HA0025', '裁制', '01', '<p>熔岩菇可通过缝纫机使用布料制成洋红色T恤。熔岩菇还可用作染料罐和缝纫机的<b>紫色</b>染料。</p>', NULL),
	(7007, 'harvest', 'HA0015', '', '01', '<p><b>番红花</b>是在冬季通过采集找到的花。番红花可在鹈鹕镇、铁路、巴士站、深山、煤矿森林或边远森林找到。也可通过种植冬季野生种子来获得番红花。在冬季翻动垃圾桶也有概率找到番红花。</p>', NULL),
	(7008, 'harvest', 'HA0015', '治疗效果', '01', '<p>番红花是可食用的，但不会增加能量或健康。</p>', NULL),
	(7009, 'harvest', 'HA0015', '收集包', '01', '<p>它用于工艺室的 冬季采集收集包.</p>', NULL),
	(7010, 'harvest', 'HA0015', '裁制', '01', '<p>番红花可通过缝纫机使用布料制成上衣。番红花可用于染色。放置在染料罐内时，番红花可用作<b>紫色</b>染料。放置在缝纫机内时，番红花还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(7011, 'harvest', 'HA0015', '任务', '01', '<ul><li>番红花会在冬季出现在皮埃尔的杂货店外边的“布告栏”上的随机任务，完成任务可以获得 180 和 150 友谊点。</li></ul>', NULL),
	(7012, 'harvest', 'HA0007', '', '01', '<p><b>甜豌豆</b>是花的一种，可以在夏季通过采集获得。它在鹈鹕镇最为常见，夏季可采集物品中仅有它能在镇里找到。甜豌豆也会在秘密森林、巴士站、铁路和森林农场地图中生长。它也可以通过种植夏季种子获得。在夏季翻动垃圾桶也有概率找到甜豌豆。</p>', NULL),
	(7013, 'harvest', 'HA0007', '治疗效果', '01', '<p>甜豌豆可以食用，但是不会增加生命值和能量。</p>', NULL),
	(7014, 'harvest', 'HA0007', '收集包', '01', '<p>工艺室的 夏季采集收集包需要一个甜豌豆。</p>', NULL),
	(7015, 'harvest', 'HA0007', '裁制', '01', '<p>甜豌豆可通过缝纫机使用布料制成上衣。甜豌豆可用于染色。放置在染料罐内时，甜豌豆可用作<b>紫色</b>染料。放置在缝纫机内时，甜豌豆还可用作<b>紫色</b>染料，染色强度为<b>中等</b>。</p>', NULL),
	(7016, 'harvest', 'HA0007', '任务', '01', '<ul><li>在夏季，甜豌豆会出现在皮埃尔的杂货店外边的布告栏上的随机任务之中，完成该任务的奖励是 150 和150 友谊点 。</li></ul>', NULL),
	(7017, 'harvest', 'HA0004', '', '01', '<p><strong>韭葱</strong>在春季可以通过采集找到，或者可以通过种植春季种子获得。在春季翻动垃圾桶也有概率找到韭葱。无尽财富之雕像会在乔治生日时（秋24日）生产一个普通品质的韭葱。</p><p>韭葱在深山和边远森林地区很常见，这两个地区58%的可采集物品都是韭葱。除此之外，巴士站（20%）、铁路（20%）和森林农场（25%）三个地点也能采集到韭葱。</p>', NULL),
	(7018, 'harvest', 'HA0004', '收集包', '01', '<p>韭葱是工艺室的 春季采集收集包中的一个物品。</p>', NULL),
	(7020, 'harvest', 'HA0004', '裁制', '01', '<p>韭葱可通过缝纫机使用布料制成黄绿色束腰上衣。韭葱可用于染色。放置在缝纫机内时，韭葱可用作<strong>白色</strong>染料。</p>', NULL),
	(7021, 'harvest', 'HA0004', '任务', '01', '<ul><li>在任务"奶奶的礼物"中，艾芙琳会在第二年的春季的15号给玩家寄一封信，请求玩家帮忙提供1颗韭葱。奖励是500和1心友谊。</li><li>韭葱在春季会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励有180和150友谊点。</li><li>在刘易斯房子外的特别任务板上随机出现特别任务：给乔治的礼物，艾芙琳会要求玩家收集并提供12颗韭葱。</li></ul>', NULL),
	(7022, 'harvest', 'HA0014', '', '01', '<p><b>雪山药</b>是一个只能在冬季获得的采集物品。自然生成的雪山药不会出现在地面上，而是深藏在雪泥之下，要获得雪山药，必须使用锄头进行挖掘（无法通过其他工具获取，甚至炸弹都不行）。</p><p>获得雪山药几率最高的方式是挖掘远古斑点（11%~16%，取决于地图）。在任意户外地点，挖掘可耕种地块时也有4%的几率获得一个雪山药。农场上例外，只能在挖掘远古斑点时获得雪山药，挖掘普通可耕种地块时无法获得雪山药；沙漠中也无法获得雪山药。经锄头挖掘获得的雪山药不会提供任何经验值。</p><p>雪山药也可以通过种植冬季种子获得，在这种情况下，它算作野生种子作物：此时雪山药生长在地面上，可以在不使用工具的情况下收获，每摘取一个雪山药获得7点采集经验值，并且受收集者和植物学家职业影响。</p><p>在冬季翻动垃圾桶也有概率找到雪山药。</p>', NULL),
	(7023, 'harvest', 'HA0014', '收集包', '01', '<p>雪山药用于工艺室的 冬季采集收集包。</p>', NULL),
	(7024, 'harvest', 'HA0014', '裁制', '01', '<p>雪山药可通过缝纫机使用布料制成上衣。雪山药可用于染色。放置在缝纫机内时，雪山药可用作<b>白色</b>染料。</p>', NULL),
	(7025, 'harvest', 'HA0014', '任务', '01', '<ul><li>需要雪山药的求助任务可能在冬季期间随机出现在皮埃尔的杂货店外的“布告栏”上，报酬是300和150友谊点。</li></ul>', NULL),
	(7026, 'harvest', 'HA0016', '', '01', '<p><strong>大葱</strong>是一种可采集物品，仅于春季出现在煤矿森林的东南部大岛屿（即下水道入口北侧）。有别于其他可采集物品，大葱为每日刷新，也就是说所有未收获的大葱会在一天结束时被移除，新的大葱随即出现。电视节目离地而居会在游戏第4天给出关于大葱的建议。大葱也会在旅行货车中以100 ~ 1,000的价格随机出售。</p><p>收获1根大葱给予玩家3点采集经验值。每个大葱植株仅可收获1根大葱。采集者职业对大葱无效。</p><p>在游戏早期，玩家很难获得足够的食物，此时大葱是很好的补充能量的方法。</p><p>一旦玩家跟贾斯和文森特都达到8心好感度，便可触发特殊剧情。之后会获得青葱技术，可以让大葱的售出价格变为原来的五倍。</p>', NULL),
	(7027, 'harvest', 'HA0016', '送礼', '01', '<h3>用大葱果汁送礼</h3><p>大葱果汁是可用于送礼的最便宜果汁。</p><h3>用腌制大葱送礼</h3><p>腌制大葱是可用于送礼的最便宜腌菜。</p><p><br></p>', NULL),
	(7028, 'harvest', 'HA0016', '收集包', '01', '<p>大葱可用于工艺室（重新混合后）的 春季采集收集包。</p>', NULL),
	(7030, 'harvest', 'HA0016', '裁制', '01', '<p>大葱可通过缝纫机使用布料制成春季T恤。大葱可用于染色。放置在缝纫机内时，大葱可用作<strong>白色</strong>染料。</p>', NULL),
	(7031, 'harvest', 'HA0016', '任务', '01', '<p>不用于任何任务。</p>', NULL),
	(7032, 'harvest', 'HA0062', '', '01', '<p><br></p><p><strong>鸟蛤</strong>是一种鱼，可以在任何季节使用蟹笼在海洋中找到。它也可以从海滩采集。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落鸟蛤。它也可能出现在旅行货车中，售价为150 ~ 1,000。像其他从蟹笼中捕获的鱼一样，鸟蛤不能直接食用，但可以用于烹饪。</p>', NULL),
	(7033, 'harvest', 'HA0062', '收集包', '01', '<p>鸟蛤是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(7034, 'harvest', 'HA0062', '裁制', '01', '<p>鸟蛤可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。鸟蛤可用于染色。放置在缝纫机内时，鸟蛤可用作<strong>深灰色</strong>染料。</p>', NULL),
	(7035, 'harvest', 'HA0062', '鱼塘', '01', '<p>鸟蛤可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p><p>升级任务物品的需求、鱼的数量及对应产物概率见下表。</p>', NULL),
	(7036, 'harvest', 'HA0062', '任务', '01', '<ul><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个鸟蛤。</li></ul>', NULL),
	(7037, 'harvest', 'HA0013', '', '01', '<p><b>水晶果</b>可在冬季通过采集获得，或经杀死矿井（40-80层）的灰尘精灵的掉落物获得，或是种植冬季种子获得。在冬季翻动垃圾桶也有概率找到水晶果。</p>', NULL),
	(7038, 'harvest', 'HA0013', '收集包', '01', '<p>工艺室的 冬季采集收集包需要一个水晶果。</p>', NULL),
	(7039, 'harvest', 'HA0013', '裁制', '01', '<p>水晶果可通过缝纫机使用布料制成上衣。水晶果还可用作染料罐和缝纫机的<b>蓝色</b>染料。</p>', NULL),
	(7040, 'harvest', 'HA0013', '任务', '01', '<ul><li>水晶果可能在冬季期间随机出现在皮埃尔的杂货店门外的“布告栏”上，完成任务能获得 450 和 150 友谊点。</li><li>饲养有冰柱鱼的鱼塘在数量从5提升到7的时候，会出现要求玩家提供一个冬根的鱼塘求助任务。</li></ul>', NULL),
	(7041, 'harvest', 'HA0011', '', '01', '<p><strong>黑莓</strong>是一种能在星露谷通过采集而找到的水果。它能够在黑莓收获季（秋季8~11）户外的灌木丛或地上中找到。它也能通过种植秋季种子得到。在秋季翻动垃圾桶也有概率找到黑莓。</p><p>在黑莓收获季时，在每个生产黑莓的灌木丛中获取的数量随着采集的等级的上升而增加。等级4时，每个灌木丛能得到2个黑莓。等级8时，每个灌木丛会得到3个黑莓。如果采集等级通过进食烹饪的食物被暂时地提高到12或13级，那么每个灌木丛就能得到4个黑莓。每收获1个黑莓会获得1点采集经验值。</p>', NULL),
	(7042, 'harvest', 'HA0011', '品质', '01', '<p>黑莓在黑莓收获季时，被玩家从灌木丛中摇下来时为普通品质。除非在植物学家的影响下，它们会永远呈现为铱星品质。除了在植物学家的影响下，从地上拾起或种植秋季种子而得到的黑莓会表现为普通、银星、金星品质。</p>', NULL),
	(7043, 'harvest', 'HA0011', '收集包', '01', '<p>被用于工艺室的 秋季采集收集包.</p>', NULL),
	(7045, 'harvest', 'HA0011', '裁制', '01', '<p>黑莓可通过缝纫机使用布料制成上衣。黑莓可用于染色。放置在染料罐内时，黑莓可用作<strong>蓝色</strong>染料。放置在缝纫机内时，黑莓还可用作<strong>深蓝色</strong>染料。</p>', NULL),
	(7046, 'harvest', 'HA0011', '任务', '01', '<ul><li>黑莓可能会随机地出现在皮埃尔的杂货店外的"需要帮助" 公告板中，完成奖励为60和150友谊点数。</li></ul>', NULL),
	(7047, 'harvest', 'HA0066', '', '01', '<p><br></p><p><strong>蛤</strong>是一种鱼，可以在任何季节的海洋中使用蟹笼捕获。它也可以在海滩采集获得。宠物乌龟达到最大好感度后，每天第一次被抚摸也有可能掉落蛤。偶尔会出现在旅行货车中，售价150 ~ 1,000。冬日星盛宴期间，皮埃尔的摊位可能会以500的价格出售一个蛤。在挑战之神庙已激活，或处于“深处的危险”任务期间时，在矿井1~29层也有可能采集到蛤。</p>', NULL),
	(7048, 'harvest', 'HA0066', '收集包', '01', '<p>蛤是鱼缸的 蟹笼收集包的一个可选项。</p>', NULL),
	(7049, 'harvest', 'HA0066', '建筑', '01', '<p>从法师塔购买水之图腾柱需要10个蛤。</p>', NULL),
	(7050, 'harvest', 'HA0066', '裁制', '01', '<p>蛤可通过缝纫机使用布料制成可染色的 男款水手服 或女款水手服。蛤可用于染色。放置在染料罐内时，蛤可用作<strong>蓝色</strong>染料。放置在缝纫机内时，蛤还可用作<strong>淡青色</strong>染料。</p>', NULL),
	(7051, 'harvest', 'HA0066', '鱼塘', '01', '<p>蛤可以通过鱼塘养殖，每 2 天繁殖一条。鱼塘的初始容量为 3，完成任务可升级鱼塘提高容量；可最多容纳 10 条鱼，需要完成 3 次任务。</p><p>升级任务物品的需求、鱼的数量及对应产物概率见下表。</p>', NULL),
	(7052, 'harvest', 'HA0066', '任务', '01', '<ul><li>某些海鱼可能会在鱼塘扩容任务中要求 2 个蛤，以将鱼塘的容量从 5 增加到 7。</li><li>在浣熊的请求的第一个任务、第六个以及之后的任务中，浣熊可能会要求玩家提供 5 个蛤。</li></ul>', NULL),
	(7053, 'harvest', 'HA0066', '趣闻', '01', '<ul><li>在先前版本中，艾芙琳讨厌蛤，但她还是有关于童年收集蛤的对话。随后版本更新使艾芙琳对蛤的态度由讨厌变成了喜欢。</li><li>1.6版本前，尽管它可以使用蟹笼捕获，但游戏并没有将蛤归类为“鱼”。因此，它不能用于可以使用任意鱼的食谱、不能放入鱼塘、不受渔夫或垂钓者职业加成，也不算入四个钓鱼相关的成就中。并且，在收集品页面中，蛤在“售出的物品”一类，而不是“鱼”类。</li></ul>', NULL),
	(7054, 'harvest', 'HA0003', '', '01', '<p><b>黄水仙</b>可以在春季的野外采集采集到，或是从春季种子里种出来。皮埃尔也会在花舞节以50的价格出售（限10个）。沙漠节期间，艾芙琳会以 15卡利科三花蛋的价格在商铺出售黄水仙（限5个）。在春季翻动垃圾桶也有概率找到黄水仙。无尽财富之雕像每年会在桑迪生日时（秋15日）生产一个普通品质的黄水仙。</p><p>尽管在现实中，黄水仙是一种花，但在游戏中，它被分类为“采集品”而不是“花”。</p>', NULL),
	(7055, 'harvest', 'HA0003', '效果', '01', '<p>黄水仙可以吃，但它不会提供任何能量或生命值。</p>', NULL),
	(7056, 'harvest', 'HA0003', '收集包', '01', '<p>它用于工艺室的 春季采集收集包.</p>', NULL),
	(7057, 'harvest', 'HA0003', '裁制', '01', '<p>黄水仙可通过缝纫机使用布料制成上衣。黄水仙还可用作染料罐和缝纫机的<b>黄色</b>染料。</p>', NULL),
	(7058, 'harvest', 'HA0003', '任务', '01', '<ul><li>在春季，黄水仙会出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中，奖励是 90 和 150友谊点。</li></ul>', NULL),
	(7059, 'harvest', 'HA0065', '', '01', '<p><b>海草</b>可以在任何季节，通过在海边钓鱼获得。在冬季的夜市的钓鱼潜水艇上钓鱼，将有41%的机率钓到海草。蟹笼不能捕获海草。</p><p>海草会在海滩的潮汐池南边狭长的海岸上生成，但此处生成海草的概率相当低（每晚只有3%的几率）。当鱼塘内饲养的鸟蛤、螃蟹、龙虾、蚌、牡蛎或虾等咸水鱼（蟹笼鱼类）数量至少1个的时候，鱼塘产出1~3个海草的几率是8.8%。</p><p>使用钓鱼竿钓到海草只能获得3点钓鱼经验值，以及一个普通品质的海草。从沙滩上经采集捡起一个海草能获得7点钓鱼经验值，海草的品质则会因为采集技能的等级不同而不同（可能会捡到铱星品质的海草）。</p><p>在任何季节的任何时间，翻动垃圾桶也有概率找到海草。</p><p>海草会显示在收集品 - 鱼的页面之中，并计入和钓鱼相关的四个成就之中。然而，除了上述情况之外，海草不会被分类为鱼类，因此玩家不能把海草卖给鱼店的威利。 </p>', NULL),
	(7060, 'harvest', 'HA0065', '地图', '01', '<p>海草可以在海洋和沙滩上找到。</p>', NULL),
	(7061, 'harvest', 'HA0065', '收集包', '01', '<p>海草不用于任何收集包。</p>', NULL),
	(7062, 'harvest', 'HA0065', '裁制', '01', '<p>海草可通过缝纫机使用布料制成海藻上衣。海草还可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
	(7063, 'harvest', 'HA0065', '任务', '01', '<ul><li>海草会在任意季节，出现在皮埃尔的杂货店外边的布告栏上的随机求助任务之中。奖励为 60和150友谊点。</li><li>当鱼塘的养殖数量从3提高到5的时候，鱼塘的求助任务会出现有些种类的鱼会要求玩家提供1~2个海草的情况。</li></ul>', NULL),
	(7064, 'harvest', 'HA0012', '', '01', '<p><strong>冬根</strong>是一个只能在冬季以采集的方式找到的物品。与地面上发现的其他可采集物品不同，要获得被掩盖在雪泥之下的冬根，必须使用锄头进行挖掘（其他工具不行，但是炸弹可以）。</p><p>挖掘远古斑点是找到冬根成功几率最高的方式（17%~24%，每张地图间的出现概率互相独立）。在任意地点，挖掘标准的可耕种方格有4%的几率找到一个冬根。有希望找到冬根的位置是出现在农场上的远古斑点，而不是可耕种土地。冬根不会在沙漠生成，并且经锄头挖掘获得的冬根不会提供任何经验值。</p><p>冬根可以通过种植冬季种子获得，在这种情况下，它们是一种标准的野生种子作物；此时冬根生长在地面上，可以在不使用工具的情况下收获，每摘取一个冬根获得7个采集经验值。</p><p>冬根偶尔也在冬季会出现在垃圾桶或旅行货车的售卖货物中。矿井（40层~80层）中或秘密森林的蓝色史莱姆有8%的掉落几率掉落一个冬根。</p><p>把冬根放入种子生产器会生成1~3个冬季种子。</p>', NULL),
	(7065, 'harvest', 'HA0012', '收集包', '01', '<p>它用于工艺室的 冬季采集收集包。</p>', NULL),
	(7067, 'harvest', 'HA0012', '裁制', '01', '<p>冬根可通过缝纫机使用布料制成橘红色领结衫。冬根还可用作染料罐和缝纫机的<strong>橙色</strong>染料。</p>', NULL),
	(7068, 'harvest', 'HA0012', '任务', '01', '<ul><li>冬根会随机出现在冬季皮埃尔的杂货店外的“求助”布告栏中，可以获得 140 和150友谊点。</li></ul>', NULL),
	(7069, 'harvest', 'HA0022', '', '01', '<p><b>鸡油菌</b>可以通过在秋天的秘密森林中采集获得，或者在选择了蘑菇选项的农场山洞和姜岛的蘑菇洞生长，也可以通过收获蘑菇树桩获得。</p>', NULL),
	(7070, 'harvest', 'HA0022', '收集包', '01', '<p>鸡油菌不被用于任何收集包。</p>', NULL),
	(7071, 'harvest', 'HA0022', '裁制', '01', '<p>鸡油菌可通过缝纫机使用布料制成橘黄色上衣。鸡油菌还可用作染料罐和缝纫机的<b>橙色</b>染料。</p>', NULL),
	(7072, 'harvest', 'HA0022', '任务', '01', '<p>鸡油菌不被用于任何任务。</p>', NULL),
	(7073, 'harvest', 'HA0116', '', '01', '<p><b>苔藓</b>是一种从苔藓覆盖的树木中获得的资源。在除冬季之外的每一天，会有一定数量的普通树木（枫树、橡树、松树和桃花心木树）随机地被苔藓覆盖。此外，有两种类型的苔雨树可以长出苔藓。这两种树的苔藓生长速度更快，当其覆盖苔藓并种植在普通树附近时，苔藓在普通树上的生长速度也加快。可以对这些树木使用任何工具，武器或炸弹来移除和收集苔藓，无须砍伐它们。一棵树会掉落一到两片苔藓。</p><p>用斧头砍伐一个挂有树液采集器的树的第一下会在掉落苔藓的同时移除采集器，然而用十字镐的第一击只会移除采集器。使用其他方法只会收集到苔藓，而不会击落采集器。</p><p>苔藓在苔雨期间变得更加常见，覆盖在大多数苔藓树木上。苔雨天气事件也会产生掉落苔藓的专属杂草，其有10%概率掉落苔藓。该天气下产生的大型杂草会掉落 2-3 个苔藓。</p><p>在夏天晴天，苔藓生长速度更慢，采集后回复速度也更慢。</p><p>值得注意的是，收获每一片苔藓都可以让你获得1点采集的经验。</p><p>可以将醋倒在树上阻止生长苔藓。</p>', NULL),
	(7074, 'harvest', 'HA0116', '苔藓的生长', '01', '<p>苔藓只能生长在完全长成的橡树、枫树、松树或种类1、2的苔雨树上。任何其他类型的树都不能长苔藓。树的生长周期也至少要达到14。</p><p>每天开始时，所有符合条件的树木都有概率生成苔藓。树长出苔藓的概率受天气和季节因素影响。苔藓在晴天的生成几率为10%，如果是夏季，则会降至3.3%。在雨天，生成几率为20%。</p><p>在满足以下条件之一时，树木将有50%的概率增加10%的苔藓生成概率：</p><ul><li>这棵树是一颗（非临时的）苔雨树。</li><li>以此树为中心的5×5方形区域内有一颗长着苔藓的1类或2类苔雨树。</li></ul><p>在苔雨期间，所有满足生成苔藓条件的树木都会立即长出苔藓，无论其生长阶段如何。</p><p>在冬季，苔藓不会生长（温室除外），并且树上所有预先存在的苔藓都会被清除。</p><p>从一颗树上收集苔藓将使此树生长阶段减少至第n阶段，n=12-掉落的苔藓数。</p>', NULL),
	(7075, 'harvest', 'HA0116', '收集包', '01', '<p>10 个苔藓可用于 工艺室（重新混合后）的 森林收集包。</p>', NULL),
	(7076, 'harvest', 'HA0116', '裁制', '01', '<p>苔藓不用于裁缝。苔藓可用作染料罐和缝纫机的<b>绿色</b>染料。</p>', NULL),
