REPLACE INTO `stardew_paper` (`id`, `sys_code`, `data_mode`, `name_ch`, `image_url`, `paper_note`, `paper_tip`, `paper_other`, `remark`) VALUES
	(1, 'PA0012', 1, '秘密纸条#12', NULL, '在走运的日子里，我在垃圾桶里找到了一些好东西。\n有时你会在星之果实餐吧的垃圾桶里找到“当日特供”……通常还很新鲜！\n\n至于甜点，我会翻翻乔治·莫尔纳家的垃圾桶，看看有没有饼干。\n\n珍宝的话，我会看看铁匠铺和博物馆旁的垃圾桶。', '-', '-', NULL),
	(2, 'PA0009', 1, '秘密纸条#9', NULL, '亚历克斯的力量训练食谱：\n*完美早餐\n*鲑鱼晚餐\n\n（我已学会爱上这些食物……我感受到了蛋白质的力量！！）', '-', '-', NULL),
	(3, 'PA0007', 1, '秘密纸条#7', NULL, '这是某人日记中的一页……\n……镇里只有少数几个“老”单身汉，他们都并不完美！哈维确实焦虑而软弱，但我知道他会成为一个忠诚专一的丈夫。他喜欢咖啡和腌菜。\n\n艾利欧特有些浮夸和矫情，但他的下巴确实很好看。他喜欢蟹黄糕和石榴。\n\n谢恩邋遢且反社会。但我认为他粗鲁的外在是一种保护机制，将他内心的柔软与世界隔离开来。他喜欢啤酒、披萨和爆炒青椒。', '-', '-', NULL),
	(4, 'PA0003', 1, '秘密纸条#3', NULL, '这是莉亚的笔迹写的。\n我对完美晚餐的构想是沙拉、山羊乳酪、松露和果酒。至于甜点，我需要一个虞美人籽松糕。好吃！如果有人给我其中之一，我会很感动的。', '-', '-', NULL),
	(5, 'PA0004', 1, '秘密纸条#4', NULL, '这是玛鲁的笔记\n我最伟大的发明还需要一些部件！\n\n*金锭\n*铱锭\n*电池组\n*钻石\n*草莓', '-', '-', NULL),
	(6, 'PA0005', 1, '秘密纸条#5', NULL, '看起来像是潘妮的笔迹：\n我想给每个人买他们喜欢的东西！\n\n妈妈：防风草、琉璃山药、不要啤酒！\n贾斯：玫瑰仙子、葡萄干布丁\n文森特：粉红蛋糕、葡萄\n莫尔纳先生：韭葱、炒蘑菇\n莫尔纳奶奶：甜菜、郁金香', '-', '-', NULL),
	(7, 'PA0025', 1, '秘密纸条#25', NULL, '我向妈妈借了一条项链，但在澡堂附近丢了……如果她发现项链不见了，肯定会大发雷霆！', '在春季、夏季、秋季的温泉外的水池处使用鱼竿，可获得华丽的项链。将它交给卡洛琳可以提升她的50点友谊值，或交给阿比盖尔可提升她的友谊值100点。', '-', NULL),
	(8, 'PA0013', 1, '秘密纸条#13', NULL, '每个季节最后一天的中午12点整，检查一下游乐场上方的灌木丛吧。', '在任何季节的28日中午12点之时，调查位于游玩场上方的树丛后可以获得一只祝尼魔毛绒玩具。', '-', NULL),
	(9, 'PA0021', 1, '秘密纸条#21', '/paper/st/SecretNote21.png', '-', '晚上12点40分，从鹈鹕镇到海滩的大桥西北边的第二个灌木丛。玛妮和刘易斯从里面跳了出来。 他们在里面做什么！？\n（注意：这一事件甚至可以在冬季16日完成，那时玛妮在从夜市返回的途中，正出现在屏幕上。）', '-', NULL),
	(10, 'PA0022', 1, '秘密纸条#22', NULL, '欢迎， <玩家角色>...\n你已在黑暗隧道中找到了我的“秘密”吗？\n\n我期待着与你会面！', '带着一个 电池组去到 巴士站，沿着巴士站那条路，一直往西方走进入隧道. 把电池组放在隧道中间墙壁的盒子里，然后开启神秘的齐任务..\n注意，找到秘密纸条 #22不是齐先生的任务触发的必要条件。', '-', NULL),
	(11, 'PA0014', 1, '秘密纸条#14', NULL, '我在社区中心背面藏了些东西。', '在社区中心背面被屋顶遮挡住，靠右侧木栅栏的地方, 藏有一个石祝尼魔雕像，可以使用镐子获得。', '-', NULL),
	(12, 'PA0018', 1, '秘密纸条#18', '/paper/st/SecretNote18.png', '-', '前往卡利科沙漠，使用锄头挖掘沙漠东南角的长椅旁左下角的地块，可以获得一个诡异玩偶（黄）.', '-', NULL),
	(13, 'PA0006', 1, '秘密纸条#6', NULL, '星之果实餐吧特别订单\n刘易斯镇长：秋日恩赐\n（配两份高纤维面包）\n\n玛妮：南瓜派\n（多加鲜奶油！）\n\n德米特里厄斯：豆类火锅\n（加辣）\n\n卡洛琳：鱼肉卷\n（她要三倍酱汁！最好多放几张餐巾纸……）', '-', '-', NULL),
	(14, 'PA0002', 1, '秘密纸条#2', NULL, '这是山姆的假日购物清单\n每个人的最爱塞巴斯蒂安：泪晶、生鱼片\n潘妮：绿宝石、虞美人花\n文森特：葡萄、蔓越莓糖果\n妈妈：香酥鲈鱼、薄煎饼\n爸爸：意式蕨菜炖饭、烤榛子\n我：仙人掌果子、枫糖棒、披萨', '-', '-', NULL),
	(15, 'PA0001', 1, '秘密纸条#1', NULL, '这是阿比盖尔日记中的一页\n“我喜欢的事物：雕刻南瓜的香气、在我的枕头下放块紫水晶、巧克力蛋糕、香辣鳗鱼的刺激，以及妈妈做的黑莓脆皮饼带来的慰藉（我爱吃！）”', '-', '-', NULL),
	(16, 'PA0011', 1, '秘密纸条#11', '/paper/st/SecretNote11.png', '-', '-', '-', NULL),
	(17, 'PA0019', 1, '秘密纸条#19', '/paper/st/SecretNote19.png', '-', '这是一幅1柳巷的图画和一系列箭头组成的纸条。从图上绿色的方块处出发，即这栋房子门口的那个位置，按照箭头的方向一直走到无法继续向前移动时转向下一个方向。首先向左走到障碍物，再向上走，直到碰到障碍物，以此类推。\n整个路线会引导着你绕过这栋房子，穿过小镇，最后找到藏在镇长刘易斯的家后面的刘易斯黄金像。雕像可以通过互动键获得。\n\n将雕像放置在鹈鹕镇上会解锁更多秘密。', '刘易斯黄金像可以被放置在鹈鹕镇的任意位置。如果雕像没有被路过的村民摧毁，第二天会被替换成腐烂的植物，玩家会收到一封未署名的信件附赠50金。之后雕像会出现在刘易斯的卧室（概率90%）或是玛妮的卧室（概率10%）。你可以将雕像收回并且反复放置在鹈鹕镇上，每一次都会被腐烂的植物取代。但未署名的信件只能收一次。', NULL),
	(18, 'PA0016', 1, '秘密纸条#16', '/paper/st/SecretNote16.png', '-', '在铁路场景中，挖一下在纸条的✘所在的位置，可以挖出一个财宝箱。', '-', NULL),
	(19, 'PA0010', 1, '秘密纸条#10', NULL, '有人\n在\n头骨山洞\n的\n第100层\n等你…', '阅读秘密纸条#10将会把"秘文纸条" 任务添加到任务日志里。为了完成任务，你要到达骷髅洞穴的第100层。一旦到了那里，就会出现一段剧情，齐先生会祝贺你在没有使用楼梯的情况“光荣地”达到了100层，或是对你使用楼梯的做法表示“很聪明”但不够“光荣”。 无论在哪种情况下，他都会奖励你一杯“铱蛇奶”，这会使你的生命值永久增加25点。\nBug: 在尚不明确的条件下，有可能发生以下情况：玩家获得并在同一天阅读了秘密纸条#10，然而保存的游戏文件不会记录其已被接收以及阅读，并且相关任务也不会添加到日志中。玩家下一次获得秘密纸条#10时，游戏可能会按照设计进行。', '-', NULL),
	(20, 'PA0020', 1, '秘密纸条#20', '/paper/st/SecretNote20.png', '-', '这是一幅小镇广场的图画。从广场中心出发，朝着图纸所指示的方向走，直到你碰到障碍物为止切换下一个方向。图纸的路线会引导你穿过小镇，跨过Joja超市旁的桥，最后到达Joja超市旁的卡车。与卡车互动会引导你与卡车司机进行对话。他会请求你给他带一个兔子的脚。如果你的背包里有这个物品，你可以换取一个特殊的魅力，它能永久给你增加运气。', '-', NULL),
	(21, 'PA0008', 1, '秘密纸条#8', NULL, '致海莉和艾米丽\n希望你们俩一切安好！给你们寄了最爱的礼物：海莉的粉红蛋糕和向日葵，艾米丽的宝石和动物毛！\n\n——爱你们的妈妈和爸爸。', '-', '-', NULL),
	(22, 'PA0024', 1, '秘密纸条#24', NULL, '这是M.贾思博的书中的一页：\n……这些生物，有一些人称之为“森林精灵”或是“祝尼魔”。据说它们会出现在废弃破败的建筑物之中。\n\n通常来说……当人类离开一个地方，大自然开始收回她的领土时，祝尼魔就一定会出现。\n\n民间传说认为，祝尼魔跟他们的小屋里放置的宝石会显示出某种共鸣关系……\n\n当然，这些说法的来源都很可疑，未经验证……据我所知，甚至还没有任何实际证据表明这种生物确实存在！', '将一块宝石放置在祝尼魔屋中后，收集作物的祝尼魔的颜色会改变。祝尼魔会与宝石的颜色保持一致，甚至在放入五彩碎片后会变化五彩的颜色。\n游戏中，让祝尼魔变色时使用的代码与在缝纫机上染色时的代码相同。可以在染色页面上找到完整的颜色表。', '-', NULL),
	(23, 'PA0017', 1, '秘密纸条#17', '/paper/st/SecretNote17.png', '-', '沿着Joja超市旁的河岸向北走到尽头后，用锄头挖掘最北的图块可以获得一个诡异玩偶（绿）', '-', NULL),
	(24, 'PA0023', 1, '秘密纸条#23', NULL, '如果伲渎到着个……到密谜竖林来。清带点沨唐酱。', '读取秘密纸条 #23的同时会将“奇怪纸条”的任务加入日志。在背包中放入枫糖浆，在6am到7pm之间进入秘密森林以触发剧情。一头熊会将它关于浆果的特殊知识分享给你。黑莓和美洲大树莓的价格会永久增加至原来的3倍。\n动画结束后，一个熊爪图标的“熊的知识”会出现在玩家的钱包内。', '-', NULL),
	(25, 'PA0015', 1, '秘密纸条#15', NULL, '美人鱼秀：1-5-4-2-3', '在 夜市期间，进入最右侧的船，看美人鱼秀。表演结束以后，按照纸条所示的顺序点击贝壳 (1为最左，5为最右) ，即可获得一个珍珠。', '-', NULL),
	(32, 'PA0026', 1, '秘密纸条#26', NULL, '《古代农耕秘辛》第37行：\r\n喂祝尼魔吃葡萄干，他们干活起来可带劲了......', '葡萄干是通过将5个葡萄放入烘干机中获得的，加工时间为1750分钟(≈29小时)。将葡萄干放置在祝尼魔屋中,会使祝尼魔有几率得到双倍收获。祝尼魔们每周会吃掉一个葡萄干，屋外还会挂一个小牌子。你可以在小屋里放置多个葡萄干，这些葡萄干会随着时间的推移而消耗。\r\n', '-', NULL),
	(33, 'PA0027', 1, '秘密纸条#27', NULL, '第37天\r\n亲爱的孩子：\r\n\r\n你找到这张纸条的时候，想必已经在星露谷住了很长一段时间了吧。希望你的生活一切顺利！\r\n\r\n星露谷对我来说是一个很亲切的地方......你能继承家里耕种的传统，用朴素的行动给这里带来生机和富饶，我感到非常光荣。\r\n\r\n继续加油吧！\r\n\r\n——爷爷\r\n\r\nP.S.......我在山谷的某个地方给你留了一个特别的秘密，那里汇集了我找到的最厉害的东西。等你准备好的时候，自然会找到的。\r\n\r\n', '在煤矿森林南部，开启下水道入口附近的精通山洞。', '-', NULL);
