REPLACE INTO `stardew_wallet` (`id`, `sys_code`, `name_ch`, `type`, `prop_usage`, `obtain`, `data_mode`) VALUES
	(77, 'WA0001', '森林魔法', '1', '可以与矿井中的矮人和火山地牢商店的矮人交流。', '完成任务“会见法师”获得', 1),
	(78, 'WA0002', '矮人语教程', '1', '可以与矿井中的矮人和火山地牢商店的矮人交流。', '通过捐赠全部4种矮人卷轴到博物馆获得', 1),
	(79, 'WA0003', '生锈的钥匙', '1', '用来进入下水道。', '向博物馆捐赠超过60件物品', 1),
	(80, 'WA0004', '会员卡', '1', '用来进入赌场。', '完成神秘的齐任务后,在房子旁边的木材堆处获得', 1),
	(81, 'WA0005', '特殊的魅力', '1', '永久提升每天的运气。', '秘密纸条#20', 1),
	(82, 'WA0006', '头骨钥匙', '1', '打开骷髅洞穴的门，解锁星之果实餐吧里的祝尼魔赛车游戏机。', '首次到达矿井的120层', 1),
	(83, 'WA0007', '放大镜', '1', '获得找到秘密纸条的能力。', '"冬日谜团"任务', 1),
	(84, 'WA0008', '黑暗护身符', '1', '任务物品，开放女巫小屋的三个祭坛。', '黑暗护身符可以在突变虫穴的箱子里找到。获得它是“黑暗护身符”任务的一部分。它可以用来打开铁路东北端的通道。', 1),
	(85, 'WA0009', '魔法墨水', '1', '任务物品，开放魔法建筑。', '可在女巫小屋的桌子上找到。进入女巫小屋需要先完成任务“哥布林问题”。如果玩家把魔法墨水交给法师，他会向你表示感谢，之后，玩家就可以通过法师塔中的一本书来购置几个新的农场建筑。', 1),
	(86, 'WA0010', '熊的知识', '1', '提升美洲大树莓及黑莓的价格为原来的3倍。', '若要获得熊的知识，玩家必须找到秘密纸条#23，然后携带枫糖浆前往秘密森林。之后会出现过场动画，其中，一只熊会来拿枫糖浆，并通过给予对浆果的特殊知识来感谢玩家。', 1),
	(87, 'WA0011', '青葱技术', '1', '提升大葱的价格为原来的五倍。', '玩家需要首先与文森特和贾斯的好感度均达到8颗心。然后在一个春天的晴天，上午6点至下午5点之间进入煤矿森林会触发过场动画：文森特会教给玩家如何清理大葱上的虫子。', 1),
	(88, 'WA0012', '小镇钥匙', '1', '允许玩家在任何时间进入小镇上的任何建筑物。', '齐先生的核桃房：20齐钻', 1),
	(89, 'WA0013', '价格目录', '2', '玩家可以看到物品的价值了。', '在书摊老板处以3,000金的价格购买。', 1),
	(90, 'WA0014', '洞穴地图绘制法', '2', '马龙取回物品时费用打5折。', '当玩家杀死1000只怪物后进入探险家公会的后方房间中，打开房间中的箱子获得。\n\n或在第三年以及之后的书摊老板处以20,000金购买（出现概率为~9%）。', 1),
	(91, 'WA0015', '风之道 第一部分', '2', '你跑步的速度变快了一点。', '在处书摊老板以15,000金购买。', 1),
	(92, 'WA0016', '风之道 第二部分', '2', '你跑步的速度变快了一点。', '在购买过风之道第一部分之后可以在处书摊老板以35,000金购买。', 1),
	(93, 'WA0017', '怪物图鉴', '2', '怪物有小概率掉落双倍战利品。', '由怪物掉落获取的能力之书。怪物掉落这本书的概率从0.01%开始计算，每杀死一只怪物增加0.015%，直到掉落为止。之后的获取概率会永久保持在0.05%。\n\n在玩家杀死10个以上怪物之前不会掉落这本书。从第3年开始，玩家也可以用20,000金从书摊老板处购买这本书（出现概率为~9%）。', 1),
	(94, 'WA0018', '交友导论', '2', '与人增进友谊的速度变快了一点。', '在镇长的庄园中的兑奖机兑换第9件奖品获得，或在第三年以及之后的书摊老板处以20,000金购买（出现概率为~9%）。', 1),
	(95, 'WA0019', '铜墙铁壁', '2', '获得+1防御。', '通过挖掘远古斑点获得，出现概率大约~0.8%，每挖掘一个远古斑点，出现概率就会增加0.2%，直到出现为止，后续出现的概率将会固定在1.3%。挖掘出2本后，它将不再会被挖出。在第三年以及之后的书摊老板处以20,000金购买（出现概率为~9%）。', 1),
	(96, 'WA0020', '伐木秘事', '2', '砍倒的树有5%的概率掉落双倍木头。', '砍倒树木时随机掉落，概率从0.03%开始递增，每次砍树增加0.001%，直到本书掉落，概率最多增加到0.1%。玩家至少要砍20颗树才能掉落本书。\n\n玩家也可以第三年之后在书摊老板处以20,000金的价格购买（出现概率为~9%）。', 1),
	(97, 'WA0021', '浣熊日记', '2', '杂草有更大的概率掉落混合种子。', '第二次完成浣熊任务时获得。在满足要求后，这本书也可以999个纤维在浣熊的妻子那里兑换。\n\n从第3年开始，玩家也可以用20,000金从书摊老板处购买这本书（出现概率为~9%）。', 1),
	(98, 'WA0022', '海之宝石', '2', '钓鱼有机会开出鱼籽。', '可以从钓鱼宝箱中获得，宝箱中包含这本书的概率从2%开始，每打开一个宝箱概率就会增加0.1%，直到玩家第一次获得为止。然后宝箱开出这本书的概率永久保持在2.1%。从第3年开始，玩家也可以用20,000金从书摊老板处购买这本书（出现概率~9%）。', 1),
	(99, 'WA0023', '矮人安全手册', '2', '炸弹对你造成的伤害减少25%。', '从矿井中矮人处以,000金购买（可以购买多次），你也可以在第三年及之后的书摊老板处以4,000金购买（出现概率为~9%）。', 1),
	(100, 'WA0024', '捕蟹秘籍', '2', '蟹笼有25%的概率产出双倍。', '在鱿鱼节通过钓鱼获得8只鱿鱼后领取铱星奖励获得。从第三年开始，玩家可以在书摊老板处花费20,000金购买。', 1),
	(101, 'WA0025', '小巷自助餐', '2', '在垃圾桶里找到物品的概率更高。', '可以在Joja超市和铁匠铺之间被树挡住的黄金垃圾桶中找到，需要通过一个隐藏在栅栏上的洞。进入该区域之后，需要升级到至少钢制斧头和钢制镐子才能清除道路上的大树桩和大石块。玩家也可以用20,000金从书摊老板处购买这本书（出现概率为~9%）。', 1),
	(102, 'WA0026', '钻石猎人', '2', '手动凿开石头有概率掉落钻石。', '在火山地牢第五层的矮人商店使用钻石（10）兑换。', 1),
	(103, 'WA0027', '谜之书', '2', '你找到谜之盒的概率稍微变高了一些。', '可以打开谜之盒或金色谜之盒获得。在谜之盒中的出现概率大约~1%，在金色谜之盒中的出现概率大约~2%，每打开一个谜之盒或金色谜之盒，出现概率就会增加0.04%，直到出现为止，后续谜之盒出现的概率将会固定在~0.5%，后续金色谜之盒出现的概率将会固定在~1%。在玩家打开10个谜之盒或金色谜之盒之前，此书不会从盒子里开出。', 1),
	(104, 'WA0028', '马术秘籍', '2', '骑马的速度稍微加快。', '在书摊老板处购买，价格为25,000金。', 1),
	(105, 'WA0029', '古代珍宝鉴定指南', '2', '出售古物时的价格更高了。', '通过打开古物宝藏获得（获得概率为~3.57%），也可以在第3年之后在书摊老板以20,000金购买（出现概率为~9%）。', 1),
	(106, 'WA0030', '草中窜', '2', '在草丛和庄稼中的移动速度大幅增加。', '可以从书摊老板处以25,000金购买获得。', 1),
	(107, 'WA0031', '动物目录', '2', '玛妮不在的时候也可以购买产品。', '可以在第2年及以后从玛妮处以5,000金购买。', 1),
	(108, 'WA0032', '星之书', '3', '阅读即可为所有技能增加250点经验。', '在书摊老板处以15,000金购买（出现概率为25%）。\n\n在完成浣熊的第5个请求的奖励中有概率获得（出现概率为20%）。\n\n当玩家已经获取过一本星之书后，可以在谜之盒和金色谜之盒中出现。', 1),
	(109, 'WA0033', '星露谷年历', '3', '阅读即可获得耕种经验。', '在书摊老板处以5,000金-10,000金的价格购买。\n\n在旅行商人处以6,000金的价格购买（出现概率为1%）。\n\n通过钓鱼宝箱获得，普通钓鱼宝箱概率为0.12%，金色钓鱼宝箱概率为1.5%。\n\n兑奖机有20%概率在第15位出现。\n\n通过打开谜之盒和金色谜之盒获得。\n\n击杀铱石魔有0.2%的概率掉落。\n\n击杀任何怪物有0.018%的概率掉落。\n\n在矿井、骷髅洞穴和火山地牢打破木箱和木桶有0.024%的概率掉落。\n\n通过摇晃树木有0.024%的概率随着种子一起掉落，或者通过每次砍伐树木有0.004%的概率掉落。\n\n挖掘远古斑点有0.11%的概率掉落。', 1),
	(110, 'WA0034', '鱼饵和浮漂', '3', '阅读即可获得250点钓鱼经验。', '在书摊老板处以5,000金-10,000金的价格购买。\n\n在旅行商人处以6,000金的价格购买（出现概率为1%）。\n\n通过钓鱼宝箱获得，普通钓鱼宝箱概率为0.12%，金色钓鱼宝箱概率为1.5%。\n\n兑奖机有20%概率在第15位出现。\n\n通过打开谜之盒和金色谜之盒获得。\n\n击杀铱石魔有0.2%的概率掉落。\n\n击杀任何怪物有0.018%的概率掉落。\n\n在矿井、骷髅洞穴和火山地牢打破木箱和木桶有0.024%的概率掉落。\n\n通过摇晃树木有0.024%的概率随着种子一起掉落，或者通过每次砍伐树木有0.004%的概率掉落。\n\n挖掘远古斑点有0.11%的概率掉落。', 1),
	(111, 'WA0035', '采矿月刊', '3', '阅读即可获得250点采矿经验。', '在书摊老板处以5,000金-10,000金的价格购买。\n\n在旅行商人处以6,000金的价格购买（出现概率为1%）。\n\n通过钓鱼宝箱获得，普通钓鱼宝箱概率为0.12%，金色钓鱼宝箱概率为1.5%。\n\n兑奖机有20%概率在第15位出现。\n\n通过打开谜之盒和金色谜之盒获得。\n\n击杀铱石魔有0.2%的概率掉落。\n\n击杀任何怪物有0.018%的概率掉落。\n\n在矿井、骷髅洞穴和火山地牢打破木箱和木桶有0.024%的概率掉落。\n\n通过摇晃树木有0.024%的概率随着种子一起掉落，或者通过每次砍伐树木有0.004%的概率掉落。\n\n挖掘远古斑点有0.11%的概率掉落。', 1),
	(112, 'WA0036', '战斗季刊', '3', '阅读即可获得250点战斗经验。', '在书摊老板处以5,000金-10,000金的价格购买。\n\n在旅行商人处以6,000金的价格购买（出现概率为1%）。\n\n通过钓鱼宝箱获得，普通钓鱼宝箱概率为0.12%，金色钓鱼宝箱概率为1.5%。\n\n兑奖机有20%概率在第15位出现。\n\n通过打开谜之盒和金色谜之盒获得。\n\n击杀铱石魔有0.2%的概率掉落。\n\n击杀任何怪物有0.018%的概率掉落。\n\n在矿井、骷髅洞穴和火山地牢打破木箱和木桶有0.024%的概率掉落。\n\n通过摇晃树木有0.024%的概率随着种子一起掉落，或者通过每次砍伐树木有0.004%的概率掉落。\n\n挖掘远古斑点有0.11%的概率掉落。', 1),
	(113, 'WA0037', '樵夫周刊', '3', '阅读即可获得250点采集经验。', '在书摊老板处以5,000金-10,000金的价格购买。\n\n在旅行商人处以6,000金的价格购买（出现概率为1%）。\n\n通过钓鱼宝箱获得，普通钓鱼宝箱概率为0.12%，金色钓鱼宝箱概率为1.5%。\n\n兑奖机有20%概率在第15位出现。\n\n通过打开谜之盒和金色谜之盒获得。\n\n击杀铱石魔有0.2%的概率掉落。\n\n击杀任何怪物有0.018%的概率掉落。\n\n在矿井、骷髅洞穴和火山地牢打破木箱和木桶有0.024%的概率掉落。\n\n通过摇晃树木有0.024%的概率随着种子一起掉落，或者通过每次砍伐树木有0.004%的概率掉落。\n\n挖掘远古斑点有0.11%的概率掉落。', 1),
	(114, 'WA0038', '酱料女皇烹饪秘籍', '3', '可以学习你尚未掌握的酱料女皇食谱。', '玩家可以在收集了100个金色核桃后，在书摊老板处以50,000金的价格购买。', 1),
	(115, 'WA0039', '耕种精通', '4', '你可以找到金色动物饼干了，它可以使一只农场动物的产量永久翻倍，对猪无效。', '在玩家所有五项技能达到最高水平后解锁。消耗精通点数用于解锁一系列精通技能。', 1),
	(116, 'WA0040', '采矿精通', '4', '宝石矿的产量翻倍了。', '在玩家所有五项技能达到最高水平后解锁。消耗精通点数用于解锁一系列精通技能。', 1),
	(117, 'WA0041', '采集精通', '4', '你可以找到金色谜之盒了，里面装有优质物品。', '在玩家所有五项技能达到最高水平后解锁。消耗精通点数用于解锁一系列精通技能。', 1),
	(118, 'WA0042', '钓鱼精通', '4', '你可以钓到金色钓鱼宝箱了。', '在玩家所有五项技能达到最高水平后解锁。消耗精通点数用于解锁一系列精通技能。', 1),
	(119, 'WA0043', '战斗精通', '4', '解锁了一个新的饰品装备栏。饰品会赋予特殊能力，在探险时可以找到。', '在玩家所有五项技能达到最高水平后解锁。消耗精通点数用于解锁一系列精通技能。', 1);
REPLACE INTO `stardew_walnut` (`id`, `name_ch`, `remark`, `walnut_num`, `type`) VALUES
	(1, '金色椰子', '金色椰子可在姜岛的各地找到（挖掘远古斑点、摇动棕榈树等方式），也可以找姜岛商人，用 10 个椰子交换获得一个金色椰子。首个椰子必为核桃。', 1, '01'),
	(2, '钓鱼', '在姜岛任何地方钓鱼都有几率获得金色核桃，一共可以获得 5 个。每次钓鱼有 15% 机会，直到收集完 5 个。', 5, '01'),
	(3, '兑换奖励', '{"header":["奖励","描述","位置","花费"],"rowData":[["N/A","解锁通往姜岛北部的通道。","雷欧的房子","1"],["叫醒乌龟","解锁通往姜岛西部的通道。","姜岛南部","10"],["修好睡觉小屋","解锁姜岛西部农场的房屋，可在里面睡觉从而在岛上度过当日（即第二个家）。","姜岛农场小屋","20"],["传递信件","可以在姜岛查看信件。","姜岛农场","5"],["建个传送塔","建设将玩家传送回农场的建筑农场尖塔（仅在修好睡觉小屋后可用），类似于传送图腾。","姜岛农场","20"],["修好桥","修复去挖掘场之桥梁。间接解锁岛屿办事处。","姜岛北部","10"],["建造贸易小屋","解锁姜岛商人的商店。","姜岛北部","10"],["建座桥","解锁通往火山内部的永久桥梁，使玩家不再需要浇水即可进入火山内部。","火山地牢入口","5"],["开条捷径","在火山地牢第 5 层挖一个出口到姜岛北部（单向出口）。","火山地牢第 5 层","5"],["建个度假村","在港口附近建设度假村。鹈鹕镇村民可能会过来。","姜岛南部","20"],["鹦鹉特快","开启岛上传送系统鹦鹉特快（类似于矿车）。","姜岛","10"],["齐钻（2）","把建设后剩下的金核桃换成齐钻。仅在购买其他所有后可用","齐先生的核桃房","1"]]}', 1, '06'),
	(4, '探索', '在前往雷欧家的丛林中有一棵核桃树。', 1, '02'),
	(5, '解谜', '在通往雷欧家台阶左侧的祭坛内放一根香蕉会有一只大猩猩出现并奖励玩家 3 个金色核桃。', 3, '02'),
	(6, '解谜', '用斧头砍一下雷欧家的树。', 1, '02'),
	(7, '探索', '跟着丛林中的石头小径向右走进入宝石鸟神龛，在这个区域的南侧有一棵核桃树。', 1, '02'),
	(8, '解谜', '在姜岛东部丛林跟着跟着石头小径向右上角走，有一条不明显的小路通往一个隐藏的神龛，里面有四尊鸟的雕像。每尊雕像上面刻着对应姜岛某一部分的图案（火山代表北部，海浪代表南部，椰子树代表东部，圆形图案代表西部）。\n每当岛上下雨时，一只宝石鸟会在岛的东南西北某一方位出现。靠近这只鸟将会把它惊起，留下一枚宝石。玩家需要将宝石放置在对应的雕像上。\n\n四只宝石鸟出现在以下位置：\n\n岛屿北部：岛屿办事处右下角两棵树的位置。\n岛屿东部：在丛林中到雷欧的小屋的路中间位置\n岛屿南部：玩家登陆岛屿时的码头所在的沙滩左下角位置。\n岛屿西部：农场左边小湖的右侧几棵树中间。\n一共有5种可能的宝石：海蓝宝石，黄水晶，紫水晶，绿宝石 和 红宝石。\n\n在玩家放置最后 1 颗宝石后，神龛会被激活并奖励玩家 5 个金色核桃。\n\n注意：每个存档各个方向对应的宝石是随机的，且放置的宝石不需要与鸟类掉落的宝石相同——来自任何来源的正确类型的宝石都可以使用。', 5, '02'),
	(9, '探索', '先去岛屿北部入口，然后按图片上的路线，向右走到第一棵树那里，再向下走有一条被树挡起来的隐藏小路，继续往下就到了岛屿南部的峭壁上，那里有一棵核桃树。', 1, '05'),
	(10, '钓鱼', '在花 20 个核桃修好南边的度假村后，就可以前往岛屿的东南方向的海湾。海湾中间有一个星型的潮水池，可以在里面钓鱼得到 1 个金色核桃。', 1, '05'),
	(11, '探索', '在花 20 个核桃修好南边的度假村后，就可以前往岛屿的东南方向的海湾。在海湾的右上角有被星星和月亮图案围起来的一片区域，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '05'),
	(12, '解谜', '在花 20 个核桃修好南边的度假村后，就可以前往岛屿的东南方向的海湾。日记残页 #9所提到的，下雨时会有一条美人鱼坐在海岸旁的巨大岩石上，演奏出美人鱼之歌会奖励 5 个金色核桃。为了演奏美人鱼之歌需要将 5 个长笛块放置在 5 个对应的小石块上。每个小石块里，大的石头代表 5，小的石头代表 1，因此，正确的音调为 2-11-9-5-7。调整音调可以使用右键点击对应长笛块，初始音调为 1，例如调到 11 就要右键点击长笛块 10 次。全部调整好之后，按照正确的音调演奏，美人鱼就会奖励 5 个金色核桃。', 5, '05'),
	(13, '解谜', '在花 20 个核桃修好南边的度假村后，就可以前往岛屿的东南方向的海湾。在海湾右上角一块木板的位置可以进入海盗湾。在偶数天且非下雨天气的晚上 8 点后，海盗会出现在海盗湾。与海盗湾内右侧的海盗对话以开始飞镖小游戏。每次完成小游戏的目标（依次为 20 镖、15 镖、10 镖内投出 301 分整）会奖励玩家一个金色核桃，一共可以获得 3 个。', 3, '05'),
	(14, '探索', '在花 20 个核桃修好南边的度假村后，就可以前往岛屿的东南方向的海湾。在海湾右上角一块木板的位置可以进入海盗湾。在海盗湾的右下角位置有被几个木桶围起来的一小块空地，可以使用锄头挖出 1 个金色核桃。', 1, '05'),
	(16, '探索', '前往岛屿北部最南边的台阶入口，向左上角走，在一棵从峭壁上突出来的棕榈树下面有一圈石头，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(17, '探索', '前往岛屿北部最南边的台阶入口，向左上角走，有一条被树林挡住的路，按照图片标识的路线进去，有一棵核桃树。', 1, '04'),
	(18, '探索', '前往岛屿北部最南边的台阶入口，在台阶与姜岛商人中间有一棵桃花心木树和一棵棕榈树,两棵树中间有一圈小草，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(19, '探索', '前往岛屿北部最南边的台阶入口，然后向右边走，有一棵从峭壁上突出来的棕榈树旁边还有另一棵棕榈树，这棵树旁边有一圈石头，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(20, '探索', '在岛屿办事处右下角有两棵棕榈树，这两棵树的右上角的沙地上有一个浅色的纹理，可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(21, '探索', '前往挖掘场（需要花费 10 个核桃修好桥才能前往），右边有个洞穴，从洞穴旁边的台阶上去，有一圈石头，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(22, '探索', '前往挖掘场（需要花费 10 个核桃修好桥才能前往），右边有个洞穴，从洞穴旁边的台阶上去再向左走，通过一座桥后有一棵核桃树。', 1, '04'),
	(23, '探索', '前往挖掘场（需要花费 10 个核桃修好桥才能前往），右边有个洞穴，从洞穴旁边的台阶上去后右边的峭壁里有一条隐藏的路，按图示路线过去有两棵核桃树。', 2, '04'),
	(24, '岛屿办事处', '完成岛屿办事处的巨大动物化石捐赠以获得 6 个金色核桃。', 6, '04'),
	(25, '岛屿办事处', '完成岛屿办事处的蛇化石捐赠以获得 3 个金色核桃。', 3, '04'),
	(26, '岛屿办事处', '完成岛屿办事处的青蛙木乃伊捐赠以获得 1 个金色核桃。', 1, '04'),
	(27, '岛屿办事处', '完成岛屿办事处的蝙蝠木乃伊捐赠以获得 1 个金色核桃。', 1, '04'),
	(28, '岛屿办事处', '完成岛屿办事处的紫色花的岛屿调查，一共有 22 朵紫色花，完成后奖励 1 个金色核桃。', 1, '04'),
	(29, '岛屿办事处', '完成岛屿办事处的紫色海星的岛屿调查，一共有 18 只紫色海星，完成后奖励 1 个金色核桃。', 1, '04'),
	(30, '探索', '前往火山地牢的入口，右下角有一棵棕榈树，旁边有一棵被棕榈树挡起来的核桃树。', 1, '04'),
	(31, '探索', '前往鹦鹉特快的火山传送点，左下方向有一圈石头，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(32, '探索', '前往鹦鹉特快的火山传送点，左侧峭壁上有一棵突出来的棕榈树，使用弹弓可以打下来一个核桃，日记残页 #3里也有隐晦地提到。', 1, '04'),
	(33, '日记残页 #10', '藏宝点位于火山地牢入口外左下角一棵从峭壁上突出来的棕榈树上面，用锄头挖掘可以获得一枚鸵鸟蛋和一个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '04'),
	(34, '探索', '地图最左上角有两棵从火山上突出来的棕榈树，旁边有一圈石头，中间的空地可以使用锄头挖出 1 个金色核桃。', 1, '04'),
	(35, '探索', '从火山地牢左侧的一条隐蔽小路可以进去树林内，按照图片标识的路线移动有一棵核桃树。', 1, '04'),
	(36, '探索', '先进入火山地牢，在岩浆中用喷壶浇水浇出一条路到最左边有一个出口，出去之后回到外面有两棵核桃树。', 2, '04'),
	(37, '采矿', '在火山地牢内采矿有几率获得金色核桃，一共可以获得 5 个。', 5, '04'),
	(38, '击杀怪物', '在火山地牢内击杀怪物有几率获得金色核桃，一共可以获得 5 个。', 5, '04'),
	(39, '金属板条箱', '在火山地牢内打破如图所示的金属板条箱时，有几率获得金色核桃，一共可以获得 5 个。', 5, '04'),
	(40, '火山宝箱', '在火山地牢内打开如图所示的火山地牢宝箱，有几率获得金色核桃。一共可以获得 2 个，普通宝箱和稀有宝箱中各一个。', 2, '04'),
	(41, '探索', '到达火山地牢的第10层。有两棵核桃树分别位于第 10 层的入口和出口。', 2, '04'),
	(42, '收获作物', '收获农场的一次性的农作物时有几率获得金色核桃，一共可以获得 5 个。\n注意：需要种植（不会重新生长的）一次性且非镰刀收获的作物，如芋头等。使用铱镰刀收割一次性的农作物也能获得金色核桃。', 5, '03'),
	(43, '解谜', '姜岛的饕餮青蛙会依次要求玩家在姜岛的农场种植3种它描述的特定作物。当每种作物成熟以后，玩家与之对话可以获得 5 个金色核桃奖励。\n每次需要种植的作物如下：\n\n“李给窝种……粉红色的东西……多兹的辣个……” ：甜瓜；\n“窝想要看到辣个长的、黄色的、挠痒痒滴东西……辣个在风中摇晃滴东西……” ：小麦；\n“窝想要次辣个很刺激的地上的果子，会浪口气变臭的辣个……” ：蒜。\n注意：3 种作物可以一起种下，但是务必先与青蛙对话获得奖励再收获作物。', 15, '03'),
	(44, '日记残页 #6', '藏宝点位于小屋南边的海滩，用锄头挖掘可以获得一颗珍珠和一个金色核桃。', 1, '03'),
	(45, '采矿', '开采岛屿西部沙滩上的蚌矿，有几率获得金色核桃，一共可以获得 5 个。', 5, '03'),
	(46, '探索', '岛屿西部沙滩岸边有一艘沉船，可以从沉船的左上角的隐藏小路进去，里面有一棵核桃树。', 1, '03'),
	(47, '解谜', '岛屿西部沙滩有一个小的绿色怪物在几个地洞之间钻来钻去，可以使用升级后的喷壶蓄力浇水或使用可放置物品堵住洞口击杀该怪物来获取 1 个金色核桃。', 1, '03'),
	(48, '探索', '找到图片中姜岛农场南边的沙滩上的 3 个海星围起来的区域，在中间有一块空地，可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '03'),
	(49, '探索', '找到图片中西侧沙滩的潮水池旁边 4 个海星围起来的菱形区域，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '03'),
	(50, '探索', '找到图片中西侧沙滩的潮水池中间的 X 形状标记，可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '03'),
	(52, '探索', '位于姜岛农场西边的池塘左下角的椰子树后面有一棵核桃树。', 1, '03'),
	(53, '任务', '完成海盗的妻子该任务以获得 5 个金色核桃。', 5, '03'),
	(54, '日记残页 #4', '藏宝点位于贝啼的小屋北边，用锄头挖掘可以获得一个优质浮标和一个金色核桃。', 1, '03'),
	(55, '探索', '在齐先生的核桃房的大门右下角的峭壁下有一棵核桃树。', 1, '03'),
	(56, '探索', '在齐先生的核桃房左侧海滩边缘有一条隐藏的小路，可以一直走上去，最上面有一棵核桃树。', 1, '03'),
	(57, '击杀怪物', '在老虎史莱姆小树林中击杀老虎史莱姆有几率获得 1 个金色核桃。', 1, '03'),
	(58, '探索', '在老虎史莱姆小树林中的一棵桃花心木树后面有棵核桃树。', 1, '03'),
	(60, '探索', '在老虎史莱姆小树林中的一棵桃花心木树后面有被一圈草围着的一块空地，可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '03'),
	(61, '探索', '前往鹦鹉特快位于农场的传送点左侧，在图片上桥的左边有一棵被树林挡住的核桃树。', 1, '03'),
	(62, '解谜', '完成位于老虎史莱姆小树林东北侧洞穴内的五彩水晶谜题以获得 3 个金色核桃。右侧的图片展示了从鹦鹉特快在农场的传送点到达洞穴入口的路线。\n玩家进入洞穴后，点击中间的狮子石像，开启一个小游戏。玩家需要记住五个水晶的闪烁顺序，并按该顺序点击水晶。小游戏每一轮都会更快（共五轮，依次为 3-7 次点击），完成这个小游戏之后，玩家可以获得 3 个金色核桃。\n\n中途失败一次都只能重新开始。', 3, '03'),
	(63, '探索', '前往老虎史莱姆小树林，登上台阶后向右顺着峭壁边缘走有一条小路，在最后有一棵核桃树。', 1, '03'),
	(64, '探索', '前往鹦鹉特快位于农场的传送点右侧，找到被鹅卵石形状图案围起来的菱形区域，中间的空地可以使用锄头挖出 1 个金色核桃。', 1, '03'),
	(66, '探索', '前往鹦鹉特快位于农场的传送点右侧，按着图片中杂草的路线可以走进去，走到最右上角，有一棵隐藏的核桃树，鼠标右键点击一下（有摇树的音效），会获得 1 个金色核桃。', 1, '03'),
	(67, '探索', '前往鹦鹉特快位于农场的传送点左侧，可以从那条小河右侧的悬崖上往下走，有一条被树林挡住的小路，有一棵核桃树。', 1, '03'),
	(68, '探索', '找到图片中西侧沙滩的潮水池左下角沙子凹痕围起来的菱形区域，中间的空地可以使用锄头挖出 1 个金色核桃。\n注意：这只能由多人游戏农场的主人来做。', 1, '03');
